INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('a843d5608b4c8adb601c7b9814a167440acc2955', '{"url": "https://www.semanticscholar.org/paper/a843d5608b4c8adb601c7b9814a167440acc2955", "year": 2021, "title": "The anterior paired lateral neuron normalizes odour-evoked activity in the Drosophila mushroom body calyx.", "venue": "eLife", "authors": [{"name": "Luigi Prisco", "authorId": "1897126552"}, {"name": "S. Deimel", "authorId": "2097772486"}, {"name": "Hanna Yeliseyeva", "authorId": "2128140709"}, {"name": "A. Fiala", "authorId": "144958832"}, {"name": "G. Tavosanis", "authorId": "4653559"}], "journal": {"name": "eLife", "volume": "10"}, "paperId": "a843d5608b4c8adb601c7b9814a167440acc2955", "abstract": "To identify and memorize discrete but similar environmental inputs, the brain needs to distinguish between subtle differences of activity patterns in defined neuronal populations. The Kenyon cells of the Drosophila adult mushroom body (MB) respond sparsely to complex olfactory input, a property that is thought to support stimuli discrimination in the MB. To understand how this property emerges, we investigated the role of the inhibitory anterior paired lateral neuron (APL) in the input circuit of the MB, the calyx. Within the calyx, presynaptic boutons of projection neurons (PNs) form large synaptic microglomeruli (MGs) with dendrites of postsynaptic Kenyon cells (KCs). Combining EM data analysis and in vivo calcium imaging, we show that APL, via inhibitory and reciprocal synapses targeting both PN boutons and KC dendrites, normalizes odour-evoked representations in MGs of the calyx. APL response scales with the PN input strength and is regionalized around PN input distribution. Our data indicate that the formation of a sparse code by the Kenyon cells requires APL-driven normalization of their MG postsynaptic responses. This work provides experimental insights on how inhibition shapes sensory information representation in a higher brain centre, thereby supporting stimuli discrimination and allowing for efficient associative memory formation.", "citations": [{"title": "Sleep benefits different stages of memory in Drosophila", "paperId": "c9ac00e60c49e275da8de38e9584066529fdd7d3"}], "externalIds": {"DOI": "10.7554/eLife.74172", "PubMed": "34964714", "CorpusId": 245538677}, "isOpenAccess": false, "citationCount": 1, "fieldsOfStudy": ["Medicine"], "openAccessPdf": null, "citationStyles": {"bibtex": "@[''JournalArticle'']{Prisco2021TheAP,\n author = {Luigi Prisco and S. Deimel and Hanna Yeliseyeva and A. Fiala and G. Tavosanis},\n booktitle = {eLife},\n journal = {eLife},\n title = {The anterior paired lateral neuron normalizes odour-evoked activity in the Drosophila mushroom body calyx.},\n volume = {10},\n year = {2021}\n}\n"}, "referenceCount": 120, "publicationDate": "2021-12-29", "s2FieldsOfStudy": [{"source": "external", "category": "Medicine"}, {"source": "s2-fos-model", "category": "Biology"}], "publicationTypes": ["JournalArticle"], "publicationVenue": {"id": "07365b9a-c0ce-4dd3-b93b-a02e1c81e0c6", "url": "https://epub.uni-regensburg.de/40444/", "issn": "2050-084X", "name": "eLife", "type": "journal", "alternate_urls": ["https://elifesciences.org/", "https://elife.elifesciences.org/", "http://elifesciences.org/"]}, "influentialCitationCount": 0}', '2023-01-27 10:46:29.282475', '2023-01-27 10:46:29.282475', 'The anterior paired lateral neuron normalizes odour-evoked activity in the Drosophila mushroom body calyx.', 2021, false, null, null, null, null);
INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('8648a09611db310d6040ffa0788e6c86d66038fa', '{"url": "https://www.semanticscholar.org/paper/8648a09611db310d6040ffa0788e6c86d66038fa", "year": 2021, "title": "Structured connectivity in the cerebellum enables noise-resilient pattern separation", "venue": "bioRxiv", "authors": [{"name": "Tri M Nguyen", "authorId": "2116108808"}, {"name": "Logan A. Thomas", "authorId": "78587807"}, {"name": "Jeffrey L. Rhoades", "authorId": "152611723"}, {"name": "Ilaria Ricchi", "authorId": "2106212514"}, {"name": "X. C. Yuan", "authorId": "2151461271"}, {"name": "Arlo Sheridan", "authorId": "49636458"}, {"name": "David Grant Colburn Hildebrand", "authorId": "49280828"}, {"name": "Jan Funke", "authorId": "1890094"}, {"name": "W. Regehr", "authorId": "5639106"}, {"name": "W. Lee", "authorId": "2004120"}], "journal": {"name": "bioRxiv"}, "paperId": "8648a09611db310d6040ffa0788e6c86d66038fa", "abstract": "The cerebellum is thought to detect and correct errors between intended and executed commands1–3 and is critical for social behaviors, cognition and emotion4–6. Computations for motor control must be performed quickly to correct errors in real time and should be sensitive to small differences between patterns for fine error correction while being resilient to noise7. Influential theories of cerebellar information processing have largely assumed random network connectivity, which increases the encoding capacity of the network’s first layer8–13. However, maximizing encoding capacity reduces resiliency to noise7. To understand how neuronal circuits address this fundamental tradeoff, we mapped the feedforward connectivity in the mouse cerebellar cortex using automated large-scale transmission electron microscopy (EM) and convolutional neural network-based image segmentation. We found that both the input and output layers of the circuit exhibit redundant and selective connectivity motifs, which contrast with prevailing models. Numerical simulations suggest these redundant, non-random connectivity motifs increase discriminability of similar input patterns at a minimal cost to the network’s overall encoding capacity. This work reveals how neuronal network structure can balance encoding capacity and redundancy, unveiling principles of biological network architecture with implications for artificial neural network design.", "citations": [{"title": "Automated Synapse Detection Method for Cerebellar Connectomics", "paperId": "4840bf9e2efc4ced1eb5151864853f5a909d4de3"}], "externalIds": {"DOI": "10.1101/2021.11.29.470455", "CorpusId": 244802223}, "isOpenAccess": true, "citationCount": 1, "fieldsOfStudy": ["Biology"], "openAccessPdf": {"url": "https://www.biorxiv.org/content/biorxiv/early/2021/11/30/2021.11.29.470455.full.pdf", "status": "GREEN"}, "citationStyles": {"bibtex": "@None{Nguyen2021StructuredCI,\n author = {Tri M Nguyen and Logan A. Thomas and Jeffrey L. Rhoades and Ilaria Ricchi and X. C. Yuan and Arlo Sheridan and David Grant Colburn Hildebrand and Jan Funke and W. Regehr and W. Lee},\n booktitle = {bioRxiv},\n journal = {bioRxiv},\n title = {Structured connectivity in the cerebellum enables noise-resilient pattern separation},\n year = {2021}\n}\n"}, "referenceCount": 102, "publicationDate": "2021-11-30", "s2FieldsOfStudy": [{"source": "external", "category": "Biology"}, {"source": "s2-fos-model", "category": "Computer Science"}], "publicationTypes": null, "publicationVenue": {"id": "027ffd21-ebb0-4af8-baf5-911124292fd0", "url": "http://biorxiv.org/", "name": "bioRxiv", "type": "journal"}, "influentialCitationCount": 0}', '2023-01-27 10:46:32.952358', '2023-01-27 10:46:32.952358', 'Structured connectivity in the cerebellum enables noise-resilient pattern separation', 2021, false, null, null, null, null);
INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('c5536ef56db86a7f89d6b9d0eb3db894481c3372', '{"url": "https://www.semanticscholar.org/paper/c5536ef56db86a7f89d6b9d0eb3db894481c3372", "year": 2021, "title": "Local olfactory interneurons provide the basis for neurochemical regionalization of olfactory glomeruli in crustaceans", "venue": "The Journal of comparative neurology", "authors": [{"name": "S. Harzsch", "authorId": "4437028"}, {"name": "H. Dircksen", "authorId": "6813610"}, {"name": "B. Hansson", "authorId": "2199148"}], "journal": {"name": "Journal of Comparative Neurology", "pages": "1399 - 1422", "volume": "530"}, "paperId": "c5536ef56db86a7f89d6b9d0eb3db894481c3372", "abstract": "The primary olfactory centers of metazoans as diverse as arthropods and mammals consist of an array of fields of dense synaptic neuropil, the olfactory glomeruli. However, the neurochemical structure of crustacean olfactory glomeruli is largely understudied when compared to the insects. We analyzed the glomerular architecture in selected species of hermit crabs using immunohistochemistry against presynaptic proteins, the neuropeptides orcokinin, RFamide and allatostatin, and the biogenic amine serotonin. Our study reveals an unexpected level of structural complexity, unmatched by what is found in the insect olfactory glomeruli. Peptidergic and aminergic interneurons provide the structural basis for a regionalization of the crustacean glomeruli into longitudinal and concentric compartments. Our data suggest that local olfactory interneurons take a central computational role in modulating the information transfer from olfactory sensory neurons to projection neurons within the glomeruli. Furthermore, we found yet unknown neuronal elements mediating lateral inhibitory interactions across the glomerular array that may play a central role in modulating the transfer of sensory input to the output neurons through presynaptic inhibition. Our study is another step in understanding the function of crustacean olfactory glomeruli as highly complex units of local olfactory processing.", "citations": [], "externalIds": {"DOI": "10.1002/cne.25283", "PubMed": "34843626", "CorpusId": 244750700}, "isOpenAccess": false, "citationCount": 0, "fieldsOfStudy": ["Medicine"], "openAccessPdf": null, "citationStyles": {"bibtex": "@[''JournalArticle'']{Harzsch2021LocalOI,\n author = {S. Harzsch and H. Dircksen and B. Hansson},\n booktitle = {The Journal of comparative neurology},\n journal = {Journal of Comparative Neurology},\n pages = {1399 - 1422},\n title = {Local olfactory interneurons provide the basis for neurochemical regionalization of olfactory glomeruli in crustaceans},\n volume = {530},\n year = {2021}\n}\n"}, "referenceCount": 144, "publicationDate": "2021-11-29", "s2FieldsOfStudy": [{"source": "external", "category": "Medicine"}, {"source": "s2-fos-model", "category": "Biology"}], "publicationTypes": ["JournalArticle"], "publicationVenue": null, "influentialCitationCount": 0}', '2023-01-27 10:46:36.577273', '2023-01-27 10:46:36.577273', 'Local olfactory interneurons provide the basis for neurochemical regionalization of olfactory glomeruli in crustaceans', 2021, false, null, null, null, null);
