INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('a6513abc3f4149703241664319af18f070d44137', '{"url": "https://www.semanticscholar.org/paper/a6513abc3f4149703241664319af18f070d44137", "year": 2020, "title": "The neural basis for a persistent internal state in Drosophila females", "venue": "bioRxiv", "authors": [{"name": "David S Deutsch", "authorId": "2028241054"}, {"name": "Diego A. Pacheco", "authorId": "7587012"}, {"name": "Lucas Encarnacion-Rivera", "authorId": "1926231192"}, {"name": "T. Pereira", "authorId": "39103529"}, {"name": "R. Fathy", "authorId": "82263611"}, {"name": "Adam J. Calhoun", "authorId": "4252392"}, {"name": "Elise C. Ireland", "authorId": "2028267155"}, {"name": "A. Burke", "authorId": "1579777445"}, {"name": "Sven Dorkenwald", "authorId": "7997808"}, {"name": "Claire E McKellar", "authorId": "7667018"}, {"name": "T. Macrina", "authorId": "5056982"}, {"name": "Ran Lu", "authorId": "1406193129"}, {"name": "Kisuk Lee", "authorId": "2785521"}, {"name": "Nico Kemnitz", "authorId": "41124209"}, {"name": "Dodam Ih", "authorId": "14972923"}, {"name": "Manuel A. Castro", "authorId": "2092627229"}, {"name": "Akhilesh Halageri", "authorId": "1579775589"}, {"name": "Chris S. Jordan", "authorId": "41130241"}, {"name": "W. Silversmith", "authorId": "2108734"}, {"name": "Jingpeng Wu", "authorId": "31421132"}, {"name": "H. Seung", "authorId": "144924970"}, {"name": "M. Murthy", "authorId": "145776127"}], "journal": {"name": "eLife", "volume": "9"}, "paperId": "a6513abc3f4149703241664319af18f070d44137", "abstract": "Sustained changes in mood or action require persistent changes in neural activity, but it has been difficult to identify and characterize the neural circuit mechanisms that underlie persistent activity and contribute to long-lasting changes in behavior. Here, we focus on changes in the behavioral state of Drosophila females that persist for minutes following optogenetic activation of a single class of central brain neurons termed pC1. We find that female pC1 neurons drive a variety of persistent behaviors in the presence of males, including increased receptivity, shoving, and chasing. By reconstructing cells in a volume electron microscopic image of the female brain, we classify 7 different pC1 cell types and, using cell type specific driver lines, determine that one of these, pC1-Alpha, is responsible for driving persistent female shoving and chasing. Using calcium imaging, we locate sites of minutes-long persistent neural activity in the brain, which include pC1 neurons themselves. Finally, we exhaustively reconstruct all synaptic partners of a single pC1-Alpha neuron, and find recurrent connectivity that could support the persistent neural activity. Our work thus links minutes-long persistent changes in behavior with persistent neural activity and recurrent circuit architecture in the female brain.", "citations": [{"title": "Modulation and Neural Correlates of Postmating Sleep Plasticity in Drosophila Females", "paperId": "49fccc03f04ed963948f64c4cf76459163a2f299"}, {"title": "Flexibility of neural circuits regulating mating behaviors in mice and flies", "paperId": "459debf1e78a0990b2ca27c70080d0ddb7fc6bf9"}, {"title": "Connectomics and the neural basis of behaviour", "paperId": "e67ce2076b1edb61228f9401bda7a0ea81f3055a"}, {"title": "Neural Control of Action Selection Among Innate Behaviors", "paperId": "219077e2b7c483024ac9c87a90cd7f0b6ffdbb1e"}, {"title": "The emergence and influence of internal states", "paperId": "8c2368c105302d57c078cdf44559058f60fc475a"}, {"title": "The Neuromodulatory Basis of Aggression: Lessons From the Humble Fruit Fly", "paperId": "c189e808578b1d2757ba001011afd8c9f226fd50"}, {"title": "Context-dependent control of behavior in Drosophila", "paperId": "cef850f3a71115204f91b715f656e4a293842a2e"}, {"title": "Neural mechanisms underlying the temporal organization of naturalist