INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('6c32d89c783891ba23860a8999e8150812c99b73', '{"url": "https://www.semanticscholar.org/paper/6c32d89c783891ba23860a8999e8150812c99b73", "year": 2022, "title": "Drosophila olfaction: past, present and future", "venue": "Proceedings of the Royal Society B", "authors": [{"name": "R. Benton", "authorId": "144583541"}], "journal": {"name": "Proceedings of the Royal Society B: Biological Sciences", "volume": "289"}, "paperId": "6c32d89c783891ba23860a8999e8150812c99b73", "abstract": "Among the many wonders of nature, the sense of smell of the fly Drosophila melanogaster might seem, at first glance, of esoteric interest. Nevertheless, for over a century, the ‘nose’ of this insect has been an extraordinary system to explore questions in animal behaviour, ecology and evolution, neuroscience, physiology and molecular genetics. The insights gained are relevant for our understanding of the sensory biology of vertebrates, including humans, and other insect species, encompassing those detrimental to human health. Here, I present an overview of our current knowledge of D. melanogaster olfaction, from molecules to behaviours, with an emphasis on the historical motivations of studies and illustration of how technical innovations have enabled advances. I also highlight some of the pressing and long-term questions.", "citations": [{"title": "Sparse and stereotyped encoding implicates a core glomerulus for ant alarm behavior", "paperId": "bf34b92167664ef9eaf460501916f1b9b9c800df"}], "externalIds": {"DOI": "10.1098/rspb.2022.2054", "PubMed": "36515118", "CorpusId": 254592063, "PubMedCentral": "9748782"}, "isOpenAccess": false, "citationCount": 1, "fieldsOfStudy": ["Medicine"], "openAccessPdf": null, "citationStyles": {"bibtex": "@[''Review'', ''JournalArticle'']{Benton2022DrosophilaOP,\n author = {R. Benton},\n booktitle = {Proceedings of the Royal Society B},\n journal = {Proceedings of the Royal Society B: Biological Sciences},\n title = {Drosophila olfaction: past, present and future},\n volume = {289},\n year = {2022}\n}\n"}, "referenceCount": 307, "publicationDate": "2022-12-14", "s2FieldsOfStudy": [{"source": "external", "category": "Medicine"}, {"source": "s2-fos-model", "category": "Biology"}], "publicationTypes": ["Review", "JournalArticle"], "publicationVenue": null, "influentialCitationCount": 1}', '2023-01-27 10:42:50.348465', '2023-01-27 10:42:50.348465', 'Drosophila olfaction: past, present and future', 2022, false, null, null, null, null);
INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('bd04c9a4e66dbb8ad2217c6f7ddce1fad1157919', '{"url": "https://www.semanticscholar.org/paper/bd04c9a4e66dbb8ad2217c6f7ddce1fad1157919", "year": 2022, "title": "The cellular architecture of memory modules in Drosophila supports stochastic input integration", "venue": "bioRxiv", "authors": [{"name": "Omar A. Hafez", "authorId": "1997469787"}, {"name": "Benjamin Escribano", "authorId": "152269544"}, {"name": "Rouven L. Ziegler", "authorId": "2038088128"}, {"name": "Jan J. Hirtz", "authorId": "46265820"}, {"name": "E. Niebur", "authorId": "3271571"}, {"name": "Jan Pielage", "authorId": "4568001"}], "journal": {"name": "bioRxiv"}, "paperId": "bd04c9a4e66dbb8ad2217c6f7ddce1fad1157919", "abstract": "The ability to associate neutral stimuli with valence information and to store these associations as memories forms the basis for decision making. To determine the underlying computational principles, we build a realistic computational model of a central decision module within the Drosophila mushroom body (MB), the fly’s center for learning and memory. Our model combines the electron microscopy-based architecture of one MB output neuron (MBON-α3), the synaptic connectivity of its 948 presynaptic Kenyon cells (KCs), and its membrane properties obtained from patch-clamp recordings. We show that this neuron is electrotonically compact and that synaptic input corresponding to simulated odor input robustly drives its spiking behavior. Therefore, sparse innervation by KCs can efficiently control and modulate MBON activity in response to learning with minimal requirements on the specificity of synaptic localization. This architecture allows efficient storage of large numbers of memories using the flexible stochastic connectivity of the circuit.", "citations": [], "externalIds": {"DOI": "10.1101/2020.12.06.413500", "CorpusId": 247085404}, "isOpenAccess": true, "citationCount": 0, "fieldsOfStudy": ["Biology"], "openAccessPdf": {"url": "https://www.biorxiv.org/content/biorxiv/early/2021/03/04/2020.12.06.413500.full.pdf", "status": "GREEN"}, "citationStyles": {"bibtex": "@None{Hafez2022TheCA,\n author = {Omar A. Hafez and Benjamin Escribano and Rouven L. Ziegler and Jan J. Hirtz and E. Niebur and Jan Pielage},\n booktitle = {bioRxiv},\n journal = {bioRxiv},\n title = {The cellular architecture of memory modules in Drosophila supports stochastic input integration},\n year = {2022}\n}\n"}, "referenceCount": 84, "publicationDate": "2022-12-12", "s2FieldsOfStudy": [{"source": "external", "category": "Biology"}, {"source": "s2-fos-model", "category": "Biology"}, {"source": "s2-fos-model", "category": "Computer Science"}], "publicationTypes": null, "publicationVenue": {"id": "027ffd21-ebb0-4af8-baf5-911124292fd0", "url": "http://biorxiv.org/", "name": "bioRxiv", "type": "journal"}, "influentialCitationCount": 0}', '2023-01-27 10:45:56.275864', '2023-01-27 10:45:56.275864', 'The cellular architecture of memory modules in Drosophila supports stochastic input integration', 2022, false, null, null, null, null);
INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('b60e981fd5268a290dbf4e7d8346036396ecaf7d', '{"url": "https://www.semanticscholar.org/paper/b60e981fd5268a290dbf4e7d8346036396ecaf7d", "year": 2022, "title": "Meta-Learning the Inductive Biases of Simple Neural Circuits", "venue": "", "authors": [{"name": "W. Dorrell", "authorId": "116946849"}, {"name": "Maria Yuffa", "authorId": "2192518028"}, {"name": "P. Latham", "authorId": "9967304"}], "journal": null, "paperId": "b60e981fd5268a290dbf4e7d8346036396ecaf7d", "abstract": "Animals receive noisy and incomplete information, from which we must learn how to react in novel situations. A fundamental problem is that training data is always ﬁnite, making it unclear how to generalise to unseen data. But, animals do react appropriately to unseen data, wielding Occam’s razor to select a parsimo-nious explanation of the observations. How they do this is called their inductive bias, and it is implicitly built into the operation of animals’ neural circuits. This relationship between an observed circuit and its inductive bias is a useful explanatory window for neuroscience, allowing design choices to be understood normatively. However, it is generally very difﬁcult to map circuit structure to inductive bias. In this work we present a neural network tool to bridge this gap. The tool allows us to meta-learn the inductive bias of neural circuits by learning functions that a neural circuit ﬁnds easy to generalise, since easy-to-generalise functions are exactly those the circuit chooses to explain incomplete data. We show that in systems where the inductive bias is known analytically, i.e. linear and kernel regression, our tool recovers it. Then, we show it is able to ﬂexibly extract inductive biases from differentiable circuits, including spiking neural networks, and use it to interpret recent connectomic data through their effect on generalisation. This illustrates the intended use of our tool: understanding the role of otherwise opaque pieces of neural functionality through the inductive bias they induce.", "citations": [], "externalIds": {"ArXiv": "2211.13544", "CorpusId": 254018268}, "isOpenAccess": false, "citationCount": 0, "fieldsOfStudy": ["Biology"], "openAccessPdf": null, "citationStyles": {"bibtex": "@None{Dorrell2022Meta-LearningTI,\n author = {W. Dorrell and Maria Yuffa and P. Latham},\n title = {Meta-Learning the Inductive Biases of Simple Neural Circuits},\n year = {2022}\n}\n"}, "referenceCount": 38, "publicationDate": "2022-11-24", "s2FieldsOfStudy": [{"source": "external", "category": "Biology"}, {"source": "s2-fos-model", "category": "Computer Science"}], "publicationTypes": null, "publicationVenue": null, "influentialCitationCount": 0}', '2023-01-27 10:45:59.924654', '2023-01-27 10:45:59.924654', 'Meta-Learning the Inductive Biases of Simple Neural Circuits', 2022, false, null, null, null, null);
