INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('7234495241b9d7393fb27182686eb065985fd647', '{"url": "https://www.semanticscholar.org/paper/7234495241b9d7393fb27182686eb065985fd647", "year": 2022, "title": "Multi-Layered Maps of Neuropil with Segmentation-Guided Contrastive Learning", "venue": "bioRxiv", "authors": [{"name": "Sven Dorkenwald", "authorId": "2122374817"}, {"name": "Peter H. Li", "authorId": "1410187169"}, {"name": "Michał Januszewski", "authorId": "34172684"}, {"name": "D. Berger", "authorId": "35725792"}, {"name": "Jeremy B. Maitin-Shepard", "authorId": "1406775898"}, {"name": "Agnes L. Bodor", "authorId": "40584201"}, {"name": "F. Collman", "authorId": "6598643"}, {"name": "C. Schneider-Mizell", "authorId": "1397852111"}, {"name": "N. D. da Costa", "authorId": "51079681"}, {"name": "J. Lichtman", "authorId": "27103730"}, {"name": "Viren Jain", "authorId": "35086944"}], "journal": {"name": "bioRxiv"}, "paperId": "7234495241b9d7393fb27182686eb065985fd647", "abstract": "Maps of the nervous system that identify individual cells along with their type, subcellular components, and connectivity have the potential to reveal fundamental organizational principles of neural circuits. Volumetric nanometer-resolution imaging of brain tissue provides the raw data needed to build such maps, but inferring all the relevant cellular and subcellular annotation layers is challenging. Here, we present Segmentation-Guided Contrastive Learning of Representations (“SegCLR”), a self-supervised machine learning technique that produces highly informative representations of cells directly from 3d electron microscope imagery and segmentations. When applied to volumes of human and mouse cerebral cortex, SegCLR enabled the classification of cellular subcompartments (axon, dendrite, soma, astrocytic process) with 4,000-fold less labeled data compared to fully supervised approaches. Surprisingly, SegCLR also enabled inference of cell types (neurons, glia, and subtypes of each) from fragments with lengths as small as 10 micrometers, a task that can be difficult for humans to perform and whose feasibility greatly enhances the utility of imaging portions of brains in which many neuron fragments terminate at a volume boundary. These predictions were further augmented via Gaussian process uncertainty estimation to enable analyses restricted to high confidence subsets of the data. Finally, SegCLR enabled detailed exploration of layer-5 pyramidal cell subtypes and automated large-scale statistical analysis of upstream and downstream synaptic partners in mouse visual cortex.", "citations": [{"title": "Synthetic data enable experiments in atomistic machine learning", "paperId": "f64d3a093cbb4b1211527719478627590b959f71"}], "externalIds": {"DOI": "10.1101/2022.03.29.486320", "CorpusId": 247857249}, "isOpenAccess": true, "citationCount": 2, "fieldsOfStudy": ["Biology"], "openAccessPdf": {"url": "https://www.biorxiv.org/content/biorxiv/early/2022/03/30/2022.03.29.486320.full.pdf", "status": "GREEN"}, "citationStyles": {"bibtex": "@None{Dorkenwald2022Multi-LayeredMO,\n author = {Sven Dorkenwald and Peter H. Li and Michał Januszewski and D. Berger and Jeremy B. Maitin-Shepard and Agnes L. Bodor and F. Collman and C. Schneider-Mizell and N. D. da Costa and J. Lichtman and Viren Jain},\n booktitle = {bioRxiv},\n journal = {bioRxiv},\n title = {Multi-Layered Maps of Neuropil with Segmentation-Guided Contrastive Learning},\n year = {2022}\n}\n"}, "referenceCount": 73, "publicationDate": "2022-11-08", "s2FieldsOfStudy": [{"source": "external", "category": "Biology"}, {"source": "s2-fos-model", "category": "Biology"}], "publicationTypes": null, "publicationVenue": {"id": "027ffd21-ebb0-4af8-baf5-911124292fd0", "url": "http://biorxiv.org/", "name": "bioRxiv", "type": "journal"}, "influentialCitationCount": 0}', '2023-01-27 10:46:07.428171', '2023-01-27 10:46: