INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('062de529b091acf2d965a7c697715529b4ff9c88', '{"url": "https://www.semanticscholar.org/paper/062de529b091acf2d965a7c697715529b4ff9c88", "year": 2022, "title": "Olfactory responses of Drosophila are encoded in the organization of projection neurons", "venue": "bioRxiv", "authors": [{"name": "Kiri Choi", "authorId": "2080146"}, {"name": "Won Kyu Kim", "authorId": "2156834360"}, {"name": "Changbong Hyeon", "authorId": "2457647"}], "journal": {"name": "eLife", "volume": "11"}, "paperId": "062de529b091acf2d965a7c697715529b4ff9c88", "abstract": "The projection neurons (PNs), reconstructed from electron microscope (EM) images of the Drosophila olfactory system, offer a detailed view of neuronal anatomy, providing glimpses into information flow in the brain. About 150 uPNs constituting 58 glomeruli in the antennal lobe (AL) are bundled together in the axonal extension, routing the olfactory signal received at AL to mushroom body (MB) calyx and lateral horn (LH). Here we quantify the neuronal organization by inter-PN distances and examine its relationship with the odor types sensed by Drosophila. The homotypic uPNs that constitute glomeruli are tightly bundled and stereotyped in position throughout the neuropils, even though the glomerular PN organization in AL is no longer sustained in the higher brain center. Instead, odor-type dependent clusters consisting of multiple homotypes innervate the MB calyx and LH. Pheromone-encoding and hygro/thermo-sensing homotypes are spatially segregated in MB calyx, whereas two distinct clusters of food-related homotypes are found in LH in addition to the segregation of pheromone-encoding and hygro/thermo-sensing homotypes. We find that there are statistically significant associations between the spatial organization among a group of homotypic uPNs and certain stereotyped olfactory responses. Additionally, the signals from some of the tightly bundled homotypes converge to a specific group of lateral horn neurons (LHNs), which indicates that homotype (or odor type) specific integration of signals occurs at the synaptic interface between PNs and LHNs. Our findings suggest that before neural computation in the inner brain, some of the olfactory information are already encoded in the spatial organization of uPNs, illuminating that a certain degree of labeled-line strategy is at work in the Drosophila olfactory system.", "citations": [], "externalIds": {"DOI": "10.1101/2022.02.23.481655", "PubMed": "36173095", "CorpusId": 247159820, "PubMedCentral": "9616568"}, "isOpenAccess": false, "citationCount": 0, "fieldsOfStudy": ["Medicine", "Biology"], "openAccessPdf": null, "citationStyles": {"bibtex": "@[''JournalArticle'']{Choi2022OlfactoryRO,\n author = {Kiri Choi and Won Kyu Kim and Changbong Hyeon},\n booktitle = {bioRxiv},\n journal = {eLife},\n title = {Olfactory responses of Drosophila are encoded in the organization of projection neurons},\n volume = {11},\n year = {2022}\n}\n"}, "referenceCount": 92, "publicationDate": "2022-07-31", "s2FieldsOfStudy": [{"source": "external", "category": "Medicine"}, {"source": "external", "category": "Biology"}, {"source": "s2-fos-model", "category": "Biology"}], "publicationTypes": ["JournalArticle"], "publicationVenue": {"id": "027ffd21-ebb0-4af8-baf5-911124292fd0", "url": "http://biorxiv.org/", "name": "bioRxiv", "type": "journal"}, "influentialCitationCount": 0}', '2023-01-27 10:46:14.748432', '2023-01-27 10:46:14.748432', 'Olfactory responses of Drosophila are encoded in the organization of projection neurons', 2022, false, null, null, null, null);
