INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('1cd72757ee075e80c3cab9b0fbe2db732616b474', '{"url": "https://www.semanticscholar.org/paper/1cd72757ee075e80c3cab9b0fbe2db732616b474", "year": 2021, "title": "Mushroom body input connections form independently of sensory activity in Drosophila melanogaster\n", "venue": "Current Biology", "authors": [{"name": "T. Hayashi", "authorId": "2107749731"}, {"name": "Alexander John MacKenzie", "authorId": "2140380785"}, {"name": "Ishani Ganguly", "authorId": "2050115494"}, {"name": "Hayley Marie Smihula", "authorId": "2139750334"}, {"name": "Miles Solomon Jacob", "authorId": "1571201605"}, {"name": "A. Litwin-Kumar", "authorId": "1401265175"}, {"name": "S. Caron", "authorId": "1521776106"}], "journal": {"name": "Current Biology", "pages": "4000-4012.e5", "volume": "32"}, "paperId": "1cd72757ee075e80c3cab9b0fbe2db732616b474", "abstract": "Associative brain centers, such as the insect mushroom body, need to represent sensory information in an efficient manner. In Drosophila melanogaster, the Kenyon cells of the mushroom body integrate inputs from a random set of olfactory projection neurons, but some projection neurons — namely those activated by a few ethologically meaningful odors — connect to Kenyon cells more frequently than others. This biased and random connectivity pattern is conceivably advantageous, as it enables the mushroom body to represent a large number of odors as unique activity patterns while prioritizing the representation of a few specific odors. How this connectivity pattern is established remains largely unknown. Here, we test whether the mechanisms patterning the connections between Kenyon cells and projection neurons depend on sensory activity or whether they are hardwired. We mapped a large number of mushroom body input connections in anosmic flies — flies lacking the obligate odorant co-receptor Orco — and in wildtype flies. Statistical analyses of these datasets reveal that the random and biased connectivity pattern observed between Kenyon cells and projection neurons forms normally in the absence of most olfactory sensory activity. This finding supports the idea that even comparatively subtle, population-level patterns of neuronal connectivity can be encoded by fixed genetic programs and are likely to be the result of evolved prioritization of ecologically and ethologically salient stimuli.", "citations": [], "externalIds": {"DOI": "10.1101/2021.11.06.467552", "PubMed": "35977547", "CorpusId": 243865078}, "isOpenAccess": true, "citationCount": 0, "fieldsOfStudy": ["Medicine", "Biology"], "openAccessPdf": {"url": "https://www.biorxiv.org/content/biorxiv/early/2021/11/07/2021.11.06.467552.full.pdf", "status": "GREEN"}, "citationStyles": {"bibtex": "@[''JournalArticle'']{Hayashi2021MushroomBI,\n author = {T. Hayashi and Alexander John MacKenzie and Ishani Ganguly and Hayley Marie Smihula and Miles Solomon Jacob and A. Litwin-Kumar and S. Caron},\n booktitle = {Current Biology},\n journal = {Current Biology},\n pages = {4000-4012.e5},\n title = {Mushroom body input connections form independently of sensory activity in Drosophila melanogaster\n},\n volume = {32},\n year = {2021}\n}\n"}, "referenceCount": 53, "publicationDate": "2021-11-07", "s2FieldsOfStudy": [{"source": "external", "category": "Medicine"}, {"source": "external", "category": "Biology"}, {"source": "s2-fos-model", "category": "Biology"}], "publicationTypes": ["JournalArticle"], "publicationVenue": {"id": "9469269e-53d7-4955-b1a5-17a75bee7634", "url": "http://www.current-biology.com/", "issn": "0960-9822", "name": "Current Biology", "type": "journal", "alternate_urls": ["http://www.sciencedirect.com/science/journal/09609822"], "alternate_names": ["Curr Biology"]}, "influentialCitationCount": 0}', '2023-01-27 10:46:21.994116', '2023-01-27 10:46:21.994116', 'Mushroom body input connections form independently of sensory activity in Drosophila melanogaster
