INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('641ee65c532f9de8fc0ceb4796d9038abc4640d4', '{"url": "https://www.semanticscholar.org/paper/641ee65c532f9de8fc0ceb4796d9038abc4640d4", "year": 2020, "title": "Information flow, cell types and stereotypy in a full olfactory connectome", "venue": "bioRxiv", "authors": [{"name": "P. Schlegel", "authorId": "28566201"}, {"name": "A. S. Bates", "authorId": "51292941"}, {"name": "Tomke Stürner", "authorId": "5927789"}, {"name": "Sridhar R. Jagannathan", "authorId": "152800165"}, {"name": "N. Drummond", "authorId": "1577866398"}, {"name": "Joseph Hsu", "authorId": "2072740448"}, {"name": "L. Capdevila", "authorId": "2042055808"}, {"name": "A. Javier", "authorId": "10729416"}, {"name": "E. Marin", "authorId": "3928565"}, {"name": "A. Barth-Maron", "authorId": "1397243493"}, {"name": "I. Tamimi", "authorId": "1580293725"}, {"name": "Feng Li", "authorId": "2146311675"}, {"name": "G. Rubin", "authorId": "34660241"}, {"name": "Stephen M. Plaza", "authorId": "1846140"}, {"name": "Marta Costa", "authorId": "145739913"}, {"name": "G. Jefferis", "authorId": "145196228"}], "journal": {"name": "eLife", "volume": "10"}, "paperId": "641ee65c532f9de8fc0ceb4796d9038abc4640d4", "abstract": "The hemibrain connectome provides large scale connectivity and morphology information for the majority of the central brain of Drosophila melanogaster. Using this data set, we provide a complete description of the Drosophila olfactory system, covering all first, second and lateral horn-associated third-order neurons. We develop a generally applicable strategy to extract information flow and layered organisation from connectome graphs, mapping olfactory input to descending interneurons. This identifies a range of motifs including highly lateralised circuits in the antennal lobe and patterns of convergence downstream of the mushroom body and lateral horn. Leveraging a second data set we provide a first quantitative assessment of inter – versus intra-individual stereotypy. Comparing neurons across two brains (three hemispheres) reveals striking similarity in neuronal morphology across brains. Connectivity correlates with morphology and neurons of the same morphological type show similar connection variability within the same brain as across two brains.", "citations": [{"title": "Behavioral algorithms and neural mechanisms underlying odor-modulated locomotion in insects.", "paperId": "2caf6857b87b12fedc7286b0ddf5cef9bff09443"}, {"title": "Drosophila olfaction: past, present and future", "paperId": "6c32d89c783891ba23860a8999e8150812c99b73"}, {"title": "ViMo Visual Analysis of Neuronal Connectivity Motifs", "paperId": "606622ff5f693ba8ca9d3c72c5765d07fcd141e8"}, {"title": "Generative network modeling reveals quantitative definitions of bilateral symmetry exhibited by a whole insect brain connectome", "paperId": "f2d84761d99eab3d143c0ac17842c48c6a959d99"}, {"title": "The connectome of an insect brain", "paperId": "0910cb7d82c4b4f0212b06ea14e1fe5637970289"}, {"title": "Locomotor and olfactory responses in dopamine neurons of the Drosophila superior-lateral brain", "paperId": "9c063562ef8e93954d38406d163aad82c6c7ea96"}, {"title": "Pheromone sensing in Drosophila requires support cell-expressed Osiris 8", "paperId": "5dbac61d9d1bd33ffce553e3b89587315322d662"}, {"title": "Diversification of neuropil organization in specialized and broadly tuned olfactory glomerular circuits in Drosophila melanogaster", "paperId": "28ac216ee31eead6d8f75cfd1a2f3caa57a23a68"}, {"title": "Connectomics and the neural basis of behaviour", "paperId": "e67ce2076b1edb61228f9401bda7a0ea81f3055a"}, {"title": "Bisected graph matching improves automated pairing of bilaterally homologous neurons from connectomes", "paperId": "a4ccf38959d3232c16951ca405ab41a76d39e379"}, {"title": "Dopaminergic neurons dynamically update sensory values during navigation"