INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('273e9e7a167153feb78603eb51b27e7857602795', '{"url": "https://www.semanticscholar.org/paper/273e9e7a167153feb78603eb51b27e7857602795", "year": null, "title": "Olfactory responses of Drosophila 1 are encoded in the organization of 2 projection neurons 3", "venue": "", "authors": [{"name": "K. Choi", "authorId": "2110474845"}, {"name": "Wonkyum Kim", "authorId": "2152203425"}, {"name": "Changbong Hyeon", "authorId": "2457647"}], "journal": null, "paperId": "273e9e7a167153feb78603eb51b27e7857602795", "abstract": "The projection neurons (PNs), reconstructed from electron microscope (EM) images 8 of the Drosophila olfactory system, offer a detailed view of neuronal anatomy, providing glimpses 9 into information ﬂow in the brain. About 150 uPNs constituting 58 glomeruli in the antennal lobe 10 (AL) are bundled together in the axonal extension, routing the olfactory signal received at AL to 11 mushroom body (MB) calyx and lateral horn (LH). Here we quantify the neuronal organization in 12 terms of the inter-PN distances and examine its relationship with the odor types sensed by 13 Drosophila . The homotypic uPNs that constitute glomeruli are tightly bundled and stereotyped in 14 position throughout the neuropils, even though the glomerular PN organization in AL is no longer 15 sustained in the higher brain center. Instead, odor-type dependent clusters consisting of multiple 16 homotypes innervate the MB calyx and LH. Pheromone-encoding and hygro/thermo-sensing 17 homotypes are spatially segregated in MB calyx, whereas two distinct clusters of food-related 18 homotypes are found in LH in addition to the segregation of pheromone-encoding and 19 hygro/thermo-sensing homotypes. We ﬁnd that there are statistically signiﬁcant associations 20 between the spatial organization among a group of homotypic uPNs and certain stereotyped 21 olfactory responses. Additionally, the signals from some of the tightly bundled homotypes 22 converge to a speciﬁc group of lateral horn neurons (LHNs), which indicates that homotype (or 23 odor type) speciﬁc", "citations": [{"title": "Environmentally sustainable pest control options for Drosophila suzukii", "paperId": "07942795f448be219d72cdac94256be712c68a97"}], "externalIds": {"CorpusId": 252648320}, "isOpenAccess": false, "citationCount": 1, "fieldsOfStudy": null, "openAccessPdf": null, "citationStyles": {"bibtex": "@None{None,\n author = {K. Choi and Wonkyum Kim and Changbong Hyeon},\n title = {Olfactory responses of Drosophila 1 are encoded in the organization of 2 projection neurons 3}\n}\n"}, "referenceCount": 82, "publicationDate": null, "s2FieldsOfStudy": [{"source": "s2-fos-model", "category": "Biology"}], "publicationTypes": null, "publicationVenue": null, "influentialCitationCount": 0}', '2023-01-27 10:48:23.453541', '2023-01-27 10:48:23.453541', 'Olfactory responses of Drosophila 1 are encoded in the organization of 2 projection neurons 3', null, false, null, null, null, null);
