INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('bf34b92167664ef9eaf460501916f1b9b9c800df', '{"url": "https://www.semanticscholar.org/paper/bf34b92167664ef9eaf460501916f1b9b9c800df", "year": 2022, "title": "Sparse and stereotyped encoding implicates a core glomerulus for ant alarm behavior", "venue": "bioRxiv", "authors": [{"name": "Taylor Hart", "authorId": "2117302303"}, {"name": "Dominic Frank", "authorId": "2198759028"}, {"name": "L. E. Lopes", "authorId": "39869471"}, {"name": "L. Olivos-Cisneros", "authorId": "1398959360"}, {"name": "Kip D. Lacy", "authorId": "13003259"}, {"name": "W. Trible", "authorId": "38728264"}, {"name": "Amelia Ritger", "authorId": "2198759951"}, {"name": "Stephany Valdés-Rodríguez", "authorId": "2193296020"}, {"name": "D. Kronauer", "authorId": "3630731"}], "journal": {"name": "bioRxiv"}, "paperId": "bf34b92167664ef9eaf460501916f1b9b9c800df", "abstract": "Ants communicate via large arrays of pheromones and possess expanded, highly complex olfactory systems, with antennal lobes in the brain comprising ~500 glomeruli. This expansion implies that odors could activate hundreds of glomeruli, which would pose challenges for higher order processing. To study this problem, we generated the first transgenic ants, expressing the genetically encoded calcium indicator GCaMP6s in olfactory sensory neurons. Using two-photon imaging, we mapped complete glomerular responses to four ant alarm pheromones. Alarm pheromones robustly activated ≤6 glomeruli, and activity maps for the three pheromones inducing panic-alarm in our study species converged on a single glomerulus. These results demonstrate that, rather than using broadly tuned combinatorial encoding, ants employ precise, narrowly tuned, and stereotyped representation of alarm pheromone cues. The identification of a central sensory hub glomerulus for alarm behavior suggests that a simple neural architecture is sufficient to translate pheromone perception into behavioral outputs.", "citations": [], "externalIds": {"DOI": "10.1101/2022.12.29.522224", "CorpusId": 255264956}, "isOpenAccess": true, "citationCount": 0, "fieldsOfStudy": ["Biology"], "openAccessPdf": {"url": "https://www.biorxiv.org/content/biorxiv/early/2022/12/29/2022.12.29.522224.full.pdf", "status": "GREEN"}, "citationStyles": {"bibtex": "@None{Hart2022SparseAS,\n author = {Taylor Hart and Dominic Frank and L. E. Lopes and L. Olivos-Cisneros and Kip D. Lacy and W. Trible and Amelia Ritger and Stephany Valdés-Rodríguez and D. Kronauer},\n booktitle = {bioRxiv},\n journal = {bioRxiv},\n title = {Sparse and stereotyped encoding implicates a core glomerulus for ant alarm behavior},\n year = {2022}\n}\n"}, "referenceCount": 111, "publicationDate": "2022-12-29", "s2FieldsOfStudy": [{"source": "external", "category": "Biology"}, {"source": "s2-fos-model", "category": "Biology"}], "publicationTypes": null, "publicationVenue": {"id": "027ffd21-ebb0-4af8-baf5-911124292fd0", "url": "http://biorxiv.org/", "name": "bioRxiv", "type": "journal"}, "influentialCitationCount": 0}', '2023-01-27 10:48:30.696173', '2023-01-27 10:48:30.696173', 'Sparse and stereotyped encoding implicates a core glomerulus for ant alarm behavior', 2022, false, null, null, null, null);
INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('afb38ff1fee65101810048490683203aaa2fc07a', '{"url": "https://www.semanticscholar.org/paper/afb38ff1fee65101810048490683203aaa2fc07a", "year": 2022, "title": "How innovations in methodology offer new prospects for volume electron microscopy", "venue": "Journal of Microscopy", "authors": [{"name": "Arent J Kievits", "authorId": "2175565569"}, {"name": "R. Lane", "authorId": "2040543528"}, {"name": "Elizabeth C. Carroll", "authorId": "2070532487"}, {"name": "J. Hoogenboom", "authorId": "1924977"}], "journal": {"name": "Journal of Microscopy", "pages": "114 - 137", "volume": "287"}, "paperId": "afb38ff1fee65101810048490683203aaa2fc07a", "abstract": "Detailed knowledge of biological structure has been key in understanding biology at several levels of organisation, from organs to cells and proteins. Volume electron microscopy (volume EM) provides high resolution 3D structural information about tissues on the nanometre scale. However, the throughput rate of conventional electron microscopes has limited the volume size and number of samples that can be imaged. Recent improvements in methodology are currently driving a revolution in volume EM, making possible the structural imaging of whole organs and small organisms. In turn, these recent developments in image acquisition have created or stressed bottlenecks in other parts of the pipeline, like sample preparation, image analysis and data management. While the progress in image analysis is stunning due to the advent of automatic segmentation and server‐based annotation tools, several challenges remain. Here we discuss recent trends in volume EM, emerging methods for increasing throughput and implications for sample preparation, image analysis and data management.", "citations": [], "externalIds": {"DOI": "10.1111/jmi.13134", "PubMed": "35810393", "CorpusId": 250409559, "PubMedCentral": "9546337"}, "isOpenAccess": true, "citationCount": 0, "fieldsOfStudy": ["Medicine"], "openAccessPdf": {"url": "https://onlinelibrary.wiley.com/doi/pdfdirect/10.1111/jmi.13134", "status": "HYBRID"}, "citationStyles": {"bibtex": "@[''Review'', ''JournalArticle'']{Kievits2022HowII,\n author = {Arent J Kievits and R. Lane and Elizabeth C. Carroll and J. Hoogenboom},\n booktitle = {Journal of Microscopy},\n journal = {Journal of Microscopy},\n pages = {114 - 137},\n title = {How innovations in methodology offer new prospects for volume electron microscopy},\n volume = {287},\n year = {2022}\n}\n"}, "referenceCount": 190, "publicationDate": "2022-07-10", "s2FieldsOfStudy": [{"source": "external", "category": "Medicine"}, {"source": "s2-fos-model", "category": "Biology"}], "publicationTypes": ["Review", "JournalArticle"], "publicationVenue": {"id": "52565dee-9c42-40a4-a69e-411601da6587", "url": "http://www.blackwell-synergy.com/servlet/useragent?close=1997&code=jmi&func=showIssues#C1997", "issn": "0022-2720", "name": "Journal of Microscopy", "type": "journal", "alternate_urls": ["https://onlinelibrary.wiley.com/journal/13652818", "http://onlinelibrary.wiley.com/journal/10.1111/(ISSN)1365-2818"], "alternate_issns": ["0021-7921"], "alternate_names": ["J microsc", "Journal de microscopie", "J Microsc"]}, "influentialCitationCount": 0}', '2023-01-27 10:57:14.735167', '2023-01-27 10:57:14.735167', 'How innovations in methodology offer new prospects for volume electron microscopy', 2022, false, null, null, null, null);
INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('4b03f6a6804d901ce995d85daeb928f73ab759a1', '{"url": "https://www.semanticscholar.org/paper/4b03f6a6804d901ce995d85daeb928f73ab759a1", "year": 2022, "title": "Theory of the Multiregional Neocortex: Large-Scale Neural Dynamics and Distributed Cognition.", "venue": "Annual Review of Neuroscience", "authors": [{"name": "Xiao-Jing Wang", "authorId": "2115451696"}], "journal": {"name": "Annual review of neuroscience", "pages": "\n          533-560\n        ", "volume": "45"}, "paperId": "4b03f6a6804d901ce995d85daeb928f73ab759a1", "abstract": "The neocortex is a complex neurobiological system with many interacting regions. How these regions work together to subserve flexible behavior and cognition has become increasingly amenable to rigorous research. Here, I review recent experimental and theoretical work on the modus operandi of a multiregional cortex. These studies revealed several general principles for the neocortical interareal connectivity, low-dimensional macroscopic gradients of biological properties across cortical areas, and a hierarchy of timescales for information processing. Theoretical work suggests testable predictions regarding differential excitation and inhibition along feedforward and feedback pathways in the cortical hierarchy. Furthermore, modeling of distributed working memory and simple decision-making has given rise to a novel mathematical concept, dubbed bifurcation in space, that potentially explains how different cortical areas, with a canonical circuit organization but gradients of biological heterogeneities, are able to subserve their respective (e.g., sensory coding versus executive control) functions in a modularly organized brain.", "citations": [{"title": "Risk factors and screening for neurocognitive impacts of therapy.", "paperId": "199fc7c0d0428b82fe80a5b7481c553c2e70cf96"}, {"title": "Efficient processing of natural scenes in visual cortex", "paperId": "4e0200dca99b3be3834fdd46722192e2c8f2fb13"}, {"title": "Routing States Transition During Oscillatory Bursts and Attention States", "paperId": "6a6b3ecddee70287f245ee3c7f6e9d936155431b"}, {"title": "Flexible sensory-motor mapping rules manifest in correlated variability of stimulus and action codes across the brain", "paperId": "0a39058cb90b03d814d9966ca42f8229db49219a"}, {"title": "Large Language Models and the Reverse Turing Test", "paperId": "292da1c4640c105aa5d7919a1ded9a1225c07d4d"}], "externalIds": {"DOI": "10.1146/annurev-neuro-110920-035434", "PubMed": "35803587", "CorpusId": 250388349}, "isOpenAccess": false, "citationCount": 5, "fieldsOfStudy": ["Medicine"], "openAccessPdf": null, "citationStyles": {"bibtex": "@[''Review'', ''JournalArticle'']{Wang2022TheoryOT,\n author = {Xiao-Jing Wang},\n booktitle = {Annual Review of Neuroscience},\n journal = {Annual review of neuroscience},\n pages = {\n          533-560\n        },\n title = {Theory of the Multiregional Neocortex: Large-Scale Neural Dynamics and Distributed Cognition.},\n volume = {45},\n year = {2022}\n}\n"}, "referenceCount": 203, "publicationDate": "2022-07-08", "s2FieldsOfStudy": [{"source": "external", "category": "Medicine"}, {"source": "s2-fos-model", "category": "Biology"}, {"source": "s2-fos-model", "category": "Psychology"}], "publicationTypes": ["Review", "JournalArticle"], "publicationVenue": {"id": "d9caa671-3be6-48bc-9710-f96334848b4c", "url": "https://www.annualreviews.org/journal/neuro", "issn": "0147-006X", "name": "Annual Review of Neuroscience", "type": "journal", "alternate_urls": ["http://arjournals.annualreviews.org/loi/neuro", "https://www.annualreviews.org/loi/neuro"], "alternate_names": ["Annu Rev Neurosci"]}, "influentialCitationCount": 0}', '2023-01-27 10:57:18.411418', '2023-01-27 10:57:18.411418', 'Theory of the Multiregional Neocortex: Large-Scale Neural Dynamics and Distributed Cognition.', 2022, fa