INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('b82f01a87fe726d8356f3e44fb9c1ba47d349eac', '{"url": "https://www.semanticscholar.org/paper/b82f01a87fe726d8356f3e44fb9c1ba47d349eac", "year": 2022, "title": "Uncovering features of synapses in primary visual cortex through contrastive representation learning", "venue": "bioRxiv", "authors": [{"name": "A. Wilson", "authorId": "1434750166"}, {"name": "M. Babadi", "authorId": "2862417"}], "journal": {"name": "bioRxiv"}, "paperId": "b82f01a87fe726d8356f3e44fb9c1ba47d349eac", "abstract": "3D EM connectomics image volumes are now surpassing sizes of 1 mm3, and are therefore beginning to contain multiple meaningful spatial scales of brain circuitry simultaneously. However, the sheer density of information in such datasets makes the development of unbiased, scalable machine learning techniques a necessity for extracting novel insights without extremely time-consuming, intensive labor. In this paper, we present SynapseCLR, a self-supervised contrastive representation learning method for 3D electron microscopy (EM) data, and use the method to extract feature representations of synapses from a 3D EM dataset from mouse visual cortex. We show that our representations separate synapses according to both their overall physical appearance and structural annotations of known functional importance. We further demonstrate the utility of our methodology for several valuable downstream tasks for the growing field of 3D EM connectomics. These include one-shot identification of defective synapse segmentations, dataset-wide similarity-based querying, and accurate imputation of annotations for unlabeled synapses, using only manual annotation of 0.2% of synapses in the dataset. In particular, we show that excitatory vs. inhibitory neuronal cell types can be assigned to individual synapses and highly truncated neurites with accuracy exceeding 99.8%, making this population accessible to connectomics analysis. Finally, we present a data-driven and unsupervised study of the manifold of synaptic structural variation, revealing its intrinsic axes of variation and showing that synapse structure is also strongly correlated with inhibitory neuronal subtypes.", "citations": [], "externalIds": {"DOI": "10.1101/2022.06.07.495207", "CorpusId": 249613201}, "isOpenAccess": true, "citationCount": 0, "fieldsOfStudy": ["Biology"], "openAccessPdf": {"url": "https://www.biorxiv.org/content/biorxiv/early/2022/06/09/2022.06.07.495207.full.pdf", "status": "GREEN"}, "citationStyles": {"bibtex": "@None{Wilson2022UncoveringFO,\n author = {A. Wilson and M. Babadi},\n booktitle = {bioRxiv},\n journal = {bioRxiv},\n title = {Uncovering features of synapses in primary visual cortex through contrastive representation learning},\n year = {2022}\n}\n"}, "referenceCount": 90, "publicationDate": "2022-06-09", "s2FieldsOfStudy": [{"source": "external", "category": "Biology"}, {"source": "s2-fos-model", "category": "Biology"}, {"source": "s2-fos-model", "category": "Computer Science"}], "publicationTypes": null, "publicationVenue": {"id": "027ffd21-ebb0-4af8-baf5-911124292fd0", "url": "http://biorxiv.org/", "name": "bioRxiv", "type": "journal"}, "influentialCitationCount": 0}', '2023-01-27 10:57:25.723810', '2023-01-27 10:57:25.723810', 'Uncovering features of synapses in primary visual cortex through contrastive representation learning', 2022, false, null, null, null, null);
INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('51d5fca6764ca818e8981537faf14c0fefc83a50', '{"url": "https://www.semanticscholar.org/paper/51d5fca6764ca818e8981537faf14c0fefc83a50", "year": 2022, "title": "Generating parallel representations of position and identity in the olfactory system", "venue": "bioRxiv", "authors": [{"name": "István Taisz", "authorId": "116251476"}, {"name": "E. Donà", "authorId": "5352022"}, {"name": "Daniel Münch", "authorId": "38487247"}, {"name": "Shanice Bailey", "authorId": "2163460309"}, {"name": "William J. Morris", "authorId": "2165478700"}, {"name": "Kimberly I. Meechan", "authorId": "1573416828"}, {"name": "Katie M. Stevens", "authorId": "2165479664"}, {"name": "Irene Varela", "authorId": "2165479311"}, {"name": "Marina Gkantia", "authorId": "1581730029"}, {"name": "P. Schlegel", "authorId": "28566201"}, {"name": "C. Ribeiro", "authorId": "145654606"}, {"name": "G. Jefferis", "authorId": "145196228"}, {"name": "Dana S. Galili", "authorId": "2165478765"}], "journal": {"name": "bioRxiv"}, "paperId": "51d5fca6764ca818e8981537faf14c0fefc83a50", "abstract": "Sex pheromones are key social signals in most animals. In Drosophila a dedicated olfactory channel senses a male pheromone, cis-vaccenyl acetate (cVA) that promotes female courtship while repelling males. Here we show that flies use separate cVA processing streams to extract qualitative and positional information. cVA olfactory neurons are sensitive to concentration differences in a 5 mm range around a male. Second-order projection neurons detect inter-antennal differences in cVA concentration, encoding the angular position of a male. We identify a circuit mechanism increasing left-right contrast through an interneuron which provides contralateral inhibition. At the third layer of the circuit we identify neurons with distinct response properties and sensory integration motifs. One population is selectively tuned to an approaching male with speed-dependent responses. A second population responds tonically to a male’s presence and controls female mating decisions. A third population integrates a male taste cue with cVA; only a simultaneous presentation of both signals promotes female mating via this pathway. Thus the olfactory system generates a range of complex percepts in discrete populations of central neurons that allow the expression of appropriate behaviors depending on context. Such separation of olfactory features resembles the mammalian what and where visual streams. Highlights cVA male pheromone has a 5 mm signaling range, activating two parallel central pathways Pheromone-sensing neurons have spatial receptive fields sharpened by contralateral inhibition Position (where) and identity (what) are separated at the 3rd layer of cVA processing Integrating taste and cVA in sexually dimorphic aSP-g controls female receptivity", "citations": [{"title": "Drosophila olfaction: past, present and future", "paperId": "6c32d89c783891ba23860a8999e8150812c99b73"}, {"title": "Organization of the parallel antennal-lobe tracts in the moth", "paperId": "753a5e590839477f14fa4d2b952fc6bc6ecfbf88"}, {"title": "Connectomics and the neural basis of behaviour", "paperId": "e67ce2076b1edb61228f9401bda7a0ea81f3055a"}, {"title": "Selective integration of diverse taste inputs within a single taste modality", "paperId": "94dd8cebc9f2b465b2c2dfe123edc3c217b215f2"}], "externalIds": {"DOI": "10.1101/2022.05.13.491877", "CorpusId": 248836017}, "isOpenAccess": true, "citationCount": 7, "fieldsOfStudy": ["Biology"], "openAccessPdf": {"url": "https://www.biorxiv.org/content/biorxiv/early/2022/06/09/2022.05.13.491877.full.pdf", "status": "GREEN"}, "citationStyles": {"bibtex": "@None{Taisz2022GeneratingPR,\n author = {István Taisz and E. Donà and Daniel Münch and Shanice Bailey and William J. Morris and Kimberly I. Meechan and Katie M. Stevens and Irene Varela and Marina Gkantia and P. Schlegel and C. Ribeiro and G. Jefferis and Dana S. Galili},\n booktitle = {bioRxiv},\n journal = {bioRxiv},\n title = {Generating parallel representations of position and identity in the olfactory system},\n year = {2022}\n}\n"}, "referenceCount": 115, "publicationDate": "2022-06-08", "s2FieldsOfStudy": [{"source": "external", "category": "Biology"}, {"source": "s2-fos-model", "category": "Biology"}], "publicationTypes": null, "publicationVenue": {"id": "027ffd21-ebb0-4af8-baf5-911124292fd0", "url": "http://biorxiv.org/", "name": "bioRxiv", "type": "journal"}, "influentialCitationCount": 1}', '2023-01-27 10:57:29.152807', '2023-01-27 10:57:29.152807', 'Generating parallel representations of position and identity in the olfactory system', 2022, false, null, null, null, null);
INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('ebaf6fafb4adb7f3657fdbb869249c727ffccf7e', '{"url": "https://www.semanticscholar.org/paper/ebaf6fafb4adb7f3657fdbb869249c727ffccf7e", "year": 2022, "title": "A Survey of Visualization and Analysis in High‐Resolution Connectomics", "venue": "Computer graphics forum (Print)", "authors": [{"name": "J. Beyer", "authorId": "47273741"}, {"name": "Jakob Troidl", "authorId": "1995573509"}, {"name": "S. Boorboor", "authorId": "51218383"}, {"name": "M. Hadwiger", "authorId": "1761518"}, {"name": "A. Kaufman", "authorId": "1716343"}, {"name": "Hanspeter Pfister", "authorId": "2062947008"}], "journal": {"name": "Computer Graphics Forum", "volume": "41"}, "paperId": "ebaf6fafb4adb7f3657fdbb869249c727ffccf7e", "abstract": "The field of connectomics aims to reconstruct the wiring diagram of Neurons and synapses to enable new insights into the workings of the brain. Reconstructing and analyzing the Neuronal connectivity, however, relies on many individual steps, starting from high‐resolution data acquisition to automated segmentation, proofreading, interactive data exploration, and circuit analysis. All of these steps have to handle large and complex datasets and rely on or benefit from integrated visualization methods. In this state‐of‐the‐art report, we describe visualization methods that can be applied throughout the connectomics pipeline, from data acquisition to circuit analysis. We first define the different steps of the pipeline and focus on how visualization is currently integrated into these steps. We also survey open science initiatives in connectomics, including usable open‐source tools and publicly available datasets. Finally, we discuss open challenges and possible future directions of this exciting research field.", "citations": [{"title": "ViMo Visual Analysis of Neuronal Connectivity Motifs", "paperId": "606622ff5f693ba8ca9d3c72c5765d07fcd141e8"}, {"title": "Igneous: Distributed dense 3D segmentation meshing, neuron skeletonization, and hierarchical downsampling", "paperId": "b6703153b7200dc664e3d523a94df24e2a489815"}, {"title": "A Stratification Matrix Viewer for Analysis of Neural Network Data", "paperId": "ea522818d1ac5df8ec6b36b30ef9ae63d706be7b"}], "externalIds": {"DOI": "10.1111/cgf.14574", "DBLP": "journals/cgf/BeyerTBHKP22", "CorpusId": 248912992}, "isOpenAccess": false, "citationCount": 3, "fieldsOfStudy": ["Computer Science"], "openAccessPdf": null, "citationStyles": {"bibtex": "@[''JournalArticle'', ''Review'']{Beyer2022ASO,\n author = {J. Beyer and Jakob Troidl and S. Boorboor and M. Hadwiger and A. Kaufman and Hanspeter Pfister},\n booktitle = {Computer graphics forum (Print)},\n journal = {Computer Graphics Forum},\n title = {A Survey of Visualization and Analysis in High‐Resolution Connectomics},\n volume = {41},\n year = {2022}\n}\n"}, "referenceCount": 245, "publicationDate": "2022-06-01", "s2FieldsOfStudy": [{"source": "external", "category": "Computer Science"}, {"source": "s2-fos-model", "category": "Computer Science"}], "publicationTypes": ["JournalArticle", "Review"], "publicationVenue": {"id": "22e7b8f6-41b8-4144-8d46-e9a581042098", "url": "http://onlinelibrary.wiley.com/doi/10.1111/cgf.12532/abstract", "issn": "0167-7055", "name": "Computer graphics forum (Print)", "type": "journal", "alternate_names": ["Comput Graph Forum", "Computer Graphics Forum", "Comput graph forum (print"]}, "influentialCitationCount": 0}', '2023-01-27 10:57:32.653622', '2023-01-27 10:57:32.653622', 'A Survey of Visualization and Analysis in High‐Resolution Connectomics', 2022, false, null, null, null, null);
INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('cc7ab47f3720f3e9f16e20972d75965bc6eee842', '{"url": "https://www.semanticscholar.org/paper/cc7ab47f3720f3e9f16e20972d75965bc6eee842", "year": 2022, "title": "An essential experimental control for functional connectivity mapping with optogenetics", "venue": "bioRxiv", "authors": [{"name": "David Tadres", "authorId": "51955370"}, {"name": "Hiroshi M. Shiozaki", "authorId": "6365181"}, {"name": "I. Tastekin", "authorId": "6455185"}, {"name": "D. Stern", "authorId": "6930718"}, {"name": "Matthieu Louis", "authorId": "50577023"}], "journal": {"name": "bioRxiv"}, "paperId": "cc7ab47f3720f3e9f16e20972d75965bc6eee842", "abstract": "To establish functional connectivity between two candidate neurons that might form a circuit element, a common approach is to activate an optogenetic tool such as Chrimson in the candidate pre-synaptic neuron and monitor fluorescence of the calcium-sensitive indicator GCaMP in a candidate post-synaptic neuron. While performing such experiments, we found that low levels of leaky Chrimson expression can lead to strong artifactual GCaMP signals in presumptive postsynaptic neurons even when Chrimson is not intentionally expressed in any particular neurons. Withholding all-trans retinal, the chromophore required as a co-factor for Chrimson response to light, eliminates GCaMP signal but does not provide an experimental control for leaky Chrimson expression. Leaky Chrimson expression appears to be an inherent feature of current Chrimson transgenes, since artifactual connectivity was detected with Chrimson transgenes integrated into three different genomic locations (two insertions tested in larvae; a third insertion tested in the adult fly). These false-positive signals may complicate the interpretation of functional connectivity experiments. We illustrate how a no-Gal4 negative control improves interpretability of functional connectivity assays. We also propose a simple but effective procedure to identify experimental conditions that minimize potentially incorrect interpretations caused by leaky Chrimson expression.", "citations": [], "externalIds": {"DOI": "10.1101/2022.05.26.493610", "CorpusId": 249241868}, "isOpenAccess": true, "citationCount": 0, "fieldsOfStudy": ["Biology"], "openAccessPdf": {"url": "https://www.biorxiv.org/content/biorxiv/early/2022/05/28/2022.05.26.493610.full.pdf", "status": "GREEN"}, "citationStyles": {"bibtex": "@None{Tadres2022AnEE,\n author = {David Tadres and Hiroshi M. Shiozaki and I. Tastekin and D. Stern and Matthieu Louis},\n booktitle = {bioRxiv},\n journal = {bioRxiv},\n title = {An essential experimental control for functional connectivity mapping with optogenetics},\n year = {2022}\n}\n"}, "referenceCount": 55, "publicationDate": "2022-05-28", "s2FieldsOfStudy": [{"source": "external", "category": "Biology"}, {"source": "s2-fos-model", "category": "Biology"}], "publicationTypes": null, "publicationVenue": {"id": "027ffd21-ebb0-4af8-baf5-911124292fd0", "url": "http://biorxiv.org/", "name": "bioRxiv", "type": "journal"}, "influentialCitationCount": 0}', '2023-01-27 10:57:36.283994', '2023-01-27 10:57:36.283994', 'An essential experimental control for functional connectivity mapping with optogenetics', 2022, false, null, null, null, null);
INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('f64d3a093cbb4b1211527719478627590b959f71', '{"url": "https://www.semanticscholar.org/paper/f64d3a093cbb4b1211527719478627590b959f71", "year": 2022, "title": "Synthetic data enable experiments in atomistic machine learning", "venue": "ArXiv", "authors": [{"name": "John L. A. Gardner", "authorId": "2191695726"}, {"name": "Z. Beaulieu", "authorId": "123179570"}, {"name": "Volker L. Deringer", "authorId": "2432235"}], "journal": {"name": "ArXiv", "volume": "abs/2211.16443"}, "paperId": "f64d3a093cbb4b1211527719478627590b959f71", "abstract": ",", "citations": [{"title": "Transfer learning for chemically accurate interatomic neural network potentials", "paperId": "03914d96209cf70d7455d2dcfb6719a66287516a"}], "externalIds": {"DOI": "10.48550/arXiv.2211.16443", "DBLP": "journals/corr/abs-2211-16443", "ArXiv": "2211.16443", "CorpusId": 254069840}, "isOpenAccess": false, "citationCount": 1, "fieldsOfStudy": ["Computer Science", "Physics"], "openAccessPdf": null, "citationStyles": {"bibtex": "@[''JournalArticle'']{Gardner2022SyntheticDE,\n author = {John L. A. Gardner and Z. Beaulieu and Volker L. Deringer},\n booktitle = {ArXiv},\n journal = {ArXiv},\n title = {Synthetic data enable experiments in atomistic machine learning},\n volume = {abs/2211.16443},\n year = {2022}\n}\n"}, "referenceCount": 80, "publicationDate": "2022-11-29", "s2FieldsOfStudy": [{"source": "external", "category": "Computer Science"}, {"source": "external", "category": "Physics"}, {"source": "s2-fos-model", "category": "Computer Science"}], "publicationTypes": ["JournalArticle"], "publicationVenue": null, "influentialCitationCount": 1}', '2023-01-27 10:48:34.319504', '2023-01-27 10:48:34.319504', 'Synthetic data enable experiments in atomistic machine learning', 2022, false, null, null, null, null);
INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('41338a67c0167da5eb5ea746245abfb4a85e151a', '{"url": "https://www.semanticscholar.org/paper/41338a67c0167da5eb5ea746245abfb4a85e151a", "year": 2023, "title": "AI of Brain and Cognitive Sciences: From the Perspective of First Principles", "venue": "", "authors": [{"name": "Luyao Chen", "authorId": null}, {"name": "Zhiqiang Chen", "authorId": "2202263911"}, {"name": "Longsheng Jiang", "authorId": "2316539"}, {"name": "Xiang-Lan Liu", "authorId": "27111202"}, {"name": "Linlu Xu", "authorId": null}, {"name": "Bo Zhang", "authorId": null}, {"name": "Xiaolong Zou", "authorId": "46927650"}, {"name": "Jin-qiao Gao", "authorId": "72208543"}, {"name": "Yu Zhu", "authorId": null}, {"name": "Xizi Gong", "authorId": "114895139"}, {"name": "S. Yu", "authorId": "117217525"}, {"name": "Sen Song", "authorId": "2202226904"}, {"name": "Liangyi Chen", "authorId": "2202505050"}, {"name": "Fang Fang", "authorId": "2202112768"}, {"name": "Si Wu", "authorId": "2149346864"}, {"name": "Jia Liu", "authorId": "48210927"}], "journal": null, "paperId": "41338a67c0167da5eb5ea746245abfb4a85e151a", "abstract": "Nowadays, we have witnessed the great success of AI in various applications, including image classification, game playing, protein structure analysis, language translation, and content generation. Despite these powerful applications, there are still many tasks in our daily life that are rather simple to humans but pose great challenges to AI. These include image and language understanding, few-shot learning, abstract concepts, and low-energy cost computing. Thus, learning from the brain is still a promising way that can shed light on the development of next-generation AI. The brain is arguably the only known intelligent machine in the universe, which is the product of evolution for animals surviving in the natural environment. At the behavior level, psychology and cognitive sciences have demonstrated that human and animal brains can execute very intelligent high-level cognitive functions. At the structure level, cognitive and computational neurosciences have unveiled that the brain has extremely complicated but elegant network forms to support its functions. Over years, people are gathering knowledge about the structure and functions of the brain, and this process is accelerating recently along with the initiation of giant brain projects worldwide. Here, we argue that the general principles of brain functions are the most valuable things to inspire the development of AI. These general principles are the standard rules of the brain extracting, representing, manipulating, and retrieving information, and here we call them the first principles of the brain. This paper collects six such first principles. They are attractor network, criticality, random network, sparse coding, relational memory, and perceptual learning. On each topic, we review its biological background, fundamental property, potential application to AI, and future development.", "citations": [], "externalIds": {"ArXiv": "2301.08382", "CorpusId": 256080679}, "isOpenAccess": false, "citationCount": 0, "fieldsOfStudy": ["Biology"], "openAccessPdf": null, "citationStyles": {"bibtex": "@[''Review'']{Chen2023AIOB,\n author = {Luyao Chen and Zhiqiang Chen and Longsheng Jiang and Xiang-Lan Liu and Linlu Xu and Bo Zhang and Xiaolong Zou and Jin-qiao Gao and Yu Zhu and Xizi Gong and S. Yu and Sen Song and Liangyi Chen and Fang Fang and Si Wu and Jia Liu},\n title = {AI of Brain and Cognitive Sciences: From the Perspective of First Principles},\n year = {2023}\n}\n"}, "referenceCount": 272, "publicationDate": "2023-01-20", "s2FieldsOfStudy": [{"source": "external", "category": "Biology"}, {"source": "s2-fos-model", "category": "Computer Science"}, {"source": "s2-fos-model", "category": "Biology"}], "publicationTypes": ["Review"], "publicationVenue": null, "influentialCitationCount": 0}', '2023-01-27 10:48:37.948771', '2023-01-27 10:48:37.948771', 'AI of Brain and Cognitive Sciences: From the Perspective of First Principles', 2023, false, null, null, null, null);
INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('9088b5478fc706f368d2d1fd5661aba9384d782b', '{"url": "https://www.semanticscholar.org/paper/9088b5478fc706f368d2d1fd5661aba9384d782b", "year": 2022, "title": "Endocrine cybernetics: neuropeptides as molecular switches in behavioural decisions", "venue": "Open Biology", "authors": [{"name": "D. Nässel", "authorId": "7499211"}, {"name": "M. Zandawala", "authorId": "4662002"}], "journal": {"name": "Open Biology", "volume": "12"}, "paperId": "9088b5478fc706f368d2d1fd5661aba9384d782b", "abstract": "Plasticity in animal behaviour relies on the ability to integrate external and internal cues from the changing environment and hence modulate activity in synaptic circuits of the brain. This context-dependent neuromodulation is largely based on non-synaptic signalling with neuropeptides. Here, we describe select peptidergic systems in the Drosophila brain that act at different levels of a hierarchy to modulate behaviour and associated physiology. These systems modulate circuits in brain regions, such as the central complex and the mushroom bodies, which supervise specific behaviours. At the top level of the hierarchy there are small numbers of large peptidergic neurons that arborize widely in multiple areas of the brain to orchestrate or modulate global activity in a state and context-dependent manner. At the bottom level local peptidergic neurons provide executive neuromodulation of sensory gain and intrinsically in restricted parts of specific neuronal circuits. The orchestrating neurons receive interoceptive signals that mediate energy and sleep homeostasis, metabolic state and circadian timing, as well as external cues that affect food search, aggression or mating. Some of these cues can be triggers of conflicting behaviours such as mating versus aggression, or sleep versus feeding, and peptidergic neurons participate in circuits, enabling behaviour choices and switches.", "citations": [{"title": "Selection and Comparative Gene Expression of Midgut-Specific Targets for Drosophila suzukii", "paperId": "6dd847dbf78800680564fc1172d55b5f1c27aeb4"}, {"title": "System-wide mapping of neuropeptide-GPCR interactions in C. elegans", "paperId": "61a58110c88cac658f9efa5d3397695f560efb91"}, {"title": "The incidence of candidate binding sites for β-arrestin in Drosophila neuropeptide GPCRs", "paperId": "ed8df20c560b5d55b46ce9fc5a9e9b12a36841c3"}], "externalIds": {"DOI": "10.1098/rsob.220174", "PubMed": "35892199", "CorpusId": 251071320, "PubMedCentral": "9326288"}, "isOpenAccess": false, "citationCount": 3, "fieldsOfStudy": ["Medicine"], "openAccessPdf": null, "citationStyles": {"bibtex": "@[''Review'', ''JournalArticle'']{Nässel2022EndocrineCN,\n author = {D. Nässel and M. Zandawala},\n booktitle = {Open Biology},\n journal = {Open Biology},\n title = {Endocrine cybernetics: neuropeptides as molecular switches in behavioural decisions},\n volume = {12},\n year = {2022}\n}\n"}, "referenceCount": 360, "publicationDate": "2022-07-01", "s2FieldsOfStudy": [{"source": "external", "category": "Medicine"}, {"source": "s2-fos-model", "category": "Biology"}, {"source": "s2-fos-model", "category": "Psychology"}], "publicationTypes": ["Review", "JournalArticle"], "publicationVenue": {"id": "5c640382-d1f2-4258-80f5-11f02570f2fc", "url": "https://royalsocietypublishing.org/journal/rsob", "issn": "2046-2441", "name": "Open Biology", "type": "journal", "alternate_urls": ["http://rsob.royalsocietypublishing.org/"]}, "influentialCitationCount": 0}', '2023-01-27 10:48:41.561105', '2023-01-27 10:48:41.561105', 'Endocrine cybernetics: neuropeptides as molecular switches in behavioural decisions', 2022, false, null, null, null, null);
INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('1dd4b9b27250e20172260db7fbb4392ff5fec872', '{"url": "https://www.semanticscholar.org/paper/1dd4b9b27250e20172260db7fbb4392ff5fec872", "year": 2022, "title": "Interorgan communication through peripherally derived peptide hormones in Drosophila", "venue": "Fly", "authors": [{"name": "Naoki Okamoto", "authorId": "49703623"}, {"name": "Akira Watanabe", "authorId": "2154754330"}], "journal": {"name": "Fly", "pages": "152 - 176", "volume": "16"}, "paperId": "1dd4b9b27250e20172260db7fbb4392ff5fec872", "abstract": "ABSTRACT In multicellular organisms, endocrine factors such as hormones and cytokines regulate development and homoeostasis through communication between different organs. For understanding such interorgan communications through endocrine factors, the fruit fly Drosophila melanogaster serves as an excellent model system due to conservation of essential endocrine systems between flies and mammals and availability of powerful genetic tools. In Drosophila and other insects, functions of neuropeptides or peptide hormones from the central nervous system have been extensively studied. However, a series of recent studies conducted in Drosophila revealed that peptide hormones derived from peripheral tissues also play critical roles in regulating multiple biological processes, including growth, metabolism, reproduction, and behaviour. Here, we summarise recent advances in understanding target organs/tissues and functions of peripherally derived peptide hormones in Drosophila and describe how these hormones contribute to various biological events through interorgan communications.", "citations": [{"title": "Insulin-like peptide 8 (Ilp8) regulates female fecundity in flies", "paperId": "19325dd95cb9faa25bf2a0bd5f11a82ef91e1557"}, {"title": "The multiple lncRNAs encoding hsrω gene is essential for oogenesis in Drosophila", "paperId": "de694ad100936e90e73a8d1d4a89a36b3557c3ff"}, {"title": "A gut-derived hormone suppresses sugar appetite and regulates food choice in Drosophila", "paperId": "bb0db0dbb81086cfe14115326de52d19937360c0"}, {"title": "The incidence of candidate binding sites for β-arrestin in Drosophila neuropeptide GPCRs", "paperId": "ed8df20c560b5d55b46ce9fc5a9e9b12a36841c3"}, {"title": "Endocrine cybernetics: neuropeptides as molecular switches in behavioural decisions", "paperId": "9088b5478fc706f368d2d1fd5661aba9384d782b"}], "externalIds": {"DOI": "10.1080/19336934.2022.2061834", "PubMed": "35499154", "CorpusId": 248494316, "PubMedCentral": "9067537"}, "isOpenAccess": false, "citationCount": 5, "fieldsOfStudy": ["Medicine"], "openAccessPdf": null, "citationStyles": {"bibtex": "@[''Review'', ''JournalArticle'']{Okamoto2022InterorganCT,\n author = {Naoki Okamoto and Akira Watanabe},\n booktitle = {Fly},\n journal = {Fly},\n pages = {152 - 176},\n title = {Interorgan communication through peripherally derived peptide hormones in Drosophila},\n volume = {16},\n year = {2022}\n}\n"}, "referenceCount": 270, "publicationDate": "2022-05-01", "s2FieldsOfStudy": [{"source": "external", "category": "Medicine"}, {"source": "s2-fos-model", "category": "Biology"}], "publicationTypes": ["Review", "JournalArticle"], "publicationVenue": {"id": "b532b8f2-0702-41c7-bc2e-150a98398c87", "url": "http://landesbioscience.com/journals/fly", "issn": "1933-6934", "name": "Fly", "type": "journal", "alternate_urls": ["http://www.tandfonline.com/loi/kfly20", "https://www.tandfonline.com/toc/kfly20/current", "http://rzblx1.uni-regensburg.de/ezeit/warpto.phtml?colors=7&jour_id=85222"]}, "influentialCitationCount": 0}', '2023-01-27 10:56:06.354589', '2023-01-27 10:56:06.354589', 'Interorgan communication through peripherally derived peptide hormones in Drosophila', 2022, false, null, null, null, null);
