INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('99e7574093cc5d22fd70084860bce9de328d6a6d', '{"url": "https://www.semanticscholar.org/paper/99e7574093cc5d22fd70084860bce9de328d6a6d", "year": 2023, "title": "Virtual Fly Brain—An interactive atlas of the Drosophila nervous system", "venue": "Frontiers in Physiology", "authors": [{"name": "Robert Court", "authorId": "49501264"}, {"name": "Marta Costa", "authorId": "145739913"}, {"name": "C. Pilgrim", "authorId": "114890897"}, {"name": "G. Millburn", "authorId": "3038190"}, {"name": "Alex Holmes", "authorId": null}, {"name": "A. McLachlan", "authorId": "144604646"}, {"name": "Aoife Larkin", "authorId": "39350891"}, {"name": "N. Matentzoglu", "authorId": "1404374941"}, {"name": "Hüseyin Kir", "authorId": "2005581748"}, {"name": "Helen Parkinson", "authorId": "2068505041"}, {"name": "Nicolas H. Brown", "authorId": "2202575516"}, {"name": "C. O’Kane", "authorId": "1398161331"}, {"name": "J. Armstrong", "authorId": "143873116"}, {"name": "G. Jefferis", "authorId": "145196228"}, {"name": "D. Osumi-Sutherland", "authorId": "1403853027"}], "journal": {"volume": "14"}, "paperId": "99e7574093cc5d22fd70084860bce9de328d6a6d", "abstract": "As a model organism, Drosophila is uniquely placed to contribute to our understanding of how brains control complex behavior. Not only does it have complex adaptive behaviors, but also a uniquely powerful genetic toolkit, increasingly complete dense connectomic maps of the central nervous system and a rapidly growing set of transcriptomic profiles of cell types. But this also poses a challenge: Given the massive amounts of available data, how are researchers to Find, Access, Integrate and Reuse (FAIR) relevant data in order to develop an integrated anatomical and molecular picture of circuits, inform hypothesis generation, and find reagents for experiments to test these hypotheses? The Virtual Fly Brain (virtualflybrain.org) web application & API provide a solution to this problem, using FAIR principles to integrate 3D images of neurons and brain regions, connectomics, transcriptomics and reagent expression data covering the whole CNS in both larva and adult. Users can search for neurons, neuroanatomy and reagents by name, location, or connectivity, via text search, clicking on 3D images, search-by-image, and queries by type (e.g., dopaminergic neuron) or properties (e.g., synaptic input in the antennal lobe). Returned results include cross-registered 3D images that can be explored in linked 2D and 3D browsers or downloaded under open licenses, and extensive descriptions of cell types and regions curated from the literature. These solutions are potentially extensible to cover similar atlasing and data integration challenges in vertebrates.", "citations": [], "externalIds": {"DOI": "10.3389/fphys.2023.1076533", "CorpusId": 256234513}, "isOpenAccess": true, "citationCount": 0, "fieldsOfStudy": null, "openAccessPdf": {"url": "https://www.frontiersin.org/articles/10.3389/fphys.2023.1076533/pdf", "status": "GOLD"}, "citationStyles": {"bibtex": "@None{Court2023VirtualFB,\n author = {Robert Court and Marta Costa and C. Pilgrim and G. Millburn and Alex Holmes and A. McLachlan and Aoife Larkin and N. Matentzoglu and Hüseyin Kir and Helen Parkinson and Nicolas H. Brown and C. O’Kane and J. Armstrong and G. Jefferis and D. Osumi-Sutherland},\n booktitle = {Frontiers in Physiology},\n title = {Virtual Fly Brain—An interactive atlas of the Drosophila nervous system},\n volume = {14},\n year = {2023}\n}\n"}, "referenceCount": 63, "publicationDate": "2023-01-26", "s2FieldsOfStudy": [{"source": "s2-fos-model", "category": "Biology"}], "publicationTypes": null, "publicationVenue": {"id": "e28a224b-8ddb-49a6-82d2-17f23c68b30c", "url": "https://www.frontiersin.org/journals/physiology", "issn": "1664-042X", "name": "Frontiers in Physiology", "type": "journal", "alternate_urls": ["http://www.frontiersin.org/physiology/about", "http://frontiersin.org/physiology"], "alternate_names": ["Front Physiol"]}, "influentialCitationCount": 0}', '2023-01-27 10:56:13.373993', '2023-01-27 10:56:13.373993', 'Virtual Fly Brain—An interactive atlas of the Drosophila nervous system', 2023, false, null, null, null, null);
