INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('d5646f22977036240a5fb2358ef517f4360c848c', '{"url": "https://www.semanticscholar.org/paper/d5646f22977036240a5fb2358ef517f4360c848c", "year": 2023, "title": "Drosophila integrates visual evidence and counterevidence in self motion estimation", "venue": "bioRxiv", "authors": [{"name": "Ryosuke Tanaka", "authorId": "1903894102"}, {"name": "Baohua Zhou", "authorId": "2109060806"}, {"name": "Margarida Agrochao", "authorId": "7027838"}, {"name": "Bara A. Badwan", "authorId": "150931540"}, {"name": "Braedyn Au", "authorId": "103653417"}, {"name": "Natalia C. B. Matos", "authorId": "2199425349"}, {"name": "D. Clark", "authorId": "17420654"}], "journal": {"name": "bioRxiv"}, "paperId": "d5646f22977036240a5fb2358ef517f4360c848c", "abstract": "In selecting a behavior, animals should weigh sensory evidence both for and against their beliefs about the world. For instance, animals use optic flow to estimate and control their own rotation. However, existing models of flow detection can confuse the movement of external objects with genuine self motion. Here, we show that stationary patterns on the retina, which constitute negative evidence against self rotation, are used by the fruit fly Drosophila to suppress inappropriate stabilizing rotational behavior. In parallel in silico experiments, we show that artificial neural networks trained to distinguish self and world motion incorporate similar negative evidence. We used neural measurements and genetic manipulations to identify components of the circuitry for stationary pattern detection, which is parallel to the fly’s motion- and optic flow-detectors. Our results exemplify how the compact brain of the fly incorporates negative evidence to improve heading stability, exploiting geometrical constraints of the visual world.", "citations": [], "externalIds": {"DOI": "10.1101/2023.01.04.522814", "CorpusId": 255478045}, "isOpenAccess": true, "citationCount": 0, "fieldsOfStudy": ["Biology"], "openAccessPdf": {"url": "https://www.biorxiv.org/content/biorxiv/early/2023/01/05/2023.01.04.522814.full.pdf", "status": "GREEN"}, "citationStyles": {"bibtex": "@None{Tanaka2023DrosophilaIV,\n author = {Ryosuke Tanaka and Baohua Zhou and Margarida Agrochao and Bara A. Badwan and Braedyn Au and Natalia C. B. Matos and D. Clark},\n booktitle = {bioRxiv},\n journal = {bioRxiv},\n title = {Drosophila integrates visual evidence and counterevidence in self motion estimation},\n year = {2023}\n}\n"}, "referenceCount": 124, "publicationDate": "2023-01-05", "s2FieldsOfStudy": [{"source": "external", "category": "Biology"}, {"source": "s2-fos-model", "category": "Biology"}], "publicationTypes": null, "publicationVenue": {"id": "027ffd21-ebb0-4af8-baf5-911124292fd0", "url": "http://biorxiv.org/", "name": "bioRxiv", "type": "journal"}, "influentialCitationCount": 0}', '2023-01-27 10:56:20.416327', '2023-01-27 10:56:20.416327', 'Drosophila integrates visual evidence and counterevidence in self motion estimation', 2023, false, null, null, null, null);
INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('2caf6857b87b12fedc7286b0ddf5cef9bff09443', '{"url": "https://www.semanticscholar.org/paper/2caf6857b87b12fedc7286b0ddf5cef9bff09443", "year": 2023, "title": "Behavioral algorithms and neural mechanisms underlying odor-modulated locomotion in insects.", "venue": "Journal of Experimental Biology", "authors": [{"name": "Samuel P Wechsler", "authorId": "2159347848"}, {"name": "Vikas Bhandawat", "authorId": "5667655"}], "journal": {"name": "The Journal of experimental biology", "volume": "226 1"}, "paperId": "2caf6857b87b12fedc7286b0ddf5cef9bff09443", "abstract": "Odors released from mates and resources such as a host and food are often the first sensory signals that an animal can detect. Changes in locomotion in response to odors are an important mechanism by which animals access resources important to their survival. Odor-modulated changes in locomotion in insects constitute a whole suite of flexible behaviors that allow insects to close in on these resources from long distances and perform local searches to locate and subsequently assess them. Here, we review changes in odor-mediated locomotion across many insect species. We emphasize that changes in locomotion induced by odors are diverse. In particular, the olfactory stimulus is sporadic at long distances and becomes more continuous at short distances. This distance-dependent change in temporal profile produces a corresponding change in an insect''s locomotory strategy. We also discuss the neural circuits underlying odor modulation of locomotion.", "citations": [], "externalIds": {"DOI": "10.1242/jeb.200261", "PubMed": "36637433", "CorpusId": 255773352}, "isOpenAccess": false, "citationCount": 0, "fieldsOfStudy": ["Medicine"], "openAccessPdf": null, "citationStyles": {"bibtex": "@[''JournalArticle'', ''Review'']{Wechsler2023BehavioralAA,\n author = {Samuel P Wechsler and Vikas Bhandawat},\n booktitle = {Journal of Experimental Biology},\n journal = {The Journal of experimental biology},\n title = {Behavioral algorithms and neural mechanisms underlying odor-modulated locomotion in insects.},\n volume = {226 1},\n year = {2023}\n}\n"}, "referenceCount": 206, "publicationDate": "2023-01-01", "s2FieldsOfStudy": [{"source": "external", "category": "Medicine"}, {"source": "s2-fos-model", "category": "Biology"}, {"source": "s2-fos-model", "category": "Psychology"}, {"source": "s2-fos-model", "category": "Engineering"}], "publicationTypes": ["JournalArticle", "Review"], "publicationVenue": {"id": "5fb10eaf-a09b-4e33-a397-37d9210767e5", "url": "https://jeb.biologists.org/", "issn": "0022-0949", "name": "Journal of Experimental Biology", "type": "journal", "alternate_urls": ["http://jeb.biologists.org/"], "alternate_names": ["J Exp Biology", "The Journal of Experimental Biology"]}, "influentialCitationCount": 0}', '2023-01-27 10:56:24.040088', '2023-01-27 10:56:24.040088', 'Behavioral algorithms and neural mechanisms underlying odor-modulated locomotion in insects.', 2023, false, null, null, null, null);
INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('1e124b9f834d9f48c547f90650a9fcd5e9e071f8', '{"url": "https://www.semanticscholar.org/paper/1e124b9f834d9f48c547f90650a9fcd5e9e071f8", "year": 2022, "title": "Tools for comprehensive reconstruction and analysis of Drosophila motor circuits", "venue": "bioRxiv", "authors": [{"name": "Anthony W. Azevedo", "authorId": "38987729"}, {"name": "Ellen Lesser", "authorId": "3986207"}, {"name": "Brandon Mark", "authorId": "27155198"}, {"name": "Jasper S. Phelps", "authorId": "1943456298"}, {"name": "Leila Elabbady", "authorId": "91459622"}, {"name": "Sumiya Kuroda", "authorId": "2196149175"}, {"name": "Anne Sustar", "authorId": "4317513"}, {"name": "Anthony Moussa", "authorId": "143977020"}, {"name": "Avinash Kandelwal", "authorId": "2197168429"}, {"name": "Chris J. Dallmann", "authorId": "8428576"}, {"name": "S. Agrawal", "authorId": "2087023545"}, {"name": "Su-Yee J. Lee", "authorId": "2130699208"}, {"name": "B. Pratt", "authorId": "47549903"}, {"name": "A. Cook", "authorId": "46909391"}, {"name": "K. Skutt-Kakaria", "authorId": "1402277444"}, {"name": "Stephan Gerhard", "authorId": "2197169109"}, {"name": "Ran Lu", "authorId": "1406193129"}, {"name": "Nico Kemnitz", "authorId": "41124209"}, {"name": "Kisuk Lee", "authorId": "2152541203"}, {"name": "Akhilesh Halageri", "authorId": "1579775589"}, {"name": "Manuel A. Castro", "authorId": "2092627229"}, {"name": "Dodam Ih", "authorId": "14972923"}, {"name": "Jay Gager", "authorId": "1926231262"}, {"name": "M. Tammam", "authorId": "2074629469"}, {"name": "Sven Dorkenwald", "authorId": "2122374817"}, {"name": "F. Collman", "authorId": "6598643"}, {"name": "C. Schneider-Mizell", "authorId": "1397852111"}, {"name": "Derrick Brittain", "authorId": "145536550"}, {"name": "Chris S. Jordan", "authorId": "41130241"}, {"name": "M. Dickinson", "authorId": "35024991"}, {"name": "A. Pacureanu", "authorId": "48370955"}, {"name": "H. Seung", "authorId": "144924970"}, {"name": "T. Macrina", "authorId": "5056982"}, {"name": "W. Lee", "authorId": "2004120"}, {"name": "John C. Tuthill", "authorId": "35795877"}], "journal": {"name": "bioRxiv"}, "paperId": "1e124b9f834d9f48c547f90650a9fcd5e9e071f8", "abstract": "Like the vertebrate spinal cord, the insect ventral nerve cord (VNC) mediates limb sensation and motor control. Here, we applied automated tools for electron microscopy (EM) volume alignment, neuron reconstruction, and synapse prediction to create a draft connectome of the Drosophila VNC. To interpret the VNC connectome, it is crucial to know its relationship with the rest of the body. We therefore mapped the muscle targets of leg and wing motor neurons in the connectome by comparing their morphology to genetic driver lines, dye fills, and x-ray holographic nano-tomography volumes of the fly leg and wing. Knowing the outputs of the connectome allowed us to identify neural circuits that coordinate the wings with the middle and front legs during escape takeoff. We provide the draft VNC connectome and motor neuron atlas, along with tools for programmatic and interactive access, as community resources.", "citations": [], "externalIds": {"DOI": "10.1101/2022.12.15.520299", "CorpusId": 254736092}, "isOpenAccess": true, "citationCount": 0, "fieldsOfStudy": ["Biology"], "openAccessPdf": {"url": "https://www.biorxiv.org/content/biorxiv/early/2022/12/15/2022.12.15.520299.full.pdf", "status": "GREEN"}, "citationStyles": {"bibtex": "@None{Azevedo2022ToolsFC,\n author = {Anthony W. Azevedo and Ellen Lesser and Brandon Mark and Jasper S. Phelps and Leila Elabbady and Sumiya Kuroda and Anne Sustar and Anthony Moussa and Avinash Kandelwal and Chris J. Dallmann and S. Agrawal and Su-Yee J. Lee and B. Pratt and A. Cook and K. Skutt-Kakaria and Stephan Gerhard and Ran Lu and Nico Kemnitz and Kisuk Lee and Akhilesh Halageri and Manuel A. Castro and Dodam Ih and Jay Gager and M. Tammam and Sven Dorkenwald and F. Collman and C. Schneider-Mizell and Derrick Brittain and Chris S. Jordan and M. Dickinson and A. Pacureanu and H. Seung and T. Macrina and W. Lee and John C. Tuthill},\n booktitle = {bioRxiv},\n journal = {bioRxiv},\n title = {Tools for comprehensive reconstruction and analysis of Drosophila motor circuits},\n year = {2022}\n}\n"}, "referenceCount": 97, "publicationDate": "2022-12-15", "s2FieldsOfStudy": [{"source": "external", "category": "Biology"}, {"source": "s2-fos-model", "category": "Biology"}], "publicationTypes": null, "publicationVenue": {"id": "027ffd21-ebb0-4af8-baf5-911124292fd0", "url": "http://biorxiv.org/", "name": "bioRxiv", "type": "journal"}, "influentialCitationCount": 0}', '2023-01-27 10:56:27.682435', '2023-01-27 10:56:27.682435', 'Tools for comprehensive reconstruction and analysis of Drosophila motor circuits', 2022, false, null, null, null, null);
INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('33cc748747d7c91c432214ec3b34c8591f87418c', '{"url": "https://www.semanticscholar.org/paper/33cc748747d7c91c432214ec3b34c8591f87418c", "year": 2022, "title": "Eye structure shapes neuron function in Drosophila motion vision", "venue": "bioRxiv", "authors": [{"name": "Arthur Zhao", "authorId": "1657013303"}, {"name": "Eyal Gruntman", "authorId": "2637995"}, {"name": "A. Nern", "authorId": "4469690"}, {"name": "N. Iyer", "authorId": "47171905"}, {"name": "E. M. Rogers", "authorId": "40015945"}, {"name": "Sanna Koskela", "authorId": "50545044"}, {"name": "I. Siwanowicz", "authorId": "4302022"}, {"name": "M. Dreher", "authorId": "1572706864"}, {"name": "Miriam A. Flynn", "authorId": "2117558607"}, {"name": "Connor W. Laughland", "authorId": "2117559826"}, {"name": "H. Ludwig", "authorId": "2117558544"}, {"name": "Alex G. Thomson", "authorId": "2196128708"}, {"name": "Cullen P. Moran", "authorId": "2196265329"}, {"name": "Bruck Gezahegn", "authorId": "10783749"}, {"name": "D. Bock", "authorId": "35571193"}, {"name": "Michael B. Reiser", "authorId": "3307045"}], "journal": {"name": "bioRxiv"}, "paperId": "33cc748747d7c91c432214ec3b34c8591f87418c", "abstract": "Many animals rely on vision to navigate through their environment. The pattern of changes in the visual scene induced by self-motion is the optic flow1, which is first estimated in local patches by directionally selective (DS) neurons2–4. But how should the arrays of DS neurons, each responsive to motion in a preferred direction at a specific retinal position, be organized to support robust decoding of optic flow by downstream circuits? Understanding this global organization is challenging because it requires mapping fine, local features of neurons across the animal’s field of view3. In Drosophila, the asymmetric dendrites of the T4 and T5 DS neurons establish their preferred direction, making it possible to predict DS responses from anatomy4,5. Here we report that the preferred directions of fly DS neurons vary at different retinal positions and show that this spatial variation is established by the anatomy of the compound eye. To estimate the preferred directions across the visual field, we reconstructed hundreds of T4 neurons in a full brain EM volume6 and discovered unexpectedly stereotypical dendritic arborizations that are independent of location. We then used whole-head μCT scans to map the viewing directions of all compound eye facets and found a non-uniform sampling of visual space that explains the spatial variation in preferred directions. Our findings show that the organization of preferred directions in the fly is largely determined by the compound eye, exposing an intimate and unexpected connection between the peripheral structure of the eye, functional properties of neurons deep in the brain, and the control of body movements.", "citations": [], "externalIds": {"DOI": "10.1101/2022.12.14.520178", "CorpusId": 254736078}, "isOpenAccess": true, "citationCount": 0, "fieldsOfStudy": ["Biology"], "openAccessPdf": {"url": "https://www.biorxiv.org/content/biorxiv/early/2022/12/15/2022.12.14.520178.full.pdf", "status": "GREEN"}, "citationStyles": {"bibtex": "@None{Zhao2022EyeSS,\n author = {Arthur Zhao and Eyal Gruntman and A. Nern and N. Iyer and E. M. Rogers and Sanna Koskela and I. Siwanowicz and M. Dreher and Miriam A. Flynn and Connor W. Laughland and H. Ludwig and Alex G. Thomson and Cullen P. Moran and Bruck Gezahegn and D. Bock and Michael B. Reiser},\n booktitle = {bioRxiv},\n journal = {bioRxiv},\n title = {Eye structure shapes neuron function in Drosophila motion vision},\n year = {2022}\n}\n"}, "referenceCount": 70, "publicationDate": "2022-12-15", "s2FieldsOfStudy": [{"source": "external", "category": "Biology"}, {"source": "s2-fos-model", "category": "Biology"}], "publicationTypes": null, "publicationVenue": {"id": "027ffd21-ebb0-4af8-baf5-911124292fd0", "url": "http://biorxiv.org/", "name": "bioRxiv", "type": "journal"}, "influentialCitationCount": 0}', '2023-01-27 10:56:31.297015', '2023-01-27 10:56:31.297015', 'Eye structure shapes neuron function in Drosophila motion vision', 2022, false, null, null, null, null);
INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('606622ff5f693ba8ca9d3c72c5765d07fcd141e8', '{"url": "https://www.semanticscholar.org/paper/606622ff5f693ba8ca9d3c72c5765d07fcd141e8", "year": 2022, "title": "ViMo Visual Analysis of Neuronal Connectivity Motifs", "venue": "bioRxiv", "authors": [{"name": "Jakob Troidl", "authorId": "1995573509"}, {"name": "Simon Warchol", "authorId": "2130925892"}, {"name": "Jinhan Choi", "authorId": "2195399418"}, {"name": "Jordan K. Matelsky", "authorId": "24023618"}, {"name": "Nagaraju Dhanyasi", "authorId": "6866526"}, {"name": "Xueying Wang", "authorId": "2108013123"}, {"name": "B. Wester", "authorId": "1916423"}, {"name": "D. Wei", "authorId": "1766333"}, {"name": "J. Lichtman", "authorId": "1698121"}, {"name": "H. Pfister", "authorId": "143758236"}, {"name": "J. Beyer", "authorId": "47273741"}], "journal": {"name": "bioRxiv"}, "paperId": "606622ff5f693ba8ca9d3c72c5765d07fcd141e8", "abstract": "Recent advances in high-resolution connectomics provide researchers access to accurate reconstructions of vast neuronal circuits and brain networks for the first time. Neuroscientists anticipate analyzing these networks to gain a better understanding of information processing in the brain. In particular, scientists are interested in identifying specific network motifs, i.e., repeating subgraphs of the larger brain network that are believed to be neuronal building blocks. To analyze these motifs, it is crucial to review instances of a motif in the brain network and then map the graph structure to the detailed 3D reconstructions of the involved neurons and synapses. We present Vimo, an interactive visual approach to analyze neuronal motifs and motif chains in large brain networks. Experts can sketch network motifs intuitively in a visual interface and specify structural properties of the involved neurons and synapses to query large connectomics datasets. Motif instances (MIs) can be explored in high-resolution 3D renderings of the involved neurons and synapses. To reduce visual clutter and simplify the analysis of MIs, we designed a continuous focus&context metaphor inspired by continuous visual abstractions [MAAB∗18] that allows the user to transition from the highly-detailed rendering of the anatomical structure to views that emphasize the underlying motif structure and synaptic connectivity. Furthermore, Vimo supports the identification of motif chains where a motif is used repeatedly to form a longer synaptic chain. We evaluate Vimo in a user study with seven domain experts and an in-depth case study on motifs in the central complex (CX) of the fruit fly brain. CCS Concepts Human-Centered Computing → Visual motif analysis, Focus&Context, Scientific visualization, Neuroscience;", "citations": [], "externalIds": {"DOI": "10.1101/2022.12.09.519772", "CorpusId": 254593080}, "isOpenAccess": true, "citationCount": 0, "fieldsOfStudy": ["Biology"], "openAccessPdf": {"url": "https://www.biorxiv.org/content/biorxiv/early/2022/12/11/2022.12.09.519772.full.pdf", "status": "GREEN"}, "citationStyles": {"bibtex": "@[''Review'']{Troidl2022ViMoVA,\n author = {Jakob Troidl and Simon Warchol and Jinhan Choi and Jordan K. Matelsky and Nagaraju Dhanyasi and Xueying Wang and B. Wester and D. Wei and J. Lichtman and H. Pfister and J. Beyer},\n booktitle = {bioRxiv},\n journal = {bioRxiv},\n title = {ViMo Visual Analysis of Neuronal Connectivity Motifs},\n year = {2022}\n}\n"}, "referenceCount": 43, "publicationDate": "2022-12-11", "s2FieldsOfStudy": [{"source": "external", "category": "Biology"}, {"source": "s2-fos-model", "category": "Biology"}, {"source": "s2-fos-model", "category": "Computer Science"}], "publicationTypes": ["Review"], "publicationVenue": {"id": "027ffd21-ebb0-4af8-baf5-911124292fd0", "url": "http://biorxiv.org/", "name": "bioRxiv", "type": "journal"}, "influentialCitationCount": 0}', '2023-01-27 10:56:34.957391', '2023-01-27 10:56:34.957391', 'ViMo Visual Analysis of Neuronal Connectivity Motifs', 2022, false, null, null, null, null);
INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('b6703153b7200dc664e3d523a94df24e2a489815', '{"url": "https://www.semanticscholar.org/paper/b6703153b7200dc664e3d523a94df24e2a489815", "year": 2022, "title": "Igneous: Distributed dense 3D segmentation meshing, neuron skeletonization, and hierarchical downsampling", "venue": "Frontiers in Neural Circuits", "authors": [{"name": "W. Silversmith", "authorId": "2108734"}, {"name": "A. Zlateski", "authorId": "3134596"}, {"name": "J. A. Bae", "authorId": "2072616109"}, {"name": "Ignacio Tartavull", "authorId": "24011805"}, {"name": "Nico Kemnitz", "authorId": "41124209"}, {"name": "Jingpeng Wu", "authorId": "31421132"}, {"name": "H. Seung", "authorId": "144924970"}], "journal": {"name": "Frontiers in Neural Circuits", "volume": "16"}, "paperId": "b6703153b7200dc664e3d523a94df24e2a489815", "abstract": "Three-dimensional electron microscopy images of brain tissue and their dense segmentations are now petascale and growing. These volumes require the mass production of dense segmentation-derived neuron skeletons, multi-resolution meshes, image hierarchies (for both modalities) for visualization and analysis, and tools to manage the large amount of data. However, open tools for large-scale meshing, skeletonization, and data management have been missing. Igneous is a Python-based distributed computing framework that enables economical meshing, skeletonization, image hierarchy creation, and data management using cloud or cluster computing that has been proven to scale horizontally. We sketch Igneous''s computing framework, show how to use it, and characterize its performance and data storage.", "citations": [], "externalIds": {"DOI": "10.3389/fncir.2022.977700", "PubMed": "36506593", "CorpusId": 253840910, "PubMedCentral": "9732676"}, "isOpenAccess": true, "citationCount": 0, "fieldsOfStudy": ["Medicine"], "openAccessPdf": {"url": "https://www.frontiersin.org/articles/10.3389/fncir.2022.977700/pdf", "status": "GOLD"}, "citationStyles": {"bibtex": "@[''JournalArticle'']{Silversmith2022Igneous:DD,\n author = {W. Silversmith and A. Zlateski and J. A. Bae and Ignacio Tartavull and Nico Kemnitz and Jingpeng Wu and H. Seung},\n booktitle = {Frontiers in Neural Circuits},\n journal = {Frontiers in Neural Circuits},\n title = {Igneous: Distributed dense 3D segmentation meshing, neuron skeletonization, and hierarchical downsampling},\n volume = {16},\n year = {2022}\n}\n"}, "referenceCount": 59, "publicationDate": "2022-11-25", "s2FieldsOfStudy": [{"source": "external", "category": "Medicine"}, {"source": "s2-fos-model", "category": "Computer Science"}], "publicationTypes": ["JournalArticle"], "publicationVenue": {"id": "09dc8437-eb87-49ec-a21b-ecb208a5890b", "url": "https://www.frontiersin.org/journals/neural-circuits", "issn": "1662-5110", "name": "Frontiers in Neural Circuits", "type": "journal", "alternate_urls": ["http://www.frontiersin.org/neuralcircuits/", "http://www.frontiersin.org/neural_circuits"], "alternate_names": ["Front Neural Circuit"]}, "influentialCitationCount": 0}', '2023-01-27 10:56:38.321482', '2023-01-27 10:56:38.321482', 'Igneous: Distributed dense 3D segmentation meshing, neuron skeletonization, and hierarchical downsampling', 2022, false, null, null, null, null);
INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('459debf1e78a0990b2ca27c70080d0ddb7fc6bf9', '{"url": "https://www.semanticscholar.org/paper/459debf1e78a0990b2ca27c70080d0ddb7fc6bf9", "year": 2022, "title": "Flexibility of neural circuits regulating mating behaviors in mice and flies", "venue": "Frontiers in Neural Circuits", "authors": [{"name": "T. Karigo", "authorId": "6836121"}, {"name": "David S Deutsch", "authorId": "2028241054"}], "journal": {"name": "Frontiers in Neural Circuits", "volume": "16"}, "paperId": "459debf1e78a0990b2ca27c70080d0ddb7fc6bf9", "abstract": "Mating is essential for the reproduction of animal species. As mating behaviors are high-risk and energy-consuming processes, it is critical for animals to make adaptive mating decisions. This includes not only finding a suitable mate, but also adapting mating behaviors to the animal’s needs and environmental conditions. Internal needs include physical states (e.g., hunger) and emotional states (e.g., fear), while external conditions include both social cues (e.g., the existence of predators or rivals) and non-social factors (e.g., food availability). With recent advances in behavioral neuroscience, we are now beginning to understand the neural basis of mating behaviors, particularly in genetic model organisms such as mice and flies. However, how internal and external factors are integrated by the nervous system to enable adaptive mating-related decision-making in a state- and context-dependent manner is less well understood. In this article, we review recent knowledge regarding the neural basis of flexible mating behaviors from studies of flies and mice. By contrasting the knowledge derived from these two evolutionarily distant model organisms, we discuss potential conserved and divergent neural mechanisms involved in the control of flexible mating behaviors in invertebrate and vertebrate brains.", "citations": [], "externalIds": {"DOI": "10.3389/fncir.2022.949781", "PubMed": "36426135", "CorpusId": 253386242, "PubMedCentral": "9679785"}, "isOpenAccess": true, "citationCount": 0, "fieldsOfStudy": ["Medicine"], "openAccessPdf": {"url": "https://www.frontiersin.org/articles/10.3389/fncir.2022.949781/pdf", "status": "GOLD"}, "citationStyles": {"bibtex": "@[''Review'', ''JournalArticle'']{Karigo2022FlexibilityON,\n author = {T. Karigo and David S Deutsch},\n booktitle = {Frontiers in Neural Circuits},\n journal = {Frontiers in Neural Circuits},\n title = {Flexibility of neural circuits regulating mating behaviors in mice and flies},\n volume = {16},\n year = {2022}\n}\n"}, "referenceCount": 411, "publicationDate": "2022-11-08", "s2FieldsOfStudy": [{"source": "external", "category": "Medicine"}, {"source": "s2-fos-model", "category": "Biology"}, {"source": "s2-fos-model", "category": "Psychology"}], "publicationTypes": ["Review", "JournalArticle"], "publicationVenue": {"id": "09dc8437-eb87-49ec-a21b-ecb208a5890b", "url": "https://www.frontiersin.org/journals/neural-circuits", "issn": "1662-5110", "name": "Frontiers in Neural Circuits", "type": "journal", "alternate_urls": ["http://www.frontiersin.org/neuralcircuits/", "http://www.frontiersin.org/neural_circuits"], "alternate_names": ["Front Neural Circuit"]}, "influentialCitationCount": 0}', '2023-01-27 10:56:41.983940', '2023-01-27 10:56:41.983940', 'Flexibility of neural circuits regulating mating behaviors in mice and flies', 2022, false, null, null, null, null);
INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('8c5346435df56e3f632a010e752980b05cd81cad', '{"url": "https://www.semanticscholar.org/paper/8c5346435df56e3f632a010e752980b05cd81cad", "year": 2022, "title": "Chemoreceptor co-expression in Drosophila melanogaster olfactory neurons", "venue": "eLife", "authors": [{"name": "Darya Task", "authorId": "7686107"}, {"name": "Chun-Chieh Lin", "authorId": "50555304"}, {"name": "Alina Vulpe", "authorId": "39544938"}, {"name": "A. Afify", "authorId": "40600157"}, {"name": "Sydney Ballou", "authorId": "2067352529"}, {"name": "M. Brbić", "authorId": "2117751468"}, {"name": "P. Schlegel", "authorId": "28566201"}, {"name": "J. Raji", "authorId": "31219622"}, {"name": "G. Jefferis", "authorId": "145196228"}, {"name": "Hongjie Li", "authorId": "2108545954"}, {"name": "K. Menuz", "authorId": "48364139"}, {"name": "C. Potter", "authorId": "40574640"}], "journal": {"name": "eLife", "volume": "11"}, "paperId": "8c5346435df56e3f632a010e752980b05cd81cad", "abstract": "Drosophila melanogaster olfactory neurons have long been thought to express only one chemosensory receptor gene family. There are two main olfactory receptor gene families in Drosophila, the odorant receptors (ORs) and the ionotropic receptors (IRs). The dozens of odorant-binding receptors in each family require at least one co-receptor gene in order to function: Orco for ORs, and Ir25a, Ir8a, and Ir76b for IRs. Using a new genetic knock-in strategy, we targeted the four co-receptors representing the main chemosensory families in D. melanogaster (Orco, Ir8a, Ir76b, Ir25a). Co-receptor knock-in expression patterns were verified as accurate representations of endogenous expression. We find extensive overlap in expression among the different co-receptors. As defined by innervation into antennal lobe glomeruli, Ir25a is broadly expressed in 88% of all olfactory sensory neuron classes and is co-expressed in 82% of Orco+ neuron classes, including all neuron classes in the maxillary palp. Orco, Ir8a, and Ir76b expression patterns are also more expansive than previously assumed. Single sensillum recordings from Orco-expressing Ir25a mutant antennal and palpal neurons identify changes in olfactory responses. We also find co-expression of Orco and Ir25a in Drosophila sechellia and Anopheles coluzzii olfactory neurons. These results suggest that co-expression of chemosensory receptors is common in insect olfactory neurons. Together, our data present the first comprehensive map of chemosensory co-receptor expression and reveal their unexpected widespread co-expression in the fly olfactory system.", "citations": [{"title": "Neuron-glia interaction at the receptor level affects olfactory perception in adult Drosophila", "paperId": "5bd1e88efdff1d96939c870eba2239606ffee59a"}, {"title": "Comparative transcriptomic assessment of the chemosensory receptor repertoire of Drosophila suzukii adult and larval olfactory organs.", "paperId": "b4edb8cadc03edc9cc38f75b7c9539780af528fd"}, {"title": "Neurogenetic identification of mosquito sensory neurons", "paperId": "5498a60ca06df112f06f2c99e0c173998c4ebc93"}, {"title": "Activation of pheromone-sensitive olfactory neurons by plant volatiles in the moth Agrotis ipsilon does not occur at the level of the pheromone receptor protein", "paperId": "5ce640a657c00456ef0a3155d42c6a8012a9076d"}, {"title": "Experimental and theoretical probe on mechano- and chemosensory integration in the insect antennal lobe", "paperId": "f8a280668d38da1ca9ed70d97961317c4ddd4c09"}, {"title": "Pheromone sensing in Drosophila requires support cell-expressed Osiris 8", "paperId": "5dbac61d9d1bd33ffce553e3b89587315322d662"}, {"title": "Recent Insights into Insect Olfactory Receptors and Odorant-Binding Proteins", "paperId": "c4d3ac934842764f21986705fd30f79515642d4f"}, {"title": "Chemosensory Ionotropic Receptors in Human Host-Seeking Mosquitoes.", "paperId": "61d5d5ed9b2d8aee91d070d2a2b14892c0c49580"}, {"title": "Scent of a human: The mosquito olfactory system defies dogma to ensure attraction to humans", "paperId": "68d8eb21cd4451547ff1cf8859b67dc751a94782"}, {"title": "No functional contribution of the gustatory receptor, Gr64b, co-expressed in olfactory sensory neurons of Drosophila melanogaster", "paperId": "80507d5d69808393fe065c0f40f03240f6d3ad35"}, {"title": "Sensory mechanisms for the shift from phytophagy to haematophagy in mosquitoes.", "paperId": "ec4f94ac27281a9ed8247f6882979858900ead92"}, {"title": "Differential mosquito attraction to humans is associated with skin-derived carboxylic acid levels", "paperId": "013253906a4fcb18e615fd51e151107b94aa12be"}, {"title": "Non-canonical odor coding in the mosquito", "paperId": "4ba1a20d41ac0a08980c0dd9340a0e5e5afb2508"}, {"title": "Discrete roles of Ir76b ionotropic coreceptor impact olfaction, blood feeding, and mating in the malaria vector mosquito Anopheles coluzzii", "paperId": "9a8422b412e0eff9e48fb025f1c10b59a338757a"}], "externalIds": {"DOI": "10.7554/eLife.72599", "PubMed": "35442190", "CorpusId": 248263921, "PubMedCentral": "9020824"}, "isOpenAccess": false, "citationCount": 14, "fieldsOfStudy": ["Medicine"], "openAccessPdf": null, "citationStyles": {"bibtex": "@[''JournalArticle'']{Task2022ChemoreceptorCI,\n author = {Darya Task and Chun-Chieh Lin and Alina Vulpe and A. Afify and Sydney Ballou and M. Brbić and P. Schlegel and J. Raji and G. Jefferis and Hongjie Li and K. Menuz and C. Potter},\n booktitle = {eLife},\n journal = {eLife},\n title = {Chemoreceptor co-expression in Drosophila melanogaster olfactory neurons},\n volume = {11},\n year = {2022}\n}\n"}, "referenceCount": 173, "publicationDate": "2022-04-20", "s2FieldsOfStudy": [{"source": "external", "category": "Medicine"}, {"source": "s2-fos-model", "category": "Biology"}], "publicationTypes": ["JournalArticle"], "publicationVenue": {"id": "07365b9a-c0ce-4dd3-b93b-a02e1c81e0c6", "url": "https://epub.uni-regensburg.de/40444/", "issn": "2050-084X", "name": "eLife", "type": "journal", "alternate_urls": ["https://elifesciences.org/", "https://elife.elifesciences.org/", "http://elifesciences.org/"]}, "influentialCitationCount": 2}', '2023-01-27 10:57:39.908831', '2023-01-27 10:57:39.908831', 'Chemoreceptor co-expression in Drosophila melanogaster olfactory neurons', 2022, false, null, null, null, null);
INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('3209c297a4db06543dbd45a41a97ed99804cbe52', '{"url": "https://www.semanticscholar.org/paper/3209c297a4db06543dbd45a41a97ed99804cbe52", "year": 2022, "title": "Taste quality and hunger interactions in a feeding sensorimotor circuit", "venue": "bioRxiv", "authors": [{"name": "Philip K. Shiu", "authorId": "5167316"}, {"name": "Gabriella R. Sterne", "authorId": "2091490313"}, {"name": "Stefanie Engert", "authorId": "2162740386"}, {"name": "B. Dickson", "authorId": "2454761"}, {"name": "Kristin Scott", "authorId": "144080092"}], "journal": {"name": "eLife", "volume": "11"}, "paperId": "3209c297a4db06543dbd45a41a97ed99804cbe52", "abstract": "Taste detection and hunger state dynamically regulate the decision to initiate feeding. To study how context-appropriate feeding decisions are generated, we combined synaptic resolution circuit reconstruction with targeted genetic access to specific neurons to elucidate a gustatory sensorimotor circuit for feeding initiation in adult Drosophila melanogaster. This circuit connects gustatory sensory neurons to proboscis motor neurons through three intermediate layers. Most neurons in this pathway are necessary and sufficient for proboscis extension, a feeding initiation behavior, and respond selectively to sugar taste detection. Pathway activity is amplified by hunger signals that act at select second-order neurons to promote feeding initiation in food-deprived animals. In contrast, the feeding initiation circuit is inhibited by a bitter taste pathway that impinges on premotor neurons, illuminating a local motif that weighs sugar and bitter taste detection to adjust behavioral outcome. Together, these studies reveal central mechanisms for the integration of external taste detection and internal nutritive state to flexibly execute a critical feeding decision.", "citations": [{"title": "Taste cues elicit prolonged modulation of feeding behavior in Drosophila", "paperId": "623c268a38a900036c52a8a3a1812ce13767722d"}], "externalIds": {"DOI": "10.7554/eLife.79887", "PubMed": "35791902", "CorpusId": 248219451, "PubMedCentral": "9292995"}, "isOpenAccess": false, "citationCount": 1, "fieldsOfStudy": ["Biology", "Medicine"], "openAccessPdf": null, "citationStyles": {"bibtex": "@[''JournalArticle'']{Shiu2022TasteQA,\n author = {Philip K. Shiu and Gabriella R. Sterne and Stefanie Engert and B. Dickson and Kristin Scott},\n booktitle = {bioRxiv},\n journal = {eLife},\n title = {Taste quality and hunger interactions in a feeding sensorimotor circuit},\n volume = {11},\n year = {2022}\n}\n"}, "referenceCount": 117, "publicationDate": "2022-04-13", "s2FieldsOfStudy": [{"source": "external", "category": "Biology"}, {"source": "external", "category": "Medicine"}, {"source": "s2-fos-model", "category": "Biology"}], "publicationTypes": ["JournalArticle"], "publicationVenue": {"id": "027ffd21-ebb0-4af8-baf5-911124292fd0", "url": "http://biorxiv.org/", "name": "bioRxiv", "type": "journal"}, "influentialCitationCount": 0}', '2023-01-27 10:57:43.546858', '2023-01-27 10:57:43.546858', 'Taste quality and hunger interactions in a feeding sensorimotor circuit', 2022, false, null, null, null, null);
INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('a641df4e738354237c2ba9cdb13c7dbca7d58171', '{"url": "https://www.semanticscholar.org/paper/a641df4e738354237c2ba9cdb13c7dbca7d58171", "year": 2022, "title": "Petascale pipeline for precise alignment of images from serial section electron microscopy", "venue": "bioRxiv", "authors": [{"name": "Sergiy Popovych", "authorId": "1840375"}, {"name": "T. Macrina", "authorId": "5056982"}, {"name": "Nico Kemnitz", "authorId": "41124209"}, {"name": "Manuel A. Castro", "authorId": "2092627229"}, {"name": "Barak Nehoran", "authorId": "1926235266"}, {"name": "Zhen Jia", "authorId": "2072782593"}, {"name": "J. A. Bae", "authorId": "2072616109"}, {"name": "Eric Mitchell", "authorId": "49688913"}, {"name": "Shang Mu", "authorId": "47330620"}, {"name": "Eric T. Trautman", "authorId": "3543156"}, {"name": "S. Saalfeld", "authorId": "1889009"}, {"name": "Kai Li", "authorId": "2158258070"}, {"name": "Sebastian Seung", "authorId": "2059694068"}], "journal": {"name": "bioRxiv"}, "paperId": "a641df4e738354237c2ba9cdb13c7dbca7d58171", "abstract": "The reconstruction of neural circuits from serial section electron microscopy (ssEM) images is being accelerated by automatic image segmentation methods. Segmentation accuracy is often limited by the preceding step of aligning 2D section images to create a 3D image stack. Precise and robust alignment in the presence of image artifacts is challenging, especially as datasets are attaining the petascale. We present a computational pipeline for aligning ssEM images with several key elements. Self-supervised convolutional nets are trained via metric learning to encode and align image pairs, and they are used to initialize iterative fine-tuning of alignment. A procedure called vector voting increases robustness to image artifacts or missing image data. For speedup the series is divided into blocks that are distributed to computational workers for alignment. The blocks are aligned to each other by composing transformations with decay, which achieves a global alignment without resorting to a time-consuming global optimization. We apply our pipeline to a whole fly brain dataset, and show improved accuracy relative to prior state of the art. We also demonstrate that our pipeline scales to a cubic millimeter of mouse visual cortex. Our pipeline is publicly available through two open source Python packages.", "citations": [{"title": "Tools for comprehensive reconstruction and analysis of Drosophila motor circuits", "paperId": "1e124b9f834d9f48c547f90650a9fcd5e9e071f8"}, {"title": "Fast imaging of millimeter-scale areas with beam deflection transmission electron microscopy", "paperId": "58443df22b2e215c2a4110719b804d99a0effa1e"}, {"title": "NeuVue: A Framework and Workflows for High-Throughput Electron Microscopy Connectomics Proofreading", "paperId": "4a65612c4fb70aa425c148169c0805429fac9ac0"}], "externalIds": {"DOI": "10.1101/2022.03.25.485816", "CorpusId": 247780789}, "isOpenAccess": true, "citationCount": 3, "fieldsOfStudy": ["Biology"], "openAccessPdf": {"url": "https://www.biorxiv.org/content/biorxiv/early/2022/03/27/2022.03.25.485816.full.pdf", "status": "GREEN"}, "citationStyles": {"bibtex": "@None{Popovych2022PetascalePF,\n author = {Sergiy Popovych and T. Macrina and Nico Kemnitz and Manuel A. Castro and Barak Nehoran and Zhen Jia and J. A. Bae and Eric Mitchell and Shang Mu and Eric T. Trautman and S. Saalfeld and Kai Li and Sebastian Seung},\n booktitle = {bioRxiv},\n journal = {bioRxiv},\n title = {Petascale pipeline for precise alignment of images from serial section electron microscopy},\n year = {2022}\n}\n"}, "referenceCount": 38, "publicationDate": "2022-03-27", "s2FieldsOfStudy": [{"source": "external", "category": "Biology"}, {"source": "s2-fos-model", "category": "Computer Science"}], "publicationTypes": null, "publicationVenue": {"id": "027ffd21-ebb0-4af8-baf5-911124292fd0", "url": "http://biorxiv.org/", "name": "bioRxiv", "type": "journal"}, "influentialCitationCount": 0}', '2023-01-27 10:57:47.185622', '2023-01-27 10:57:47.185622', 'Petascale pipeline for precise alignment of images from serial section electron microscopy', 2022, false, null, null, null, null);
