tudy": [{"source": "external", "category": "Medicine"}, {"source": "s2-fos-model", "category": "Biology"}], "publicationTypes": ["JournalArticle"], "publicationVenue": {"id": "dc80de17-ef26-42fc-b769-b6169e1e8fef", "url": "https://www.journals.elsevier.com/current-opinion-in-insect-science", "issn": "2214-5745", "name": "Current Opinion in Insect Science", "type": "journal", "alternate_urls": ["https://www.sciencedirect.com/journal/current-opinion-in-insect-science", "http://www.sciencedirect.com/science/journal/22145745"], "alternate_names": ["Curr Opin Insect Sci", "Curr opin insect sci", "Current opinion in insect science"]}, "influentialCitationCount": 0}', '2023-01-27 10:56:59.333192', '2023-01-27 10:56:59.333192', 'Connectomics and the neural basis of behaviour', 2022, false, null, null, null, null);
INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('f1733d1ea086ec17fc0cb35eb80f0b7596e20938', '{"url": "https://www.semanticscholar.org/paper/f1733d1ea086ec17fc0cb35eb80f0b7596e20938", "year": 2022, "title": "Neural signal propagation atlas of $\\textit{C. elegans}$", "venue": "", "authors": [{"name": "F. Randi", "authorId": "34655241"}, {"name": "A. Sharma", "authorId": "2109669224"}, {"name": "Sophie Dvali", "authorId": "52029557"}, {"name": "A. Leifer", "authorId": "49448212"}], "journal": null, "paperId": "f1733d1ea086ec17fc0cb35eb80f0b7596e20938", "abstract": "A fundamental problem in neuroscience is understanding how a network’s properties dictate its function. Connectomics provides one avenue to predict nervous system’s function. To test this explicitly, we systematically measure signal propagation in 9,692 pairs of neurons across the head of the nematode Caendorhabditis elegans by direct optogenetic activation and simultaneous whole-brain calcium imaging. We measure the sign (excitatory or inhibitory), strength, temporal properties, and causal direction of signal propagation between these neurons to create a functional atlas. We find that signal propagation differs from predictions based on anatomy. Using mutants, we show that extrasynaptic signaling not visible from anatomy contributes to this difference. We identify many instances of dense-core-vesicle dependent signaling on seconds-or-less timescales that evoke acute calcium transients— often where no direct wired connection exists but where relevant neuropeptides and receptors are expressed. We propose that here extrasynaptically released neuropeptides serve a similar function as that of classical neurotransmitters. Finally, our measured signal propagation atlas better predicts neural dynamics of spontaneous activity than does anatomy. We conclude that both synaptic and extrasynaptic signaling drive neural dynamics on short timescales and that direct measures of signal propagation are critical for interpreting neural function.", "citations": [], "externalIds": {"ArXiv": "2208.04790", "CorpusId": 254685714}, "isOpenAccess": false, "citationCount": 0, "fieldsOfStudy": ["Biology", "Physics"], "openAccessPdf": null, "citationStyles": {"bibtex": "@None{Randi2022NeuralSP,\n author = {F. Randi and A. Sharma and Sophie Dvali and A. Leifer},\n title = {Neural signal propagation atlas of $\\textit{C. elegans}$},\n year = {2022}\n}\n"}, "referenceCount": 98, "publicationDate": "2022-08-09", "s2FieldsOfStudy": [{"source": "external", "category": "Biology"}, {"source": "external", "category": "Physics"}, {"source": "s2-fos-model", "category": "Biology"}], "publicationTypes": null, "publicationVenue": null, "influentialCitationCount": 0}', '2023-01-27 10:57:03.003209', '2023-01-27 10:57:03.003209', 'Neural signal propagation atlas of $\textit{C. elegans}$', 2022, false, null, null, null, null);
INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('bcb8dbbb3b853e5d1cb2255767ba3db925991d07', '{"url": "https://www.semanticscholar.org/paper/bcb8dbbb3b853e5d1cb2255767ba3db925991d07", "year": 2022, "title": "Ultraliser: a framework for creating multiscale, high-fidelity and geometrically realistic 3D models for in silico neuroscience", "venue": "bioRxiv", "authors": [{"name": "M. Abdellah", "authorId": "1817013"}, {"name": "Juan José García Cantero", "authorId": "2179902627"}, {"name": "Nadir Román Guerrero", "authorId": "1813232466"}, {"name": "A. Foni", "authorId": "3269435"}, {"name": "J. S. Coggan", "authorId": "3297294"}, {"name": "C. Calì", "authorId": "48108207"}, {"name": "Marco Agus", "authorId": "144346127"}, {"name": "Eleftherios Zisis", "authorId": "97690221"}, {"name": "D. Keller", "authorId": "29859372"}, {"name": "M. Hadwiger", "authorId": "1761518"}, {"name": "P. Magistretti", "authorId": "7532991"}, {"name": "H. Markram", "authorId": "1754307"}, {"name": "F. Schürmann", "authorId": "2984713"}], "journal": {"name": "Briefings in Bioinformatics", "volume": "24"}, "paperId": "bcb8dbbb3b853e5d1cb2255767ba3db925991d07", "abstract": "Ultraliser is a neuroscience-specific software framework capable of creating accurate and biologically realistic 3D models of complex neuroscientific structures at intracellular (e.g. mitochondria and endoplasmic reticula), cellular (e.g. neurons and glia) and even multicellular scales of resolution (e.g. cerebral vasculature and minicolumns). Resulting models are exported as triangulated surface meshes and annotated volumes for multiple applications in in silico neuroscience, allowing scalable supercomputer simulations that can unravel intricate cellular structure-function relationships. Ultraliser implements a high performance and unconditionally robust voxelization engine adapted to create optimized watertight surface meshes and annotated voxel grids from arbitrary non-watertight triangular soups, digitized morphological skeletons or binary volumetric masks. The framework represents a major leap forward in simulation-based neuroscience, making it possible to employ high-resolution 3D structural models for quantification of surface areas and volumes, which are of the utmost importance for cellular and system simulations. The power of Ultraliser is demonstrated with several use cases in which hundreds of models are created for potential application in diverse types of simulations. Ultraliser is publicly released under the GNU GPL3 license on Github (BlueBrain/Ultraliser). Significance There is crystal clear evidence on the impact of cell shape on its signaling mechanisms. Structural models can therefore be insightful to realize the function; the more realistic the structure can be, the further we get insights into the function. Creating realistic structural models from existing ones is challenging, particularly when needed for detailed subcellular simulations. We present Ultraliser, a neuroscience-dedicated framework capable of building these structural models with realistic and detailed cellular geometries that can be used for simulations.", "citations": [], "externalIds": {"DOI": "10.1093/bib/bbac491", "PubMed": "36434788", "CorpusId": 251228504, "PubMedCentral": "9851302"}, "isOpenAccess": true, "citationCount": 0, "fieldsOfStudy": ["Medicine", "Biology"], "openAccessPdf": {"url": "https://repository.kaust.edu.sa/bitstream/10754/685939/1/bbac491.pdf", "status": "GREEN"}, "citationStyles": {"bibtex": "@[''JournalArticle'']{Abdellah2022Ultraliser:AF,\n author = {M. Abdellah and Juan José García Cantero and Nadir Román Guerrero and A. Foni and J. S. Coggan and C. Calì and Marco Agus and Eleftherios Zisis and D. Keller and M. Hadwiger and P. Magistretti and H. Markram and F. Schürmann},\n booktitle = {bioRxiv},\n journal = {Briefings in Bioinformatics},\n title = {Ultraliser: a framework for creating multiscale, high-fidelity and geometrically realistic 3D models for in silico neuroscience},\n volume = {24},\n year = {2022}\n}\n"}, "referenceCount": 107, "publicationDate": "2022-07-29", "s2FieldsOfStudy": [{"source": "external", "category": "Medicine"}, {"source": "external", "category": "Biology"}, {"source": "s2-fos-model", "category": "Biology"}], "publicationTypes": ["JournalArticle"], "publicationVenue": {"id": "027ffd21-ebb0-4af8-baf5-911124292fd0", "url": "http://biorxiv.org/", "name": "bioRxiv", "type": "journal"}, "influentialCitationCount": 0}', '2023-01-27 10:57:06.621379', '2023-01-27 10:57:06.621379', 'Ultraliser: a framework for creating multiscale, high-fidelity and geometrically realistic 3D models for in silico neuroscience', 2022, false, null, null, null, null);
INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('4a65612c4fb70aa425c148169c0805429fac9ac0', '{"url": "https://www.semanticscholar.org/paper/4a65612c4fb70aa425c148169c0805429fac9ac0", "year": 2022, "title": "NeuVue: A Framework and Workflows for High-Throughput Electron Microscopy Connectomics Proofreading", "venue": "bioRxiv", "authors": [{"name": "Daniel Xenes", "authorId": "1605998470"}, {"name": "Lindsey Kitchell", "authorId": "52007649"}, {"name": "P. Rivlin", "authorId": "2215296"}, {"name": "Rachel Brodsky", "authorId": "2179025203"}, {"name": "Hannah Gooden", "authorId": "2179025391"}, {"name": "Justin Joyce", "authorId": "1678941437"}, {"name": "Diego Luna", "authorId": "2179025249"}, {"name": "Raphael Norman-Tenazas", "authorId": "1441381238"}, {"name": "Devin Ramsden", "authorId": "1581797116"}, {"name": "Kevin Romero", "authorId": "2179024136"}, {"name": "Victoria Rose", "authorId": "2179024879"}, {"name": "Marisel Villafane-Delgado", "authorId": "1405614975"}, {"name": "William Gray-Roncal", "authorId": "1384052997"}, {"name": "B. Wester", "authorId": "1916423"}], "journal": {"name": "bioRxiv"}, "paperId": "4a65612c4fb70aa425c148169c0805429fac9ac0", "abstract": "NeuVue is a software platform created for large-scale proofreading of machine segmentation and neural circuit reconstruction in high-resolution electron microscopy connectomics datasets. The NeuVue platform provides a robust web-based interface for proofreaders to collaboratively view, annotate, and edit segmentation and connectivity data. A backend queuing service organizes proofreader tasks into purpose-driven task types and increases proofreader throughput by limiting proofreader actions to simple, atomic operations. A collection of analytical dashboards, data visualization tools, and Application Program Interface (API) capabilities provide stakeholders real-time access to proofreading progress at an individual proofreader level as well as insights on task generation priorities. NeuVue is agnostic to the underlying data being proofread and improves upon the traditional proofreader experience through quality-of-life features that streamline complex editing operations such as splitting and merging objects in dense nanoscale segmentation. NeuVue heavily leverages cloud resources to enable proofreaders to simultaneously access and edit data on the platform. Production-quality features such as load-balancing, auto-scaling, and pre-deployment testing are all integrated into the platform’s cloud architecture. Additionally, NeuVue is powered by well-supported open-source connectomics tools from the community such as Neuroglancer, PyChunkedGraph, and Connectomics Annotation Versioning Engine (CAVE). The modular design of NeuVue facilitates easy integration and adoption of useful community tools to allow proofreaders to take advantage of the latest improvements in data visualization, processing, and analysis. We demonstrate our framework through proofreading of the mouse visual cortex data generated on the IARPA MICrONS Project. This effort has yielded over 40,000 proofreader edits across the 2 petavoxels of “Minnie” neuroimaging data. 44 unique proofreaders of various skill levels have logged a cumulative 3,740 proofreading hours, and we have been able to validate the improved connectivity of thousands of neurons in the volume. With sustained development on the platform, new integrated error detection and error correction capabilities, and continuous improvements to the proofreader model, we believe that the NeuVue framework can enable high-throughput proofreading for large-scale connectomics datasets of the future.", "citations": [], "externalIds": {"DOI": "10.1101/2022.07.18.500521", "CorpusId": 251021196}, "isOpenAccess": true, "citationCount": 0, "fieldsOfStudy": ["Biology"], "openAccessPdf": {"url": "https://www.biorxiv.org/content/biorxiv/early/2022/07/20/2022.07.18.500521.full.pdf", "status": "GREEN"}, "citationStyles": {"bibtex": "@None{Xenes2022NeuVue:AF,\n author = {Daniel Xenes and Lindsey Kitchell and P. Rivlin and Rachel Brodsky and Hannah Gooden and Justin Joyce and Diego Luna and Raphael Norman-Tenazas and Devin Ramsden and Kevin Romero and Victoria Rose and Marisel Villafane-Delgado and William Gray-Roncal and B. Wester},\n booktitle = {bioRxiv},\n journal = {bioRxiv},\n title = {NeuVue: A Framework and Workflows for High-Throughput Electron Microscopy Connectomics Proofreading},\n year = {2022}\n}\n"}, "referenceCount": 25, "publicationDate": "2022-07-20", "s2FieldsOfStudy": [{"source": "external", "category": "Biology"}, {"source": "s2-fos-model", "category": "Computer Science"}], "publicationTypes": null, "publicationVenue": {"id": "027ffd21-ebb0-4af8-baf5-911124292fd0", "url": "http://biorxiv.org/", "name": "bioRxiv", "type": "journal"}, "influentialCitationCount": 0}', '2023-01-27 10:57:11.068503', '2023-01-27 10:57:11.068503', 'NeuVue: A Framework and Workflows for High-Throughput Electron Microscopy Connectomics Proofreading', 2022, false, null, null, null, null);
