INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('574fe91260f796820916c763948b7b684295fff0', '{"url": "https://www.semanticscholar.org/paper/574fe91260f796820916c763948b7b684295fff0", "year": 2022, "title": "RealNeuralNetworks.jl: An Integrated Julia Package for Skeletonization, Morphological Analysis, and Synaptic Connectivity Analysis of Terabyte-Scale 3D Neural Segmentations", "venue": "Frontiers in Neuroinformatics", "authors": [{"name": "Jingpeng Wu", "authorId": "31421132"}, {"name": "Nicholas L. Turner", "authorId": "152772071"}, {"name": "J. A. Bae", "authorId": "2072616109"}, {"name": "Ashwin Vishwanathan", "authorId": "3173442"}, {"name": "H. Seung", "authorId": "144924970"}], "journal": {"name": "Frontiers in Neuroinformatics", "volume": "16"}, "paperId": "574fe91260f796820916c763948b7b684295fff0", "abstract": "Benefiting from the rapid development of electron microscopy imaging and deep learning technologies, an increasing number of brain image datasets with segmentation and synapse detection are published. Most of the automated segmentation methods label voxels rather than producing neuron skeletons directly. A further skeletonization step is necessary for quantitative morphological analysis. Currently, several tools are published for skeletonization as well as morphological and synaptic connectivity analysis using different computer languages and environments. Recently the Julia programming language, notable for elegant syntax and high performance, has gained rapid adoption in the scientific computing community. Here, we present a Julia package, called RealNeuralNetworks.jl, for efficient sparse skeletonization, morphological analysis, and synaptic connectivity analysis. Based on a large-scale Zebrafish segmentation dataset, we illustrate the software features by performing distributed skeletonization in Google Cloud, clustering the neurons using the NBLAST algorithm, combining morphological similarity and synaptic connectivity to study their relationship. We demonstrate that RealNeuralNetworks.jl is suitable for use in terabyte-scale electron microscopy image segmentation datasets.", "citations": [{"title": "Igneous: Distributed dense 3D segmentation meshing, neuron skeletonization, and hierarchical downsampling", "paperId": "b6703153b7200dc664e3d523a94df24e2a489815"}], "externalIds": {"DOI": "10.3389/fninf.2022.828169", "DBLP": "journals/fini/WuTBVS22", "PubMed": "35311003", "CorpusId": 247171166, "PubMedCentral": "8924549"}, "isOpenAccess": true, "citationCount": 1, "fieldsOfStudy": ["Medicine", "Computer Science"], "openAccessPdf": {"url": "https://www.frontiersin.org/articles/10.3389/fninf.2022.828169/pdf", "status": "GOLD"}, "citationStyles": {"bibtex": "@[''JournalArticle'']{Wu2022RealNeuralNetworks.jl:AI,\n author = {Jingpeng Wu and Nicholas L. Turner and J. A. Bae and Ashwin Vishwanathan and H. Seung},\n booktitle = {Frontiers in Neuroinformatics},\n journal = {Frontiers in Neuroinformatics},\n title = {RealNeuralNetworks.jl: An Integrated Julia Package for Skeletonization, Morphological Analysis, and Synaptic Connectivity Analysis of Terabyte-Scale 3D Neural Segmentations},\n volume = {16},\n year = {2022}\n}\n"}, "referenceCount": 58, "publicationDate": "2022-03-02", "s2FieldsOfStudy": [{"source": "external", "category": "Medicine"}, {"source": "external", "category": "Computer Science"}, {"source": "s2-fos-model", "category": "Computer Science"}, {"source": "s2-fos-model", "category": "Biology"}], "publicationTypes": ["JournalArticle"], "publicationVenue": {"id": "82d7e641-6b2a-4a1c-86b5-bcc9918c6d56", "url": "https://www.frontiersin.org/journals/neuroinformatics", "issn": "1662-5196", "name": "Frontiers in Neuroinformatics", "type": "journal", "alternate_urls": ["http://www.frontiersin.org/neuroinformatics/", "http://journal.frontiersin.org/journal/neuroinformatics"], "alternate_names": ["Front Neuroinformatics"]}, "influentialCitationCount": 0}', '2023-01-27 10:57:58.628867', '2023-01-27 10:57:58.628867', 'RealNeuralNetworks.jl: An Integrated Julia Package for Skeletonization, Morphological Analysis, and Synaptic Connectivity Analysis of Terabyte-Scale 3D Neural Segmentations', 2022, false, null, null, null, null);
INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('595bf42ea6aedc0c19d38409dbb7d08ddcd006d6', '{"url": "https://www.semanticscholar.org/paper/595bf42ea6aedc0c19d38409dbb7d08ddcd006d6", "year": 2022, "title": "Applications of deep learning in electron microscopy.", "venue": "Microscopy", "authors": [{"name": "Kevin P Treder", "authorId": "2088864822"}, {"name": "Chen Huang", "authorId": "2150607117"}, {"name": "Judy S. Kim", "authorId": "2109175194"}, {"name": "A. Kirkland", "authorId": "4598309"}], "journal": {"name": "Microscopy", "pages": "\n          i100-i115\n        ", "volume": "71 Supplement_1"}, "paperId": "595bf42ea6aedc0c19d38409dbb7d08ddcd006d6", "abstract": "We review the growing use of machine learning in electron microscopy (EM) driven in part by the availability of fast detectors operating at kiloHertz frame rates leading to large data sets that cannot be processed using manually implemented algorithms. We summarize the various network architectures and error metrics that have been applied to a range of EM-related problems including denoising and inpainting. We then provide a review of the application of these in both physical and life sciences, highlighting how conventional networks and training data have been specifically modified for EM.", "citations": [], "externalIds": {"DOI": "10.1093/jmicro/dfab043", "PubMed": "35275181", "CorpusId": 247385571}, "isOpenAccess": true, "citationCount": 0, "fieldsOfStudy": ["Medicine"], "openAccessPdf": {"url": "https://academic.oup.com/jmicro/article-pdf/71/Supplement_1/i100/42548156/dfab043.pdf", "status": "BRONZE"}, "citationStyles": {"bibtex": "@[''JournalArticle'', ''Review'']{Treder2022ApplicationsOD,\n author = {Kevin P Treder and Chen Huang and Judy S. Kim and A. Kirkland},\n booktitle = {Microscopy},\n journal = {Microscopy},\n pages = {\n          i100-i115\n        },\n title = {Applications of deep learning in electron microscopy.},\n volume = {71 Supplement_1},\n year = {2022}\n}\n"}, "referenceCount": 181, "publicationDate": "2022-02-18", "s2FieldsOfStudy": [{"source": "external", "category": "Medicine"}, {"source": "s2-fos-model", "category": "Computer Science"}], "publicationTypes": ["JournalArticle", "Review"], "publicationVenue": {"id": "9bcabe14-4f34-4ee4-bbe0-a83fb05cb370", "url": "http://jmicro.oxfordjournals.org/", "issn": "2050-5698", "name": "Microscopy", "alternate_names": ["Microscopy"]}, "influentialCitationCount": 0}', '2023-01-27 10:58:02.253466', '2023-01-27 10:58:02.253466', 'Applications of deep learning in electron microscopy.', 2022, false, null, null, null, null);
INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('605ea2d5180da2f5b18b57761a1e75798444d54a', '{"url": "https://www.semanticscholar.org/paper/605ea2d5180da2f5b18b57761a1e75798444d54a", "year": 2022, "title": "Identifying Inputs to Visual Projection Neurons in Drosophila Lobula by Analyzing Connectomic Data", "venue": "eNeuro", "authors": [{"name": "Ryosuke Tanaka (田中涼介)", "authorId": "2175039083"}, {"name": "D. Clark", "authorId": "17420654"}], "journal": {"name": "eNeuro", "volume": "9"}, "paperId": "605ea2d5180da2f5b18b57761a1e75798444d54a", "abstract": "Visual Abstract Electron microscopy (EM)-based connectomes provide important insights into how visual circuitry of fruit fly Drosophila computes various visual features, guiding and complementing behavioral and physiological studies. However, connectomic analyses of the lobula, a neuropil putatively dedicated to detecting object-like features, remains underdeveloped, largely because of incomplete data on the inputs to the brain region. Here, we attempted to map the columnar inputs into the Drosophila lobula neuropil by performing connectivity-based and morphology-based clustering on a densely reconstructed connectome dataset. While the dataset mostly lacked visual neuropils other than lobula, which would normally help identify inputs to lobula, our clustering analysis successfully extracted clusters of cells with homogeneous connectivity and morphology, likely representing genuine cell types. We were able to draw a correspondence between the resulting clusters and previously identified cell types, revealing previously undocumented connectivity between lobula input and output neurons. While future, more complete connectomic reconstructions are necessary to verify the results presented here, they can serve as a useful basis for formulating hypotheses on mechanisms of visual feature detection in lobula.", "citatio