INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('564eda3bdb4e72494a6e8bfca32acab9067bef97', '{"url": "https://www.semanticscholar.org/paper/564eda3bdb4e72494a6e8bfca32acab9067bef97", "year": 2022, "title": "Mating-driven variability in olfactory local interneuron wiring", "venue": "Science Advances", "authors": [{"name": "Ya-Hui Chou", "authorId": "50660349"}, {"name": "Chi-Jen Yang", "authorId": "46212972"}, {"name": "Hao-Wei Huang", "authorId": "2187841962"}, {"name": "Nan-Fu Liou", "authorId": "36844861"}, {"name": "Michael Raphael Panganiban", "authorId": "2155105345"}, {"name": "David Luginbuhl", "authorId": "1758465"}, {"name": "Yijie Yin", "authorId": "2155262341"}, {"name": "István Taisz", "authorId": "116251476"}, {"name": "Liang Liang", "authorId": "144217289"}, {"name": "G. Jefferis", "authorId": "145196228"}, {"name": "L. Luo", "authorId": "145586825"}], "journal": {"name": "Science Advances", "volume": "8"}, "paperId": "564eda3bdb4e72494a6e8bfca32acab9067bef97", "abstract": "Variations in neuronal connectivity occur widely in nervous systems from invertebrates to mammals. Yet, it is unclear how neuronal variability originates, to what extent and at what time scales it exists, and what functional consequences it might carry. To assess inter- and intraindividual neuronal variability, it would be ideal to analyze the same identified neuron across different brain hemispheres and individuals. Here, using genetic labeling and electron microscopy connectomics, we show that an identified inhibitory olfactory local interneuron, TC-LN, exhibits extraordinary variability in its glomerular innervation patterns. Moreover, TC-LN’s innervation of the VL2a glomerulus, which processes food signals and modulates mating behavior, is sexually dimorphic, is influenced by female’s courtship experience, and correlates with food intake in mated females. Mating also affects output connectivity of TC-LN to specific local interneurons. We propose that mating-associated variability of TC-LNs regulates how food odor is interpreted by an inhibitory network to modulate feeding.", "citations": [{"title": "Flexibility of neural circuits regulating mating behaviors in mice and flies", "paperId": "459debf1e78a0990b2ca27c70080d0ddb7fc6bf9"}, {"title": "Connectomics and the neural basis of behaviour", "paperId": "e67ce2076b1edb61228f9401bda7a0ea81f3055a"}, {"title": "A Neuropeptidergic Signaling Pathway for Olfactory Gain Modulation", "paperId": "f99d4048e4f9175f6ec30d75c4757f830a2e76b4"}], "externalIds": {"DOI": "10.1126/sciadv.abm7723", "PubMed": "35179957", "CorpusId": 246974901, "PubMedCentral": "8856614"}, "isOpenAccess": true, "citationCount": 3, "fieldsOfStudy": ["Medicine"], "openAccessPdf": {"url": "https://www.repository.cam.ac.uk/bitstream/1810/335278/2/article.pdf", "status": "GREEN"}, "citationStyles": {"bibtex": "@[''JournalArticle'']{Chou2022Mating-drivenVI,\n author = {Ya-Hui Chou and Chi-Jen Yang and Hao-Wei Huang and Nan-Fu Liou and Michael Raphael Panganiban and David Luginbuhl and Yijie Yin and István Taisz and Liang Liang and G. Jefferis and L. Luo},\n booktitle = {Science Advances},\n journal = {Science Advances},\n title = {Mating-driven variability in olfactory local interneuron wiring},\n volume = {8},\n year = {2022}\n}\n"}, "referenceCount": 67, "publicationDate": "2022-02-01", "s2FieldsOfStudy": [{"source": "external", "category": "Medicine"}, {"source": "s2-fos-model", "category": "Biology"}, {"source": "s2-fos-model", "category": "Psychology"}], "publicationTypes": ["JournalArticle"], "publicationVenue": {"id": "cb30f0c9-2980-4b7d-bbcb-68fc5472b97c", "url": "http://www.scienceadvances.org/", "issn": "2375-2548", "name": "Science Advances", "type": "journal", "alternate_urls": ["https://advances.sciencemag.org/"], "alternate_names": ["Sci Adv"]}, "influentialCitationCount": 0}', '2023-01-27 10:58:13.218581', '2023-01