INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('a29d959673a16ccd8d2d402b4109f8ac11718cee', '{"url": "https://www.semanticscholar.org/paper/a29d959673a16ccd8d2d402b4109f8ac11718cee", "year": 2022, "title": "Curriculum learning as a tool to uncover learning principles in the brain", "venue": "International Conference on Learning Representations", "authors": [{"name": "Daniel R. Kepple", "authorId": "48167512"}, {"name": "Rainer Engelken", "authorId": "5258755"}, {"name": "Kanaka Rajan", "authorId": "36560753"}], "journal": null, "paperId": "a29d959673a16ccd8d2d402b4109f8ac11718cee", "abstract": "BSTRACT We present a novel approach to use curricula to identify principles by which a system learns. Previous work in curriculum learning has focused on how curricula can be designed to improve learning of a model on particular tasks. We consider the inverse problem: what can a curriculum tell us about how a learning system acquired a task? Using recurrent neural networks (RNNs) and models of common experimental neuroscience tasks, we demonstrate that curricula can be used to differentiate learning principles using target-based and a representation-based loss functions as use cases. In particular, we compare the performance of RNNs using a target-based learning principle versus those using a representational learning principle on three different curricula in the context of two tasks. We show that the learned state-space trajectories of RNNs trained by these two learning principles under all curricula tested are indistinguishable. However, by comparing learning times during different curricula, we can disambiguate the learning principles and challenge traditional approaches of interrogating learning systems. Although all animals in neuroscience lab settings are trained by curriculum-based procedures called shaping, almost no behavioral or neural data are collected or published on the relative successes or training times under different curricula. Our results motivate the systematic collection and curation of data during shaping by demonstrating curriculum learning in RNNs as a tool to probe and differentiate learning principles used by biological systems, over statistical analyses of learned state spaces. trained state. Our results emphasize the importance of studying animals during shaping and the value of curriculum learning in RNNs as a hypothesis test-bed for probing learning principles in the biological brain.", "citations": [{"title": "Confidence-Aware Paced-Curriculum Learning by Label Smoothing for Surgical Scene Understanding", "paperId": "84d499c5b19b26e6a063f5b17a1d6107beabf215"}, {"title": "Neural representational geometry correlates with behavioral differences between monkeys", "paperId": "e22f5ccb19909cb15af9e80be2a24948fabb0344"}, {"title": "Compositional generalization through abstract representations in human and artificial neural networks", "paperId": "a265394d782bbdb869730775399be3dfe45fc1db"}, {"title": "Distinguishing Learning Rules with Brain Machine Interfaces", "paperId": "32117d3160b05650162fd72873e651c45eb7e07c"}, {"title": "Beyond accuracy: generalization properties of bio-plausible temporal credit assignment rules", "paperId": "86ebdbdbe2b33ac8f7c19d86d3704391690f6afd"}, {"title": "An analytical theory of curriculum learning in teacher–student networks", "paperId": "296443aa35868487f7d5e478ca17695d0ad95933"}], "externalIds": {"DBLP": "conf/iclr/KeppleER22", "CorpusId": 251647193}, "isOpenAccess": false, "citationCount": 6, "fieldsOfStudy": ["Computer Science"], "openAccessPdf": null, "citationStyles": {"bibtex": "@[''JournalArticle'']{Kepple2022CurriculumLA,\n author = {Daniel R. Kepple and Rainer Engelken and Kanaka Rajan},\n booktitle = {International Conference on Learning Representations},\n title = {Curriculum learning as a tool to uncover learning principles in the brain},\n year = {2022}\n}\n"}, "referenceCount": 42, "publicationDate": null, "s2FieldsOfStudy": [{"source": "external", "category": "Computer Science"}, {"source": "s2-fos-model", "category": "Education"}, {"source": "s2-fos-model", "category": "Computer Science"}], "publicationTypes": ["JournalArticle"], "publicationVenue": {"id": "939c6e1d-0d17-4d6e-8a82-66d960df0e40", "url": "https://iclr.cc/", "name": "International Conference on Learning Representations", "type": "conference", "alternate_names": ["Int Conf Learn Represent", "ICLR"]}, "influentialCitationCount": 0}', '2023-01-27 10:58:31.573891', '2023-01-27 10:58:31.573891', 'Curriculum learning as a tool to uncover learning principles in the brain', 2022, false, null, null, null, null);
INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('18ab60e2cd524d4d8eca056dad5a279a9697246c', '{"url": "https://www.semanticscholar.org/paper/18ab60e2cd524d4d8eca056dad5a279a9697246c", "year": 2022, "title": "Connectome-constrained Latent Variable Model of Whole-Brain Neural Activity", "venue": "International Conference on Learning Representations", "authors": [{"name": "Lu Mi", "authorId": "2057207935"}, {"name": "R. Xu", "authorId": "2154865"}, {"name": "Sridhama Prakhya", "authorId": "134749139"}, {"name": "Albert Lin", "authorId": "2054896876"}, {"name": "N. Shavit", "authorId": "2613669"}, {"name": "Aravinthan D. T. Samuel", "authorId": "1744897"}, {"name": "Srinivas C. Turaga", "authorId": "3178417"}], "journal": null, "paperId": "18ab60e2cd524d4d8eca056dad5a279a9697246c", "abstract": "The availability of both anatomical connectivity and brain-wide neural activity measurements in C. elegans make the worm a promising system for learning detailed, mechanistic models of an entire nervous system in a data-driven way. However, one faces several challenges when constructing such a model. We often do not have direct experimental access to important modeling details such as single-neuron dynamics and the signs and strengths of the synaptic connectivity. Further, neural activity can only be measured in a subset of neurons, often indirectly via calcium imaging, and signiﬁcant trial-to-trial variability has been observed. To address these challenges, we introduce a connectome-constrained latent variable model (CC-LVM) of the unobserved voltage dynamics of the entire C. elegans nervous system and the observed calcium signals. We used the framework of variational autoencoders to ﬁt parameters of the mechanistic simulation constituting the generative model of the LVM to calcium imaging observations. A variational approximate posterior distribution over latent voltage traces for all neurons is efﬁciently inferred using an inference network, and constrained by a prior distribution given by the biophysical simulation of neural dynamics. We applied this model to an experimental whole-brain dataset, and found that connectomic constraints enable our LVM to predict the activity of neurons whose activity were withheld signiﬁcantly better than models unconstrained by a connectome. We explored models with different degrees of biophysical detail, and found that models with realistic conductance-based synapses provide markedly better some", "citations": [{"title": "Neural signal propagation atlas of $\\textit{C. elegans}$", "paperId": "f1733d1ea086ec17fc0cb35eb80f0b7596e20938"}, {"title": "A functional connectivity atlas of C. elegans measured by neural activation", "paperId": "bf3bcd10361ce0a0759ff046c09ec0aa1241cdc7"}], "externalIds": {"DBLP": "conf/iclr/MiXPLSST22", "CorpusId": 251498072}, "isOpenAccess": false, "citationCount": 2, "fieldsOfStudy": ["Computer Science"], "openAccessPdf": null, "citationStyles": {"bibtex": "@[''JournalArticle'']{Mi2022Connectome-constrainedLV,\n author = {Lu Mi and R. Xu and Sridhama Prakhya and Albert Lin and N. Shavit and Aravinthan D. T. Samuel and Srinivas C. Turaga},\n booktitle = {International Conference on Learning Representations},\n title = {Connectome-constrained Latent Variable Model of Whole-Brain Neural Activity},\n year = {2022}\n}\n"}, "referenceCount": 33, "publicationDate": null, "s2FieldsOfStudy": [{"source": "external", "category": "Computer Science"}, {"source": "s2-fos-model", "category": "Biology"}, {"source": "s2-fos-model", "category": "Computer Science"}], "publicationTypes": ["JournalArticle"], "publicationVenue": {"id": "939c6e1d-0d17-4d6e-8a82-66d960df0e40", "url": "https://iclr.cc/", "name": "International Conference on Learning Representations", "type": "conference", "alternate_names": ["Int Conf Learn Represent", "ICLR"]}, "influentialCitationCount": 0}', '2023-01-27 10:58:35.239149', '2023-01-27 10:58:35.239149', 'Connectome-constrained Latent Variable Model of Whole-Brain Neural Activity', 2022, false, null, null, null, null);
INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('bf3bcd10361ce0a0759ff046c09ec0aa1241cdc7', '{"url": "https://www.semanticscholar.org/paper/bf3bcd10361ce0a0759ff046c09ec0aa1241cdc7", "year": 2022, "title": "A functional connectivity atlas of C. elegans measured by neural activation", "venue": "", "authors": [{"name": "F. Randi", "authorId": "34655241"}, {"name": "A. Sharma", "authorId": "2109669224"}, {"name": "Sophie Dvali", "authorId": "52029557"}, {"name": "A. Leifer", "authorId": "49448212"}], "journal": null, "paperId": "bf3bcd10361ce0a0759ff046c09ec0aa1241cdc7", "abstract": "Neural processing and dynamics are governed by the details of how neural signals propagate from one neuron to the next through the brain. We systematically measured functional properties of neural connections in the head of the nematode Caenorhabditis elegans by direct optogenetic activation and simultaneous calcium imaging of 10,438 neuron pairs. By measuring responses to neural activation, we extracted the strength, sign, temporal properties, and direction of information ﬂow between neurons in order to create an atlas of causal functional connectivity. We ﬁnd that functional connectivity differs from predictions based on anatomy, in part, because of extrasynaptic signaling. Our measurements show that neurons such as RID signal extrasynaptically to other neurons for which there are no direct wired connections. We show that functional connectivity better predicts spontaneous activity than anatomy, suggesting that functional connectivity captures properties of the network that are critical for interpreting neural function.", "citations": [], "externalIds": {"CorpusId": 251442481}, "isOpenAccess": false, "citationCount": 0, "fieldsOfStudy": null, "openAccessPdf": null, "citationStyles": {"bibtex": "@None{Randi2022AFC,\n author = {F. Randi and A. Sharma and Sophie Dvali and A. Leifer},\n title = {A functional connectivity atlas of C. elegans measured by neural activation},\n year = {2022}\n}\n"}, "referenceCount": 95, "publicationDate": null, "s2FieldsOfStudy": [{"source": "s2-fos-model", "category": "Biology"}], "publicationTypes": null, "publicationVenue": null, "influentialCitationCount": 0}', '2023-01-27 10:58:38.980560', '2023-01-27 10:58:38.980560', 'A functional connectivity atlas of C. elegans measured by neural activation', 2022, false, null, null, null, null);
INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('aa2a8a25631e337b5bfe560474ea4eecf2ea8c06', '{"url": "https://www.semanticscholar.org/paper/aa2a8a25631e337b5bfe560474ea4eecf2ea8c06", "year": 2021, "title": "Structure and function of axo-axonic inhibition", "venue": "eLife", "authors": [{"name": "C. Schneider-Mizell", "authorId": "1397852111"}, {"name": "Agnes L. Bodor", "authorId": "40584201"}, {"name": "F. Collman", "authorId": "6598643"}, {"name": "Derrick Brittain", "authorId": "145536550"}, {"name": "Adam A. Bleckert", "authorId": "4518441"}, {"name": "Sven Dorkenwald", "authorId": "2122374817"}, {"name": "Nicholas L. Turner", "authorId": "152772071"}, {"name": "T. Macrina", "authorId": "5056982"}, {"name": "Kisuk Lee", "authorId": "2152541203"}, {"name": "Ran Lu", "authorId": "1406193129"}, {"name": "Jingpeng Wu", "authorId": "31421132"}, {"name": "J. Zhuang", "authorId": "93513284"}, {"name": "Anirban Nandi", "authorId": "39067672"}, {"name": "Brian Hu", "authorId": "1600160488"}, {"name": "J. Buchanan", "authorId": "49345106"}, {"name": "Marc M. Takeno", "authorId": "8311185"}, {"name": "Russel Torres", "authorId": "152750629"}, {"name": "Gayathri Mahalingam", "authorId": "144348673"}, {"name": "Daniel J. Bumbarger", "authorId": "3988776"}, {"name": "Yang Li", "authorId": "2154898018"}, {"name": "Tom Chartrand", "authorId": "1610831945"}, {"name": "Nico Kemnitz", "authorId": "41124209"}, {"name": "W. Silversmith", "authorId": "2108734"}, {"name": "Dodam Ih", "authorId": "14972923"}, {"name": "J. Zung", "authorId": "2730700"}, {"name": "A. Zlateski", "authorId": "3134596"}, {"name": "Ignacio Tartavull", "authorId": "24011805"}, {"name": "Sergiy Popovych", "authorId": "1840375"}, {"name": "William Wong", "authorId": "120774631"}, {"name": "Manuel A. Castro", "authorId": "2092627229"}, {"name": "Chris S. Jordan", "authorId": "41130241"}, {"name": "E. Froudarakis", "authorId": "1970917"}, {"name": "Lynne Becker", "authorId": "2065397046"}, {"name": "S. Suckow", "authorId": "40047816"}, {"name": "Jacob Reimer", "authorId": "48128495"}, {"name": "A. Tolias", "authorId": "1739838"}, {"name": "C. Anastassiou", "authorId": "2277912"}, {"name": "H. Seung", "authorId": "144924970"}, {"name": "R. Reid", "authorId": "46567235"}, {"name": "N. D. da Costa", "authorId": "51079681"}], "journal": {"name": "eLife", "volume": "10"}, "paperId": "aa2a8a25631e337b5bfe560474ea4eecf2ea8c06", "abstract": "Inhibitory neurons in mammalian cortex exhibit diverse physiological, morphological, molecular, and connectivity signatures. While considerable work has measured the average connectivity of several interneuron classes, there remains a fundamental lack of understanding of the connectivity distribution of distinct inhibitory cell types with synaptic resolution, how it relates to properties of target cells, and how it affects function. Here, we used large-scale electron microscopy and functional imaging to address these questions for chandelier cells in layer 2/3 of the mouse visual cortex. With dense reconstructions from electron microscopy, we mapped the complete chandelier input onto 153 pyramidal neurons. We found that synapse number is highly variable across the population and is correlated with several structural features of the target neuron. This variability in the number of axo-axonic ChC synapses is higher than the variability seen in perisomatic inhibition. Biophysical simulations show that the observed pattern of axo-axonic inhibition is particularly effective in controlling excitatory output when excitation and inhibition are co-active. Finally, we measured chandelier cell activity in awake animals using a cell-type-specific calcium imaging approach and saw highly correlated activity across chandelier cells. In the same experiments, in vivo chandelier population activity correlated with pupil dilation, a proxy for arousal. Together, these results suggest that chandelier cells provide a circuit-wide signal whose strength is adjusted relative to the properties of target neurons.", "citations": [{"title": "CHRNA5 links chandelier cells to protection against amyloid pathology in human aging and Alzheimer’s Disease", "paperId": "0ea9326c03edff6722e8cd55beb6ec360cc5004b"}, {"title": "Two Synaptic Convergence Motifs Define Functional Roles for Inputs to Cochlear Nucleus Bushy Cells", "paperId": "9e1afad59374c22949972964133aab103b86e604"}, {"title": "Regulation of Potassium and Chloride Concentrations in Nervous Tissue as a Method of Anticonvulsant Therapy", "paperId": "e13cfb46c333ae2a3a82b14e3ed2b2fdf8a94d8e"}, {"title": "What is a cell type and how to define it?", "paperId": "34fd8ef9487b2c7363a4aed58ca44d884f8b6a13"}, {"title": "Scalable graph analysis tools for the connectomics community", "paperId": "13ce42428e5b4fa276804cc459256533729ea3ef"}, {"title": "Microglia regulate chandelier cell axo-axonic synaptogenesis", "paperId": "b6d01ed76d9eb96d8d1daf4c873849b0d340b351"}, {"title": "Public Volume Electron Microscopy Data: An Essential Resource to Study the Brain Microvasculature", "paperId": "21ab25072969988a27a8ce2cf1a891d3b5f0fad9"}, {"title": "Reconstruction of neocortex: Organelles, compartments, cells, circuits, and activity", "paperId": "ccaa41be013f921fbd28a9d190689e9a149693a7"}, {"title": "Genetic labeling of axo-axonic cells in the basolateral amygdala", "paperId": "f578f61a01c6e1223acffad7b6637c87fd492ad6"}, {"title": "The geometry of representational drift in natural and artificial neural networks", "paperId": "f4b10a1191048f8c5b77fe1e433c685ab8057290"}, {"title": "Binary and analog variation of synapses between cortical pyramidal neurons", "paperId": "f363c5dfd87e06c553d7b70068f0e1f692559efb"}], "externalIds": {"DOI": "10.7554/eLife.73783", "PubMed": "34851292", "CorpusId": 244773802, "PubMedCentral": "8758143"}, "isOpenAccess": false, "citationCount": 11, "fieldsOfStudy": ["Medicine"], "openAccessPdf": null, "citationStyles": {"bibtex": "@[''JournalArticle'']{Schneider-Mizell2021StructureAF,\n author = {C. Schneider-Mizell and Agnes L. Bodor and F. Collman and Derrick Brittain and Adam A. Bleckert and Sven Dorkenwald and Nicholas L. Turner and T. Macrina and Kisuk Lee and Ran Lu and Jingpeng Wu and J. Zhuang and Anirban Nandi and Brian Hu and J. Buchanan and Marc M. Takeno and Russel Torres and Gayathri Mahalingam and Daniel J. Bumbarger and Yang Li and Tom Chartrand and Nico Kemnitz and W. Silversmith and Dodam Ih and J. Zung and A. Zlateski and Ignacio Tartavull and Sergiy Popovych and William Wong and Manuel A. Castro and Chris S. Jordan and E. Froudarakis and Lynne Becker and S. Suckow and Jacob Reimer and A. Tolias and C. Anastassiou and H. Seung and R. Reid and N. D. da Costa},\n booktitle = {eLife},\n journal = {eLife},\n title = {Structure and function of axo-axonic inhibition},\n volume = {10},\n year = {2021}\n}\n"}, "referenceCount": 156, "publicationDate": "2021-12-01", "s2FieldsOfStudy": [{"source": "external", "category": "Medicine"}, {"source": "s2-fos-model", "category": "Biology"}], "publicationTypes": ["JournalArticle"], "publicationVenue": {"id": "07365b9a-c0ce-4dd3-b93b-a02e1c81e0c6", "url": "https://epub.uni-regensburg.de/40444/", "issn": "2050-084X", "name": "eLife", "type": "journal", "alternate_urls": ["https://elifesciences.org/", "https://elife.elifesciences.org/", "http://elifesciences.org/"]}, "influentialCitationCount": 0}', '2023-01-27 10:58:42.579103', '2023-01-27 10:58:42.579103', 'Structure and function of axo-axonic inhibition', 2021, false, null, null, null, null);
INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('faf1ea5e936cba2efc79e2295edaac463c30bc1d', '{"url": "https://www.semanticscholar.org/paper/faf1ea5e936cba2efc79e2295edaac463c30bc1d", "year": 2021, "title": "3D reconstruction of cell nuclei in a full Drosophila brain", "venue": "", "authors": [{"name": "Shang Mu", "authorId": "47330620"}, {"name": "Szi-chieh Yu", "authorId": "6537565"}, {"name": "Nicholas L. Turner", "authorId": "152772071"}, {"name": "Claire E McKellar", "authorId": "7667018"}, {"name": "Sven Dorkenwald", "authorId": "2122374817"}, {"name": "F. Collman", "authorId": "6598643"}, {"name": "Selden Koolman", "authorId": "1926340513"}, {"name": "Merlin Moore", "authorId": "1924842078"}, {"name": "Sarah Morejohn", "authorId": "1926771547"}, {"name": "B. Silverman", "authorId": "2056371649"}, {"name": "K. Willie", "authorId": "1926285936"}, {"name": "Ryan Willie", "authorId": "1926285994"}, {"name": "Doug Bland", "authorId": "2007692704"}, {"name": "Austin Burke", "authorId": "2139914531"}, {"name": "Zoe C. Ashwood", "authorId": "1506180498"}, {"name": "Kyle Luther", "authorId": "65931428"}, {"name": "Manuel A. Castro", "authorId": "2092627229"}, {"name": "Oluwaseun Ogedengbe", "authorId": "1926318518"}, {"name": "W. Silversmith", "authorId": "2108734"}, {"name": "Jingpeng Wu", "authorId": "31421132"}, {"name": "Akhilesh Halageri", "authorId": "1579775589"}, {"name": "T. Macrina", "authorId": "5056982"}, {"name": "Nico Kemnitz", "authorId": "41124209"}, {"name": "M. Murthy", "authorId": "145776127"}, {"name": "H. Seung", "authorId": "144924970"}], "journal": {"name": "bioRxiv"}, "paperId": "faf1ea5e936cba2efc79e2295edaac463c30bc1d", "abstract": "We reconstructed all cell nuclei in a 3D image of a Drosophila brain acquired by serial section electron microscopy (EM). The total number of nuclei is approximately 133,000, at least 87% of which belong to neurons. Neuronal nuclei vary from several hundred down to roughly 5 cubic micrometers. Glial nuclei can be even smaller. The optic lobes contain more than two times the number of cells than the central brain. Our nuclear reconstruction serves as a spatial map and index to the cells in a Drosophila brain.", "citations": [{"title": "A searchable image resource of Drosophila GAL4-driver expression patterns with single neuron resolution", "paperId": "eb34c73991b6e0d7a567da8c02aac0d5008d0b2d"}, {"title": "A Survey of Visualization and Analysis in High‐Resolution Connectomics", "paperId": "ebaf6fafb4adb7f3657fdbb869249c727ffccf7e"}], "externalIds": {"DOI": "10.1101/2021.11.04.467197", "CorpusId": 243817545}, "isOpenAccess": true, "citationCount": 2, "fieldsOfStudy": ["Biology"], "openAccessPdf": {"url": "https://www.biorxiv.org/content/biorxiv/early/2021/11/04/2021.11.04.467197.full.pdf", "status": "GREEN"}, "citationStyles": {"bibtex": "@None{Mu20213DRO,\n author = {Shang Mu and Szi-chieh Yu and Nicholas L. Turner and Claire E McKellar and Sven Dorkenwald and F. Collman and Selden Koolman and Merlin Moore and Sarah Morejohn and B. Silverman and K. Willie and Ryan Willie and Doug Bland and Austin Burke and Zoe C. Ashwood and Kyle Luther and Manuel A. Castro and Oluwaseun Ogedengbe and W. Silversmith and Jingpeng Wu and Akhilesh Halageri and T. Macrina and Nico Kemnitz and M. Murthy and H. Seung},\n journal = {bioRxiv},\n title = {3D reconstruction of cell nuclei in a full Drosophila brain},\n year = {2021}\n}\n"}, "referenceCount": 29, "publicationDate": "2021-11-04", "s2FieldsOfStudy": [{"source": "external", "category": "Biology"}, {"source": "s2-fos-model", "category": "Biology"}], "publicationTypes": null, "publicationVenue": null, "influentialCitationCount": 0}', '2023-01-27 10:58:46.434671', '2023-01-27 10:58:46.434671', '3D reconstruction of cell nuclei in a full Drosophila brain', 2021, false, null, null, null, null);
INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('302b2d0cd0052892065975e91f0f0b2000de77d9', '{"url": "https://www.semanticscholar.org/paper/302b2d0cd0052892065975e91f0f0b2000de77d9", "year": 2021, "title": "Flexible Circuit Mechanisms for Context-Dependent Song Sequencing", "venue": "bioRxiv", "authors": [{"name": "Frederic A. Roemschied", "authorId": "6029087"}, {"name": "Diego A. Pacheco", "authorId": "7587012"}, {"name": "Elise C. Ireland", "authorId": "2028267155"}, {"name": "Xinping Li", "authorId": "48569016"}, {"name": "M. J. Aragon", "authorId": "2080119553"}, {"name": "Rich Pang", "authorId": "3825843"}, {"name": "M. Murthy", "authorId": "145776127"}], "journal": {"name": "bioRxiv"}, "paperId": "302b2d0cd0052892065975e91f0f0b2000de77d9", "abstract": "Many sequenced behaviors, including locomotion, reaching, and vocalization, are patterned differently in different contexts, enabling animals to adjust to their current environments. However, how contextual information shapes neural activity to flexibly alter the patterning of actions is not yet understood. Prior work indicates such flexibility could be achieved via parallel motor circuits, with differing sensitivities to sensory context [1, 2, 3]; instead we demonstrate here how a single neural pathway operates in two different regimes dependent on recent sensory history. We leverage the Drosophila song production system [4] to investigate the neural mechanisms that support male song sequence generation in two contexts: near versus far from the female. While previous studies identified several song production neurons[5, 6, 7, 8], how these neurons are organized to mediate song patterning was unknown. We find that male flies sing ‘simple’ trains of only one syllable or mode far from the female but complex song sequences consisting of alternations between modes when near to her. We characterize the male song circuit from the brain to the ventral nerve cord (VNC), and find that the VNC song pre-motor circuit is shaped by two key computations: mutual inhibition and rebound excitability [9] between nodes driving the two modes of song. Weak sensory input to a direct brain-to-VNC excitatory pathway (via pC2 brain and pIP10 descending neurons) drives simple song far from the female. Strong sensory input to the same pathway enables complex song production via simultaneous recruitment of P1a neuron-mediated disinhibition of the VNC song pre-motor circuit. Thus, proximity to the female effectively unlocks motor circuit dynamics in the correct sensory context. We construct a compact circuit model to demonstrate that these few computations are sufficient to replicate natural context-dependent song dynamics. These results have broad implications for neural population-level models of context-dependent behavior [10] and highlight that canonical circuit motifs [11, 12, 13] can be combined in novel ways to enable circuit flexibility required for dynamic communication.", "citations": [{"title": "Neural coding of distinct motor patterns during Drosophila courtship song", "paperId": "820a1af017fdd6f6a61c722c7bb5fe141521bb55"}, {"title": "Rapid reconstruction of neural circuits using tissue expansion and light sheet microscopy", "paperId": "34313331e3d146173635b7ea4cdec3b648946466"}, {"title": "Behavioral Neuroscience in the Era of Genomics: Tools and Lessons for Analyzing High-Dimensional Datasets", "paperId": "4946afe57483e4336c9c83a2751a5142296551d1"}, {"title": "Neural network organization for courtship-song feature detection in Drosophila", "paperId": "eaa1ad1f740fee4a03079af7764f8807bff9b6bf"}, {"title": "Rapid reconstruction of neural circuits using tissue expansion and lattice light sheet microscopy", "paperId": "b2e6215542288d10b9d5d1ad21d5b49e31eca742"}], "externalIds": {"DOI": "10.1101/2021.11.01.466727", "CorpusId": 242077119}, "isOpenAccess": true, "citationCount": 5, "fieldsOfStudy": ["Biology"], "openAccessPdf": {"url": "https://www.biorxiv.org/content/biorxiv/early/2021/11/02/2021.11.01.466727.full.pdf", "status": "GREEN"}, "citationStyles": {"bibtex": "@None{Roemschied2021FlexibleCM,\n author = {Frederic A. Roemschied and Diego A. Pacheco and Elise C. Ireland and Xinping Li and M. J. Aragon and Rich Pang and M. Murthy},\n booktitle = {bioRxiv},\n journal = {bioRxiv},\n title = {Flexible Circuit Mechanisms for Context-Dependent Song Sequencing},\n year = {2021}\n}\n"}, "referenceCount": 82, "publicationDate": "2021-11-02", "s2FieldsOfStudy": [{"source": "external", "category": "Biology"}, {"source": "s2-fos-model", "category": "Biology"}, {"source": "s2-fos-model", "category": "Psychology"}], "publicationTypes": null, "publicationVenue": {"id": "027ffd21-ebb0-4af8-baf5-911124292fd0", "url": "http://biorxiv.org/", "name": "bioRxiv", "type": "journal"}, "influentialCitationCount": 0}', '2023-01-27 10:58:50.183057', '2023-01-27 10:58:50.183057', 'Flexible Circuit Mechanisms for Context-Dependent Song Sequencing', 2021, false, null, null, null, null);
INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('8d51d2e27be1df78179f8b2a19fc002f46228408', '{"url": "https://www.semanticscholar.org/paper/8d51d2e27be1df78179f8b2a19fc002f46228408", "year": 2021, "title": "CONFIRMS: A Toolkit for Scalable, Black Box Connectome Assessment and Investigation", "venue": "Annual International Conference of the IEEE Engineering in Medicine and Biology Society", "authors": [{"name": "Caitlyn Bishop", "authorId": "40961045"}, {"name": "Jordan K. Matelsky", "authorId": "24023618"}, {"name": "Miller Wilt", "authorId": "91068968"}, {"name": "Joseph Downs", "authorId": "2059868486"}, {"name": "P. Rivlin", "authorId": "2215296"}, {"name": "Stephen M. Plaza", "authorId": "1846140"}, {"name": "B. Wester", "authorId": "1916423"}, {"name": "William R. Gray Roncal", "authorId": "1744493"}], "journal": {"name": "Annual International Conference of the IEEE Engineering in Medicine and Biology Society. IEEE Engineering in Medicine and Biology Society. Annual International Conference", "pages": "2444 - 2450", "volume": "2021"}, "paperId": "8d51d2e27be1df78179f8b2a19fc002f46228408", "abstract": "The nanoscale connectomics community has recently generated automated and semi-automated \"wiring diagrams\" of brain subregions from terabytes and petabytes of dense 3D neuroimagery. This process involves many challenging and imperfect technical steps, including dense 3D image segmentation, anisotropic nonrigid image alignment and coregistration, and pixel classification of each neuron and their individual synaptic connections. As data volumes continue to grow in size, and connectome generation becomes increasingly commonplace, it is important that the scientific community is able to rapidly assess the quality and accuracy of a connectome product to promote dataset analysis and reuse. In this work, we share our scalable toolkit for assessing the quality of a connectome reconstruction via targeted inquiry and large-scale graph analysis, and to provide insights into how such connectome proofreading processes may be improved and optimized in the future. We illustrate the applications and ecosystem on a recent reference dataset.Clinical relevance— Large-scale electron microscopy (EM) data offers a novel opportunity to characterize etiologies and neurological diseases and conditions at an unprecedented scale. EM is useful for low-level analyses such as biopsies; this increased scale offers new possibilities for research into areas such as neural networks if certain bottlenecks and problems are overcome.", "citations": [{"title": "NeuVue: A Framework and Workflows for High-Throughput Electron Microscopy Connectomics Proofreading", "paperId": "4a65612c4fb70aa425c148169c0805429fac9ac0"}, {"title": "Scalable graph analysis tools for the connectomics community", "paperId": "13ce42428e5b4fa276804cc459256533729ea3ef"}, {"title": "The Brain Observatory Storage Service and Database (BossDB): A Cloud-Native Approach for Petascale Neuroscience Discovery", "paperId": "73906620874b76b8ab2b8b58feae242babe49196"}], "externalIds": {"DOI": "10.1109/EMBC46164.2021.9630109", "DBLP": "conf/embc/BishopMWDRPWR21", "PubMed": "34891774", "CorpusId": 243866978, "PubMedCentral": "9073849"}, "isOpenAccess": true, "citationCount": 3, "fieldsOfStudy": ["Computer Science", "Medicine"], "openAccessPdf": {"url": "https://ieeexplore.ieee.org/ielx7/9629355/9629471/09630109.pdf", "status": "BRONZE"}, "citationStyles": {"bibtex": "@[''JournalArticle'', ''Conference'']{Bishop2021CONFIRMS:AT,\n author = {Caitlyn Bishop and Jordan K. Matelsky and Miller Wilt and Joseph Downs and P. Rivlin and Stephen M. Plaza and B. Wester and William R. Gray Roncal},\n booktitle = {Annual International Conference of the IEEE Engineering in Medicine and Biology Society},\n journal = {Annual International Conference of the IEEE Engineering in Medicine and Biology Society. IEEE Engineering in Medicine and Biology Society. Annual International Conference},\n pages = {2444 - 2450},\n title = {CONFIRMS: A Toolkit for Scalable, Black Box Connectome Assessment and Investigation},\n volume = {2021},\n year = {2021}\n}\n"}, "referenceCount": 33, "publicationDate": "2021-11-01", "s2FieldsOfStudy": [{"source": "external", "category": "Computer Science"}, {"source": "external", "category": "Medicine"}, {"source": "s2-fos-model", "category": "Computer Science"}], "publicationTypes": ["JournalArticle", "Conference"], "publicationVenue": {"id": "d7d7587f-a1e9-46a4-9db5-54c95787c6f1", "url": "https://www.inscribepublications.com/journal/josa", "issn": "2529-7856", "name": "Annual International Conference of the IEEE Engineering in Medicine and Biology Society", "type": "conference", "alternate_urls": ["http://www.sesarju.eu/sesarinnovationdays", "https://embc.embs.org/2018/"], "alternate_issns": ["2375-7477"], "alternate_names": ["Int Conf IEEE Eng Med Biology Soc", "International Conference of the IEEE Engineering in Medicine and Biology Society", "EMBC", "Annu Int Conf IEEE Eng Med Biology Soc"]}, "influentialCitationCount": 0}', '2023-01-27 10:58:53.812079', '2023-01-27 10:58:53.812079', 'CONFIRMS: A Toolkit for Scalable, Black Box Connectome Assessment and Investigation', 2021, false, null, null, null, null);
INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('c5b28df4da07e4be4eb91e45721592ff08d9dc56', '{"url": "https://www.semanticscholar.org/paper/c5b28df4da07e4be4eb91e45721592ff08d9dc56", "year": 2021, "title": "Niwaki Instead of Random Forests: Targeted Serial Sectioning Scanning Electron Microscopy With Reimaging Capabilities for Exploring Central Nervous System Cell Biology and Pathology", "venue": "Frontiers in Neuroanatomy", "authors": [{"name": "M. Schifferer", "authorId": "6740599"}, {"name": "Nicolas Snaidero", "authorId": "48992658"}, {"name": "Minou Djannatian", "authorId": "1500591175"}, {"name": "M. Kerschensteiner", "authorId": "5883434"}, {"name": "T. Misgeld", "authorId": "4007479"}], "journal": {"name": "Frontiers in Neuroanatomy", "volume": "15"}, "paperId": "c5b28df4da07e4be4eb91e45721592ff08d9dc56", "abstract": "Ultrastructural analysis of discrete neurobiological structures by volume scanning electron microscopy (SEM) often constitutes a “needle-in-the-haystack” problem and therefore relies on sophisticated search strategies. The appropriate SEM approach for a given relocation task not only depends on the desired final image quality but also on the complexity and required accuracy of the screening process. Block-face SEM techniques like Focused Ion Beam or serial block-face SEM are “one-shot” imaging runs by nature and, thus, require precise relocation prior to acquisition. In contrast, “multi-shot” approaches conserve the sectioned tissue through the collection of serial sections onto solid support and allow reimaging. These tissue libraries generated by Array Tomography or Automated Tape Collecting Ultramicrotomy can be screened at low resolution to target high resolution SEM. This is particularly useful if a structure of interest is rare or has been predetermined by correlated light microscopy, which can assign molecular, dynamic and functional information to an ultrastructure. As such approaches require bridging mm to nm scales, they rely on tissue trimming at different stages of sample processing. Relocation is facilitated by endogenous or exogenous landmarks that are visible by several imaging modalities, combined with appropriate registration strategies that allow overlaying images of various sources. Here, we discuss the opportunities of using multi-shot serial sectioning SEM approaches, as well as suitable trimming and registration techniques, to slim down the high-resolution imaging volume to the actual structure of interest and hence facilitate ambitious targeted volume SEM projects.", "citations": [{"title": "Recent technological advances in correlative light and electron microscopy for the comprehensive analysis of neural circuits", "paperId": "8c50ffa15cc4bddf1457d5615af6c6c5c7e337f1"}, {"title": "Reconstructing neural circuits using multiresolution correlated light and electron microscopy", "paperId": "f31b11968661d0f6748cb3ecec7ab5c34178281b"}, {"title": "Single-Neuron Labeling in Fixed Tissue and Targeted Volume Electron Microscopy", "paperId": "b68bebe6414e6e7e9af929c54b37e242edb17452"}], "externalIds": {"DOI": "10.3389/fnana.2021.732506", "PubMed": "34720890", "CorpusId": 238639461, "PubMedCentral": "8548362"}, "isOpenAccess": true, "citationCount": 3, "fieldsOfStudy": ["Medicine"], "openAccessPdf": {"url": "https://www.frontiersin.org/articles/10.3389/fnana.2021.732506/pdf", "status": "GOLD"}, "citationStyles": {"bibtex": "@[''Review'', ''JournalArticle'']{Schifferer2021NiwakiIO,\n author = {M. Schifferer and Nicolas Snaidero and Minou Djannatian and M. Kerschensteiner and T. Misgeld},\n booktitle = {Frontiers in Neuroanatomy},\n journal = {Frontiers in Neuroanatomy},\n title = {Niwaki Instead of Random Forests: Targeted Serial Sectioning Scanning Electron Microscopy With Reimaging Capabilities for Exploring Central Nervous System Cell Biology and Pathology},\n volume = {15},\n year = {2021}\n}\n"}, "referenceCount": 129, "publicationDate": "2021-10-13", "s2FieldsOfStudy": [{"source": "external", "category": "Medicine"}, {"source": "s2-fos-model", "category": "Biology"}], "publicationTypes": ["Review", "JournalArticle"], "publicationVenue": {"id": "4b5e3ed8-7db8-4ce9-b981-83290b44ccf5", "url": "http://www.expert-reviews.com/loi/epr", "issn": "1662-5129", "name": "Frontiers in Neuroanatomy", "type": "journal", "alternate_urls": ["http://www.frontiersin.org/neuroanatomy/", "https://www.frontiersin.org/journals/neuroanatomy"], "alternate_names": ["Front Neuroanat"]}, "influentialCitationCount": 1}', '2023-01-27 10:58:57.443686', '2023-01-27 10:58:57.443686', 'Niwaki Instead of Random Forests: Targeted Serial Sectioning Scanning Electron Microscopy With Reimaging Capabilities for Exploring Central Nervous System Cell Biology and Pathology', 2021, false, null, null, null, null);
