INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('13398588e348b083498bce11f4b6512c7f556f88', '{"url": "https://www.semanticscholar.org/paper/13398588e348b083498bce11f4b6512c7f556f88", "year": 2021, "title": "Classification and genetic targeting of cell types in the primary taste and premotor center of the adult Drosophila brain", "venue": "eLife", "authors": [{"name": "Gabriella R. Sterne", "authorId": "2091490313"}, {"name": "H. Otsuna", "authorId": "1837706"}, {"name": "B. Dickson", "authorId": "2454761"}, {"name": "Kristin Scott", "authorId": "144080092"}], "journal": {"name": "eLife", "volume": "10"}, "paperId": "13398588e348b083498bce11f4b6512c7f556f88", "abstract": "Neural circuits carry out complex computations that allow animals to evaluate food, select mates, move toward attractive stimuli, and move away from threats. In insects, the subesophageal zone (SEZ) is a brain region that receives gustatory, pheromonal, and mechanosensory inputs and contributes to the control of diverse behaviors, including feeding, grooming, and locomotion. Despite its importance in sensorimotor transformations, the study of SEZ circuits has been hindered by limited knowledge of the underlying diversity of SEZ neurons. Here, we generate a collection of split-GAL4 lines that provides precise genetic targeting of 138 different SEZ cell types in adult Drosophila melanogaster, comprising approximately one third of all SEZ neurons. We characterize the single-cell anatomy of these neurons and find that they cluster by morphology into six supergroups that organize the SEZ into discrete anatomical domains. We find that the majority of local SEZ interneurons are not classically polarized, suggesting rich local processing, whereas SEZ projection neurons tend to be classically polarized, conveying information to a limited number of higher brain regions. This study provides insight into the anatomical organization of the SEZ and generates resources that will facilitate further study of SEZ neurons and their contributions to sensory processing and behavior.", "citations": [{"title": "A searchable image resource of Drosophila GAL4-driver expression patterns with single neuron resolution", "paperId": "eb34c73991b6e0d7a567da8c02aac0d5008d0b2d"}, {"title": "Neuroanatomical basis of sexual dimorphism in the mosquito brain", "paperId": "6614536b22054ccb3d1ef1101bedee5fdef92112"}, {"title": "NeuronBridge: an intuitive web application for neuronal morphology search across large data sets", "paperId": "8a863838106cc9320c410e4f0611ad0e3a96a0f4"}, {"title": "Descending neuron population dynamics during odor-evoked and spontaneous limb-dependent behaviors", "paperId": "d118851a9e9590488613c2e99a919875d606323c"}, {"title": "Taste quality and hunger interactions in a feeding sensorimotor circuit", "paperId": "3209c297a4db06543dbd45a41a97ed99804cbe52"}, {"title": "A conditional GABAergic synaptic vesicle marker for Drosophila\n", "paperId": "b9d275db60ac8f7134df789511798a65d47df804"}, {"title": "Serotonergic neurons translate taste detection into internal nutrient regulation", "paperId": "1361e887cdbeb798515effeaae543b8c5daf1e4f"}, {"title": "A searchable image resource of 1 Drosophila GAL4-driver expression 2 patterns with single neuron 3 resolution 4", "paperId": "8f73face5602e9722723548f9531b456d49140fb"}, {"title": "Drosophila gustatory projections are segregated by taste modality and connectivity", "paperId": "4ec24bf04caa47ce0bdd6532df03d838d8a086aa"}, {"title": "Spatial and temporal control of expression with light-gated LOV-LexA", "paperId": "c000d068ba7eab41b281b415513c5772d073a5d2"}], "externalIds": {"DOI": "10.7554/eLife.71679", "PubMed": "34473057", "CorpusId": 237391363, "PubMedCentral": "8445619"}, "isOpenAccess": false, "citationCount": 10, "fieldsOfStudy": ["Medicine"], "openAccessPdf": null, "citationStyles": {"bibtex": "@[''JournalArticle'']{Sterne2021ClassificationAG,\n author = {Gabriella R. Sterne and H. Otsuna and B. Dickson and Kristin Scott},\n booktitle = {eLife},\n journal = {eLife},\n title = {Classification and genetic targeting of cell types in the primary taste and premotor center of the adult Drosophila brain},\n volume = {10},\n year = {2021}\n}\n"}, "referenceCount": 115, "publicationDate": "2021-09-02", "s2FieldsOfStudy": [{"source": "external", "category": "Medicine"}, {"source": "s2-fos-model", "category": "Biology"}], "publicationTypes": ["JournalArticle"], "publicationVenue": {"id": "07365b9a-c0ce-4dd3-b93b-a02e1c81e0c6", "url": "https://epub.uni-regensburg.de/40444/", "issn": "2050-084X", "name": "eLife", "type": "journal", "alternate_urls": ["https://elifesciences.org/", "https://elife.elifesciences.org/", "http://elifesciences.org/"]}, "influentialCitationCount": 0}', '2023-01-27 10:59:04.687685', '2023-01-27 10:59:04.687685', 'Classification and genetic targeting of cell types in the primary taste and premotor center of the adult Drosophila brain', 2021, false, null, null, null, null);
INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('badbb063a3ee4c5a9bc47c2eaa81f44ddc41e629', '{"url": "https://www.semanticscholar.org/paper/badbb063a3ee4c5a9bc47c2eaa81f44ddc41e629", "year": 2021, "title": "Classification and genetic targeting of cell types in the primary taste and premotor center of the Drosophila brain", "venue": "bioRxiv", "authors": [{"name": "Gabriella R. Sterne", "authorId": "2091490313"}, {"name": "H. Otsuna", "authorId": "1837706"}, {"name": "B. Dickson", "authorId": "2454761"}, {"name": "Kristin Scott", "authorId": "144080092"}], "journal": {"name": "bioRxiv"}, "paperId": "badbb063a3ee4c5a9bc47c2eaa81f44ddc41e629", "abstract": "Neural circuits carry out complex computations that allow animals to evaluate food, select mates, move toward attractive stimuli, and move away from threats. In insects, the subesophageal zone (SEZ) is a brain region that receives gustatory, pheromonal, and mechanosensory inputs and contributes to the control of diverse behaviors, including feeding, grooming, and locomotion. Despite its importance in sensorimotor transformations, the study of SEZ circuits has been hindered by limited knowledge of the underlying diversity of SEZ neurons. Here, we generate a collection of split-GAL4 lines that provides precise genetic targeting of 138 different SEZ cell types in D. melanogaster, comprising approximately one third of all SEZ neurons. We characterize the single cell anatomy of these neurons and find that they cluster by morphology into six supergroups that organize the SEZ into discrete anatomical domains. We find that the majority of local SEZ interneurons are not classically polarized, suggesting rich local processing, whereas SEZ projection neurons tend to be classically polarized, conveying information to a limited number of higher brain regions. This study provides insight into the anatomical organization of the SEZ and generates resources that will facilitate further study of SEZ neurons and their contributions to sensory processing and behavior.", "citations": [{"title": "Spatial and temporal control of expression with light-gated LOV-LexA", "paperId": "c000d068ba7eab41b281b415513c5772d073a5d2"}], "externalIds": {"DOI": "10.1101/2021.08.06.455433", "CorpusId": 236971402}, "isOpenAccess": false, "citationCount": 1, "fieldsOfStudy": ["Biology"], "openAccessPdf": null, "citationStyles": {"bibtex": "@None{Sterne2021ClassificationAG,\n author = {Gabriella R. Sterne and H. Otsuna and B. Dickson and Kristin Scott},\n booktitle = {bioRxiv},\n journal = {bioRxiv},\n title = {Classification and genetic targeting of cell types in the primary taste and premotor center of the Drosophila brain},\n year = {2021}\n}\n"}, "referenceCount": 86, "publicationDate": "2021-08-08", "s2FieldsOfStudy": [{"source": "external", "category": "Biology"}, {"source": "s2-fos-model", "category": "Biology"}], "publicationTypes": null, "publicationVenue": {"id": "027ffd21-ebb0-4af8-baf5-911124292fd0", "url": "http://biorxiv.org/", "name": "bioRxiv", "type": "journal"}, "influentialCitationCount": 0}', '2023-01-27 10:59:08.311092', '2023-01-27 10:59:08.311092', 'Classification and genetic targeting of cell types in the primary taste and premotor center of the Drosophila brain', 2021, false, null, null, null, null);
