"}, {"name": "Gayathri Mahalingam", "authorId": "144348673"}, {"name": "Sarah McReynolds", "authorId": "2122369400"}, {"name": "Elanine Miranda", "authorId": "1576368250"}, {"name": "Eric Mitchell", "authorId": "49688913"}, {"name": "Shanka Subhra Mondal", "authorId": "120175502"}, {"name": "Merlin Moore", "authorId": "1924842078"}, {"name": "Shang Mu", "authorId": "47330620"}, {"name": "Taliah Muhammad", "authorId": "51107585"}, {"name": "Barak Nehoran", "authorId": "1926235266"}, {"name": "Oluwaseun Ogedengbe", "authorId": "1926318518"}, {"name": "Ch. Papadopoulos", "authorId": "11354920"}, {"name": "S. Papadopoulos", "authorId": "9284143"}, {"name": "Saumil S. Patel", "authorId": "4330752"}, {"name": "X. Pitkow", "authorId": "49839819"}, {"name": "Sergiy Popovych", "authorId": "1840375"}, {"name": "Anthony Ramos", "authorId": "144603847"}, {"name": "R. Clay Reid", "authorId": "2122359289"}, {"name": "Jacob Reimer", "authorId": "48128495"}, {"name": "C. Schneider-Mizell", "authorId": "1397852111"}, {"name": "H. Sebastian Seung", "authorId": "83672784"}, {"name": "B. Silverman", "authorId": "2056371649"}, {"name": "W. Silversmith", "authorId": "2108734"}, {"name": "A. Sterling", "authorId": "41131456"}, {"name": "Fabian H Sinz", "authorId": "50095217"}, {"name": "Cameron L. Smith", "authorId": "2133856545"}, {"name": "S. Suckow", "authorId": "40047816"}, {"name": "Z. H. Tan", "authorId": "143939138"}, {"name": "A. Tolias", "authorId": "1739838"}, {"name": "Russel Torres", "authorId": "152750629"}, {"name": "Nicholas L. Turner", "authorId": "152772071"}, {"name": "Edgar Y. Walker", "authorId": "144334775"}, {"name": "Tianyu Wang", "authorId": "49980839"}, {"name": "Grace Williams", "authorId": "144416314"}, {"name": "S. Williams", "authorId": "2107720438"}, {"name": "K. Willie", "authorId": "1926285936"}, {"name": "Ryan Willie", "authorId": "1926285994"}, {"name": "William Wong", "authorId": "120774631"}, {"name": "Jingpeng Wu", "authorId": "31421132"}, {"name": "Ch