INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('b919838a17d2123e85478b4b0435fd7c69564bb5', '{"url": "https://www.semanticscholar.org/paper/b919838a17d2123e85478b4b0435fd7c69564bb5", "year": 2021, "title": "In situ X-ray-assisted electron microscopy staining for large biological samples", "venue": "bioRxiv", "authors": [{"name": "Sebastian Ströh", "authorId": "5373392"}, {"name": "Eric W. Hammerschmith", "authorId": "90929619"}, {"name": "D. Tank", "authorId": "2516142"}, {"name": "H. Seung", "authorId": "144924970"}, {"name": "A. Wanner", "authorId": "2056544372"}], "journal": {"name": "eLife", "volume": "11"}, "paperId": "b919838a17d2123e85478b4b0435fd7c69564bb5", "abstract": "Electron microscopy of biological tissue has recently seen an unprecedented increase in imaging throughput moving the ultrastructural analysis of large tissue blocks such as whole brains into the realm of the feasible. However, homogeneous, high quality electron microscopy staining of large biological samples is still a major challenge. To date, assessing the staining quality in electron microscopy requires running a sample through the entire staining protocol end-to-end, which can take weeks or even months for large samples, rendering protocol optimization for such samples to be inefficient. Here we present an in situ time-lapsed X-ray assisted staining procedure that opens the “black box” of electron microscopy staining and allows observation of individual staining steps in real time. Using this novel method we measured the accumulation of heavy metals in large tissue samples immersed in different staining solutions. We show that the measured accumulation of osmium in fixed tissue obeys empirically a quadratic dependence between the incubation time and sample size. We found that potassium ferrocyanide, a classic reducing agent for osmium tetroxide, clears the tissue after osmium staining and that the tissue expands in osmium tetroxide solution, but shrinks in reduced osmium solution. X-ray assisted staining gave access to the in situ staining kinetics and allowed us to develop a diffusion-reaction-advection model that accurately simulates the measured accumulation of osmium in tissue. These are first steps towards in silico staining experiments and simulation-guided optimization of staining protocols for large samples. Hence, X-ray assisted staining will be a useful tool for the development of reliable staining procedures for large samples such as entire brains of mice, monkeys or humans.", "citations": [{"title": "Femtosecond laser preparation of resin embedded samples for correlative microscopy workflows in life sciences", "paperId": "a486309bbf9a60aaa233593f992a6356aa2cb2fe"}, {"title": "Functional and multiscale 3D structural investigation of brain tissue through correlative in vivo physiology, synchrotron microtomography and volume electron microscopy", "paperId": "a348cca511442e042328dd60eb410f56f1bda20c"}, {"title": "High-contrast en-bloc staining of mouse whole-brain samples for EM-based connectomics", "paperId": "0827f73b8b1c6f5e79da66be837045d2551afd17"}, {"title": "Sample Preparation and Warping Accuracy for Correlative Multimodal Imaging in the Mouse Olfactory Bulb Using 2-Photon, Synchrotron X-Ray and Volume Electron Microscopy", "paperId": "a1372facc07976023225f25082c2d3c3f892364d"}], "externalIds": {"DOI": "10.1101/2021.06.19.448808", "PubMed": "36263931", "CorpusId": 235650178, "PubMedCentral": "9665849"}, "isOpenAccess": false, "citationCount": 4, "fieldsOfStudy": ["Biology", "Medicine"], "openAccessPdf": null, "citationStyles": {"bibtex": "@[''JournalArticle'']{Ströh2021InSX,\n author = {Sebastian Ströh and Eric W. Hammerschmith and D. Tank and H. Seung and A. Wanner},\n booktitle = {bioRxiv},\n journal = {eLife},\n title = {In situ X-ray-assisted electron microscopy staining for large biological samples},\n volume = {11},\n year = {2021}\n}\n"}, "referenceCount": 77, "publicationDate": "2021-06-19", "s2FieldsOfStudy": [{"source": "external", "category": "Biology"}, {"source": "external", "category": "Medicine"}, {"source": "s2-fos-model", "category": "Biology"}], "publicationTypes": ["JournalArticle"], "publicationVenue": {"id": "027ffd21-ebb0-4af8-baf5-911124292fd0", "url": "http://biorxiv.org/", "name": "bioRxiv", "type": "journal"}, "influentialCitationCount": 1}', '2023-01-27 10:59:23.139953', '2023-01-27 10:59:23.139953', 'In situ X-ray-assisted electron microscopy staining for large biological samples', 2021, false, null, null, null, null);
