INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('b2239d79f8ecffb441dad7037ac85391ad79a401', '{"url": "https://www.semanticscholar.org/paper/b2239d79f8ecffb441dad7037ac85391ad79a401", "year": null, "title": "In situ X-ray assisted electron microscopy staining for large biological 1 samples 2", "venue": "", "authors": [{"name": "Sebastian Ströh", "authorId": "5373392"}, {"name": "Eric W. Hammerschmith", "authorId": "90929619"}, {"name": "D. Tank", "authorId": "2516142"}, {"name": "H. Seung", "authorId": "2144956491"}, {"name": "3. AdrianA.", "authorId": "2188580960"}, {"name": "Wanner", "authorId": "2101736782"}], "journal": null, "paperId": "b2239d79f8ecffb441dad7037ac85391ad79a401", "abstract": "10 Electron microscopy of biological tissue has recently seen an unprecedented increase in imaging 11 throughput moving the ultrastructural analysis of large tissue blocks such as whole brains into the 12 realm of the feasible. However, homogeneous, high quality electron microscopy staining of large 13 biological samples is still a major challenge. To date, assessing the staining quality in electron 14 microscopy requires running a sample through the entire staining protocol end-to-end, which can 15 take weeks or even months for large samples, rendering protocol optimization for such samples 16 to be inefficient. Here we present an in situ time-lapsed X-ray assisted staining procedure that 17 opens the “black box” of electron microscopy staining and allows observation of individual staining 18 steps in real time. Using this novel method we measured the accumulation of heavy metals in 19 large tissue samples immersed in different staining solutions. We show that the measured 20 accumulation of osmium in fixed tissue obeys empirically a quadratic dependence between the 21 incubation time and sample size. We found that potassium ferrocyanide, a classic reducing agent 22 for osmium tetroxide, clears the tissue after osmium staining and that the tissue expands in 23 osmium tetroxide solution, but shrinks in potassium ferrocyanide reduced osmium solution. X-ray 24 assisted staining gave access to the in situ staining kinetics and allowed us to develop a diffusion25 reaction-advection model that accurately simulates the measured accumulation of osmium in 26 tissue. These are first steps towards in silico staining experiments and simulation-guided 27 optimization of staining protocols for large samples. Hence, X-ray assisted staining will be a useful 28 tool for the development of reliable staining procedures for large samples such as entire brains of 29 mice, monkeys or humans. 30", "citations": [], "externalIds": {"CorpusId": 253065032}, "isOpenAccess": false, "citationCount": 0, "fieldsOfStudy": null, "openAccessPdf": null, "citationStyles": {"bibtex": "@None{None,\n author = {Sebastian Ströh and Eric W. Hammerschmith and D. Tank and H. Seung and 3. AdrianA. and Wanner},\n title = {In situ X-ray assisted electron microscopy staining for large biological 1 samples 2}\n}\n"}, "referenceCount": 84, "publicationDate": null, "s2FieldsOfStudy": [{"source": "s2-fos-model", "category": "Biology"}], "publicationTypes": null, "publicationVenue": null, "influentialCitationCount": 0}', '2023-01-27 11:00:07.436284', '2023-01-27 11:00:07.436284', 'In situ X-ray assisted electron microscopy staining for large biological 1 samples 2', null, false, null, null, null, null);
INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('49fccc03f04ed963948f64c4cf76459163a2f299', '{"url": "https://www.semanticscholar.org/paper/49fccc03f04ed963948f64c4cf76459163a2f299", "year": 2023, "title": "Modulation and Neural Correlates of Postmating Sleep Plasticity in Drosophila Females", "venue": "bioRxiv", "authors": [{"name": "José M. Duhart", "authorId": "35694659"}, {"name": "Joseph R. Buchler", "authorId": "1649835590"}, {"name": "Show Inami", "authorId": "4857428"}, {"name": "Kyle J. Kennedy", "authorId": "2200991431"}, {"name": "B. P. Jenny", "authorId": "2200991567"}, {"name": "D. J. Afonso", "authorId": "6321165"}, {"name": "K. Koh", "authorId": "34643080"}], "journal": {"name": "bioRxiv"}, "paperId": "49fccc03f04ed963948f64c4cf76459163a2f299", "abstract": "Sleep is essential, but animals may forgo sleep to engage in other critical behaviors, such as feeding and reproduction. Previous studies have shown that female flies show decreased sleep after mating, but our understanding of the process is limited. Here, we report that postmating nighttime sleep loss is modulated by diet and sleep deprivation, demonstrating a complex interaction among sleep, reproduction, and diet. We also report that female-specific pC1 neurons and sleep-promoting dorsal fan-shaped body (dFB) neurons are required for postmating sleep plasticity. Activating pC1 neurons leads to sleep suppression on standard fly culture media but has little sleep effect on sucrose-only food. Published connectome data suggest indirect, inhibitory connections among pC1 subtypes. Using calcium imaging, we show that activating the pC1e subtype inhibits dFB neurons. We propose that pC1 and dFB neurons integrate the mating status, food context, and sleep drive to modulate postmating sleep plasticity. Highlights Diet and sleep drive modulate female nighttime postmating sleep loss Female-specific pC1 neurons are required for postmating sleep loss Sleep-promoting dFB-projecting neurons are required for postmating sleep loss Activating pC1 subtypes promotes wakefulness and inhibits dFB-projecting neurons eTOC blurb Animals evaluate environmental conditions and internal states to make behavioral choices. Duhart et al. show that the decision to stay awake after mating in Drosophila females is modulated by food composition and sleep history and mediated by female-specific pC1 neurons acting upstream of the dFB sleep center.", "citations": [], "externalIds": {"DOI": "10.1101/2023.01.11.523670", "CorpusId": 255850188}, "isOpenAccess": true, "citationCount": 0, "fieldsOfStudy": ["Biology"], "openAccessPdf": {"url": "https://www.biorxiv.org/content/biorxiv/early/2023/01/13/2023.01.11.523670.full.pdf", "status": "GREEN"}, "citationStyles": {"bibtex": "@None{Duhart2023ModulationAN,\n author = {José M. Duhart and Joseph R. Buchler and Show Inami and Kyle J. Kennedy and B. P. Jenny and D. J. Afonso and K. Koh},\n booktitle = {bioRxiv},\n journal = {bioRxiv},\n title = {Modulation and Neural Correlates of Postmating Sleep Plasticity in Drosophila Females},\n year = {2023}\n}\n"}, "referenceCount": 66, "publicationDate": "2023-01-13", "s2FieldsOfStudy": [{"source": "external", "category": "Biology"}, {"source": "s2-fos-model", "category": "Biology"}, {"source": "s2-fos-model", "category": "Psychology"}], "publicationTypes": null, "publicationVenue": {"id": "027ffd21-ebb0-4af8-baf5-911124292fd0", "url": "http://biorxiv.org/", "name": "bioRxiv", "type": "journal"}, "influentialCitationCount": 0}', '2023-01-27 11:00:45.324102', '2023-01-27 11:00:45.324102', 'Modulation and Neural Correlates of Postmating Sleep Plasticity in Drosophila Females', 2023, false, null, null, null, null);
