INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('f59e8085154ed9002550440b0d1c11d9d37d06cd', '{"url": "https://www.semanticscholar.org/paper/f59e8085154ed9002550440b0d1c11d9d37d06cd", "year": 2022, "title": "Mate Competition Drives Aggressive Behaviour in Female Drosophila", "venue": "bioRxiv", "authors": [{"name": "Miguel Gaspar", "authorId": "2053088319"}, {"name": "Sophie Dias", "authorId": "9087305"}, {"name": "M. Vasconcelos", "authorId": "3605087"}], "journal": {"name": "bioRxiv"}, "paperId": "f59e8085154ed9002550440b0d1c11d9d37d06cd", "abstract": "Aggression is an adaptive set of behaviours that allows animals to compete against one another in an environment of limited resources. In Drosophila such aggressive behaviour has been extensively studied in males. Despite recent work highlighting territorial defence in females, female aggression in Drosophila is still poorly understood. Indeed, whether females compete for mating partners, as males do, has remained unknown so far. In the present work, we report that Drosophila melanogaster females reliably display aggression towards mating pairs. This aggressive behaviour is positively associated with the female’s mating drive and relies heavily on olfaction. Furthermore, we found that food odour in combination with OR47b-dependent fly odour sensing are required for proper expression of aggressive behaviour. Taken together, we describe a social context linked to reproduction in which Drosophila females aspiring to mate produce consistent and stereotyped displays of aggression. These findings open the door for further inquiries into the neural mechanisms that govern this behaviour.", "citations": [], "externalIds": {"DOI": "10.1101/2022.02.07.479369", "CorpusId": 246764352}, "isOpenAccess": true, "citationCount": 0, "fieldsOfStudy": ["Biology"], "openAccessPdf": {"url": "https://www.biorxiv.org/content/biorxiv/early/2022/02/10/2022.02.07.479369.full.pdf", "status": "GREEN"}, "citationStyles": {"bibtex": "@None{Gaspar2022MateCD,\n author = {Miguel Gaspar and Sophie Dias and M. Vasconcelos},\n booktitle = {bioRxiv},\n journal = {bioRxiv},\n title = {Mate Competition Drives Aggressive Behaviour in Female Drosophila},\n year = {2022}\n}\n"}, "referenceCount": 90, "publicationDate": "2022-02-09", "s2FieldsOfStudy": [{"source": "external", "category": "Biology"}, {"source": "s2-fos-model", "category": "Biology"}, {"source": "s2-fos-model", "category": "Psychology"}], "publicationTypes": null, "publicationVenue": {"id": "027ffd21-ebb0-4af8-baf5-911124292fd0", "url": "http://biorxiv.org/", "name": "bioRxiv", "type": "journal"}, "influentialCitationCount": 0}', '2023-01-27 11:01:11.016726', '2023-01-27 11:01:11.016726', 'Mate Competition Drives Aggressive Behaviour in Female Drosophila', 2022, false, null, null, null, null);
INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('48e1845fd3f7d02c8f3c551d2320451341c279d2', '{"url": "https://www.semanticscholar.org/paper/48e1845fd3f7d02c8f3c551d2320451341c279d2", "year": 2021, "title": "Sexual Dimorphism in Aggression: Sex-Specific Fighting Strategies Across Species", "venue": "Frontiers in Behavioral Neuroscience", "authors": [{"name": "M. Pandolfi", "authorId": "25814554"}, {"name": "M. F. Scaia", "authorId": "4029342"}, {"name": "María Paz Fernández", "authorId": "50509769"}], "journal": {"name": "Frontiers in Behavioral Neuroscience", "volume": "15"}, "paperId": "48e1845fd3f7d02c8f3c551d2320451341c279d2", "abstract": "Aggressive behavior is thought to have evolved as a strategy for gaining access to resources such as territory, food, and potential mates. Across species, secondary sexual characteristics such as competitive aggression and territoriality are considered male-specific behaviors. However, although female–female aggression is often a behavior that is displayed almost exclusively to protect the offspring, multiple examples of female–female competitive aggression have been reported in both invertebrate and vertebrate species. Moreover, cases of intersexual aggression have been observed in a variety of species. Genetically tractable model systems such as mice, zebrafish, and fruit flies have proven extremely valuable for studying the underlying neuronal circuitry and the genetic architecture of aggressive behavior under laboratory conditions. However, most studies lack ethological or ecological perspectives and the behavioral patterns available are limited. The goal of this review is to discuss each of these forms of aggression, male intrasexual aggression, intersexual aggression and female intrasexual aggression in the context of the most common genetic animal models and discuss examples of these behaviors in other species.", "citations": [{"title": "Putative Activation of Cannabinoid Receptor Type 1 Prevents Brain Oxidative Stress and Inhibits Aggressive-Like Behavior in Zebrafish.", "paperId": "f594f787fe50e8789a531abba8c82abb7e0b120a"}, {"title": "Conspecific Identity Determines Interactive Space Area in Zebrafish Shoal", "paperId": "e76246bc0b6e0e8e68378d40c5bfe61934f3ce22"}, {"title": "Mating pair drives aggressive behavior in female Drosophila", "paperId": "bc9653a608d264b00031e37feb937a31d3fe57a3"}, {"title": "Neuroendocrine regulation of female aggression", "paperId": "0a3e88b795ddc893c3ab7ce2d3ea7e7f01f7d808"}, {"title": "The Island of Female Power? Intersexual Dominance Relationships in the Lemurs of Madagascar", "paperId": "9b457147ce10a95871ed1c359b59a386478d11ac"}, {"title": "The ornamental fighting fish is the next model organism for genetic studies", "paperId": "f056eef1d0d0310149a86a11b3bfb574ed545292"}, {"title": "Standardization of Coagulation Factor V Reference Intervals, Prothrombin Time, and Activated Partial Thromboplastin Time in Mice for Use in Factor V Deficiency Pathological Models", "paperId": "fb55614f809a827380b0c29a44fc4c7fa3b6f25a"}, {"title": "Effects of a 33-ion sequential beam galactic cosmic ray analog on male mouse behavior and evaluation of CDDO-EA as a radiation countermeasure", "paperId": "c3273bf879af68a5482d795f81bd69cfb1207ce6"}, {"title": "Ethanol enhanced MDPV- and cocaine-induced aggressive behavior in mice: Forensic implications.", "paperId": "fb406781b304415f40c47e31b619a233b1c5c02c"}], "externalIds": {"DOI": "10.3389/fnbeh.2021.659615", "PubMed": "34262439", "CorpusId": 235656122, "PubMedCentral": "8273308"}, "isOpenAccess": true, "citationCount": 9, "fieldsOfStudy": ["Medicine"], "openAccessPdf": {"url": "https://www.frontiersin.org/articles/10.3389/fnbeh.2021.659615/pdf", "status": "GOLD"}, "citationStyles": {"bibtex": "@[''Review'', ''JournalArticle'']{Pandolfi2021SexualDI,\n author = {M. Pandolfi and M. F. Scaia and María Paz Fernández},\n booktitle = {Frontiers in Behavioral Neuroscience},\n journal = {Frontiers in Behavioral Neuroscience},\n title = {Sexual Dimorphism in Aggression: Sex-Specific Fighting Strategies Across Species},\n volume = {15},\n year = {2021}\n}\n"}, "referenceCount": 180, "publicationDate": "2021-06-28", "s2FieldsOfStudy": [{"source": "external", "category": "Medicine"}, {"source": "s2-fos-model", "category": "Biology"}, {"source": "s2-fos-model", "category": "Psychology"}], "publicationTypes": ["Review", "JournalArticle"], "publicationVenue": {"id": "810ce699-eb80-4394-8aac-a6d3c87840ed", "url": "http://www.frontiersin.org/behavioral_neuroscience", "issn": "1662-5153", "name": "Frontiers in Behavioral Neuroscience", "type": "journal", "alternate_urls": ["http://www.frontiersin.org/behavioralneuroscience/", "https://www.frontiersin.org/journals/behavioral-neuroscience"], "alternate_names": ["Front Behav Neurosci"]}, "influentialCitationCount": 0}', '2023-01-27 11:01:22.147138', '2023-01-27 11:01:22.147138', 'Sexual Dimorphism in Aggression: Sex-Specific Fighting Strategies Across Species', 2021, false, null, null, null, null);
INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('a6839621c1a1213502591b5e3cf2a635435b6a72', '{"url": "https://www.semanticscholar.org/paper/a6839621c1a1213502591b5e3cf2a635435b6a72", "year": 2021, "title": "Decisions in an Innate Behavioral Sequence", "venue": "bioRxiv", "authors": [{"name": "Kevin M. Cury", "authorId": "50283382"}, {"name": "R. Axel", "authorId": "6324849"}], "journal": {"name": "bioRxiv"}, "paperId": "a6839621c1a1213502591b5e3cf2a635435b6a72", "abstract": "Innate behaviors are comprised of ordered sequences of component actions that progress to satisfy drives. We have characterized the structure of egg-laying behavior in Drosophila in detail and observed that the sequence is not merely comprised of motor acts but also acts of sensory exploration that govern the transitions between component actions. We have identified a cluster of internal sensory neurons that provide information about the progression of the egg during ovipositor burrowing, a behavior necessary for the subterraneous deposition of the egg. These neurons impart sensory feedback that allows burrowing to continue to egg deposition or to abort in favor of further exploration. Diminished activity of these neurons upon completed egg expulsion may initiate the transition to the final phase of egg-laying, allowing the cycle to repeat. Sensory feedback therefore plays a critical role at decision points between transitions affording innate behaviors with an adaptive flexibility.", "citations": [{"title": "Odor-gated oviposition behavior in an ecological specialist", "paperId": "e18998c3673ca611516d070f288752c63ec06be6"}, {"title": "A rise-to-threshold signal for a relative value deliberation", "paperId": "f8d9441b1a513babb97c1ece94750077908b7d98"}, {"title": "Neuronal control of suppression, initiation and completion of egg deposition in Drosophila melanogaster", "paperId": "a3e9652baad864a9edff6d9630105e8f979efcca"}], "externalIds": {"DOI": "10.1101/2021.04.03.438315", "CorpusId": 233175398}, "isOpenAccess": true, "citationCount": 3, "fieldsOfStudy": ["Biology"], "openAccessPdf": {"url": "https://www.biorxiv.org/content/biorxiv/early/2021/04/04/2021.04.03.438315.full.pdf", "status": "GREEN"}, "citationStyles": {"bibtex": "@None{Cury2021DecisionsIA,\n author = {Kevin M. Cury and R. Axel},\n booktitle = {bioRxiv},\n journal = {bioRxiv},\n title = {Decisions in an Innate Behavioral Sequence},\n year = {2021}\n}\n"}, "referenceCount": 68, "publicationDate": "2021-04-04", "s2FieldsOfStudy": [{"source": "external", "category": "Biology"}, {"source": "s2-fos-model", "category": "Biology"}, {"source": "s2-fos-model", "category": "Psychology"}], "publicationTypes": null, "publicationVenue": {"id": "027ffd21-ebb0-4af8-baf5-911124292fd0", "url": "http://biorxiv.org/", "name": "bioRxiv", "type": "journal"}, "influentialCitationCount": 1}', '2023-01-27 11:01:25.834789', '2023-01-27 11:01:25.834789', 'Decisions in an Innate Behavioral Sequence', 2021, false, null, null, null, null);
INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('689649df60f3179847c2b8a92638febadc69a822', '{"url": "https://www.semanticscholar.org/paper/689649df60f3179847c2b8a92638febadc69a822", "year": 2021, "title": "Sex-specific speed–accuracy trade-offs shape neural processing of acoustic signals in a grasshopper", "venue": "Proceedings of the Royal Society B", "authors": [{"name": "Jan Clemens", "authorId": "3329020"}, {"name": "B. Ronacher", "authorId": "2725123"}, {"name": "M. Reichert", "authorId": "2441199"}], "journal": {"name": "Proceedings of the Royal Society B: Biological Sciences", "volume": "288"}, "paperId": "689649df60f3179847c2b8a92638febadc69a822", "abstract": "Speed–accuracy trade-offs—being fast at the risk of being wrong—are fundamental to many decisions and natural selection is expected to resolve these trade-offs according to the costs and benefits of behaviour. We here test the prediction that females and males should integrate information from courtship signals differently because they experience different pay-offs along the speed–accuracy continuum. We fitted a neural model of decision making (a drift–diffusion model of integration to threshold) to behavioural data from the grasshopper Chorthippus biguttulus to determine the parameters of temporal integration of acoustic directional information used by male grasshoppers to locate receptive females. The model revealed that males had a low threshold for initiating a turning response, yet a large integration time constant enabled them to continue to gather information when cues were weak. This contrasts with parameters estimated for females of the same species when evaluating potential mates, in which response thresholds were much higher and behaviour was strongly influenced by unattractive stimuli. Our results reveal differences in neural integration consistent with the sex-specific costs of mate search: males often face competition and need to be fast, while females often pay high error costs and need to be deliberate.", "citations": [{"title": "Sensory biases in response to novel complex acoustic signals in male and female grey treefrogs, Hyla chrysoscelis", "paperId": "ba8b28969a1ab637fb766b10772e093d73d4b096"}, {"title": "Acoustic signalling in Orthoptera", "paperId": "84501f0a2214f04a40f3b9faf0dea0431e7008ab"}], "externalIds": {"DOI": "10.1098/rspb.2021.0005", "PubMed": "33593184", "CorpusId": 231938486, "PubMedCentral": "7935134"}, "isOpenAccess": true, "citationCount": 2, "fieldsOfStudy": ["Medicine"], "openAccessPdf": {"url": "https://royalsocietypublishing.org/doi/pdf/10.1098/rspb.2021.0005", "status": "HYBRID"}, "citationStyles": {"bibtex": "@[''JournalArticle'']{Clemens2021Sex-specificST,\n author = {Jan Clemens and B. Ronacher and M. Reichert},\n booktitle = {Proceedings of the Royal Society B},\n journal = {Proceedings of the Royal Society B: Biological Sciences},\n title = {Sex-specific speed–accuracy trade-offs shape neural processing of acoustic signals in a grasshopper},\n volume = {288},\n year = {2021}\n}\n"}, "referenceCount": 75, "publicationDate": "2021-02-17", "s2FieldsOfStudy": [{"source": "external", "category": "Medicine"}, {"source": "s2-fos-model", "category": "Biology"}], "publicationTypes": ["JournalArticle"], "publicationVenue": null, "influentialCitationCount": 0}', '2023-01-27 11:01:29.472978', '2023-01-27 11:01:29.472978', 'Sex-specific speed–accuracy trade-offs shape neural processing of acoustic signals in a grasshopper', 2021, false, null, null, null, null);
