INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('df1696050c10fffb86668e9443d39cf72fbddf88', '{"url": "https://www.semanticscholar.org/paper/df1696050c10fffb86668e9443d39cf72fbddf88", "year": 2020, "title": "Cell types and neuronal circuitry underlying female aggression in Drosophila", "venue": "eLife", "authors": [{"name": "Catherine E Schretter", "authorId": "6472682"}, {"name": "Y. Aso", "authorId": "48343951"}, {"name": "A. Robie", "authorId": "2557555"}, {"name": "M. Dreher", "authorId": "1572706864"}, {"name": "Michael-John Dolan", "authorId": "47789037"}, {"name": "Nan Chen", "authorId": "1392269194"}, {"name": "Masayoshi Ito", "authorId": "1600598494"}, {"name": "Tansy Yang", "authorId": "1508245800"}, {"name": "Ruchi Parekh", "authorId": "3298464"}, {"name": "K. Branson", "authorId": "2424812"}, {"name": "G. Rubin", "authorId": "34660241"}], "journal": {"name": "eLife", "volume": "9"}, "paperId": "df1696050c10fffb86668e9443d39cf72fbddf88", "abstract": "Aggressive social interactions are used to compete for limited resources and are regulated by complex sensory cues and the organism’s internal state. While both sexes exhibit aggression, its neuronal underpinnings are understudied in females. Here, we identify a population of sexually dimorphic aIPg neurons in the adult Drosophila melanogaster central brain whose optogenetic activation increased, and genetic inactivation reduced, female aggression. Analysis of GAL4 lines identified in an unbiased screen for increased female chasing behavior revealed the involvement of another sexually dimorphic neuron, pC1d, and implicated aIPg and pC1d neurons as core nodes regulating female aggression. Connectomic analysis demonstrated that aIPg neurons and pC1d are interconnected and suggest that aIPg neurons may exert part of their effect by gating the flow of visual information to descending neurons. Our work reveals