INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('3c01fef33280b903a04c1704010b837505151ccd', '{"url": "https://www.semanticscholar.org/paper/3c01fef33280b903a04c1704010b837505151ccd", "year": 2020, "title": "Sex-specific speed-accuracy tradeoffs shape neural processing of acoustic signals", "venue": "bioRxiv", "authors": [{"name": "Jan Clemens", "authorId": "3329020"}, {"name": "B. Ronacher", "authorId": "2725123"}, {"name": "M. Reichert", "authorId": "2441199"}], "journal": {"name": "bioRxiv"}, "paperId": "3c01fef33280b903a04c1704010b837505151ccd", "abstract": "Speed-accuracy tradeoffs – being fast at the risk of being wrong – are fundamental to many decisions and natural selection is expected to resolve these tradeoffs according to the costs and benefits of behavior. We here test the prediction that females and males should integrate information from courtship signals differently because they experience different payoffs along the speed-accuracy continuum. We fitted a neural model of decision making to behavioral data to determine the parameters of temporal integration of acoustic directional information used by male grasshoppers to locate receptive females. The model revealed that males had a low threshold for initiating a turning response, yet a large integration time constant enabled them to continue to gather information when cues were weak. This contrasts with parameters estimated for females of the same species when evaluating potential mates, in which thresholds for response were much higher and behavior was strongly influenced by unattractive stimuli. Our results reveal differences in neural integration consistent with the sex-specific costs of mate search: Males often face competition and need to be fast, females often pay high error costs and need to be deliberate. Graphical abstract", "citations": [], "externalIds": {"DOI": "10.1101/2020.07.20.212431", "MAG": "3043500882", "CorpusId": 220715701}, "isOpenAccess": true, "citationCount": 0, "fieldsOfStudy": ["Biology", "Computer Science"], "openAccessPdf": {"url": "https://www.biorxiv.org/content/biorxiv/early/2021/01/20/2020.07.20.212431.full.pdf", "status": "GREEN"}, "citationStyles": {"bibtex": "@None{Clemens2020Sex-specificST,\n author = {Jan Clemens and B. Ronacher and M. Reichert},\n booktitle = {bioRxiv},\n journal = {bioRxiv},\n title = {Sex-specific speed-accuracy tradeoffs shape neural processing of acoustic signals},\n year = {2020}\n}\n"}, "referenceCount": 71, "publicationDate": "2020-07-20", "s2FieldsOfStudy": [{"source": "external", "category": "Biology"}, {"source": "external", "category": "Computer Science"}, {"source": "s2-fos-model", "category": "Psychology"}, {"source": "s2-fos-model", "category": "Biology"}], "publicationTypes": null, "publicationVenue": {"id": "027ffd21-ebb0-4af8-baf5-911124292fd0", "url": "http://biorxiv.org/", "name": "bioRxiv", "type": "journal"}, "influentialCitationCount": 0}', '2023-01-27 11:01:44.189574', '2023-01-27 11:01:44.189574', 'Sex-specific speed-accuracy tradeoffs shape neural processing of acoustic signals', 2020, false, null, null, null, null);
INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('67f557d5364ee6b358b63c6395f2c153bf9d9397', '{"url": "https://www.semanticscholar.org/paper/67f557d5364ee6b358b63c6395f2c153bf9d9397", "year": 2020, "title": "A framework for studying behavioral evolution by reconstructing ancestral repertoires", "venue": "bioRxiv", "authors": [{"name": "Dami''an G. Hern''andez", "authorId": "2053517940"}, {"name": "Catalina Rivera", "authorId": "2059158007"}, {"name": "J. Cande", "authorId": "6857245"}, {"name": "Baohua Zhou", "authorId": "2109060806"}, {"name": "D. Stern", "authorId": "6930718"}, {"name": "Gordon J. Berman", "authorId": "2492730"}], "journal": {"name": "eLife", "volume": "10"}, "paperId": "67f557d5364ee6b358b63c6395f2c153bf9d9397", "abstract": "Although extensive behavioral changes often exist between closely related animal species, our understanding of the genetic basis underlying the evolution of behavior has remained limited. Here, we propose a new framework to study behavioral evolution by computational estimation of ancestral behavioral repertoires. We measured the behaviors of individuals from six species of fruit flies using unsupervised techniques and identified suites of stereotyped movements exhibited by each species. We then fit a Generalized Linear Mixed Model to estimate the suites of behaviors exhibited by ancestral species, as well as the intra- and inter-species behavioral covariances. We found that much of intraspecific behavioral variation is explained by differences between individuals in the status of their behavioral hidden states, what might be called their “mood.” Lastly, we propose a method to identify groups of behaviors that appear to have evolved together, illustrating how sets of behaviors, rather than individual behaviors, likely evolved. Our approach provides a new framework for identifying co-evolving behaviors and may provide new opportunities to study the genetic basis of behavioral evolution.", "citations": [{"title": "NAPS: Integrating pose estimation and tag-based tracking", "paperId": "22127eb9ae26fbad551171d5138bfd8426d3fea3"}, {"title": "Emerging technologies for behavioral research in changing environments.", "paperId": "03a2b89a030daabbb46f3b1cea0b3f27d770e3e7"}, {"title": "The MABe22 Benchmarks for Representation Learning of Multi-Agent Behavior", "paperId": "9a4f08419c7e49a44396766cdddaaf60c6eca344"}, {"title": "The what, how, and why of naturalistic behavior", "paperId": "1647b7c7598c5e93a829d148b9ff4cbdf80824eb"}, {"title": "The evolutionary trajectory of drosophilid walking", "paperId": "14cd60dbbf086a01d45829685437f1a46fbe393b"}, {"title": "Measuring the repertoire of age-related behavioral changes in Drosophila melanogaster", "paperId": "2e6c4c744beb76c9c0fdb6eaca47c250f6141fc3"}, {"title": "The promises and challenges of archiving insect behavior and natural history in a changing world.", "paperId": "72eaf8473e9f8ef1fefc62378432a94dd8ae3c69"}, {"title": "The Multi-Agent Behavior Dataset: Mouse Dyadic Social Interactions", "paperId": "377ef42249fc02b9dfd4ae8726f2b8455a7f7654"}, {"title": "Expanding evolutionary neuroscience: insights from comparing variation in behavior", "paperId": "799493669e4e9eab6593963f2f64faf781d681d7"}, {"title": "Variation and Variability in Drosophila Grooming Behavior", "paperId": "fde99f6a2455213439a95ba5b526f2c953e1c8d0"}, {"title": "The structure of behavioral variation within a genotype", "paperId": "b657efae94cde177865dbfc7e4991ed003b1efed"}], "externalIds": {"DOI": "10.1101/2020.07.17.209361", "MAG": "3043582615", "ArXiv": "2007.09689", "PubMed": "34473052", "CorpusId": 220647497, "PubMedCentral": "8445618"}, "isOpenAccess": false, "citationCount": 11, "fieldsOfStudy": ["Medicine", "Biology", "Physics", "Psychology"], "openAccessPdf": null, "citationStyles": {"bibtex": "@[''JournalArticle'']{Hern''andez2020AFF,\n author = {Dami''an G. Hern''an