INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('f18ac21f76fa6b5b89fb45900bc7386ad7e8ceaa', '{"url": "https://www.semanticscholar.org/paper/f18ac21f76fa6b5b89fb45900bc7386ad7e8ceaa", "year": 2020, "title": "Neuronal circuitry underlying female aggression in Drosophila", "venue": "bioRxiv", "authors": [{"name": "Catherine E Schretter", "authorId": "6472682"}, {"name": "Y. Aso", "authorId": "48343951"}, {"name": "M. Dreher", "authorId": "1572706864"}, {"name": "A. Robie", "authorId": "2557555"}, {"name": "Michael-John Dolan", "authorId": "47789037"}, {"name": "Nan Chen", "authorId": "1392269194"}, {"name": "Masayoshi Ito", "authorId": "1600598494"}, {"name": "Tansy Yang", "authorId": "1508245800"}, {"name": "Ruchi Parekh", "authorId": "3298464"}, {"name": "G. Rubin", "authorId": "34660241"}], "journal": {"name": "bioRxiv"}, "paperId": "f18ac21f76fa6b5b89fb45900bc7386ad7e8ceaa", "abstract": "Aggressive social interactions are used to compete for limited resources and are regulated by complex sensory cues and the organism’s internal state. While both sexes exhibit aggression, its neuronal underpinnings are understudied in females. Here, we describe a set of connected neurons in the adult Drosophila melanogaster central brain that drive female aggression. We identified a population of sexually dimorphic aIPg neurons whose optogenetic activation increased, and genetic inactivation reduced, female aggression. Analysis of GAL4 lines identified in an unbiased screen for increased female chasing behavior revealed the involvement of another sexually dimorphic neuron, pC1d, and implicated pC1d and aIPg neurons as core nodes regulating female aggression. pC1d activation increased female aggression and electron microscopy (EM) connectomic analysis demonstrated that aIPg neurons and pC1d have strong reciprocal connections. Our work reveals important regulatory components of the neuronal circuitry that underlies female aggressive social interactions and provides tools for their manipulation.", "citations": [{"title": "Neural network organization for courtship-song feature detection in Drosophila", "paperId": "eaa1ad1f740fee4a03079af7764f8807bff9b6bf"}], "externalIds": {"DOI": "10.1101/2020.05.27.118810", "MAG": "3028842131", "CorpusId": 218981609}, "isOpenAccess": false, "citationCount": 1, "fieldsOfStudy": ["Biology"], "openAccessPdf": null, "citationStyles": {"bibtex": "@None{Schretter2020NeuronalCU,\n author = {Catherine E Schretter and Y. Aso and M. Dreher and A. Robie and Michael-John Dolan and Nan Chen and Masayoshi Ito and Tansy Yang and Ruchi Parekh and G. Rubin},\n booktitle = {bioRxiv},\n journal = {bioRxiv},\n title = {Neuronal circuitry underlying female aggression in Drosophila},\n year = {2020}\n}\n"}, "referenceCount": 93, "publicationDate": "2020-05-27", "s2FieldsOfStudy": [{"source": "external", "category": "Biology"}, {"source": "s2-fos-model", "category": "Biology"}, {"source": "s2-fos-model", "category": "Psychology"}], "publicationTypes": null, "publicationVenue": {"id": "027ffd21-ebb0-4af8-baf5-911124292fd0", "url": "http://biorxiv.org/", "name": "bioRxiv", "type": "journal"}, "influentialCitationCount": 0}', '2023-01-27 11:01:51.551392', '2023-01-27 11:01:51.551392', 'Neuronal circuitry underlying female aggression in Drosophila', 2020, false, null, null, null, null);
INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('3a4e5340f8ad346b518713851dffac0af9716d96', '{"url": "https://www.semanticscholar.org/paper/3a4e5340f8ad346b518713851dffac0af9716d96", "year": 2020, "title": "Sex-determining genes distinctly regulate courtship capability and target preference via sexually dimorphic neurons", "venue": "eLife", "authors": [{"name": "Kenichi Ishii", "authorId": "1646975864"}, {"name": "Margot Wohl", "authorId": "1646968474"}, {"name": "Andre DeSouza", "authorId": "1647178410"}, {"name": "Kenta Asahina", "authorId": "49053313"}], "journal": {"name": "eLife", "volume": "9"}, "paperId": "3a4e5340f8ad346b518713851dffac0af9716d96", "abstract": "For successful mating, a male animal must execute effective courtship behaviors toward a receptive target sex, which is female. Whether the courtship execution capability and upregulation of courtship toward females are specified through separable sex-determining genetic pathways remains uncharacterized. Here, we found that one of the two Drosophila sex-determining genes, doublesex (dsx), specifies a male-specific neuronal component that serves as an execution mechanism for courtship behavior, whereas fruitless (fru) is required for enhancement of courtship behavior toward females. The dsx-dependent courtship execution mechanism includes a specific subclass within a neuronal cluster that co-express dsx and fru. This cluster contains at least another subclass that is specified cooperatively by both dsx and fru. Although these neuronal populations can also promote aggressive behavior toward male flies, this capacity requires fru-dependent mechanisms. Our results uncover how sex-determining genes specify execution capability and female-specific enhancement of courtship behavior through separable yet cooperative neurogenetic mechanisms.", "citations": [{"title": "Mating pair drives aggressive behavior in female Drosophila", "paperId": "bc9653a608d264b00031e37feb937a31d3fe57a3"}, {"title": "DMRT Transcription Factors in the Control of Nervous System Sexual Differentiation", "paperId": "1e7276afd86b91a6eaf7b3927b0444131fe234ed"}, {"title": "Neural Control of Action Selection Among Innate Behaviors", "paperId": "219077e2b7c483024ac9c87a90cd7f0b6ffdbb1e"}, {"title": "Single-cell transcriptome profiles of Drosophila fruitless-expressing neurons from both sexes", "paperId": "09b05ef53d1d6051ac3a6457f6692c5ed18a7531"}, {"title": "Mutually exclusive expression of sex-specific and non-sex-specific fruitless gene products in the Drosophila central nervous system.", "paperId": "078b56eab0aeb000cb85e1909e4fd30372a4e251"}, {"title": "Drosophila tachykininergic neurons modulate the activity of two groups of receptor-expressing neurons to regulate aggressive tone", "paperId": "802ab04966139c5c95aa04131809e4aa65187239"}, {"title": "Environmental and Molecular Modulation of Motor Individuality in Larval Zebrafish", "paperId": "5a5ba56783fc8ee295f26b42bfbef8cf58826c4d"}, {"title": "A circuit logic for sexually shared and dimorphic aggressive behaviors in Drosophila\n", "paperId": "aff008c607c8106ebbf548e2f55cafb1022b5b0f"}, {"title": "Sound production in Drosophila melanogaster: Behaviour and neurobiology", "paperId": "1ff4ab59f46afb6ad6c79f9c25852e5da09a1125"}, {"title": "Quantifying influence of human choice on the automated detection of Drosophila behavior by a supervised machine learning algorithm", "paperId": "4258b2d1be8a8175476017324e4a64e083c3090d"}, {"title": "Contact-Chemosensory Evolution Underlying Reproductive Isolation in Drosophila Species", "paperId": "93e900db532c2e09e762eb3e5f785890fe686a87"}, {"title": "Cell types and neuronal circuitry underlying female aggression in Drosophila", "paperId": "df1696050c10fffb86668e9443d39cf72fbddf88"}, {"title": "Neuroethology of acoustic communication in field crickets - from signal generation to song recognition in an insect 