INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('4b9163d386c4e11382fce8059389b850fd7cf81b', '{"url": "https://www.semanticscholar.org/paper/4b9163d386c4e11382fce8059389b850fd7cf81b", "year": 2021, "title": "Ir76b is a Co-receptor for Amine Responses in Drosophila Olfactory Neurons", "venue": "Frontiers in Cellular Neuroscience", "authors": [{"name": "Alina Vulpe", "authorId": "39544938"}, {"name": "K. Menuz", "authorId": "48364139"}], "journal": {"name": "Frontiers in Cellular Neuroscience", "volume": "15"}, "paperId": "4b9163d386c4e11382fce8059389b850fd7cf81b", "abstract": "Two large families of olfactory receptors, the Odorant Receptors (ORs) and Ionotropic Receptors (IRs), mediate responses to most odors in the insect olfactory system. Individual odorant binding “tuning” OrX receptors are expressed by olfactory neurons in basiconic and trichoid sensilla and require the co-receptor Orco. The situation for IRs is more complex. Different tuning IrX receptors are expressed by olfactory neurons in coeloconic sensilla and rely on either the Ir25a or Ir8a co-receptors; some evidence suggests that Ir76b may also act as a co-receptor, but its function has not been systematically examined. Surprisingly, recent data indicate that nearly all coeloconic olfactory neurons co-express Ir25a, Ir8a, and Ir76b. Here, we demonstrate that Ir76b and Ir25a function together in all amine-sensing olfactory receptor neurons. In most neurons, loss of either co-receptor abolishes amine responses. In contrast, amine responses persist in the absence of Ir76b or Ir25a in ac1 sensilla but are lost in a double mutant. We show that responses mediated by acid-sensing neurons do not require Ir76b, despite their expression of this co-receptor. Our study also demonstrates that one population of coeloconic olfactory neurons exhibits Ir76b/Ir25a-dependent and Orco-dependent responses to distinct odorants. Together, our data establish the role of Ir76b as a bona fide co-receptor, which acts in partnership with Ir25a. Given that these co-receptors are among the most highly conserved olfactory receptors and are often co-expressed in chemosensory neurons, our data suggest Ir76b and Ir25a also work in tandem in other insects.", "citations": [{"title": "Drosophila olfaction: past, present and future", "paperId": "6c32d89c783891ba23860a8999e8150812c99b73"}, {"title": "Cool and warm ionotropic receptors control multiple thermotaxes in Drosophila larvae", "paperId": "a82285ce95b2b67f0d2f98572338cef4aa82b579"}, {"title": "Pheromone sensing in Drosophila requires support cell-expressed Osiris 8", "paperId": "5dbac61d9d1bd33ffce553e3b89587315322d662"}, {"title": "Chemosensory Ionotropic Receptors in Human Host-Seeking Mosquitoes.", "paperId": "61d5d5ed9b2d8aee91d070d2a2b14892c0c49580"}, {"title": "Sex- and tissue-specific expression of chemosensory receptor genes in a hawkmoth", "paperId": "5effffe4d70267fa07263881da0048c4a1827e0c"}, {"title": "A spatial map of antennal-expressed olfactory ionotropic receptors in the malaria mosquito", "paperId": "faadf022e1ff104a3eaf924c711372a2ed5591d1"}, {"title": "Chemoreceptor co-expression in Drosophila melanogaster olfactory neurons", "paperId": "8c5346435df56e3f632a010e752980b05cd81cad"}, {"title": "Comparative transcriptomics of the Drosophila olfactory subsystems identifies a support cell-expressed Osiris protein required for pheromone sensing", "paperId": "082fc3fe0d869033cdb63fbb78e886fe51873fe2"}, {"title": "Drosophila melanogaster Chemosensory Pathways as Potential Targets to Curb the Insect Menace", "paperId": "8e072813c48cc0b74e57d81136b4312344151069"}, {"title": "Discrete roles of Ir76b ionotropic coreceptor impact olfaction, blood feeding, and mating in the malaria vector mosquito Anopheles coluzzii", "paperId": "9a8422b412e0eff9e48fb025f1c10b59a338757a"}], "externalIds": {"DOI": "10.3389/fncel.2021.759238", "CorpusId": 244134902, "PubMedCentral": "8635857"}, "isOpenAccess": true, "citationCount": 10, "fieldsOfStudy": null, "openAccessPdf": {"url": "https://www.frontiersin.org/articles/10.3389/fncel.2021.759238/pdf", "status": "GOLD"}, "citationStyles": {"bibtex": "@None{Vulpe2021Ir76bIA,\n author = {Alina Vulpe and K. Menuz},\n booktitle = {Frontiers in Cellular Neuroscience},\n journal = {Frontiers in Cellular Neuroscience},\n title = {Ir76b is a Co-receptor for Amine Responses in Drosophila Olfactory Neurons},\n volume = {15},\n year = {2021}\n}\n"}, "referenceCount": 37, "publicationDate": "2021-11-17", "s2FieldsOfStudy": [{"source": "s2-fos-model", "category": "Biology"}], "publicationTypes": null, "publicationVenue": {"id": "446b5506-d77e-4dee-9f31-2f35741ee52b", "url": "http://www.frontiersin.org/cellular_neuroscience", "issn": "1662-5102", "name": "Frontiers in Cellular Neuroscience", "type": "journal", "alternate_urls": ["http://www.frontiersin.org/cellularneuroscience/", "https://www.frontiersin.org/journals/cellular-neuroscience"], "alternate_names": ["Front Cell Neurosci"]}, "influentialCitationCount": 3}', '2023-01-27 11:02:20.630867', '2023-01-27 11:02:20.630867', 'Ir76b is a Co-receptor for Amine Responses in Drosophila Olfactory Neurons', 2021, false, null, null, null, null);
INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('4fc6d2ca1c6cb34f37021b3b69ccc7652e723776', '{"url": "https://www.semanticscholar.org/paper/4fc6d2ca1c6cb34f37021b3b69ccc7652e723776", "year": 2021, "title": "Olfactory Receptor Gene Regulation in Insects: Multiple Mechanisms for Singular Expression", "venue": "Frontiers in Neuroscience", "authors": [{"name": "Kaan Mika", "authorId": "1816714828"}, {"name": "R. Benton", "authorId": "144583541"}], "journal": {"name": "Frontiers in Neuroscience", "volume": "15"}, "paperId": "4fc6d2ca1c6cb34f37021b3b69ccc7652e723776", "abstract": "The singular expression of insect olfactory receptors in specific populations of olfactory sensory neurons is fundamental to the encoding of odors in patterns of neuronal activity in the brain. How a receptor gene is selected, from among a large repertoire in the genome, to be expressed in a particular neuron is an outstanding question. Focusing on Drosophila melanogaster, where most investigations have been performed, but incorporating recent insights from other insect species, we review the multilevel regulatory mechanisms of olfactory receptor expression. We discuss how cis-regulatory elements, trans-acting factors, chromatin modifications, and feedback pathways collaborate to activate and maintain expression of the chosen receptor (and to suppress others), highlighting similarities and differences with the mechanisms underlying singular receptor expression in mammals. We also consider the plasticity of receptor regulation in response to environmental cues and internal state during the lifetime of an individual, as well as the evolution of novel expression patterns over longer timescales. Finally, we describe the mechanisms and potential significance of examples of receptor co-expression.", "citations": [{"title": "Drosophila olfaction: past, present and future", "paperId": "6c32d89c783891ba23860a8999e8150812c99b73"}, {"title": "Copy number changes in co-expressed odorant receptor genes enable selection for sensory differences in drosophilid species.", "paperId": "64af902299c277e97e3bc557b1a08ad639ab3682"}, {"title": "A spatial map of antennal-expressed olfactory ionotropic receptors in the malaria mosquito", "paperId": "faadf022e1ff104a3eaf924c711372a2ed5591d1"}], "externalIds": {"DOI": "10.3389/fnins.2021.738088", "PubMed": "34602974", "CorpusId": 237521950, "PubMedCentral": "8481607"}, "isOpenAccess": true, "citationCount": 3, "fieldsOfStudy": ["Medicine"], "openAccessPdf": {"url": "https://www.frontiersin.org/articles/10.3389/fnins.2021.738088/pdf", "status": "GOLD"}, "citationStyles": {"bibtex": "@[''Review'', ''JournalArticle'']{Mika2021OlfactoryRG,\n author = {Kaan Mika and R. Benton},\n booktitle = {Frontiers in Neuroscience},\n journal = {Frontiers in Neuroscience},\n title = {Olfactory Receptor Gene Regulation in Insects: Multiple Mechanisms for Singular Expression},\n volume = {15},\n year = {2021}\n}\n"}, "referenceCount": 126, "publicationDate": "2021-09-16", "s2FieldsOfStudy": [{"source": "external", "category": "Medicine"}, {"source": "s2-fos-model", "category": "Biology"}], "publicationTypes": ["Review", "JournalArticle"], "publicationVenue": {"id": "2ca4279c-8ed7-4280-8022-09e577923a09", "url": "https://www.frontiersin.org/journals/neuroscience", "issn": "1662-453X", "name": "Frontiers in Neuroscience", "type": "journal", "alternate_urls": ["http://journal.frontiersin.org/journal/neuroscience", "http://www.frontiersin.org/neuroscience/"], "alternate_names": ["Front Neurosci"]}, "influentialCitationCount": 0}', '2023-01-27 11:02:24.293710', '2023-01-27 11:02:24.293710', 'Olfactory Receptor Gene Regulation in Insects: Multiple Mechanisms for Singular Expression', 2021, false, null, null, null, null);
INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('9a8422b412e0eff9e48fb025f1c10b59a338757a', '{"url": "https://www.semanticscholar.org/paper/9a8422b412e0eff9e48fb025f1c10b59a338757a", "year": 2021, "title": "Discrete roles of Ir76b ionotropic coreceptor impact olfaction, blood feeding, and mating in the malaria vector mosquito Anopheles coluzzii", "venue": "bioRxiv", "authors": [{"name": "Zi Ye", "authorId": "1503632227"}, {"name": "Feng Liu", "authorId": "2152943391"}, {"name": "Huahua Sun", "authorId": "2083429"}, {"name": "Adam Baker", "authorId": "2070045239"}, {"name": "L. Zwiebel", "authorId": "4120896"}], "journal": {"name": "Proceedings of the National Academy of Sciences of the United States of America", "volume": "119"}, "paperId": "9a8422b412e0eff9e48fb025f1c10b59a338757a", "abstract": "Anopheline mosquitoes rely on their highly sensitive chemosensory apparatus to detect diverse chemical stimuli that drive the host-seeking and blood-feeding behaviors required to vector pathogens for malaria and other diseases. This process incorporates a variety of chemosensory receptors and transduction pathways. We have used advanced in vivo gene-editing and -labelling approaches to localize and functionally characterize the ionotropic co-receptor AcIr76b in the malaria mosquito Anopheles coluzzii, where it impacts both olfactory and gustatory systems. AcIr76b has a broad expression pattern in female adult antennal grooved pegs, T1 and T2 sensilla on the labellum, stylets, and tarsi, as well as the larval sensory peg. AcIr76b is co-localized with the Orco odorant receptor (OR) co-receptor in a subset of cells across the female antennae and labella. In contrast to Orco and Ir8a, chemosensory co-receptors that appear essential for the activity of their respective sets of chemosensory neurons in mosquitoes, AcIr76b-/- mutants maintain wild-type peripheral responses to volatile amines on the adult palps, labellum, and the larval sensory cone. Interestingly, AcIr76b-/- mutants display significantly increased responses to amines in antennal grooved peg sensilla while coeloconic sensilla reveal significant deficits in responses to several acids and amines. Behaviorally, AcIr76b mutants manifest significantly female-specific insemination deficits and, although AcIr76b-/- mutant females are able to locate, alight, and probe artificial blood hosts, they are incapable of blood feeding successfully. Taken together, our study reveals a multi-dimensional functionality of Ir76b in Anopheline olfactory and gustatory pathways that directly impacts the vectorial capacity of these mosquitoes. Summary Chemosensory receptors play crucial roles across mosquito lifecycles where they often form functional complexes that require cognate co-receptors. To better understand mosquito chemosensory pathways in the malaria vector mosquito An. coluzzii we have utilized advanced gene editing approaches to localize and functionally characterize the ionotropic receptor co-receptor AcIr76b. Expression of AcIr76b was observed in antennal grooved pegs and other accessory olfactory appendages. Mutagenesis of AcIr76b uncovers both reduced and elevated neuronal responses to amines, which suggests a role in response modulation. In addition to olfactory phenotypes, AcIr76b mutants display significantly impaired mating and blood feeding capabilities. Our data reveals discrete roles of AcIr76b across olfactory and gustatory pathways and shed lights on the potential molecular target for vector control strategies.", "citations": [{"title": "Chemosensory detection of aversive concentrations of ammonia and basic volatile amines in insects", "paperId": "4884e21046322b424f4b01f40289e1a10a6cf900"}, {"title": "Neurogenetic identification of mosquito sensory neurons", "paperId": "5498a60ca06df112f06f2c99e0c173998c4ebc93"}, {"title": "Chemosensory Ionotropic Receptors in Human Host-Seeking Mosquitoes.", "paperId": "61d5d5ed9b2d8aee91d070d2a2b14892c0c49580"}, {"title": "A spatial map of antennal-expressed olfactory ionotropic receptors in the malaria mosquito", "paperId": "faadf022e1ff104a3eaf924c711372a2ed5591d1"}, {"title": "Chemoreceptor co-expression in Drosophila melanogaster olfactory neurons", "paperId": "8c5346435df56e3f632a010e752980b05cd81cad"}, {"title": "Differential mosquito attraction to humans is associated with skin-derived carboxylic acid levels", "paperId": "013253906a4fcb18e615fd51e151107b94aa12be"}], "externalIds": {"DOI": "10.1073/pnas.2112385119", "PubMed": "35648836", "CorpusId": 235770765, "PubMedCentral": "9191353"}, "isOpenAccess": false, "citationCount": 6, "fieldsOfStudy": ["Biology", "Medicine"], "openAccessPdf": null, "citationStyles": {"bibtex": "@[''JournalArticle'']{Ye2021DiscreteRO,\n author = {Zi Ye and Feng Liu and Huahua Sun and Adam Baker and L. Zwiebel},\n booktitle = {bioRxiv},\n journal = {Proceedings of the National Academy of Sciences of the United States of America},\n title = {Discrete roles of Ir76b ionotropic coreceptor impact olfaction, blood feeding, and mating in the malaria vector mosquito Anopheles coluzzii},\n volume = {119},\n year = {2021}\n}\n"}, "referenceCount": 83, "publicationDate": "2021-07-06", "s2FieldsOfStudy": [{"source": "external", "category": "Biology"}, {"source": "external", "category": "Medicine"}, {"source": "s2-fos-model", "category": "Biology"}], "publicationTypes": ["JournalArticle"], "publicationVenue": {"id": "027ffd21-ebb0-4af8-baf5-911124292fd0", "url": "http://biorxiv.org/", "name": "bioRxiv", "type": "journal"}, "influentialCitationCount": 1}', '2023-01-27 11:02:27.919894', '2023-01-27 11:02:27.919894', 'Discrete roles of Ir76b ionotropic coreceptor impact olfaction, blood feeding, and mating in the malaria vector mosquito Anopheles coluzzii', 2021, false, null, null, null, null);
INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('8843a62b33bc839e0c0d67dd544b0f8d1387575d', '{"url": "https://www.semanticscholar.org/paper/8843a62b33bc839e0c0d67dd544b0f8d1387575d", "year": 2021, "title": "Functional characterization of odor responses and gene expression changes in olfactory co-receptor mutants in Drosophila", "venue": "bioRxiv", "authors": [{"name": "Alina Vulpe", "authorId": "39544938"}, {"name": "Pratyajit Mohapatra", "authorId": "51887861"}, {"name": "K. Menuz", "authorId": "48364139"}], "journal": {"name": "bioRxiv"}, "paperId": "8843a62b33bc839e0c0d67dd544b0f8d1387575d", "abstract": "Two large families of olfactory receptors, the Odorant Receptors (ORs) and the Ionotropic Receptors (IRs), mediate responses to most odors in the insect olfactory system. Individual odor binding “tuning” OR receptors are expressed by olfactory neurons in basiconic and trichoid sensilla and require the co-receptor Orco to function. The situation for IRs is more complex. Different tuning IR receptors are expressed by olfactory neurons in coeloconic sensilla and rely on either the Ir25a or Ir8a co-receptors; some evidence suggests that Ir76b may also act as a coreceptor, but its function has not been systematically examined. This is particularly important as recent data indicate that nearly all coeloconic olfactory neurons co-express Ir25a, Ir8a, and Ir76b. Here, we report the effects of Drosophila olfactory co-receptor mutants on odor detection by coeloconic olfactory neurons and determine their broader impact on gene expression through RNASeq analysis. We demonstrate that Ir76b and Ir25a function together in all amine-sensing olfactory receptor neurons. In most neurons, loss of either co-receptor abolishes amine responses, whereas in ac1 sensilla, amine responses persist in the absence of Ir76b or Ir25a, but are lost in a double-mutant. Such responses do not require Ir8a. Conversely, acid-sensing ORNs require Ir8a, but not Ir76b or Ir25a. Using antennal transcriptional profiling, we find that the expression of acid-sensing IR receptors is significantly reduced in Ir8a mutants, but is unaffected by the loss of Ir25a or Ir76b. Similarly, select OR tuning receptors are also downregulated in Orco2 mutants. In contrast, expression of amine-sensing IR receptors is mostly unchanged in Ir25a and Ir76b mutants. Together, our data reveal new aspects of co-receptor function in the olfactory system. Summary Insect vectors of human disease rely on their sense of smell to locate humans for blood meals. A critical first step in olfaction is the odorant-induced activation of receptors on olfactory neurons. There are two major olfactory receptor families in insects, with each species having dozens of different odorant-binding “tuning” receptors. The receptor complexes also contain non-tuning co-receptors, which are highly conserved across insect species and are required for function. Here we characterize co-receptor mutants with electrophysiological recordings and transcriptome analysis in Drosophila. Our findings resolve the differential co-receptor dependence of olfactory neuron responses to volatile amines and acids. We also report changes in antennal gene expression that result from the absence of these co-receptors. Most notably, the absence of some co-receptors leads to a selective loss of transcript expression for the tuning olfactory receptors whose function depends on the missing co-receptors. Together our data provide new insight into the roles of co-receptors in insect olfaction.", "citations": [{"title": "Olfactory Receptor Gene Regulation in Insects: Multiple Mechanisms for Singular Expression", "paperId": "4fc6d2ca1c6cb34f37021b3b69ccc7652e723776"}, {"title": "Discrete roles of Ir76b ionotropic coreceptor impact olfaction, blood feeding, and mating in the malaria vector mosquito Anopheles coluzzii", "paperId": "9a8422b412e0eff9e48fb025f1c10b59a338757a"}, {"title": "Chemoreceptor Co-Expression in Drosophila Olfactory Neurons", "paperId": "8f88ec8107f2739fa925689cf1b66734adf71d44"}, {"title": "Widespread Polymodal Chemosensory Receptor Expression in Drosophila Olfactory Neurons", "paperId": "4e2cf6eaeac6f71e29576c2585be7d0118402c2a"}], "externalIds": {"DOI": "10.1101/2021.06.18.449017", "CorpusId": 235650000}, "isOpenAccess": true, "citationCount": 4, "fieldsOfStudy": ["Biology"], "openAccessPdf": {"url": "https://www.biorxiv.org/content/biorxiv/early/2021/06/18/2021.06.18.449017.full.pdf", "status": "GREEN"}, "citationStyles": {"bibtex": "@None{Vulpe2021FunctionalCO,\n author = {Alina Vulpe and Pratyajit Mohapatra and K. Menuz},\n booktitle = {bioRxiv},\n journal = {bioRxiv},\n title = {Functional characterization of odor responses and gene expression changes in olfactory co-receptor mutants in Drosophila},\n year = {2021}\n}\n"}, "referenceCount": 72, "publicationDate": "2021-06-18", "s2FieldsOfStudy": [{"source": "external", "category": "Biology"}, {"source": "s2-fos-model", "category": "Biology"}], "publicationTypes": null, "publicationVenue": {"id": "027ffd21-ebb0-4af8-baf5-911124292fd0", "url": "http://biorxiv.org/", "name": "bioRxiv", "type": "journal"}, "influentialCitationCount": 0}', '2023-01-27 11:02:31.700259', '2023-01-27 11:02:31.700259', 'Functional characterization of odor responses and gene expression changes in olfactory co-receptor mutants in Drosophila', 2021, false, null, null, null, null);
INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('ff72e1a5fbb1beb28839dfc9cbbe6be96960a8da', '{"url": "https://www.semanticscholar.org/paper/ff72e1a5fbb1beb28839dfc9cbbe6be96960a8da", "year": 2021, "title": "Rapid degeneration of Drosophila olfactory neurons in Orco mutant maxillary palps", "venue": "microPublication Biology", "authors": [{"name": "Darya Task", "authorId": "7686107"}, {"name": "C. Potter", "authorId": "40574640"}], "journal": {"name": "microPublication Biology", "volume": "2021"}, "paperId": "ff72e1a5fbb1beb28839dfc9cbbe6be96960a8da", "abstract": "Drosophila melanogaster vinegar flies have two olfactory organs: the antenna and maxillary palp. Olfactory neurons in these tissues respond to odorants via odorant receptors. Insect odorant receptors are heterotetramers of two proteins: an odorant binding OrX subunit and an Odorant Receptor Co-Receptor (Orco). Mutation of Orco disrupts odorant receptor formation, and abolishes olfactory responses. Some antennal olfactory neurons in Orco mutants have been previously shown to degenerate. Here, we examine if maxillary palp olfactory neurons also degenerate in Orco mutants. We find degeneration occurs both more broadly and more rapidly in Orco mutant maxillary palp olfactory neurons than reported for antennae, with ~60% of all mutant olfactory neurons absent in maxillary palps by 7 days post eclosion. Interestingly, the subset of Orco mutant olfactory neurons that express the Or42a receptor appear resistant to degeneration. These results suggest the maxillary palp might be a suitable model for examining the molecular mechanisms underlying neurodegeneration in sensory neurons.", "citations": [{"title": "Molecular characterization and expression variation of the odorant receptor co-receptor in the Formosan subterranean termite", "paperId": "9db2d15cbee2ba8e853742bb0f480ee7ba5f4f27"}, {"title": "Chemoreceptor co-expression in Drosophila melanogaster olfactory neurons", "paperId": "8c5346435df56e3f632a010e752980b05cd81cad"}, {"title": "Mushroom body input connections form independently of sensory activity in Drosophila melanogaster\n", "paperId": "1cd72757ee075e80c3cab9b0fbe2db732616b474"}, {"title": "Functional characterization of odor responses and gene expression changes in olfactory co-receptor mutants in Drosophila", "paperId": "8843a62b33bc839e0c0d67dd544b0f8d1387575d"}], "externalIds": {"DOI": "10.17912/micropub.biology.000398", "PubMed": "34007957", "CorpusId": 234766928, "PubMedCentral": "8122221"}, "isOpenAccess": false, "citationCount": 4, "fieldsOfStudy": ["Medicine"], "openAccessPdf": null, "citationStyles": {"bibtex": "@[''JournalArticle'']{Task2021RapidDO,\n author = {Darya Task and C. Potter},\n booktitle = {microPublication Biology},\n journal = {microPublication Biology},\n title = {Rapid degeneration of Drosophila olfactory neurons in Orco mutant maxillary palps},\n volume = {2021},\n year = {2021}\n}\n"}, "referenceCount": 23, "publicationDate": "2021-05-14", "s2FieldsOfStudy": [{"source": "external", "category": "Medicine"}, {"source": "s2-fos-model", "category": "Biology"}], "publicationTypes": ["JournalArticle"], "publicationVenue": {"id": "0596372c-6600-4283-873e-c3c1976d87c9", "url": "https://www.micropublication.org/", "issn": "2578-9430", "name": "microPublication Biology", "alternate_names": ["micropublication Biology"]}, "influentialCitationCount": 0}', '2023-01-27 11:02:35.320715', '2023-01-27 11:02:35.320715', 'Rapid degeneration of Drosophila olfactory neurons in Orco mutant maxillary palps', 2021, false, null, null, null, null);
INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('b5139d65d9626aff90257b95eacbea9d9d6ccfc3', '{"url": "https://www.semanticscholar.org/paper/b5139d65d9626aff90257b95eacbea9d9d6ccfc3", "year": 2021, "title": "Systematic morphological and morphometric analysis of identified olfactory receptor neurons in Drosophila melanogaster", "venue": "bioRxiv", "authors": [{"name": "Cesar Nava Gonzales", "authorId": "2067365547"}, {"name": "Quintyn McKaughan", "authorId": "2086979832"}, {"name": "E. Bushong", "authorId": "3695190"}, {"name": "Kalyani Cauwenberghs", "authorId": "2086979838"}, {"name": "Renny Ng", "authorId": "37258121"}, {"name": "Matthew Madany", "authorId": "51246732"}, {"name": "Mark Ellisman", "authorId": "1704492"}, {"name": "Chih-Ying Su", "authorId": "46712902"}], "journal": {"name": "eLife", "volume": "10"}, "paperId": "b5139d65d9626aff90257b95eacbea9d9d6ccfc3", "abstract": "The biophysical properties of sensory neurons are influenced by their morphometric and morphological features, whose precise measurements require high-quality volume electron microscopy (EM). However, systematic surveys of these nanoscale characteristics for identified neurons are scarce. Here, we characterize the morphology of Drosophila olfactory receptor neurons (ORNs) across the majority of genetically identified sensory hairs. By analyzing serial block-face electron microscopy (SBEM) images of cryofixed antennal tissues, we compile an extensive morphometric dataset based on 122 reconstructed 3D models of 33 identified ORN types. In addition, we observe multiple novel features—including extracellular vacuoles within sensillum lumen, intricate dendritic branching, mitochondria enrichment in select ORNs, novel sensillum types, and empty sensilla containing no neurons—which raise new questions pertinent to cell biology and sensory neurobiology. Our systematic survey is critical for future investigations into how the size and shape of sensory neurons influence their responses, sensitivity and circuit function.", "citations": [{"title": "Neuron-glia interaction at the receptor level affects olfactory perception in adult Drosophila", "paperId": "5bd1e88efdff1d96939c870eba2239606ffee59a"}, {"title": "Drosophila olfaction: past, present and future", "paperId": "6c32d89c783891ba23860a8999e8150812c99b73"}, {"title": "Pheromone sensing in Drosophila requires support cell-expressed Osiris 8", "paperId": "5dbac61d9d1bd33ffce553e3b89587315322d662"}, {"title": "Genetically encoded sensors for analysing neurotransmission among synaptically-connected neurons", "paperId": "9d792e9eb485d1b7b90be90bcd50c2a3af71067e"}, {"title": "Functional olfactory evolution in Drosophila suzukii and the subgenus Sophophora", "paperId": "a2da15b9ab90983cdf3e653fbbb590afa6b1091d"}, {"title": "Comparative transcriptomics of the Drosophila olfactory subsystems identifies a support cell-expressed Osiris protein required for pheromone sensing", "paperId": "082fc3fe0d869033cdb63fbb78e886fe51873fe2"}, {"title": "Homeostasis of Mitochondrial Ca2+ Stores Is Critical for Signal Amplification in Drosophila melanogaster Olfactory Sensory Neurons", "paperId": "4273267c7c09e73b3a1fc4936de3d308670e3467"}, {"title": "Drosophila melanogaster Chemosensory Pathways as Potential Targets to Curb the Insect Menace", "paperId": "8e072813c48cc0b74e57d81136b4312344151069"}, {"title": "Valence opponency in peripheral olfactory processing", "paperId": "eced298bed3d1d3658e057aed676bf49b033d109"}, {"title": "Rehydration of freeze substituted brain tissue for pre-embedding immunoelectron microscopy", "paperId": "f33df4efd77530a35832c8aa6638732016694450"}, {"title": "Response plasticity of Drosophila olfactory sensory neurons", "paperId": "39d492fd1159944d69cfc8b1d3095fc4921b76cd"}, {"title": "Functional Interaction Between Drosophila Olfactory Sensory Neurons and Their Support Cells", "paperId": "4c7c19a