INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('bef3c94fef30f7b89830989652c071a85bb75aa9', '{"url": "https://www.semanticscholar.org/paper/bef3c94fef30f7b89830989652c071a85bb75aa9", "year": 2021, "title": "Genetic basis of chemical communication in eusocial insects", "venue": "Genes & Development", "authors": [{"name": "Hua Yan", "authorId": "2047162017"}, {"name": "J. Liebig", "authorId": "40133448"}], "journal": {"name": "Genes & Development", "pages": "470 - 482", "volume": "35"}, "paperId": "bef3c94fef30f7b89830989652c071a85bb75aa9", "abstract": "In this review, Yan et al. highlight the genes and pathways that regulate queen pheromone-mediated social communication, discuss the evolutionary changes in genetic systems, and outline prospects of functional studies in sociobiology.", "citations": [{"title": "Microstructures at the distal tip of ant chemosensory sensilla", "paperId": "069710222afde33e4e9dc31ab2e6b153e7015f87"}, {"title": "Insulin signaling in the long-lived reproductive caste of ants", "paperId": "74492eb9a47fbfe85ce8a01e532e354bf0e10968"}, {"title": "Insulin signaling and extended longevity in ants", "paperId": "f93ee8e4ef41887c187954ec7fc2037f26bea64f"}, {"title": "Microstructures of Ant Chemosensory Sensilla Support a Dual Function in Detecting Both Volatile and Contact-Mediated Cues", "paperId": "d14baa67dfb882057842fd540c715e5c1a64959c"}, {"title": "Foraging Payoffs Change With Group Size in Kin and Non-kin Groups of an Argyrodinae Kleptoparasitic Spider, Argyrodes miniaceus", "paperId": "0f080410d20c143a2817a43cf337681cec0b3ef6"}, {"title": "Differential gene expression underlying the biosynthesis of Dufour’s gland signals in Bombus impatiens", "paperId": "ebba5534883df56e93cc13226c264f6c3e3def9f"}, {"title": "Anthropic action affects the cuticular chemical profile of social wasps", "paperId": "877a0059d2370fca7af0b81078e2a304371f0e3b"}, {