INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('bf82b3e8f6e7bd97e67a8f9889e0a3a05073a8cf', '{"url": "https://www.semanticscholar.org/paper/bf82b3e8f6e7bd97e67a8f9889e0a3a05073a8cf", "year": 2021, "title": "Stress and odorant receptor feedback during a critical period after hatching regulates olfactory sensory neuron differentiation in Drosophila", "venue": "PLoS Biology", "authors": [{"name": "S. Jafari", "authorId": "25694693"}, {"name": "J. Henriksson", "authorId": "50623435"}, {"name": "Hua Yan", "authorId": "2047162017"}, {"name": "M. Alenius", "authorId": "5551655"}], "journal": {"name": "PLoS Biology", "volume": "19"}, "paperId": "bf82b3e8f6e7bd97e67a8f9889e0a3a05073a8cf", "abstract": "Here, we reveal that the regulation of Drosophila odorant receptor (OR) expression during the pupal stage is permissive and imprecise. We found that directly after hatching an OR feedback mechanism both directs and refines OR expression. We demonstrate that, as in mice, dLsd1 and Su(var)3-9 balance heterochromatin formation to direct OR expression. We show that the expressed OR induces dLsd1 and Su(var)3-9 expression, linking OR level and possibly function to OR expression. OR expression refinement shows a restricted duration, suggesting that a gene regulatory critical period brings olfactory sensory neuron differentiation to an end. Consistent with a change in differentiation, stress during the critical period represses dLsd1 and Su(var)3-9 expression and makes the early permissive OR expression permanent. This induced permissive gene regulatory state makes OR expression resilient to stress later in life. Hence, during a critical period OR feedback, similar to in mouse OR selection, defines adult OR expression in Drosophila.", "citations": [{"title": "Hedgehog-mediated gut-taste neuron axis controls sweet perception in Drosophila", "paperId": "36a5a7737821411921d457811b5c