INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('a4594681fe94ed22691feb5a9139f3f69de1d626', '{"url": "https://www.semanticscholar.org/paper/a4594681fe94ed22691feb5a9139f3f69de1d626", "year": 2020, "title": "Non-canonical odor coding ensures unbreakable mosquito attraction to humans", "venue": "", "authors": [{"name": "Meg A. Younger", "authorId": "145063424"}, {"name": "M. Herre", "authorId": "6833315"}, {"name": "Alison R. Ehrlich", "authorId": "2024641120"}, {"name": "Zhongyan Gong", "authorId": "1600506107"}, {"name": "Zachary N. Gilbert", "authorId": "2024810077"}, {"name": "Saher Rahiel", "authorId": "2024846260"}, {"name": "B. Matthews", "authorId": "5464230"}, {"name": "L. Vosshall", "authorId": "1974361"}], "journal": {"name": "bioRxiv", "volume": ""}, "paperId": "a4594681fe94ed22691feb5a9139f3f69de1d626", "abstract": "Female Aedes aegypti mosquitoes show strong innate attraction to humans. This chemosensory behavior is critical to species survival because females require a blood-meal to reproduce. Humans, the preferred host of Ae. aegypti, produce a complex blend of odor cues along with carbon dioxide (CO2) that attracts females ready to bite. Mosquitoes detect these cues with heteromeric ligand-gated ion channels encoded by three different chemosensory receptor gene families. A common theme in other species is that olfactory neurons express a single receptor that defines their chemical specificity and that they extend axons that converge upon dedicated glomeruli in the first sensory processing center in the brain. Such an organization permits the brain to segregate olfactory information and monitor activity of individual glomeruli to interpret what smell has been encountered. We have discovered that Ae. aegypti uses an entirely different organizational principle for its olfactory system. Using genetic strains that label subpopulations of olfactory neurons, we found that many neurons co-express multiple members of at least two of the chemosensory receptor families. This unexpected co-expression is functional, as assessed by in vivo calcium imaging showing that a given glomerulus is activated by multiple ligands detected by different receptor families. This has direct functional consequences for mosquito behavior. Mutant mosquitoes that cannot sense CO2 can be behaviorally activated by a volatile amine that stimulates the CO2 glomerulus. This non-canonical olfactory system organization featuring overlapping receptor expression may explain the female mosquito’s robust and “unbreakable’ attraction to humans.", "citations": [{"title": "Humidity sensors that alert mosquitoes to nearby hosts and egg-laying sites", "paperId": "13d7e85b0b985cd69c1e0f5d6a29e04d4139d3da"}, {"title": "Arrestin-mediated desensitization enables intraneuronal olfactory discrimination in Caenorhabditis elegans", "paperId": "451cfb680942ba0d54234d36efc68da88fa6b8a4"}, {"title": "No functional contribution of the gustatory receptor, Gr64b, co-expressed in olfactory sensory neurons of Drosophila melanogaster", "paperId": "80507d5d69808393fe065c0f40f03240f6d3ad35"}, {"title": "Maternally Instigated Diapause in Aedes albopictus: Coordinating Experience and Internal State for Survival in Variable Environments", "paperId": "fc8836a758b277f93377deb4b057dd1d127be40d"}, {"title": "Chemoreceptor co-expression in Drosophila melanogaster olfactory neurons", "paperId": "8c5346435df56e3f632a010e752980b05cd81cad"}, {"title": "Odorant-receptor-mediated regulation of chemosensory gene expression in the malaria mosquito Anopheles gambiae", "paperId": "715c5cf74a886084cbe78154437ff7807406478e"}, {"title": "Considerations for homology-based DNA repair in mosquitoes: Impact of sequence heterology and donor template source", "paperId": "6c9edfd32df919bcf144908f64cec759422b1c5f"}, {"title": "Differential mosquito attraction to humans is associated with skin-derived carboxylic acid levels", "paperId": "013253906a4fcb18e615fd51e151107b94aa12be"}, {"title": "Competing beetles attract egg laying in a hawkmoth", "paperId": "94236a62b3fd76486065b82dfc9deb18033af49e"}, {"title": "Multimodal mechanisms of repellency in arthropods", "paperId": "3ad1dfaf5d311bd5264bc8481f7dec00df03f545"}, {"title": "Response plasticity of Drosophila olfactory sensory neurons", "paperId": "39d492fd1159944d69cfc8b1d3095fc4921b76cd"}, {"title": "The closer the better: sensory tools and host-association in blood-sucking insects", "paperId": "82f8f9616f9be579523985baf20753c1cf5590db"}, {"title": "Ir76b is a Co-receptor for Amine Responses in Drosophila Olfactory Neurons", "paperId": "4b9163d386c4e11382fce8059389b850fd7cf81b"}, {"title": "Synergistic Coding of Carbon Dioxide and a Human Sweat Odorant in the Mosquito Brain", "paperId": "ea31827538eee5c53dcea0462722e5495a2f1ad9"}, {"title": "Olfactory System Morphology Suggests Colony Size Drives Trait Evolution in Odorous Ants (Formicidae: Dolichoderinae)", "paperId": "276d858590d13609da040b4208c6c7426bb2b741"}, {"title": "HCR RNA-FISH protocol for the whole-mount brains of Drosophila and other insects v1", "paperId": "990e727b978ea20371f80ff0b74f01e9a010e851"}, {"title": "A persistent behavioral state enables sustained predation of humans by mosquitoes", "paperId": "c19a06cd202e25b5569a216f991e80561c1c253b"}, {"title": "Human attractive cues and mosquito host-seeking behavior.", "paperId": "ac05b4713a43bc11501df485ff217255438fe242"}, {"title": "Olfactory Receptor Gene Regulation in Insects: Multiple Mechanisms for Singular Expression", "paperId": "4fc6d2ca1c6cb34f37021b3b69ccc7652e723776"}, {"title": "Discrete roles of Ir76b ionotropic coreceptor impact olfaction, blood feeding, and mating in the malaria vector mosquito Anopheles coluzzii", "paperId": "9a8422b412e0eff9e48fb025f1c10b59a338757a"}, {"title": "Functional characterization of odor responses and gene expression changes in olfactory co-receptor mutants