INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('1f6d85c5801bf966ceb694b781d532273b5c3820', '{"url": "https://www.semanticscholar.org/paper/1f6d85c5801bf966ceb694b781d532273b5c3820", "year": 2020, "title": "Single-cell transcriptomes of developing and adult olfactory receptor neurons in Drosophila", "venue": "bioRxiv", "authors": [{"name": "C. McLaughlin", "authorId": "49141960"}, {"name": "Maria Brbic", "authorId": "2312031"}, {"name": "Qijing Xie", "authorId": "7246519"}, {"name": "Tongchao Li", "authorId": "48779687"}, {"name": "Felix Horns", "authorId": "48575890"}, {"name": "Sai Saroja Kolluru", "authorId": "118316760"}, {"name": "Justus M. Kebschull", "authorId": "6481849"}, {"name": "David Vacek", "authorId": "101648191"}, {"name": "Anthony Xie", "authorId": "98809709"}, {"name": "Jiefu Li", "authorId": "150151368"}, {"name": "Robert C. Jones", "authorId": "121278961"}, {"name": "J. Leskovec", "authorId": "1702139"}, {"name": "S. Quake", "authorId": "3977842"}, {"name": "L. Luo", "authorId": "145586825"}, {"name": "Hongjie Li", "authorId": "2108545954"}], "journal": {"name": "eLife", "volume": "10"}, "paperId": "1f6d85c5801bf966ceb694b781d532273b5c3820", "abstract": "Recognition of environmental cues is essential for the survival of all organisms. Precise transcriptional changes occur to enable the generation and function of the neural circuits underlying sensory perception. To gain insight into these changes, we generated single-cell transcriptomes of Drosophila olfactory receptor neurons (ORNs), thermosensory and hygrosensory neurons from the third antennal segment at an early developmental and adult stage. We discovered that ORNs maintain expression of the same olfactory receptors across development. Using these receptors and computational approaches, we matched transcriptomic clusters corresponding to anatomically and physiologically defined neuronal types across multiple developmental stages. Cell-type-specific transcriptomes, in part, reflected axon trajectory choices in early development and sensory modality in adults. Our analysis also uncovered type-specific and broadly expressed genes that could modulate adult sensory responses. Collectively, our data reveal important transcriptomic features of sensory neuron biology and provides a resource for future studies of their development and physiology.", "citations": [{"title": "Emergent dynamics of adult stem cell lineages from single nucleus and single cell RNA-Seq of Drosophila testes", "paperId": "4d00396cc36d56690d608e820021e4282e98f118"}, {"title": "Drosophila olfaction: past, present and future", "paperId": "6c32d89c783891ba23860a8999e8150812c99b73"}, {"title": "Activation of pheromone-sensitive olfactory neurons by plant volatiles in the moth Agrotis ipsilon does not occur at the level of the pheromone receptor protein", "paperId": "5ce640a657c00456ef0a3155d42c6a8012a9076d"}, {"title": "Charting the development of Drosophila leg sensory organs at single-cell resolution", "paperId": "74b619cefc9a4cad22dcdd68656772b67d0dd802"}, {"title": "Technological advances in mosquito olfaction neurogenetics.", "paperId": "574b3bc6851e8ab7694012225c582e0231d27149"}, {"title": "Single cell RNA sequencing of the adult Drosophila eye reveals distinct clusters and novel marker genes for all major cell types", "paperId": "4d354d4a41c5a5fd4e9a0dafed060d79315b7e62"}, {"title": "Genetic mechanisms modulating behaviour through plastic chemosensory responses in insects", "paperId": "6fde2ebb847b47cc245b6ec9dba69d21c1756ab1"}, {"title": "Pentylamine inhibits humidity detection in insect vectors of human and plant borne pathogens", "paperId": "5b96efd87c459c6969742633f0cf6c58ce5f6614"}, {"title": "Single cell RNA-seq analysis reveals temporally-regulated and quiescence-regulated gene expression in Drosophila larval neuroblasts", "paperId": "f307fc1cbdf8a3c04176c541da44a7c499b256f