INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('12c1e98ddbdde57a17c12dc916ff119e10a665d2', '{"url": "https://www.semanticscholar.org/paper/12c1e98ddbdde57a17c12dc916ff119e10a665d2", "year": 2022, "title": "Investigating Echo State Network Performance with Biologically-Inspired Hierarchical Network Structure", "venue": "IEEE International Joint Conference on Neural Network", "authors": [{"name": "Shane McDaniel", "authorId": "2067561088"}, {"name": "Marisel Villafane-Delgado", "authorId": "1405614975"}, {"name": "Erik C. Johnson", "authorId": "2118693758"}], "journal": {"name": "2022 International Joint Conference on Neural Networks (IJCNN)", "pages": "01-08"}, "paperId": "12c1e98ddbdde57a17c12dc916ff119e10a665d2", "abstract": "The nervous systems of animals exhibit complex recurrent topologies. These networks often show key properties like small-worldness and clustering, forming a complex hierarchical topology that is very different from many standard random graphs. Current machine learning techniques are dominated by feed-forward networks without recurrent connectivity, or specialized architectures for gated recurrent connectivity (such as Long-Short Term Memory Networks). Reservoir computing networks, such as echo state networks, represent an exciting alternative form of recurrent networks, which require limited training data compared to other recurrent networks. Echo state networks, however, typically use randomized network topologies. We introduce a novel reservoir computing network, with a hierarchical network structure inspired by organization of biological networks, utilizing hierarchical stochastic block models. We demonstrate the use of this network for predicting dynamic system evolution, and we compare this network to existing echo state network topologies. We find that the best performing networks utilize our new topology, and, for Mackey-Glass system prediction as well as MNIST classification, the overall distribution of solutions is improved. For the Mackey-Glass system, the median population NRMSE improved from 0.856 to 0.08, and for MNIST the median population accuracy increased from 92.5% to 96.8%. This may be preliminary indication of improving network function through changing network topology. The spectral radii of the produced HSBM topologies do not always obey the echo state property, and we show that both the spectral radius as well as common graph metrics do not predict the best performing networks. This argues for further investigation into the structural properties of high performing networks utilizing reservoir computing.", "citations": [], "externalIds": {"DOI": "10.1109/IJCNN55064.2022.9892248", "DBLP": "conf/ijcnn/McDanielVJ22", "CorpusId": 252625896}, "isOpenAccess": false, "citationCount": 0, "fieldsOfStudy": ["Computer Science"], "openAccessPdf": null, "citationStyles": {"bibtex": "@[''JournalArticle'', ''Conference'']{McDaniel2022InvestigatingES,\n author = {Shane McDaniel and Marisel Villafane-Delgado and Erik C. Johnson},\n booktitle = {IEEE International Joint Conference on Neural Network},\n journal = {2022 International Joint Conference on Neural Networks (IJCNN)},\n pages = {01-08},\n title = {Investigating Echo State Network Performance with Biologically-Inspired Hierarchical Network Structure},\n year = {2022}\n}\n"}, "referenceCount": 34, "publicationDate": "2022-07-18", "s2FieldsOfStudy": [{"source": "external", "category": "Computer Science"}, {"source": "s2-fos-model", "category": "Computer Science"}], "publicationTypes": ["JournalArticle", "Conference"], "publicationVenue": {"id": "f80ba4a3-7aed-4021-b4d8-e4f50668847a", "url": "http://www.wikicfp.com/cfp/program?id=1573", "name": "IEEE International Joint Conference on Neural Network", "type": "conference", "alternate_names": ["IJCNN", "IEEE Int Jt Conf Neural Netw", "Int Jt Conf Neural Netw", "International Joint Conference on Neural Network"]}, "influentialCitationCount": 0}', '2023-01-27 11:03:12.855383', '2023-01-27 11:03:12.855383', 'Investigating Echo State Network Performance with Biologically-Inspired Hierarchical Network Structure', 2022, false, null, null, null, null);
INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('21ab25072969988a27a8ce2cf1a891d3b5f0fad9', '{"url": "https://www.semanticscholar.org/paper/21ab25072969988a27a8ce2cf1a891d3b5f0fad9", "year": 2022, "title": "Public Volume Electron Microscopy Data: An Essential Resource to Study the Brain Microvasculature", "venue": "bioRxiv", "authors": [{"name": "Stephanie K. Bonney", "authorId": "2093855588"}, {"name": "V. Coelho-Santos", "authorId": "1404043376"}, {"name": "Sheng-Fu Huang", "authorId": "47156852"}, {"name": "Marc M. Takeno", "authorId": "8311185"}, {"name": "Joergen Kornfeld", "authorId": "2065342452"}, {"name": "A. Keller", "authorId": "10231612"}, {"name": "Andy Y. Shih", "authorId": "2137246771"}], "journal": {"name": "Frontiers in Cell and Developmental Biology", "volume": "10"}, "paperId": "21ab25072969988a27a8ce2cf1a891d3b5f0fad9", "abstract": "Electron microscopy is the primary approach to study ultrastructural features of the cerebrovasculature. However, 2D snapshots of a vascular bed capture only a small fraction of its complexity. Recent efforts to synaptically map neuronal circuitry using volume electron microscopy have also sampled the brain microvasculature in 3D. Here, we perform a meta-analysis of 6 data sets spanning different species and brain regions, including 2 data sets from the MICrONS consortium that have made efforts to segment vasculature in addition to all parenchymal cell types in mouse visual cortex. Exploration of these data have revealed rich information for detailed investigation of the cerebrovasculature. Neurovascular unit cell types (including, but not limited to, endothelial cells, mural cells, perivascular fibroblasts, microglia, and astrocytes) could be discerned across broad microvascular zones. Image contrast was sufficient to identify subcellular details, including endothelial junctions, caveolae, peg-and-socket interactions, mitochondria, Golgi cisternae, microvilli and other cellular protrusions of potential significance to vascular signaling. Additionally, noncellular structures including the basement membrane and perivascular spaces were visible and could be traced between arterio-venous zones along the vascular wall. These explorations revealed structural features that may be important for vascular functions, such as blood-brain barrier integrity, blood flow control, brain clearance, and bioenergetics. They also identified limitations where accuracy and consistency of segmentation could be further honed by future efforts. The purpose of this article is to introduce these valuable community resources within the framework of cerebrovascular research by providing an assessment of their vascular contents, identifying features of significance for further study, and discussing next step ideas for refining vascular segmentation and analysis.", "citations": [{"title": "Ultrastructure of Precapillary Sphincters and the Neurovascular Unit", "paperId": "ea42685a3ff41eacfc139f08d57ad992907d68d0"}, {"title": "Individual Astrocyte Morphology in the Collagenous Lamina Cribrosa Revealed by Multicolor DiOlistic Labeling", "paperId": "183d9e40d80efbbb9d8f6cc15eb9886935aedfbb"}, {"title": "Estimates for the astrocyte endfoot sheath permeability of the extra-cellular pathway", "paperId": "cb2a3a48e39303db647bb4bdd52e405ef7b8cb99"}, {"title": "Multiscale imaging informs translational mouse modeling of neurological disease", "paperId": "23910f5c027fd4741c7bd20b4ca4ce90da8dd1a1"}, {"title": "Pericyte heterogeneity identified by 3D ultrastructural analysis of the microvessel wall", "paperId": "fbff9e7efa16f40435c7583c96ed0e18cee9effb"}, {"title": "Reciprocal Interactions between Oligodendrocyte Precursor Cells and the Neurovascular Unit in Health and Disease", "paperId": "f2f3f03a97d07d99e9a196dab821d6fa50367414"}, {"title": "A Survey of Visualization and Analysis in High‐Resolution Connectomics", "paperId": "ebaf6fafb4adb7f3657fdbb869249c727ffccf7e"}], "externalIds": {"DOI": "10.1101/2022.02.20.481154", "PubMed": "35450291", "CorpusId": 247077828, "PubMedCentral": "9016339"}, "isOpenAccess": true, "citationCount": 7, "fieldsOfStudy": ["Biology", "Medicine"], "openAccessPdf": {"url": "https://www.frontiersin.org/articles/10.3389/fcell.2022.849469/pdf", "status": "GREEN"}, "citationStyles": {"bibtex": "@[''JournalArticle'']{Bonney2022PublicVE,\n author = {Stephanie K. Bonney and V. Coelho-Santos and Sheng-Fu Huang and Marc M. Takeno and Joergen Kornfeld and A. Keller and Andy Y. Shih},\n booktitle = {bioRxiv},\n journal = {Frontiers in Cell and Developmental Biology},\n title = {Public Volume Electron Microscopy Data: An Essential Resource to Study the Brain Microvasculature},\n volume = {10},\n year = {2022}\n}\n"}, "referenceCount": 71, "publicationDate": "2022-02-22", "s2FieldsOfStudy": [{"source": "external", "category": "Biology"}, {"source": "external", "category": "Medicine"}, {"source": "s2-fos-model", "category": "Biology"}], "publicationTypes": ["JournalArticle"], "publicationVenue": {"id": "027ffd21-ebb0-4af8-baf5-911124292fd0", "url": "http://biorxiv.org/", "name": "bioRxiv", "type": "journal"}, "influentialCitationCount": 1}', '2023-01-27 11:03:16.452561', '2023-01-27 11:03:16.452561', 'Public Volume Electron Microscopy Data: An Essential Resource to Study the Brain Microvasculature', 2022, false, null, null, null, null);
INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('25907f2754bcfa5463cd78c0919c2d278dfb14b8', '{"url": "https://www.semanticscholar.org/paper/25907f2754bcfa5463cd78c0919c2d278dfb14b8", "year": 2022, "title": "NMNAT2 is the major NAD+ provider for vesicular glycolysis generating on-board energy for fast axonal transport cargos", "venue": "bioRxiv", "authors": [{"name": "Sen Yang", "authorId": "2155448548"}, {"name": "Zhen-Xian Niou", "authorId": "1470768202"}, {"name": "Jacob LaMar", "authorId": "2154033593"}, {"name": "A. Enriquez", "authorId": "91883597"}, {"name": "Jui-Yen Huang", "authorId": "3953637"}, {"name": "Jason M. Tennessen", "authorId": "5596793"}, {"name": "Vidhya Rangaraju", "authorId": "5604578"}, {"name": "Hui-Chen Lu", "authorId": "2115781395"}], "journal": {"name": "bioRxiv"}, "paperId": "25907f2754bcfa5463cd78c0919c2d278dfb14b8", "abstract": "Axonal transport, an ATP demanding process, plays a critical role in maintaining axonal and neuronal health. ATP in neurons is synthesized by glycolysis and mitochondrial respiration, both driven by proper NAD+/NADH redox potentials. NMNAT2 is the major neuronal NAD+ synthesizing enzyme and is a key axonal maintenance factor. Here, we show that NMNAT2 co-migrates with fast vesicular cargos in axons and is required for fast axonal transport in the distal axons of cortical neurons. Using SoNar sensor imaging to detect axonal NAD+ and NADH, we show that NMNAT2 is critical in maintaining NAD+/NADH potentials in distal axons. With Syn-ATP sensor imaging to detect synaptic vesicle ATP levels (sv-ATP), we demonstrate that glycolysis is the major provider of sv-ATP and NMNAT2 deletion significantly reduces sv-ATP levels. NAD+ supplementation of NMNAT2 KO neurons restores sv-ATP levels and fast axonal transports in a glycolysis-dependent manner. Together, these data show that NMNAT2 maintains the local NAD+/NADH redox potential and sustains “on-board” glycolysis to meet the bioenergetic demands of fast vesicular transport in distal axons. Intriguingly, mitochondrial respiration contributes significantly to sv-ATP levels in NMNAT2 KO axons. This finding suggests that NMNAT2 deletion induces metabolic plasticity by engaging mitochondria. Surprisingly, NMN, the substrate for NMNAT2 in NAD+ synthesis, supplementation restores sv-ATP and axonal transport in NMNAT2 KO axons with an efficacy similar to NAD+. NMN’s restoration of NMNAT2 KO distal axonal sv-ATP levels and vesicle transport requires mitochondrial respiration, while rescue by NAD+ is independent of mitochondrial respiration. Based on these findings, we hypothesize that NMN rescues glycolysis to restore axonal transport in NMNAT2 KO axons by taking advantage of the compensatory ATP-generating metabolic pathways triggered by NMNAT2 loss.", "citations": [{"title": "Emerging evidence for compromised axonal bioenergetics and axoglial metabolic coupling as drivers of neurodegeneration", "paperId": "6303c9693ae42d2966b54a0943e854242c6ca61c"}], "externalIds": {"DOI": "10.1101/2022.02.06.479307", "CorpusId": 246725916}, "isOpenAccess": true, "citationCount": 1, "fieldsOfStudy": ["Biology"], "openAccessPdf": {"url": "https://www.biorxiv.org/content/biorxiv/early/2022/02/09/2022.02.06.479307.full.pdf", "status": "GREEN"}, "citationStyles": {"bibtex": "@None{Yang2022NMNAT2IT,\n author = {Sen Yang and Zhen-Xian Niou and Jacob LaMar and A. Enriquez and Jui-Yen Huang and Jason M. Tennessen and Vidhya Rangaraju and Hui-Chen Lu},\n booktitle = {bioRxiv},\n journal = {bioRxiv},\n title = {NMNAT2 is the major NAD+ provider for vesicular glycolysis generating on-board energy for fast axonal transport cargos},\n year = {2022}\n}\n"}, "referenceCount": 90, "publicationDate": "2022-02-09", "s2FieldsOfStudy": [{"source": "external", "category": "Biology"}, {"source": "s2-fos-model", "category": "Biology"}], "publicationTypes": null, "publicationVenue": {"id": "027ffd21-ebb0-4af8-baf5-911124292fd0", "url": "http://biorxiv.org/", "name": "bioRxiv", "type": "journal"}, "influentialCitationCount": 0}', '2023-01-27 11:03:20.067931', '2023-01-27 11:03:20.067931', 'NMNAT2 is the major NAD+ provider for vesicular glycolysis generating on-board energy for fast axonal transport cargos', 2022, false, null, null, null, null);
INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('9e17fa9339a3dca3667254a6d9dfc4bcfa83fc94', '{"url": "https://www.semanticscholar.org/paper/9e17fa9339a3dca3667254a6d9dfc4bcfa83fc94", "year": 2022, "title": "Synaptic connectivity to L2/3 of primary visual cortex measured by two-photon optogenetic stimulation", "venue": "eLife", "authors": [{"name": "Travis A Hage", "authorId": "6866003"}, {"name": "Alice Bosma-Moody", "authorId": "1441323099"}, {"name": "Christopher A Baker", "authorId": "2064978610"}, {"name": "Megan B. Kratz", "authorId": "1904893"}, {"name": "Luke Campagnola", "authorId": "3349416"}, {"name": "T. Jarsky", "authorId": "3507536"}, {"name": "Hongkui Zeng", "authorId": "2194809"}, {"name": "G. Murphy", "authorId": "6070839"}], "journal": {"name": "eLife", "volume": "11"}, "paperId": "9e17fa9339a3dca3667254a6d9dfc4bcfa83fc94", "abstract": "Understanding cortical microcircuits requires thorough measurement of physiological properties of synaptic connections formed within and between diverse subclasses of neurons. Towards this goal, we combined spatially precise optogenetic stimulation with multicellular recording to deeply characterize intralaminar and translaminar monosynaptic connections to supragranular (L2/3) neurons in the mouse visual cortex. The reliability and specificity of multiphoton optogenetic stimulation were measured across multiple Cre lines, and measurements of connectivity were verified by comparison to paired recordings and targeted patching of optically identified presynaptic cells. With a focus on translaminar pathways, excitatory and inhibitory synaptic connections from genetically defined presynaptic populations were characterized by their relative abundance, spatial profiles, strength, and short-term dynamics. Consistent with the canonical cortical microcircuit, layer 4 excitatory neurons and interneurons within L2/3 represented the most common sources of input to L2/3 pyramidal cells. More surprisingly, we also observed strong excitatory connections from layer 5 intratelencephalic neurons and potent translaminar inhibition from multiple interneuron subclasses. The hybrid approach revealed convergence to and divergence from excitatory and inhibitory neurons within and across cortical layers. Divergent excitatory connections often spanned hundreds of microns of horizontal space. In contrast, divergent inhibitory connections were more frequently measured from postsynaptic targets near each other.", "citations": [{"title": "All-optical physiology resolves a synaptic basis for behavioral timescale plasticity", "paperId": "16fcf6cc2a1d61d42a8f3bc790ae008899714b34"}, {"title": "The logic of recurrent circuits in the primary visual cortex", "paperId": "5111d87e2238bd525136710edba3bb138c66ffa4"}, {"title": "Rapid learning of neural circuitry from holographic ensemble stimulation enabled by model-based compressed sensing", "paperId": "927a7be70c19cf9fbe4784aaf95a9a0bb6ec4e86"}, {"title": "All-optical inter-layers functional connectivity investigation in the mouse retina", "paperId": "dec46fc85a5c2c0863c2fb10983933a59372b09b"}, {"title": "Quantitative Census of Local Somatic Features in Mouse Visual Cortex", "paperId": "142298f5b11bf2c76536c93aa256835d69dca723"}, {"title": "Region-Specific Characteristics of Astrocytes and Microglia: A Possible Involvement in Aging and Diseases", "paperId": "82d303dcaf18dea4ac6b274e4167fdf69b9e28a0"}, {"title": "Parvalbumin-Positive Interneurons Regulate Cortical Sensory Plasticity in Adulthood and Development Through Shared Mechanisms", "paperId": "784a146e17845cbaa0111034237474adf5a62690"}, {"title": "Predictable Fluctuations in Excitatory Synaptic Strength Due to Natural Variation in Presynaptic Firing Rate", "paperId": "edb4503a5fc4498e99775cf25aa7deb86eca47b7"}], "externalIds": {"DOI": "10.7554/eLife.71103", "PubMed": "35060903", "CorpusId": 246201827, "PubMedCentral": "8824465"}, "isOpenAccess": false, "citationCount": 8, "fieldsOfStudy": ["Medicine"], "openAccessPdf": null, "citationStyles": {"bibtex": "@[''JournalArticle'']{Hage2022SynapticCT,\n author = {Travis A Hage and Alice Bosma-Moody and Christopher A Baker and Megan B. Kratz and Luke Campagnola and T. Jarsky and Hongkui Zeng and G. Murphy},\n booktitle = {eLife},\n journal = {eLife},\n title = {Synaptic connectivity to L2/3 of primary visual cortex measured by two-photon optogenetic stimulation},\n volume = {11},\n year = {2022}\n}\n"}, "referenceCount": 153, "publicationDate": "2022-01-21", "s2FieldsOfStudy": [{"source": "external", "category": "Medicine"}, {"source": "s2-fos-model", "category": "Biology"}], "publicationTypes": ["JournalArticle"], "publicationVenue": {"id": "07365b9a-c0ce-4dd3-b93b-a02e1c81e0c6", "url": "https://epub.uni-regensburg.de/40444/", "issn": "2050-084X", "name": "eLife", "type": "journal", "alternate_urls": ["https://elifesciences.org/", "https://elife.elifesciences.org/", "http://elifesciences.org/"]}, "influentialCitationCount": 0}', '2023-01-27 11:03:23.674663', '2023-01-27 11:03:23.674663', 'Synaptic connectivity to L2/3 of primary visual cortex measured by two-photon optogenetic stimulation', 2022, false, null, null, null, null);
INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('8d9ac66b50817db49f34dc20c84971b2aa73fafc', '{"url": "https://www.semanticscholar.org/paper/8d9ac66b50817db49f34dc20c84971b2aa73fafc", "year": 2019, "title": "Retinal horizontal cells use different synaptic sites for global feedforward and local feedback signaling", "venue": "Current Biology", "authors": [{"name": "Christian Behrens", "authorId": "14387386"}, {"name": "Yue Zhang", "authorId": "2145913925"}, {"name": "S. C. Yadav", "authorId": "31213399"}, {"name": "S. Haverkamp", "authorId": "5450972"}, {"name": "S. Irsen", "authorId": "1867913"}, {"name": "Maria M. Korympidou", "authorId": "1396357264"}, {"name": "Anna Schaedler", "authorId": "10746325"}, {"name": "Karin Dedek", "authorId": "4362355"}, {"name": "Robert G. Smith", "authorId": "2115488282"}, {"name": "Thomas Euler", "authorId": "2674942"}, {"name": "Philipp Berens", "authorId": "1689077"}, {"name": "T. Schubert", "authorId": "4489849"}], "journal": {"name": "Current Biology", "pages": "545-558.e5", "volume": "32"}, "paperId": "8d9ac66b50817db49f34dc20c84971b2aa73fafc", "abstract": "In the outer plexiform layer (OPL) of the mouse retina, two types of cone photoreceptors (cones) provide input to more than a dozen types of cone bipolar cells (CBCs). This transmission is modulated by a single horizontal cell (HC) type, the only interneuron in the outer retina. Horizontal cells form feedback synapses with cones and feedforward synapses with CBCs. However, the exact computational role of HCs is still debated. Along with performing global signaling within their laterally coupled network, HCs also provide local, cone-specific feedback. Specifically, it has not been clear which synaptic structures HCs use to provide local feedback to cones and global forward signaling to CBCs. Here, we reconstructed in a serial block-face electron microscopy volume the dendritic trees of five HCs as well as cone axon terminals and CBC dendrites to quantitatively analyze their connectivity. In addition to the fine HC dendritic tips invaginating cone axon terminals, we also identified “bulbs”, short segments of increased dendritic diameter on the primary dendrites of HCs. These bulbs are located well below the cone axon terminal base and make contact to other cells mostly identified as other HCs or CBCs. Using immunolabeling we show that HC bulbs express vesicular gamma-aminobutyric acid transporters and co-localize with GABA receptor γ2 subunits. Together, this suggests the existence of two synaptic strata in the mouse OPL, spatially separating cone-specific feedback and feedforward signaling to CBCs. A biophysics-based computational model of a HC dendritic branch supports the hypothesis that the spatial arrangement of synaptic contacts allows simultaneous local feedback and global feedforward signaling.", "citations": [{"title": "Super-resolution STED imaging in the inner and outer whole-mount mouse retina", "paperId": "330fe6cc81ea24e82ce404875c6ab76c78dc070d"}, {"title": "Sustained deep-tissue voltage recording using a fast indicator evolved for two-photon microscopy", "paperId": "7be29a1ba9e39c3fe95af75e6dec0f9d40c80638"}, {"title": "Amacrine cells differentially balance zebrafish colour circuits in the central and peripheral retina", "paperId": "e2f5ffe454eb67a47023b3b09a569f7832baa4bf"}, {"title": "Investigation of heterocellular features of the mouse retinal neurovascular unit by 3D electron microscopy.", "paperId": "9e80ed6a209a0691acb5cf6770c1b7daacac8acc"}, {"title": "Transience of the Retinal Output Is Determined by a Great Variety of Circuit Elements", "paperId": "9f81030e61714498b1b54a5647d3fbff001f5c1d"}, {"title": "Visual processing: When two synaptic strata are better than one", "paperId": "8ca63982b3109cc08b09829565b8d8295b75a937"}, {"title": "Amacrine cells shape retinal functions while dynamically preserving circuits for colour vision", "paperId": "938c6d0a362c783f7b095afa4ad2cfde0186f4f6"}, {"title": "Spectral inference reveals principal cone-integration rules of the zebrafish inner retina", "paperId": "e552efb99f53015ef4203beedbfcb218dd689f2f"}, {"title": "The RNA‐binding protein and stress granule component ATAXIN‐2 is expressed in mouse and human tissues associated with glaucoma pathogenesis", "paperId": "024783d1e1d39ff2d2a74cbdca213f2ad8ee39d8"}], "externalIds": {"DOI": "10.1101/780031", "MAG": "2976724692", "PubMed": "34910950", "CorpusId": 204163059}, "isOpenAccess": true, "citationCount": 9, "fieldsOfStudy": ["Physics", "Biology", "Medicine"], "openAccessPdf": {"url": "https://www.biorxiv.org/content/biorxiv/early/2019/09/24/780031.full.pdf", "status": "GREEN"}, "citationStyles": {"bibtex": "@[''JournalArticle'']{Behrens2019RetinalHC,\n author = {Christian Behrens and Yue Zhang and S. C. Yadav and S. Haverkamp and S. Irsen and Maria M. Korympidou and Anna Schaedler and Karin Dedek and Robert G. Smith and Thomas Euler and Philipp Berens and T. Schubert},\n booktitle = {Current Biology},\n journal = {Current Biology},\n pages = {545-558.e5},\n title = {Retinal horizontal cells use different synaptic sites for global feedforward and local feedback signaling},\n volume = {32},\n year = {2019}\n}\n"}, "referenceCount": 105, "publicationDate": "2019-09-24", "s2FieldsOfStudy": [{"source": "external", "category": "Physics"}, {"source": "external", "category": "Biology"}, {"source": "external", "category": "Medicine"}, {"source": "s2-fos-model", "category": "Biology"}], "publicationTypes": ["JournalArticle"], "publicationVenue": {"id": "9469269e-53d7-4955-b1a5-17a75bee7634", "url": "http://www.current-biology.com/", "issn": "0960-9822", "name": "Current Biology", "type": "journal", "alternate_urls": ["http://www.sciencedirect.com/science/journal/09609822"], "alternate_names": ["Curr Biology"]}, "influentialCitationCount": 1}', '2023-01-27 11:03:27.296616', '2023-01-27 11:03:27.296616', 'Retinal horizontal cells use different synaptic sites for global feedforward and local feedback signaling', 2019, false, null, null, null, null);
INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('70f237df98d9ca9fae68116349becb6efe5580d8', '{"url": "https://www.semanticscholar.org/paper/70f237df98d9ca9fae68116349becb6efe5580d8", "year": 2022, "title": "Towards a Comprehensive Optical Connectome at Single Synapse Resolution via Expansion Microscopy", "venue": "Frontiers in Synaptic Neuroscience", "authors": [{"name": "Madison A. Sneve", "authorId": "2150277132"}, {"name": "Kiryl D Piatkevich", "authorId": "6440263"}], "journal": {"name": "Frontiers in Synaptic Neuroscience", "volume": "13"}, "paperId": "70f237df98d9ca9fae68116349becb6efe5580d8", "abstract": "Mapping and determining the molecular identity of individual synapses is a crucial step towards the comprehensive reconstruction of neuronal circuits. Throughout the history of neuroscience, microscopy has been a key technology for mapping brain circuits. However, subdiffraction size and high density of synapses in brain tissue make this process extremely challenging. Electron microscopy (EM), with its nanoscale resolution, offers one approach to this challenge yet comes with many practical limitations, and to date has only been used in very small samples such as C. elegans, tadpole larvae, fruit fly brain, or very small pieces of mammalian brain tissue. Moreover, EM datasets require tedious data tracing. Light microscopy in combination with tissue expansion via physical magnification—known as expansion microscopy (ExM)—offers an alternative approach to this problem. ExM enables nanoscale imaging of large biological samples, which in combination with multicolor neuronal and synaptic labeling offers the unprecedented capability to trace and map entire neuronal circuits in fully automated mode. Recent advances in new methods for synaptic staining as well as new types of optical molecular probes with superior stability, specificity, and brightness provide new modalities for studying brain circuits. Here we review advanced methods and molecular probes for fluorescence staining of the synapses in the brain that are compatible with currently available expansion microscopy techniques. In particular, we will describe genetically encoded probes for synaptic labeling in mice, zebrafish, Drosophila fruit flies, and C. elegans, which enable the visualization of post-synaptic scaffolds and receptors, presynaptic terminals and vesicles, and even a snapshot of the synaptic activity itself. We will address current methods for applying these probes in ExM experiments, as well as appropriate vectors for the delivery of these molecular constructs. In addition, we offer experimental considerations and limitations for using each of these tools as well as our perspective on emerging tools.", "citations": [{"title": "Dual-expression system for blue fluorescent protein optimization", "paperId": "f2740d8477ab216026717c5518b9d8c53a025f96"}, {"title": "Blind demixing methods for recovering dense neuronal morphology from barcode imaging data", "paperId": "5a8d95deee34f4dbc4cc9c0d54e811e659e099c9"}], "externalIds": {"DOI": "10.3389/fnsyn.2021.754814", "PubMed": "35115916", "CorpusId": 246001314, "PubMedCentral": "8803729"}, "isOpenAccess": true, "citationCount": 2, "fieldsOfStudy": ["Medicine"], "openAccessPdf": {"url": "https://www.frontiersin.org/articles/10.3389/fnsyn.2021.754814/pdf", "status": "GOLD"}, "citationStyles": {"bibtex": "@[''Review'', ''JournalArticle'']{Sneve2022TowardsAC,\n author = {Madison A. Sneve and Kiryl D Piatkevich},\n booktitle = {Frontiers in Synaptic Neuroscience},\n journal = {Frontiers in Synaptic Neuroscience},\n title = {Towards a Comprehensive Optical Connectome at Single Synapse Resolution via Expansion Microscopy},\n volume = {13},\n year = {2022}\n}\n"}, "referenceCount": 176, "publicationDate": "2022-01-18", "s2FieldsOfStudy": [{"source": "external", "category": "Medicine"}, {"source": "s2-fos-model", "category": "Biology"}], "publicationTypes": ["Review", "JournalArticle"], "publicationVenue": {"id": "561269ea-eb46-472d-ad90-fbb75c3080e5", "url": "https://www.frontiersin.org/journals/synaptic-neuroscience", "issn": "1663-3563", "name": "Frontiers in Synaptic Neuroscience", "type": "journal", "alternate_urls": ["http://www.frontiersin.org/synaptic_neuroscience", "http://www.frontiersin.org/synapticneuroscience/", "http://journal.frontiersin.org/journal/synaptic-neuroscience"], "alternate_names": ["Front Synaptic Neurosci"]}, "influentialCitationCount": 0}', '2023-01-27 11:03:30.910743', '2023-01-27 11:03:30.910743', 'Towards a Comprehensive Optical Connectome at Single Synapse Resolution via Expansion Microscopy', 2022, false, null, null, null, null);
INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('8376e8f8c9af578bc7a0f8c9164c8a3c0cd32917', '{"url": "https://www.semanticscholar.org/paper/8376e8f8c9af578bc7a0f8c9164c8a3c0cd32917", "year": 2022, "title": "Genetic Mechanisms Underlying the Evolution of Connectivity in the Human Cortex", "venue": "Frontiers in Neural Circuits", "authors": [{"name": "Ewoud R E Schmidt", "authorId": "41226131"}, {"name": "F. Polleux", "authorId": "3813269"}], "journal": {"name": "Frontiers in Neural Circuits", "volume": "15"}, "paperId": "8376e8f8c9af578bc7a0f8c9164c8a3c0cd32917", "abstract": "One of the most salient features defining modern humans is our remarkable cognitive capacity, which is unrivaled by any other species. Although we still lack a complete understanding of how the human brain gives rise to these unique abilities, the past several decades have witnessed significant progress in uncovering some of the genetic, cellular, and molecular mechanisms shaping the development and function of the human brain. These features include an expansion of brain size and in particular cortical expansion, distinct physiological properties of human neurons, and modified synaptic development. Together they specify the human brain as a large primate brain with a unique underlying neuronal circuit architecture. Here, we review some of the known human-specific features of neuronal connectivity, and we outline how novel insights into the human genome led to the identification of human-specific genetic modifiers that played a role in the evolution of human brain development and function. Novel experimental paradigms are starting to provide a framework for understanding how the emergence of these human-specific genomic innovations shaped the structure and function of neuronal circuits in the human brain.", "citations": [{"title": "A cross-species proteomic map of synapse development reveals neoteny during human postsynaptic density maturation", "paperId": "cad7b6dc4e57e520be7390479a8cb109065289db"}, {"title": "Molecular mechanisms of synaptogenesis", "paperId": "63ed2477177b7907a37a36725c6be7c36c9bdcba"}, {"title": "Angular gyrus: an anatomical case study for association cortex", "paperId": "4243e490d4ba80504fa8fb4dc19175cea86839e7"}], "externalIds": {"DOI": "10.3389/fncir.2021.787164", "PubMed": "35069126", "CorpusId": 245773253, "PubMedCentral": "8777274"}, "isOpenAccess": true, "citationCount": 3, "fieldsOfStudy": ["Medicine"], "openAccessPdf": {"url": "https://www.frontiersin.org/articles/10.3389/fncir.2021.787164/pdf", "status": "GOLD"}, "citationStyles": {"bibtex": "@[''Review'', ''JournalArticle'']{Schmidt2022GeneticMU,\n author = {Ewoud R E Schmidt and F. Polleux},\n booktitle = {Frontiers in Neural Circuits},\n journal = {Frontiers in Neural Circuits},\n title = {Genetic Mechanisms Underlying the Evolution of Connectivity in the Human Cortex},\n volume = {15},\n year = {2022}\n}\n"}, "referenceCount": 149, "publicationDate": "2022-01-07", "s2FieldsOfStudy": [{"source": "external", "category": "Medicine"}, {"source": "s2-fos-model", "category": "Biology"}, {"source": "s2-fos-model", "category": "Psychology"}], "publicationTypes": ["Review", "JournalArticle"], "publicationVenue": {"id": "09dc8437-eb87-49ec-a21b-ecb208a5890b", "url": "https://www.frontiersin.org/journals/neural-circuits", "issn": "1662-5110", "name": "Frontiers in Neural Circuits", "type": "journal", "alternate_urls": ["http://www.frontiersin.org/neuralcircuits/", "http://www.frontiersin.org/neural_circuits"], "alternate_names": ["Front Neural Circuit"]}, "influentialCitationCount": 0}', '2023-01-27 11:03:34.523663', '2023-01-27 11:03:34.523663', 'Genetic Mechanisms Underlying the Evolution of Connectivity in the Human Cortex', 2022, false, null, null, null, null);
INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('ff67e7991085386ea8e993a954d8e7a7270fd14e', '{"url": "https://www.semanticscholar.org/paper/ff67e7991085386ea8e993a954d8e7a7270fd14e", "year": 2021, "title": "Interpreting the Entire Connectivity of Individual Neurons in Micropatterned Neural Culture With an Integrated Connectome Analyzer of a Neuronal Network (iCANN)", "venue": "Frontiers in Neuroanatomy", "authors": [{"name": "June Hoan Kim", "authorId": "2109240438"}, {"name": "J. Ryu", "authorId": "27054445"}, {"name": "Boram Lee", "authorId": "2122831430"}, {"name": "Uikyu Chae", "authorId": "39329374"}, {"name": "J. W. Son", "authorId": "93563466"}, {"name": "B. Park", "authorId": "2111067842"}, {"name": "Il‐Joo Cho", "authorId": "2106133936"}, {"name": "Woong Sun", "authorId": "2002475"}], "journal": {"name": "Frontiers in Neuroanatomy", "volume": "15"}, "paperId": "ff67e7991085386ea8e993a954d8e7a7270fd14e", "abstract": "The function of a neural circuit can be determined by the following: (1) characteristics of individual neurons composing the circuit, (2) their distinct connection structure, and (3) their neural circuit activity. However, prior research on correlations between these three factors revealed many limitations. In particular, profiling and modeling of the connectivity of complex neural circuits at the cellular level are highly challenging. To reduce the burden of the analysis, we suggest a new approach with simplification of the neural connection in an array of honeycomb patterns on 2D, using a microcontact printing technique. Through a series of guided neuronal growths in defined honeycomb patterns, a simplified neuronal circuit was achieved. Our approach allowed us to obtain the whole network connectivity at cellular resolution using a combination of stochastic multicolor labeling via viral transfection. Therefore, we were able to identify several types of hub neurons with distinct connectivity features. We also compared the structural differences between different circuits using three-node motif analysis. This new model system, iCANN, is the first experimental model of neural computation at the cellular level, providing neuronal circuit structures for the study of the relationship between anatomical structure and function of the neuronal network.", "citations": [{"title": "In vitro cell models merging circadian rhythms and brain waves for personalized neuromedicine", "paperId": "31a37e80cf24851214ac4802e2b52bfaf3f67eba"}, {"title": "Multilevel resistive switching of ZnO memristive crossbar prototype for artificial neural systems", "paperId": "b612d5c4d446086a61e75dfa747beec0f1c536e0"}, {"title": "Nanoscale Patterning of In Vitro Neuronal Circuits.", "paperId": "cd07e56a759717274b5c40df60c833ce5a9fd7db"}, {"title": "Two-Wavelength Computational Holography for Aberration-Corrected Simultaneous Optogenetic Stimulation and Inhibition of In Vitro Biological Samples", "paperId": "f9fe532609da52406ccbb9250fd3c8e02cca962d"}, {"title": "Nanoscale patterning of in vitro neuronal circuits", "paperId": "67dacd2f33b49bf4b193c464369a2e7d84955e0a"}], "externalIds": {"DOI": "10.3389/fnana.2021.746057", "PubMed": "34744642", "CorpusId": 239029664, "PubMedCentral": "8564400"}, "isOpenAccess": true, "citationCount": 5, "fieldsOfStudy": ["Medicine"], "openAccessPdf": {"url": "https://www.frontiersin.org/articles/10.3389/fnana.2021.746057/pdf", "status": "GOLD"}, "citationStyles": {"bibtex": "@[''JournalArticle'']{Kim2021InterpretingTE,\n author = {June Hoan Kim and J. Ryu and Boram Lee and Uikyu Chae and J. W. Son and B. Park and Il‐Joo Cho and Woong Sun},\n booktitle = {Frontiers in Neuroanatomy},\n journal = {Frontiers in Neuroanatomy},\n title = {Interpreting the Entire Connectivity of Individual Neurons in Micropatterned Neural Culture With an Integrated Connectome Analyzer of a Neuronal Network (iCANN)},\n volume = {15},\n year = {2021}\n}\n"}, "referenceCount": 67, "publicationDate": "2021-10-20", "s2FieldsOfStudy": [{"source": "external", "category": "Medicine"}, {"source": "s2-fos-model", "category": "Biology"}], "publicationTypes": ["JournalArticle"], "publicationVenue": {"id": "4b5e3ed8-7db8-4ce9-b981-83290b44ccf5", "url": "http://www.expert-reviews.com/loi/epr", "issn": "1662-5129", "name": "Frontiers in Neuroanatomy", "type": "journal", "alternate_urls": ["http://www.frontiersin.org/neuroanatomy/", "https://www.frontiersin.org/journals/neuroanatomy"], "alternate_names": ["Front Neuroanat"]}, "influentialCitationCount": 0}', '2023-01-27 11:03:38.121990', '2023-01-27 11:03:38.121990', 'Interpreting the Entire Connectivity of Individual Neurons in Micropatterned Neural Culture With an Integrated Connectome Analyzer of a Neuronal Network (iCANN)', 2021, false, null, null, null, null);
