INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('f633f8e7a5bce87d6e0833b7312edeefee913fc4', '{"url": "https://www.semanticscholar.org/paper/f633f8e7a5bce87d6e0833b7312edeefee913fc4", "year": 2021, "title": "Reducing Manual Operation Time to Obtain a Segmentation Learning Model for Volume Electron Microscopy Using Stepwise Deep Learning With Manual Correction.", "venue": "Microscopy", "authors": [{"name": "K. Konishi", "authorId": "2059947457"}, {"name": "T. Nonaka", "authorId": "2052109202"}, {"name": "Shunsuke Takei", "authorId": "49102018"}, {"name": "K. Ohta", "authorId": "51253962"}, {"name": "H. Nishioka", "authorId": "50101296"}, {"name": "M. Suga", "authorId": "35017434"}], "journal": {"name": "Microscopy"}, "paperId": "f633f8e7a5bce87d6e0833b7312edeefee913fc4", "abstract": "Three-dimensional (3D) observation of a biological sample using serial-section electron microscopy is widely used. However, organelle segmentation requires a significant amount of manual time. Therefore, several studies have been conducted to improve their efficiency. One such promising method is 3D deep learning (DL), which is highly accurate. However, the creation of training data for 3D DL still requires manual time and effort. In this study, we developed a highly efficient integrated image segmentation tool that includes stepwise DL with manual correction. The tool has four functions: efficient tracers for annotation, model training/inference for organelle segmentation using a lightweight convolutional neural network, efficient proofreading, and model refinement. We applied this tool to increase the training data step by step (stepwise annotation method) to segment the mitochondria in the cells of the cerebral cortex. We found that the stepwise annotation method reduced the manual operation time by one-third compared with that of the fully manual method, where all the training data were created manually. Moreover, we demonstrated that the F1 score, the metric of segmentation accuracy, was 0.9 by training the 3D DL model with these training data. The stepwise annotation method using this tool and the 3D DL model improved the segmentation efficiency for various organelles.", "citations": [{"title": "An annotated instance segmentation XXL-CT dataset from a historic airplane", "paperId": "0f582a978936969369871f6053e5f07c091e6f05"}, {"title": "Folded, undulating, and fibrous doxorubicin sulfate crystals in liposomes.", "paperId": "e76cda131e463f5a76ea620b422d67361f46e3f0"}, {"title": "Morphomics via Next-generation Electron Microscopy", "paperId": "aed0ad5b1eadfbad9a3c6ef84dd7a400bcb9261b"}], "externalIds": {"DOI": "10.1093/jmicro/dfab025", "PubMed": "34259875", "CorpusId": 235821767}, "isOpenAccess": false, "citationCount": 3, "fieldsOfStudy": ["Medicine"], "openAccessPdf": null, "citationStyles": {"bibtex": "@[''JournalArticle'']{Konishi2021ReducingMO,\n author = {K. Konishi and T. Nonaka and Shunsuke Takei and K. Ohta and H. Nishioka and M. Suga},\n booktitle = {Microscopy},\n journal = {Microscopy},\n title = {Reducing Manual Operation Time to Obtain a Segmentation Learning Model for Volume Electron Microscopy Using Stepwise Deep Learning With Manual Correction.},\n year = {2021}\n}\n"}, "referenceCount": 52, "publicationDate": "2021-07-14", "s2FieldsOfStudy": [{"source": "external", "category": "Medicine"}, {"source": "s2-fos-model", "category": "Biology"}], "publicationTypes": ["JournalArticle"], "publicationVenue": {"id": "9bcabe14-4f34-4ee4-bbe0-a83fb05cb370", "url": "http://jmicro.oxfordjournals.org/", "issn": "2050-5698", "name": "Microscopy", "alternate_names": ["Microscopy"]}, "influentialCitationCount": 0}', '2023-01-27 11:03:45.467492', '2023-01-27 11:03:45.467492', 'Reducing Manual Operation Time to Obtain a Segmentation Learning Model for Volume Electron Microscopy Using Stepwise Deep Learning With Manual Correction.', 2021, false, null, null, null, null);
INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('2cba1d4a2fae50cfe585aca027f41ec40d72068b', '{"url": "https://www.semanticscholar.org/paper/2cba1d4a2fae50cfe585aca027f41ec40d72068b", "year": 2021, "title": "Large-scale image segmentation based on distributed clustering algorithms", "venue": "ArXiv", "authors": [{"name": "Ran Lu", "authorId": "1406193129"}, {"name": "A. Zlateski", "authorId": "3134596"}, {"name": "H. Seung", "authorId": "144924970"}], "journal": {"name": "ArXiv", "volume": "abs/2106.10795"}, "paperId": "2cba1d4a2fae50cfe585aca027f41ec40d72068b", "abstract": "Many approaches to 3D image segmentation are based on hierarchical clustering of supervoxels into image regions. Here we describe a distributed algorithm capable of handling a tremendous number of supervoxels. The algorithm works recursively, the regions are divided into chunks that are processed independently in parallel by multiple workers. At each round of the recursive procedure, the chunk size in all dimensions are doubled until a single chunk encompasses the entire image. The final result is provably independent of the chunking scheme, and the same as if the entire image were processed without division into chunks. This is nontrivial because a pair of adjacent regions is scored by some statistical property (e.g. mean or median) of the affinities at the interface, and the interface may extend over arbitrarily many chunks. The trick is to delay merge decisions for regions that touch chunk boundaries, and only complete them in a later round after the regions are fully contained within a chunk. We demonstrate the algorithm by clustering an affinity graph with over 1.5 trillion edges between 135 billion supervoxels derived from a 3D electron microscopic brain image.", "citations": [{"title": "Petascale neural circuit reconstruction: automated methods", "paperId": "ce7b1bfa25a169d5186ef4a4b458c692a06e11db"}, {"title": "Functional connectomics spanning multiple areas of mouse visual cortex", "paperId": "f0496f38817670b66bd19292842e61272d3b75c0"}], "externalIds": {"DBLP": "journals/corr/abs-2106-10795", "ArXiv": "2106.10795", "CorpusId": 235490538}, "isOpenAccess": false, "citationCount": 2, "fieldsOfStudy": ["Computer Science"], "openAccessPdf": null, "citationStyles": {"bibtex": "@[''JournalArticle'']{Lu2021Large-scaleIS,\n author = {Ran Lu and A. Zlateski and H. Seung},\n booktitle = {ArXiv},\n journal = {ArXiv},\n title = {Large-scale image segmentation based on distributed clustering algorithms},\n volume = {abs/2106.10795},\n year = {2021}\n}\n"}, "referenceCount": 18, "publicationDate": "2021-06-21", "s2FieldsOfStudy": [{"source": "external", "category": "Computer Science"}, {"source": "s2-fos-model", "category": "Computer Science"}], "publicationTypes": ["JournalArticle"], "publicationVenue": null, "influentialCitationCount": 0}', '2023-01-27 11:03:49.280035', '2023-01-27 11:03:49.280035', 'Large-scale image segmentation based on distributed clustering algorithms', 2021, false, null, null, null, null);
INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('9bac460e2b4934b61b7768ed33c2943bb60319ba', '{"url": "https://www.semanticscholar.org/paper/9bac460e2b4934b61b7768ed33c2943bb60319ba", "year": 2021, "title": "The Role of Hub Neurons in Modulating Cortical Dynamics", "venue": "bioRxiv", "authors": [{"name": "Eyal Gal", "authorId": "47529481"}, {"name": "Oren Amsalem", "authorId": "3789206"}, {"name": "Alon Schindel", "authorId": "2111688225"}, {"name": "M. London", "authorId": "144133398"}, {"name": "F. Schürmann", "authorId": "2984713"}, {"name": "H. Markram", "authorId": "1754307"}, {"name": "I. Segev", "authorId": "1483614387"}], "journal": {"name": "Frontiers in Neural Circuits", "volume": "15"}, "paperId": "9bac460e2b4934b61b7768ed33c2943bb60319ba", "abstract": "Many neurodegenerative diseases are associated with the death of specific neuron types in particular brain regions. What makes the death of specific neuron types particularly harmful for the integrity and dynamics of the respective network is not well understood. To start addressing this question we used the most up-to-date biologically-realistic dense neocortical microcircuit (NMC) of rodent, which has reconstructed a volume of 0.3 mm3 and containing 31,000 neurons, 36 million synapses, and 55 morphological cell types arranged in 6 cortical layers. Using modern network science tools, we identified “hub-neurons” in the NMC, that are connected synaptically to a large number of their neighbors and systematically examined the impact of abolishing these cells. In general, the structural integrity of the network is robust to cells’ attack; yet, attacking hub neurons strongly impacted the “small worldness” topology of the network, whereas similar attacks on random neurons have a negligible effect. Such hub-specific attacks are also impactful on the network dynamics, both when the network is at its spontaneous synchronous state and when it was presented with synchronized thalamo-cortical visual-like input. We found that attacking layer 5 hub neurons are most harmful to the structural and functional integrity of the NMC. The significance of our results for understanding the role of specific neuron types and cortical layers for disease manifestation is discussed.", "citations": [{"title": "A detailed input-output characterization of single neurons reveals the synaptic basis of spontaneous spiking in recurrent networks", "paperId": "50d41aa14653ff7ebcfbb66528e3e08b1194ee0b"}, {"title": "Generalization of generative model for neuronal ensemble inference method", "paperId": "5e8865b74413920ddaf480a40a13507de7d330cf"}, {"title": "Modeling and Simulation of Rat Non-Barrel Somatosensory Cortex. Part I: Modeling Anatomy", "paperId": "19992023c5578bc5a90ca8e4cf14e85a9b444ddc"}, {"title": "Gestational immune activation disrupts hypothalamic neurocircuits of maternal care behavior", "paperId": "6068bd0b8bc0cabffe9b4beec7fa23cd831a1957"}, {"title": "“Older circuits get richer”: neurogenesis timeline shapes hubness and neurogenetic profiles of the adult human brain", "paperId": "de540a8678492680c77ae440dd922161da0c2814"}, {"title": "Unraveling Brain Microcircuits, Dendritic Spines, and Synaptic Processing Using Multiple Complementary Approaches", "paperId": "4187fd788fce511b050fe26ae26ed923b531fbbb"}], "externalIds": {"DOI": "10.3389/fncir.2021.718270", "PubMed": "34630046", "CorpusId": 235418803, "PubMedCentral": "8500625"}, "isOpenAccess": true, "citationCount": 6, "fieldsOfStudy": ["Biology", "Medicine"], "openAccessPdf": {"url": "https://www.frontiersin.org/articles/10.3389/fncir.2021.718270/pdf", "status": "GOLD"}, "citationStyles": {"bibtex": "@[''JournalArticle'']{Gal2021TheRO,\n author = {Eyal Gal and Oren Amsalem and Alon Schindel and M. London and F. Schürmann and H. Markram and I. Segev},\n booktitle = {bioRxiv},\n journal = {Frontiers in Neural Circuits},\n title = {The 