INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('ac0959a053405a78826ac7c8f7d6d00a6337d50c', '{"url": "https://www.semanticscholar.org/paper/ac0959a053405a78826ac7c8f7d6d00a6337d50c", "year": 2021, "title": "3D neuronal mitochondrial morphology in axons, dendrites, and somata of the aging mouse hippocampus", "venue": "bioRxiv", "authors": [{"name": "J. Faitg", "authorId": "30021043"}, {"name": "Clay Lacefield", "authorId": "3981893"}, {"name": "T. Davey", "authorId": "48082451"}, {"name": "K. White", "authorId": "50311791"}, {"name": "Ross Laws", "authorId": "2051743078"}, {"name": "Stylianos Kosmidis", "authorId": "47775560"}, {"name": "A. Reeve", "authorId": "39629892"}, {"name": "E. Kandel", "authorId": "5177815"}, {"name": "A. Vincent", "authorId": "33339838"}, {"name": "M. Picard", "authorId": "2137392"}], "journal": {"name": "Cell reports", "pages": "109509 - 109509", "volume": "36"}, "paperId": "ac0959a053405a78826ac7c8f7d6d00a6337d50c", "abstract": "The brain’s ability to process complex informations relies on the constant supply of energy through aerobic respiration by mitochondria. Neurons contain three anatomically distinct compartments – the soma, dendrites, and projecting axons – which have different energetic and biochemical requirements, as well as different mitochondrial morphologies in cultured systems. Here we apply a quantitative three-dimensional electron microscopy approach to map mitochondrial network morphology and complexity in the mouse brain. We examine three neuronal sub-compartments – the soma, dendrites, myelinated axons – in the dentate gyrus and CA1 of the mouse hippocampus, two subregions with distinct principal cell types and functions. We also establish compartment-specific differences in mitochondrial morphology across these cell types between young and old mice, highlighting differences in age-related morphological recalibrati