INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('67fdfa3824e0a2a2f7e63162795a8be2c466b5fe', '{"url": "https://www.semanticscholar.org/paper/67fdfa3824e0a2a2f7e63162795a8be2c466b5fe", "year": 2020, "title": "Rethinking brain-wide interactions through multi-region ‘network of networks’ models", "venue": "Current Opinion in Neurobiology", "authors": [{"name": "M. Perich", "authorId": "5228640"}, {"name": "Kanaka Rajan", "authorId": "36560753"}], "journal": {"name": "Current Opinion in Neurobiology", "pages": "146-151", "volume": "65"}, "paperId": "67fdfa3824e0a2a2f7e63162795a8be2c466b5fe", "abstract": "The neural control of behavior is distributed across many functionally and anatomically distinct brain regions even in small nervous systems. While classical neuroscience models treated these regions as a set of hierarchically isolated nodes, the brain comprises a recurrently interconnected network in which each region is intimately modulated by many others. Uncovering these interactions is now possible through experimental techniques that access large neural populations from many brain regions simultaneously. Harnessing these large-scale datasets, however, requires new theoretical approaches. Here, we review recent work to understand brain-wide interactions using multi-region ''network of networks'' models and discuss how they can guide future experiments. We also emphasize the importance of multi-region recordings, and posit that studying individual components in isolation will be insufficient to understand the neural basis of behavior.", "citations": [{"title": "Whole-brain tracking of cocaine and sugar rewards processing", "paperId": "426596a582ebeb61ac533e20dd6c531ec5c0d244"}, {"title": "Distributed context-dependent choice information in mouse posterior cortex", "paperId": "60a27e7ef2ea9d9dae3593f623bda72c04d7228c"}, {"title": "A new theoretical framework jointly explains behavioral and neural variability across subjects performing flexible decision-making", "paperId": "e6d36fd72e3f40a3288c5e63e38c26eb272d66ce"}, {"title": "Flexible selection of task-relevant features through population gating", "paperId": "5cb7a877580c981ec4836e95bbb74d02eac0eee1"}, {"title": "Feedback-based motor control can guide plasticity and drive rapid learning", "paperId": "af16737604802fcfbcc8e3aaef48e0e088f05475"}, {"title": "Multiregion neuronal activity: the forest and the trees", "paperId": "95271faa8bc9cb9eb1e7bfc9addc30f78c651d11"}, {"title": "Preserved neural population dynamics across animals performing similar behaviour", "paperId": "3e7b73c13fa56c7873cb72c5f3883fc5054f81d1"}, {"title": "Seeing the forest and the tree: Building representations of both individual and collective dynamics with transformers", "paperId": "323c220fb19295e0ada03d7c0f7975e5c97db749"}, {"title": "Neural mechanisms of persistent aggression", "paperId": "1cf78bca74ba15bb021547cd27e817c40ded7b9f"}, {"title": "Regenerated iron: How important are different zooplankton groups to oceanic productivity?", "paperId": "d061a82673831921c4858cec971c40413373a5a4"}, {"title": "Efficient and robust multi-task learning in the brain with modular latent primitives", "paperId": "b1b9f680da032028eff377e2cda6e24538ba6381"}, {"title": "A brain basis of dynamical intelligence for AI and computational neuroscience", "paperId": "0d68a7305c454b78e3f72b890549d5186f1b25d7"}, {"title": "Characterizing off-target corticospinal responses to double-cone transcranial magnetic stimulation", "paperId": "a1e0c6f2cf7d6ee3f0285b7a800d3dda387339d9"}, {"title": "Motor cortex activity across movement speeds is predicted by network-level strategies for generating muscle activity", "paperId": "b252944c9a217af93738c175f75432ec6a959619"}, {"title": "Efficient and robust multi-task learning in the brain with modular task primitives", "paperId": "d8d225d5f110fe79a1c314a368b1b244f4e44803"}, {"title": "Inferring brain-wide interactions using data-constrained recurrent neural network models", "paperId": "baca7b0c09335ec73c9dd8d8ac97ea85998b0266"}], "externalIds": {"DOI": "10.31219/osf.io/58qwj", "MAG": "3108014229", "PubMed": "33254073", "CorpusId": 227180672}, "isOpenAccess": false, "citationCount": 25, "fieldsOfStudy": ["Computer Science", "Medicine"], "openAccessPdf": null, "citationStyles": {"bibtex": "@[''Review'', ''JournalArticle'']{Perich2020RethinkingBI,\n author = {M. Perich and Kanaka Rajan},\n booktitle = {Current Opinion in Neurobiology},\n journal = {Current Opinion in Neurobiology},\n pages = {146-151},\n title = {Rethinking brain-wide interactions through multi-region ‘network of networks’ models},\n volume = {65},\n year = {2020}\n}\n"}, "referenceCount": 60, "publicationDate": "2020-11-13", "s2FieldsOfStudy": [{"source": "external", "category": "Computer Science"}, {"source": "external", "category": "Medicine"}, {"source": "s2-fos-model", "category": "Biology"}, {"source": "s2-fos-model", "category": "Psychology"}], "publicationTypes": ["Review", "JournalArticle"], "publicationVenue": {"id": "d0db851a-b0a0-41e2-bef3-3861d0389a5b", "url": "http://www.current-opinion.com/journals/current-opinion-in-neurobiology/", "issn": "0959-4388", "name": "Current Opinion in Neurobiology", "type": "journal", "alternate_urls": ["http://www.sciencedirect.com/science/journal/09594388", "https://www.journals.elsevier.com/current-opinion-in-neurobiology"], "alternate_names": ["Curr Opin Neurobiol"]}, "influentialCitationCount": 0}', '2023-01-27 11:04:14.746295', '2023-01-27 11:04:14.746295', 'Rethinking brain-wide interactions through multi-region ‘network of networks’ models', 2020, false, null, null, null, null);
INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('65baf3bc82b41b65913ee1ce3ba717b4dc56cd6a', '{"url": "https://www.semanticscholar.org/paper/65baf3bc82b41b65913ee1ce3ba717b4dc56cd6a", "year": 2020, "title": "Primate neuronal connections are sparse as compared to mouse", "venue": "bioRxiv", "authors": [{"name": "G.A. Wildenberg", "authorId": "1970495370"}, {"name": "M. Rosén", "authorId": "1970883309"}, {"name": "J. Lundell", "authorId": "40564847"}, {"name": "D. Paukner", "authorId": "103648610"}, {"name": "D. Freedman", "authorId": "39414845"}, {"name": "N. Kasthuri", "authorId": "48299334"}], "journal": {"name": "bioRxiv"}, "paperId": "65baf3bc82b41b65913ee1ce3ba717b4dc56cd6a", "abstract": "Detailing how primate and mouse neurons differ is critical to understanding how brains evolve and for translating findings from mice into humans. However, scarce data about neuronal connections, particularly in primates, makes such comparisons difficult. We use large volume electron microscopy to address this gap, reconstructing 11,415 synapses onto morphologically equivalent adult primate and mouse neurons in Layer 2/3 of primary visual cortex (V1). Neurons from both species distribute in cortex at similar densities, have similar somatic volumes, and dendritic branching patterns, but primate excitatory and inhibitory neurons receive ∼4 times fewer excitatory and inhibitory connections than equivalent mouse neurons. The reduction in excitatory inputs is larger, resulting in a 2-fold decreased ratio of excitatory to inhibitory inputs in primate neurons. Finally, despite reductions in inhibitory synapse number, neighboring excitatory primate neurons are co-innervated by inhibitory axons at rates and proportions similar to mouse neurons. We present a first glimpse of large-scale connectivity comparisons across mouse and primate brains which underscore the need for future analyses since such differences likely represent a substantial part of how mouse and primate brains differ.", "citations": [{"title": "Distributed functions of prefrontal and parietal cortices during sequential categorical decisions", "paperId": "f8a17a034a9b30f6cb1532d98c1a443a4fb571c9"}], "externalIds": {"DOI": "10.1101/2020.09.24.311852", "MAG": "3089247728", "CorpusId": 221952809}, "isOpenAccess": true, "citationCount": 1, "fieldsOfStudy": ["Biology"], "openAccessPdf": {"url": "https://www.biorxiv.org/content/biorxiv/early/2020/12/20/2020.09.24.311852.full.pdf", "status": "GREEN"}, "citationStyles": {"bibtex": "@None{Wildenberg2020PrimateNC,\n author = {G.A. Wildenberg and M. Rosén and J. Lundell and D. Paukner and D. Freedman and N. Kasthuri},\n booktitle = {bioRxiv},\n journal = {bioRxiv},\n title = {Primate neuronal connections are sparse as compared to mouse},\n year = {2020}\n}\n"}, "referenceCount": 109, "publicationDate": "2020-09-24", "s2FieldsOfStudy": [{"source": "external", "category": "Biology"}, {"source": "s2-fos-model", "category": "Biology"}], "publicationTypes": null, "publicationVenue": {"id": "027ffd21-ebb0-4af8-baf5-911124292fd0", "url": "http://biorxiv.org/", "name": "bioRxiv", "type": "journal"}, "influentialCitationCount": 0}', '2023-01-27 11:04:18.359118', '2023-01-27 11:04:18.359118', 'Primate neuronal connections are sparse as compared to mouse', 2020, false, null, null, null, null);
INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('08cd650a07b5d008e5b5d7aaf9c73b4e573f0427', '{"url": "https://www.semanticscholar.org/paper/08cd650a07b5d008e5b5d7aaf9c73b4e573f0427", "year": 2020, "title": "Connectome subgraph isomorphisms and graph queries with DotMotif", "venue": "", "authors": [{"name": "Jordan K. Matelsky", "authorId": "24023618"}, {"name": "E. Reilly", "authorId": "47664576"}, {"name": "Erik C. Johnson", "authorId": "2118693758"}, {"name": "B. Wester", "authorId": "1916423"}, {"name": "William Gray-Roncal", "authorId": "1384052997"}], "journal": {"name": "bioRxiv", "volume": ""}, "paperId": "08cd650a07b5d008e5b5d7aaf9c73b4e573f0427", "abstract": "As connectomics datasets continue to grow in size and complexity, methods to search for and identify interesting graph patterns offer a promising approach to quickly reduce data dimensionality and enable discovery. Recent advances in neuroscience have enabled brain structure exploration at the level of individual synaptic connections. These graphs are often too large to be analyzed manually, presenting significant barriers to searching for structure and testing hypotheses. We combine graph database and analysis libraries with an easy-to-use neuroscience grammar suitable for rapidly constructing queries and searching for subgraphs and patterns of interest. This abstracts many of the computer science and graph theory challenges associated with nanoscale brain network analysis and allows scientists to quickly achieve reproducible findings at scale. We demonstrate these tools to search for motifs on simulated data and real public connectomics datasets, and share simple and complex structures relevant to the neuroscience community. We contextualize these results and provide case studies to motivate future neuroscience questions. All of our tools are released open source to empower other scientists to use and extend these methods.", "citations": [], "externalIds": {"DOI": "10.1101/2020.06.08.140533", "MAG": "3034145774", "CorpusId": 219604253}, "isOpenAccess": true, "citationCount": 0, "fieldsOfStudy": ["Computer Science"], "openAccessPdf": {"url": "https://www.nature.com/articles/s41598-021-91025-5.pdf", "status": "GREEN"}, "citationStyles": {"bibtex": "@None{Matelsky2020ConnectomeSI,\n author = {Jordan K. Matelsky and E. Reilly and Erik C. Johnson and B. Wester and William Gray-Roncal},\n journal = {bioRxiv},\n title = {Connectome subgraph isomorphisms and graph queries with DotMotif},\n year = {2020}\n}\n"}, "referenceCount": 46, "publicationDate": "2020-06-09", "s2FieldsOfStudy": [{"source": "external", "category": "Computer Science"}, {"source": "s2-fos-model", "category": "Computer Science"}], "publicationTypes": null, "publicationVenue": null, "influentialCitationCount": 0}', '2023-01-27 11:04:21.955794', '2023-01-27 11:04:21.955794', 'Connectome subgraph isomorphisms and graph queries with DotMotif', 2020, false, null, null, null, null);
INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('341b290c41f0c5290ce9ed52848df385867a9158', '{"url": "https://www.semanticscholar.org/paper/341b290c41f0c5290ce9ed52848df385867a9158", "year": 2020, "title": "DotMotif: an open-source tool for connectome subgraph isomorphism search and graph queries", "venue": "bioRxiv", "authors": [{"name": "Jordan K. Matelsky", "authorId": "24023618"}, {"name": "E. Reilly", "authorId": "47664576"}, {"name": "Erik C. Johnson", "authorId": "2118693758"}, {"name": "J. Stiso", "authorId": "8792576"}, {"name": "D. Bassett", "authorId": "2264281"}, {"name": "B. Wester", "authorId": "1916423"}, {"name": "William Gray-Roncal", "authorId": "1384052997"}], "journal": {"name": "Scientific Reports", "volume": "11"}, "paperId": "341b290c41f0c5290ce9ed52848df385867a9158", "abstract": "Recent advances in neuroscience have enabled the exploration of brain structure at the level of individual synaptic connections. These connectomics datasets continue to grow in size and complexity; methods to search for and identify interesting graph patterns offer a promising approach to quickly reduce data dimensionality and enable discovery. These graphs are often too large to be analyzed manually, presenting significant barriers to searching for structure and testing hypotheses. We combine graph database and analysis libraries with an easy-to-use neuroscience grammar suitable for rapidly constructing queries and searching for subgraphs and patterns of interest. Our approach abstracts many of the computer science and graph theory challenges associated with nanoscale brain network analysis and allows scientists to quickly conduct research at scale. We demonstrate the utility of these tools by searching for motifs on simulated data and real public connectomics datasets, and we share simple and complex structures relevant to the neuroscience community. We contextualize our findings and provide case studies and software to motivate future neuroscience exploration.", "citations": [{"title": "ViMo Visual Analysis of Neuronal Connectivity Motifs", "paperId": "606622ff5f693ba8ca9d3c72c5765d07fcd141e8"}, {"title": "Cell lineage predicts neural connectivity beyond cell type", "paperId": "d4d848191a6a35ea806810b9057b3697a9ea1e69"}, {"title": "neuPrint: An open access tool for EM connectomics", "paperId": "8dc968cf7c20a294941935bc44b68e131d9e3e58"}, {"title": "Investigating Echo State Network Performance with Biologically-Inspired Hierarchical Network Structure", "paperId": "12c1e98ddbdde57a17c12dc916ff119e10a665d2"}, {"title": "Edge-colored directed subgraph enumeration on the connectome", "paperId": "61b8616ef1ee13b324a876becdc78d3f0c6adb82"}, {"title": "Scalable graph analysis tools for the connectomics community", "paperId": "13ce42428e5b4fa276804cc459256533729ea3ef"}, {"title": "A Survey of Visualization and Analysis in High‐Resolution Connectomics", "paperId": "ebaf6fafb4adb7f3657fdbb869249c727ffccf7e"}, {"title": "Connectomics Annotation Metadata Standardization for Increased Accessibility and Queryability", "paperId": "fbccee9f4998714bb88c01f544acfe48418d9f96"}, {"title": "A Programmable Ontology Encompassing the Functional Logic of the Drosophila Brain", "paperId": "3f2d07badd7850faf948302ad9461f277c373482"}, {"title": "CONFIRMS: A Toolkit for Scalable, Black Box Connectome Assessment and Investigation", "paperId": "8d51d2e27be1df78179f8b2a19fc002f46228408"}, {"title": "Ramsey’s Theory Meets The Human Brain Connectome", "paperId": "180412d2c68d3819a2314f40a40a72e7caa89556"}, {"title": "Circuit motifs and graph properties of connectome development in C. elegans", "paperId": "72073747fb023e3ffc65bb204212e9322b801551"}], "externalIds": {"DOI": "10.1101/2020.06.08.140533", "PubMed": "34158519", "CorpusId": 231676037, "PubMedCentral": "8219732"}, "isOpenAccess": true, "citationCount": 12, "fieldsOfStudy": ["Biology", "Medicine"], "openAccessPdf": {"url": "https://www.nature.com/articles/s41598-021-91025-5.pdf", "status": "GREEN"}, "citationStyles": {"bibtex": "@[''JournalArticle'']{Matelsky2020DotMotif:AO,\n author = {Jordan K. Matelsky and E. Reilly and Erik C. Johnson and J. Stiso and D. Bassett and B. Wester and William Gray-Roncal},\n booktitle = {bioRxiv},\n journal = {Scientific Reports},\n title = {DotMotif: an open-source tool for connectome subgraph isomorphism search and graph queries},\n volume = {11},\n year = {2020}\n}\n"}, "referenceCount": 73, "publicationDate": "2020-06-09", "s2FieldsOfStudy": [{"source": "external", "category": "Biology"}, {"source": "external", "category": "Medicine"}, {"source": "s2-fos-model", "category": "Computer Science"}], "publicationTypes": ["JournalArticle"], "publicationVenue": {"id": "027ffd21-ebb0-4af8-baf5-911124292fd0", "url": "http://biorxiv.org/", "name": "bioRxiv", "type": "journal"}, "influentialCitationCount": 0}', '2023-01-27 11:04:25.557202', '2023-01-27 11:04:25.557202', 'DotMotif: an open-source tool for connectome subgraph isomorphism search and graph queries', 2020, false, null, null, null, null);
INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('f363c5dfd87e06c553d7b70068f0e1f692559efb', '{"url": "https://www.semanticscholar.org/paper/f363c5dfd87e06c553d7b70068f0e1f692559efb", "year": 2019, "title": "Binary and analog variation of synapses between cortical pyramidal neurons", "venue": "bioRxiv", "authors": [{"name": "Sven Dorkenwald", "authorId": "7997808"}, {"name": "Nicholas L. Turner", "authorId": "152772071"}, {"name": "T. Macrina", "authorId": "5056982"}, {"name": "Kisuk Lee", "authorId": "2785521"}, {"name": "Ran Lu", "authorId": "1406193129"}, {"name": "Jingpeng Wu", "authorId": "31421132"}, {"name": "Agnes L. Bodor", "authorId": "40584201"}, {"name": "Adam A. Bleckert", "authorId": "4518441"}, {"name": "Derrick Brittain", "authorId": "145536550"}, {"name": "Nico Kemnitz", "authorId": "41124209"}, {"name": "W. Silversmith", "authorId": "2108734"}, {"name": "Dodam Ih", "authorId": "14972923"}, {"name": "J. Zung", "authorId": "2730700"}, {"name": "A. Zlateski", "authorId": "3134596"}, {"name": "Ignacio Tartavull", "authorId": "24011805"}, {"name": "Szi-chieh Yu", "authorId": "6537565"}, {"name": "Sergiy Popovych", "authorId": "1840375"}, {"name": "William Wong", "authorId": "120774631"}, {"name": "Manuel A. Castro", "authorId": "2092627229"}, {"name": "Chris S. Jordan", "authorId": "41130241"}, {"name": "A. Wilson", "authorId": "1434750166"}, {"name": "E. Froudarakis", "authorId": "1970917"}, {"name": "J. Buchanan", "authorId": "49345106"}, {"name": "Marc M. Takeno", "authorId": "8311185"}, {"name": "Russel Torres", "authorId": "152750629"}, {"name": "Gayathri Mahalingam", "authorId": "144348673"}, {"name": "F. Collman", "authorId": "6598643"}, {"name": "C. Schneider-Mizell", "authorId": "1397852111"}, {"name": "Daniel J. Bumbarger", "authorId": "3988776"}, {"name": "Yang Li", "authorId": "2154898018"}, {"name": "Lynne Becker", "authorId": "2065397046"}, {"name": "S. Suckow", "authorId": "40047816"}, {"name": "Jacob Reimer", "authorId": "48128495"}, {"name": "A. Tolias", "authorId": "1739838"}, {"name": "N. D. da Costa", "authorId": "51079681"}, {"name": "R. Reid", "authorId": "46567235"}, {"name": "H. Seung", "authorId": "144924970"}], "journal": {"name": "eLife", "volume": "11"}, "paperId": "f363c5dfd87e06c553d7b70068f0e1f692559efb", "abstract": "Learning from experience depends at least in part on changes in neuronal connections. We present the largest map of connectivity to date between cortical neurons of a defined type (L2/3 pyramidal cells), which was enabled by automated analysis of serial section electron microscopy images with improved handling of image defects. We used the map to identify constraints on the learning algorithms employed by the cortex. Previous cortical studies modeled a continuum of synapse sizes (Arellano et al., 2007) by a log-normal distribution (Loewenstein, Kuras and Rumpel, 2011; de Vivo et al., 2017; Santuy et al., 2018). A continuum is consistent with most neural network models of learning, in which synaptic strength is a continuously graded analog variable. Here we show that synapse size, when restricted to synapses between L2/3 pyramidal cells, is well-modeled by the sum of a binary variable and an analog variable drawn from a log-normal distribution. Two synapses sharing the same presynaptic and postsynaptic cells are known to be correlated in size (Sorra and Harris, 1993; Koester and Johnston, 2005; Bartol et al., 2015; Kasthuri et al., 2015; Dvorkin and Ziv, 2016; Bloss et al., 2018; Motta et al., 2019). We show that the binary variables of the two synapses are highly correlated, while the analog variables are not. Binary variation could be the outcome of a Hebbian or other synaptic plasticity rule depending on activity signals that are relatively uniform across neuronal arbors, while analog variation may be dominated by other influences. We discuss the implications for the stability-plasticity dilemma.", "citations": [{"title": "RoboEM: automated 3D flight tracing for synaptic-resolution connectomics", "paperId": "6a3cc42c98a6b36c649bb203ec54afd32167ef32"}, {"title": "Ultraliser: a framework for creating multiscale, high-fidelity and geometrically realistic 3D models for in silico neuroscience", "paperId": "bcb8dbbb3b853e5d1cb2255767ba3db925991d07"}, {"title": "NeuVue: A Framework and Workflows for High-Throughput Electron Microscopy Connectomics Proofreading", "paperId": "4a65612c4fb70aa425c148169c0805429fac9ac0"}, {"title": "Edge-colored directed subgraph enumeration on the connectome", "paperId": "61b8616ef1ee13b324a876becdc78d3f0c6adb82"}, {"title": "Uncovering features of synapses in primary visual cortex through contrastive representation learning", "paperId": "b82f01a87fe726d8356f3e44fb9c1ba47d349eac"}, {"title": "Scalable graph analysis tools for the connectomics community", "paperId": "13ce42428e5b4fa276804cc459256533729ea3ef"}, {"title": "A Survey of Visualization and Analysis in High‐Resolution Connectomics", "paperId": "ebaf6fafb4adb7f3657fdbb869249c727ffccf7e"}, {"title": "Heavy–tailed neuronal connectivity arises from Hebbian self–organization", "paperId": "7b89e7a73fe96093ac9f7706c77fd57374ad203a"}, {"title": "Core neurons are structural crossroads of cortical dynamics", "paperId": "0d01f09f185daf4f31f193b4d3121ef654b13062"}, {"title": "Synaptic wiring motifs in posterior parietal cortex support decision-making", "paperId": "ec034cf297461dc3d1949756cc6eb24978bf7a1a"}, {"title": "Scalable Biologically-Aware Skeleton Generation for Connectomic Volumes", "paperId": "fda7a48230f3bab9616733d9d7836ec237a3ed9a"}, {"title": "Finding Needles in a Haystack with Light: Resolving the Microcircuitry of the Brain with Fluorescence Microscopy", "paperId": "63da9d73a81dc3a180cde7c606bcd48efa2985f8"}, {"title": "Public Volume Electron Microscopy Data: An Essential Resource to Study the Brain Microvasculature", "paperId": "21ab250729