INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('a8b3f13957c71a6e95196a643a524561eb98735f', '{"url": "https://www.semanticscholar.org/paper/a8b3f13957c71a6e95196a643a524561eb98735f", "year": 2020, "title": "Molecular characterization of projection neuron subtypes in the mouse olfactory bulb", "venue": "bioRxiv", "authors": [{"name": "S. Zeppilli", "authorId": "2033753016"}, {"name": "Tobias Ackels", "authorId": "3495972"}, {"name": "Robin Attey", "authorId": "1678930623"}, {"name": "Nell Klimpert", "authorId": "2033755342"}, {"name": "K. Ritola", "authorId": "5254282"}, {"name": "S. Boeing", "authorId": "4041891"}, {"name": "Anton Crombach", "authorId": "2453423"}, {"name": "A. Schaefer", "authorId": "40461570"}, {"name": "A. Fleischmann", "authorId": "6343038"}], "journal": {"name": "eLife", "volume": "10"}, "paperId": "a8b3f13957c71a6e95196a643a524561eb98735f", "abstract": "Projection neurons (PNs) in the mammalian olfactory bulb (OB) receive direct input from the nose and project to diverse cortical and subcortical areas. Morphological and physiological studies have highlighted functional heterogeneity, yet no molecular markers have been described that delineate PN subtypes. Here, we used viral injections into olfactory cortex and fluorescent nucleus sorting to enrich PNs for high-throughput single nucleus and bulk RNA deep sequencing. Transcriptome analysis and RNA in situ hybridization identified three mitral and five tufted cell populations with characteristic transcription factor network topology and cell adhesion and excitability-related gene expression. Finally, by integrating bulk and snRNA-seq data we propose that different mitral cell populations selectively project to different regions of olfactory cortex. Together, we have identified potential molecular and gene regulatory mechanisms underlying PN diversity and provide new molecular entry points into studying the diverse functional roles of mitral and tufted cell subtypes.", "citations": [{"title": "Molecular diversity and connectivity of accessory olfactory system neurons", "paperId": "cfc76a0d32007dba406612b361611161d1f98eb9"}, {"title": "High-throughput sequencing of single neuron projections reveals spatial organization in the olfactory cortex", "paperId": "5d06ad748e2502a85af763487f431cfd6750f1ac"}, {"title": "Anatomical and Functional Connectivity at the Dendrodendritic Reciprocal Mitral Cell–Granule Cell Synapse: Impact on Recurrent and Lateral Inhibition", "paperId": "278ca2ed9bdea47c6f7bfa40e187cdee3cb7fb75"}, {"title": "Coupling of Mouse Olfactory Bulb Projection Neurons to Fluctuating Odor Pulses", "paperId": "033b3604d2dffe43b68e429fa4d245b2879deb51"}, {"title": "Development of the mammalian main olfactory bulb", "paperId": "c653c60ab8292bd5d485c1c5248a15f9d87e9df1"}, {"title": "Generation and Characterization of a Cell Type-Specific, Inducible Cre-Driver Line to Study Olfactory Processing", "paperId": "6cc7df573084201b1bfd779ff3c46c2ccde57de3"}, {"title": "Wiring logic of the early rodent olfactory system revealed by high-throughput sequencing of single neuron projections", "paperId": "277cb14663998a4a26724f42d5372ebeaec3403a"}, {"title": "Transcriptome analysis for the development of cell-type specific labeling to study olfactory circuits", "paperId": "49907af1c036b47fed1a85cefe4e0787f9a137ac"}, {"title": "Coupling of mouse olfactory bulb projection neurons to fluctuating odour pulses", "paperId": "15ee27b55c5a444f6afbf784100b4334ea7ccfc0"}], "externalIds": {"DOI": "10.1101/2020.11.30.405571", "MAG": "3108573622", "PubMed": "34292150", "CorpusId": 227912407, "PubMedCentral": "8352594"}, "isOpenAccess": false, "citationCount": 9, "fieldsOfStudy": ["Biology", "Medicine"], "openAccessPdf": null, "citationStyles": {"bibtex": "@[''JournalArticle'']{Zeppilli2020MolecularCO,\n author = {S. Zeppilli and Tobias Ackels and Robin Attey and Nell Klimpert a