INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('3b86f55306a056ba1c1d06aeab0c9c3925f5eaa2', '{"url": "https://www.semanticscholar.org/paper/3b86f55306a056ba1c1d06aeab0c9c3925f5eaa2", "year": 2022, "title": "Cerebellum lineage allocation, morphogenesis and repair: impact of interplay amongst cells.", "venue": "Development", "authors": [{"name": "A. Joyner", "authorId": "2210060"}, {"name": "N. S. Bayin", "authorId": "6192292"}], "journal": {"name": "Development", "volume": "149 18"}, "paperId": "3b86f55306a056ba1c1d06aeab0c9c3925f5eaa2", "abstract": "The cerebellum has a simple cytoarchitecture consisting of a folded cortex with three cell layers that surrounds a nuclear structure housing the output neurons. The excitatory neurons are generated from a unique progenitor zone, the rhombic lip, whereas the inhibitory neurons and astrocytes are generated from the ventricular zone. The growth phase of the cerebellum is driven by lineage-restricted progenitor populations derived from each zone. Research during the past decade has uncovered the importance of cell-to-cell communication between the lineages through largely unknown signaling mechanisms for regulating the scaling of cell numbers and cell plasticity during mouse development and following injury in the neonatal (P0-P14) cerebellum. This Review focuses on how the interplay between cell types is key to morphogenesis, production of robust neural circuits and replenishment of cells after injury, and ends with a discussion of the implications of the greater complexity of the human cerebellar progenitor zones for development and disease.", "citations": [], "externalIds": {"DOI": "10.1242/dev.185587", "PubMed": "36172987", "CorpusId": 252597345}, "isOpenAccess": false, "citationCount": 0, "fieldsOfStudy": ["Medicine"], "openAccessPdf": null, "citationStyles": {"bibtex": "@[''JournalArticle'', ''Review'']{Joyner2022CerebellumLA,\n author = {A. Joyner and N. S. Bayin},\n booktitle = {Development},\n journal = {Development},\n title = {Cerebellum lineage allocation, morphogenesis and repair: impact of interplay amongst cells.},\n volume = {149 18},\n year = {2022}\n}\n"}, "referenceCount": 119, "publicationDate": "2022-09-15", "s2FieldsOfStudy": [{"source": "external", "category": "Medicine"}, {"source": "s2-fos-model", "category": "Biology"}], "publicationTypes": ["JournalArticle", "Review"], "publicationVenue": {"id": "179847ef-8e79-48e2-99ee-769211d3e729", "url": "https://dev.biologists.org/", "issn": "0950-1991", "name": "Development", "type": "journal", "alternate_urls": ["http://dev.biologists.org/", "http://journals.ecs.soton.ac.uk/journals/dev.html"], "alternate_issns": ["0212-2448"]}, "influentialCitationCount": 0}', '2023-01-27 11:04:43.865209', '2023-01-27 11:04:43.865209', 'Cerebellum lineage allocation, morphogenesis and repair: impact of interplay amongst cells.', 2022, false, null, null, null, null);
