INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('d4a314ea536233572c9658f20b4cfb508edbb1f5', '{"url": "https://www.semanticscholar.org/paper/d4a314ea536233572c9658f20b4cfb508edbb1f5", "year": 2022, "title": "The Cerebellar Cortex.", "venue": "Annual Review of Neuroscience", "authors": [{"name": "Court Hull", "authorId": "46644558"}, {"name": "W. Regehr", "authorId": "5639106"}], "journal": {"name": "Annual review of neuroscience", "pages": "\n          151-175\n        ", "volume": "45"}, "paperId": "d4a314ea536233572c9658f20b4cfb508edbb1f5", "abstract": "The cerebellar cortex is an important system for relating neural circuits and learning. Its promise reflects the longstanding idea that it contains simple, repeated circuit modules with only a few cell types and a single plasticity mechanism that mediates learning according to classical Marr-Albus models. However, emerging data have revealed surprising diversity in neuron types, synaptic connections, and plasticity mechanisms, both locally and regionally within the cerebellar cortex. In light of these findings, it is not surprising that attempts to generate a holistic model of cerebellar learning across different behaviors have not been successful. While the cerebellum remains an ideal system for linking neuronal function with behavior, it is necessary to update the cerebellar circuit framework to achieve its great promise. In this review, we highlight recent advances in our understanding of cerebellar-cortical cell types, synaptic connections, signaling mechanisms, and forms of plasticity that enrich cerebellar processing.", "citations": [{"title": "Genetic Variant in GRM1 Underlies Congenital Cerebellar Ataxia with No Obvious Intellectual Disability", "paperId": "9815f6df2685f3fa98cef201b0cbb76d3ce17626"}, {"title": "Feedback inhibition underlies new computational functions of cerebellar interneurons", "paperId": "7dc6468239773bb07cdbc07af9f030c0dc756f32"}], "externalIds": {"DOI": "10.1146/annurev-neuro-091421-125115", "PubMed": "35803588", "CorpusId": 250387943}, "isOpenAccess": false, "citationCount": 2, "fieldsOfStudy": ["Medicine"], "openAccessPdf": null, "citationStyles": {"bibtex": "@[''Review'', ''JournalArticle'']{Hull2022TheCC,\n author = {Court Hull and W. Regehr},\n booktitle = {Annual Review of Neuroscience},\n journal = {Annual review of neuroscience},\n pages = {\n          151-175\n        },\n title = {The Cerebellar Cortex.},\n volume = {45},\n year = {2022}\n}\n"}, "referenceCount": 130, "publicationDate": "2022-07-08", "s2FieldsOfStudy": [{"source": "external", "category": "Medicine"}, {"source": "s2-fos-model", "category": "Biology"}], "publicationTypes": ["Review", "JournalArticle"], "publicationVenue": {"id": "d9caa671-3be6-48bc-9710-f96334848b4c", "url": "https://www.annualreviews.org/journal/neuro", "issn": "0147-006X", "name": "Annual Review of Neuroscience", "type": "journal", "alternate_urls": ["http://arjournals.annualreviews.org/loi/neuro", "https://www.annualreviews.org/loi/neuro"], "alternate_names": ["Annu Rev Neurosci"]}, "influentialCitationCount": 0}', '2023-01-27 11:04:51.276944', '2023-01-27 11:04:51.276944', 'The Cerebellar Cortex.', 2022, false, null, null, null, null);
INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('5c21b73269e7e9ca5d7aecaf7dc24d4ce4be7b70', '{"url": "https://www.semanticscholar.org/paper/5c21b73269e7e9ca5d7aecaf7dc24d4ce4be7b70", "year": 2022, "title": "Cerebellum Directly Modulates the Substantia Nigra Dopaminergic Activity", "venue": "bioRxiv", "authors": [{"name": "Samantha Washburn", "authorId": "1411743137"}, {"name": "M. Oñate", "authorId": "10182105"}, {"name": "Junichi Yoshida", "authorId": "2060468397"}, {"name": "Jorge Vera", "authorId": "2057738975"}, {"name": "R. K. B.", "authorId": "2166065513"}, {"name": "L. Khatami", "authorId": "2077213267"}, {"name": "F. Nadim", "authorId": "1787090"}, {"name": "K. Khodakhah", "authorId": "5266454"}], "journal": {"name": "bioRxiv"}, "paperId": "5c21b73269e7e9ca5d7aecaf7dc24d4ce4be7b70", "abstract": "Evidence of direct reciprocal connections between the cerebellum and basal ganglia has challenged the long-held notion that these structures function independently. While anatomical studies have suggested the presence of cerebellar projections to the substantia nigra pars compacta (SNc), the nature and function of these connections (Cb-SNc) is unknown. Here we show that the Cb-SNc form monosynaptic glutamatergic synapses with both dopaminergic and non-dopaminergic neurons in the SNc. Optogenetic activation Cb-SNc axons in the SNc rapidly increases SNc activity, elevates striatal dopamine levels, and increases the probability of locomotion. During ongoing behavior, Cb-SNc axons are bilaterally activated prior to ambulation and unilateral lever manipulation. The Cb-SNc axons show prominent activation to water reward, and higher activation for sweet water, suggesting that the pathway also encodes reward value. Thus, the cerebellum directly, rapidly, and effectively modulates basal ganglia dopamine levels and conveys information related to movement initiation, vigor, and possibly reward processing.", "citations": [], "externalIds": {"DOI": "10.1101/2022.05.20.492532", "CorpusId": 248990766}, "isOpenAccess": true, "citationCount": 0, "fieldsOfStudy": ["Biology"], "openAccessPdf": {"url": "https://www.biorxiv.org/content/biorxiv/early/2022/05/22/2022.05.20.492532.full.pdf", "status": "GREEN"}, "citationStyles": {"bibtex": "@None{Washburn2022CerebellumDM,\n author = {Samantha Washburn and M. Oñate and Junichi Yoshida and Jorge Vera and R. K. B. and L. Khatami and F. Nadim and K. Khodakhah},\n booktitle = {bioRxiv},\n journal = {bioRxiv},\n title = {Cerebellum Directly Modulates the Substantia Nigra Dopaminergic Activity},\n year = {2022}\n}\n"}, "referenceCount": 116, "publicationDate": "2022-05-22", "s2FieldsOfStudy": [{"source": "external", "category": "Biology"}, {"source": "s2-fos-model", "category": "Biology"}, {"source": "s2-fos-model", "category": "Psychology"}], "publicationTypes": null, "publicationVenue": {"id": "027ffd21-ebb0-4af8-baf5-911124292fd0", "url": "http://biorxiv.org/", "name": "bioRxiv", "type": "journal"}, "influentialCitationCount": 0}', '2023-01-27 11:04:54.934955', '2023-01-27 11:04:54.934955', 'Cerebellum Directly Modulates the Substantia Nigra Dopaminergic Activity', 2022, false, null, null, null, null);
