INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('0798df3023e149b98839ebc3bd4cd12d38975def', '{"url": "https://www.semanticscholar.org/paper/0798df3023e149b98839ebc3bd4cd12d38975def", "year": 2022, "title": "Novel Cerebello-Amygdala Connections Provide Missing Link Between Cerebellum and Limbic System", "venue": "bioRxiv", "authors": [{"name": "Se Jung Jung", "authorId": "4400134"}, {"name": "Ksenia Y. Vlasov", "authorId": "143912649"}, {"name": "Alexa F. D’Ambra", "authorId": "1796314393"}, {"name": "Abhijna Parigi", "authorId": "48839107"}, {"name": "Mihir Baya", "authorId": "2153765601"}, {"name": "Edbertt Paul Frez", "authorId": "2153765430"}, {"name": "Jacqueline Villalobos", "authorId": "2153763374"}, {"name": "Marina Fernandez-Frentzel", "authorId": "2153853715"}, {"name": "M. Anguiano", "authorId": "115530202"}, {"name": "Yoichiro Ideguchi", "authorId": "1412407565"}, {"name": "E. G. Antzoulatos", "authorId": "5870358"}, {"name": "Diasynou Fioravante", "authorId": "113211717"}], "journal": {"name": "Frontiers in Systems Neuroscience", "volume": "16"}, "paperId": "0798df3023e149b98839ebc3bd4cd12d38975def", "abstract": "The cerebellum is emerging as a powerful regulator of cognitive and affective processing and memory in both humans and animals and has been implicated in affective disorders. How the cerebellum supports affective function remains poorly understood. The short-latency (just a few ms) functional connections that were identified between the cerebellum and amygdala -a structure crucial for the processing of emotion and valence-more than 4 decades ago raise the exciting, yet untested, possibility that a cerebellum-amygdala pathway communicates information important for emotion. The major hurdle in rigorously testing this possibility is the lack of knowledge about the anatomy and functional connectivity of this pathway. Our initial anatomical tracing studies in mice excluded the existence of a direct monosynaptic pathway between cerebellum and amygdala. Using transneuronal tracing techniques, we have identified a novel disynaptic pathway that connects the cerebellar output nuclei to the basolateral amygdala. This pathway recruits the understudied intralaminar thalamus as a node. Using ex vivo optophysiology and super-resolution microscopy, we provide the first evidence for the functionality of the pathway, thus offering a missing mechanistic link between the cerebellum and amygdala. This discovery provides a connectivity blueprint between the cerebellum and a key structure of the limbic system. As such, it is the requisite first step toward obtaining new knowledge about cerebellar function in emotion, thus fundamentally advancing understanding of the neurobiology of emotion, which is perturbed in mental and autism spectrum disorders.", "citations": [{"title": "cATR Tracing Approach to Identify Individual Intermediary Neurons Based on Their Input and Output: A Proof-of-Concept Study Connecting Cerebellum and Central Hubs Implicated in Developmental Disorders", "paperId": "41a37ad489e41dfe492f01334f9d868c5e3c8426"}, {"title": "The Cerebellum as an Embodying Machine.", "paperId": "5322253be27b1410b6e626d2bdebd4654b6ee2cf"}, {"title": "A Purkinje cell to parabrachial nucleus pathway enables broad cerebellar influence over the forebrain", "paperId": "0b719fd1a456adb796afab66e9268be2a314ee44"}], "externalIds": {"DOI": "10.3389/fnsys.2022.879634", "PubMed": "35645738", "CorpusId": 246663656, "PubMedCentral": "9136059"}, "isOpenAccess": true, "citationCount": 3, "fieldsOfStudy": ["Medicine", "Biology"], "openAccessPdf": {"url": "https://www.frontiersin.org/articles/10.3389/fnsys.2022.879634/pdf", "status": "GOLD"}, "citationStyles": {"bibtex": "@[''JournalArticle'']{Jung2022NovelCC,\n author = {Se Jung Jung and Ksenia Y. Vlasov and Alexa F. D’Ambra and Abhijna Parigi and Mihir Baya and Edbertt Paul Frez and Jacqueline Villalobos and Marina Fernandez-Frentzel and M. Anguiano and Yoichiro Ideguchi and E. G. Antzoulatos and Diasynou Fioravante},\n booktitle = {bioRxiv},\n journal = {Frontiers in Systems Neuroscience},\n title = {Novel Cerebello-Amygdala Connections Provide Missing Link Between Cerebellum and Limbic System},\n volume = {16},\n year = {2022}\n}\n"}, "referenceCount": 157, "publicationDate": "2022-02-07", "s2FieldsOfStudy": [{"source": "external", "category": "Medicine"}, {"source": "external", "category": "Biology"}, {"source": "s2-fos-model", "category": "Biology"}, {"source": "s2-fos-model", "category": "Psychology"}], "publicationTypes": ["JournalArticle"], "publicationVenue": {"id": "027ffd21-ebb0-4af8-baf5-911124292fd0", "url": "http://biorxiv.org/", "name": "bioRxiv", "type": "journal"}, "influentialCitationCount": 0}', '2023-01-27 11:05:02.238199', '2023-01-27 11:05:02.238199', 'Novel Cerebello-Amygdala Connections Provide Missing Link Between Cerebellum and Limbic System', 2022, false, null, null, null, null);
INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('b3d7c373fb6e34c2793597d42889cf2b6105aaf5', '{"url": "https://www.semanticscholar.org/paper/b3d7c373fb6e34c2793597d42889cf2b6105aaf5", "year": 2021, "title": "The Serendipity of Viral Trans-Neuronal Specificity: More Than Meets the Eye", "venue": "Frontiers in Cellular Neuroscience", "authors": [{"name": "Kevin T. Beier", "authorId": "34686822"}], "journal": {"name": "Frontiers in Cellular Neuroscience", "volume": "15"}, "paperId": "b3d7c373fb6e34c2793597d42889cf2b6105aaf5", "abstract": "Trans-neuronal viruses are frequently used as neuroanatomical tools for mapping neuronal circuits. Specifically, recombinant one-step rabies viruses (RABV) have been instrumental in the widespread application of viral circuit mapping, as these viruses have enabled labs to map the direct inputs onto defined cell populations. Within the neuroscience community, it is widely believed that RABV spreads directly between neurons via synaptic connections, a hypothesis based principally on two observations. First, the virus labels neurons in a pattern consistent with known anatomical connectivity. Second, few glial cells appear to be infected following RABV injections, despite the fact that glial cells are abundant in the brain. However, there is no direct evidence that RABV can actually be transmitted through synaptic connections. Here we review the immunosubversive mechanisms that are critical to RABV’s success for infiltration of the central nervous system (CNS). These include interfering with and ultimately killing migratory T cells while maintaining levels of interferon (IFN) signaling in the brain parenchyma. Finally, we critically evaluate studies that support or are against synaptically-restricted RABV transmission and the implications of viral-host immune responses for RABV transmission in the brain.", "citations": [{"title": "Uncovering the Connectivity Logic of the Ventral Tegmental Area", "paperId": "0890f1b30df16a541bd78b6b636c9b139f547775"}], "externalIds": {"DOI": "10.3389/fncel.2021.720807", "PubMed": "34671244", "CorpusId": 238254772, "PubMedCentral": "8521040"}, "isOpenAccess": true, "citationCount": 1, "fieldsOfStudy": ["Medicine"], "openAccessPdf": {"url": "https://www.frontiersin.org/articles/10.3389/fncel.2021.720807/pdf", "status": "GOLD"}, "citationStyles": {"bibtex": "@[''Review'', ''JournalArticle'']{Beier2021TheSO,\n author = {Kevin T. Beier},\n booktitle = {Frontiers in Cellular Neuroscience},\n journal = {Frontiers in Cellular Neuroscience},\n title = {The Serendipity of Viral Trans-Neuronal Specificity: More Than Meets the Eye},\n volume = {15},\n year = {2021}\n}\n"}, "referenceCount": 105, "publicationDate": "2021-10-04", "s2FieldsOfStudy": [{"source": "external", "category": "Medicine"}, {"source": "s2-fos-model", "category": "Biology"}], "publicationTypes": ["Review", "JournalArticle"], "publicationVenue": {"id": "446b5506-d77e-4dee-9f31-2f35741ee52b", "url": "http://www.frontiersin.org/cellular_neuroscience", "issn": "1662-5102", "name": "Frontiers in Cellular Neuroscience", "type": "journal", "alternate_urls": ["http://www.frontiersin.org/cellularneuroscience/", "https://www.frontiersin.org/journals/cellular-neuroscience"], "alternate_names": ["Front Cell Neurosci"]}, "influentialCitationCount": 0}', '2023-01-27 11:05:05.828394', '2023-01-27 11:05:05.828394', 'The Serendipity of Viral Trans-Neuronal Specificity: More Than Meets the Eye', 2021, false, null, null, null, null);
