INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('1df4e7c89c2b1c89ec5f6a03cb430886ba77fe1c', '{"url": "https://www.semanticscholar.org/paper/1df4e7c89c2b1c89ec5f6a03cb430886ba77fe1c", "year": 2022, "title": "Sketch of a novel approach to a neural model", "venue": "ArXiv", "authors": [{"name": "G. Scheler", "authorId": "2576323"}], "journal": {"name": "ArXiv", "volume": "abs/2209.06865"}, "paperId": "1df4e7c89c2b1c89ec5f6a03cb430886ba77fe1c", "abstract": "In this paper, we lay out a novel model of neuroplasticity in the form of a horizontal-vertical integration model of neural processing. We believe a new approach to neural modeling will beneﬁt the ’third wave’ of AI. The ‘horizontal’ plane consists of an adaptive network of neurons connected by transmission links which generates spatio-temporal spike patterns. This ﬁts with standard computational neuroscience approaches. Additionally, for each individual neuron, there is a ’vertical’ part consisting of internal adaptive parameters steering the external, membrane-expressed parameters which are involved in neural transmission. Each neuron has a vertical modular system of parameters, corresponding to (a) external parameters at the membrane layer, divided into compartments (spines, boutons), (b) internal parameters in the submembrane zone and the cytoplasm with its intracellular protein signaling network, and (c) ‘core’ parameters in the nucleus for genetic and epigenetic information. In such models, each node (=neuron) in the horizontal network has its own internal memory. Neural transmission and information storage are systematically separated, an important conceptual advance over synaptic weight models. We discuss the membrane-based (’external’) ﬁltering and selection of outside signals for processing vs. signal loss by fast ﬂuctuations and the neuron-internal computing strategies from intracellular protein signaling to the nucleus as the ’core’ system. We want to show that the individual neuron has an important role in the computation of signals, and that many assumptions derived from the synaptic weight adjustment hypothesis of memory may not hold in a real brain. Not every transmission event leaves a trace, and the neuron is a self-programming device, rather than passively determined by current input. Ultimately we strive to build a ﬂexible memory system that processes facts and events automatically. There is room on the inside.", "citations": [], "externalIds": {"DOI": "10.48550/arXiv.2209.06865", "DBLP": "journals/corr/abs-2209-06865", "ArXiv": "2209.06865", "CorpusId": 252280548}, "isOpenAccess": false, "citationCount": 0, "fieldsOfStudy": ["Computer Science", "Biology", "Physics"], "openAccessPdf": null, "citationStyles": {"bibtex": "@[''JournalArticle'']{Scheler2022SketchOA,\n author = {G. Scheler},\n booktitle = {ArXiv},\n journal = {ArXiv},\n title = {Sketch of a novel approach to a neural model},\n volume = {abs/2209.06865},\n year = {2022}\n}\n"}, "referenceCount": 126, "publicationDate": "2022-09-14", "s2FieldsOfStudy": [{"source": "external", "category": "Computer Science"}, {"source": "external", "category": "Biology"}, {"source": "external", "category": "Physics"}, {"source": "s2-fos-model", "category": "Biology"}, {"source": "s2-fos-model", "category": "Computer Science"}], "publicationTypes": ["JournalArticle"], "publicationVenue": null, "influentialCitationCount": 0}', '2023-01-27 11:05:13.131448', '2023-01-27 11:05:13.131448', 'Sketch of a novel approach to a neural model', 2022, false, null, null, null, null);
