INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('59f950ef4d99bd963559c398174cc330b8afffd9', '{"url": "https://www.semanticscholar.org/paper/59f950ef4d99bd963559c398174cc330b8afffd9", "year": 2022, "title": "Social experience and pheromone receptor activity reprogram behavioral switch and neuromodulatory gene expression in sensory neurons", "venue": "bioRxiv", "authors": [{"name": "B. Deanhardt", "authorId": "1734915992"}, {"name": "Q. Duan", "authorId": "1888042977"}, {"name": "C. Du", "authorId": "2052318553"}, {"name": "Charles Soeder", "authorId": "27082294"}, {"name": "Alec Morlote", "authorId": "2182149011"}, {"name": "Deeya Garg", "authorId": "2182149429"}, {"name": "Corbin D. Jones", "authorId": "2115201258"}, {"name": "P. Volkan", "authorId": "4046540"}], "journal": {"name": "bioRxiv"}, "paperId": "59f950ef4d99bd963559c398174cc330b8afffd9", "abstract": "Social experience and pheromone signaling in olfactory neurons affect neuronal responses and male courtship behaviors in Drosophila. We previously showed that social experience and pheromone signaling modulate chromatin around behavioral switch gene fruitless, which encodes a transcription factor necessary and sufficient for male behaviors. Fruitless drives social experience dependent modulation of courtship behaviors and pheromone responses in sensory neurons, however, the molecular mechanisms underlying this neuromodulation remain less clear. To identify the molecular mechanisms driving social experience-dependent neuromodulation, we performed RNA-seq from antennal samples of mutants in pheromone receptors and fruitless, as well as grouped or isolated wild-type males. We found that loss of pheromone detection differentially alters the levels of fruitless exons suggesting changes in splicing patterns. In addition, many Fruitless target neuromodulatory genes, such as neurotransmitter receptors, ion channels, ion and membrane transporters, and odorant binding proteins are differentially regulated by social context and pheromone signaling. Recent studies showed that social experience and juvenile hormone signaling coregulate fru chromatin to modify pheromone responses in olfactory neurons. Interestingly, genes involved in juvenile hormone metabolism are also misregulated in different social contexts and mutant backgrounds. Our results suggest that modulation of neuronal activity and behaviors in response to social experience and pheromone signaling likely arise due to large-scale changes in transcriptional programs for neuromodulators downstream of behavioral switch gene function.", "citations": [{"title": "The effect of Drosophila attP40 background on the glomerular organization of Or47b olfactory receptor neurons", "paperId": "8bd327d0bdab35db226615915c2d0a303ce583ca"}], "externalIds": {"DOI": "10.1101/2021.06.18.449021", "CorpusId": 251694852}, "isOpenAccess": true, "citationCount": 1, "fieldsOfStudy": ["Biology"], "openAccessPdf": {"url": "https://www.biorxiv.org/content/biorxiv/early/2022/08/16/2021.06.18.449021.full.pdf", "status": "GREEN"}, "citationStyles": {"bibtex": "@None{Deanhardt2022SocialEA,\n author = {B. Deanhardt and Q. Duan and C. Du and Charles Soeder and Alec Morlote and Deeya Garg and Corbin D. Jones and P. Volkan},\n booktitle = {bioRxiv},\n journal = {bioRxiv},\n title = {Social experience and pheromone receptor activity reprogram behavioral switch and neuromodulatory gene expression in sensory neurons},\n year = {2022}\n}\n"}, "referenceCount": 138, "publicationDate": "2022-08-16", "s2FieldsOfStudy": [{"source": "external", "category": "Biology"}, {"source": "s2-fos-model", "category": "Biology"}, {"source": "s2-fos-model", "category": "Psychology"}], "publicationTypes": null, "publicationVenue": {"id": "027ffd21-ebb0-4af8-baf5-911124292fd0", "url": "http://biorxiv.org/", "name": "bioRxiv", "type": "journal"}, "influentialCitationCount": 0}', '2023-01-27 11:11:15.968193', '2023-01-27 11:11:15.968193', 'Social experience and pheromone receptor activity reprogram behavioral switch and neuromodulatory gene expression in sensory neurons', 2022, false, null, null, null, null);
INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('0a3e88b795ddc893c3ab7ce2d3ea7e7f01f7d808', '{"url": "https://www.semanticscholar.org/paper/0a3e88b795ddc893c3ab7ce2d3ea7e7f01f7d808", "year": 2022, "title": "Neuroendocrine regulation of female aggression", "venue": "Frontiers in Endocrinology", "authors": [{"name": "V. E. M. Oliveira", "authorId": "4939378"}, {"name": "J. Bakker", "authorId": "39440234"}], "journal": {"name": "Frontiers in Endocrinology", "volume": "13"}, "paperId": "0a3e88b795ddc893c3ab7ce2d3ea7e7f01f7d808", "abstract": "Classically the neurobiology of aggression has been studied exclusively in males. Thus, females have been considered mildly aggressive except during lactation. Interestingly, recent studies in rodents and humans have revealed that non-lactating females can show exacerbated and pathological aggression similarly to males. This review provides an overview of recent findings on the neuroendocrine mechanisms regulating aggressive behavior in females. In particular, the focus will be on novel rodent models of exaggerated aggression established in non-lactating females. Among the neuromodulatory systems influencing female aggression, special attention has been given to sex-steroids and sex-steroid-sensitive neuronal populations (i.e., the core nuclei of the neural pathway of aggression) as well as to the neuropeptides oxytocin and vasopressin which are major players in the regulation of social behaviors.", "citations": [{"title": "Understanding the Role of Oxidative Stress, Neuroinflammation and Abnormal Myelination in Excessive Aggression Associated with Depression: Recent Input from Mechanistic Studies", "paperId": "f6c9e80d0f52d174c5a42fc81b099b736d691adf"}, {"title": "Activation of glucagon-like peptide-1 receptors reduces the acquisition of aggression-like behaviors in male mice", "paperId": "d9a8b0e8c2ced3a08de40b7610a68d4d527627d6"}], "externalIds": {"DOI": "10.3389/fendo.2022.957114", "PubMed": "36034455", "CorpusId": 251448588, "PubMedCentral": "9399833"}, "isOpenAccess": true, "citationCount": 2, "fieldsOfStudy": ["Medicine"], "openAccessPdf": {"url": "https://www.frontiersin.org/articles/10.3389/fendo.2022.957114/pdf", "status": "GOLD"}, "citationStyles": {"bibtex": "@[''Review'', ''JournalArticle'']{Oliveira2022NeuroendocrineRO,\n author = {V. E. M. Oliveira and J. Bakker},\n booktitle = {Frontiers in Endocrinology},\n journal = {Frontiers in Endocrinology},\n title = {Neuroendocrine regulation of female aggression},\n volume = {13},\n year = {2022}\n}\n"}, "referenceCount": 124, "publicationDate": "2022-08-10", "s2FieldsOfStudy": [{"source": "external", "category": "Medicine"}, {"source": "s2-fos-model", "category": "Biology"}, {"source": "s2-fos-model", "category": "Psychology"}], "publicationTypes": ["Review", "JournalArticle"], "publicationVenue": {"id": "baea33cc-0a71-4b2a-9f01-5071f7b1abc9", "url": "http://www.frontiersin.org/Endocrinology", "issn": "1664-2392", "name": "Frontiers in Endocrinology", "type": "journal", "alternate_urls": ["https://www.frontiersin.org/journals/endocrinology"], "alternate_names": ["Front Endocrinol"]}, "influentialCitationCount": 0}', '2023-01-27 11:11:19.613191', '2023-01-27 11:11:19.613191', 'Neuroendocrine regulation of female aggression', 2022, false, null, null, null, null);
INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('57da2968b3c0258c59415260dff77da95826f0e9', '{"url": "https://www.semanticscholar.org/paper/57da2968b3c0258c59415260dff77da95826f0e9", "year": 2022, "title": "Cupid’s quiver: Integrating sensory cues in rodent mating systems", "venue": "Frontiers in Neural Circuits", "authors": [{"name": "Nerissa E G Hoglen", "authorId": "38795667"}, {"name": "Devanand S Manoli", "authorId": "5381465"}], "journal": {"name": "Frontiers in Neural Circuits", "volume": "16"}, "paperId": "57da2968b3c0258c59415260dff77da95826f0e9", "abstract": "In many animal species, males and females exploit different mating strategies, display sex-typical behaviors, and use distinct systems to recognize ethologically relevant cues. Mate selection thus requires mutual recognition across diverse social interactions based on distinct sensory signals. These sex differences in courtship and mating behaviors correspond to differences in sensory systems and downstream neural substrates engaged to recognize and respond to courtship signals. In many rodents, males tend to rely heavily on volatile olfactory and pheromone cues, while females appear to be guided more by a combination of these chemosensory signals with acoustic cues in the form of ultrasonic vocalizations. The mechanisms by which chemical and acoustic cues are integrated to control behavior are understudied in mating but are known to be important in the control of maternal behaviors. Socially monogamous species constitute a behaviorally distinct group of rodents. In these species, anatomic differences between males and females outside the nervous system are less prominent than in species with non-monogamous mating systems, and both sexes engage in more symmetric social behaviors and form attachments. Nevertheless, despite the apparent similarities in behaviors displayed by monogamous males and females, the circuitry supporting social, mating, and attachment behaviors in these species is increasingly thought to differ between the sexes. Sex differences in sensory modalities most important for mate recognition in across species are of particular interest and present a wealth of questions yet to be answered. Here, we discuss how distinct sensory cues may be integrated to drive social and attachment behaviors in rodents, and the differing roles of specific sensory systems in eliciting displays of behavior by females or males.", "citations": [], "externalIds": {"DOI": "10.3389/fncir.2022.944895", "PubMed": "35958042", "CorpusId": 251022129, "PubMedCentral": "9358210"}, "isOpenAccess": true, "citationCount": 0, "fieldsOfStudy": ["Medicine"], "openAccessPdf": {"url": "https://www.frontiersin.org/articles/10.3389/fncir.2022.944895/pdf", "status": "GOLD"}, "citationStyles": {"bibtex": "@[''Review'', ''JournalArticle'']{Hoglen2022Cupid’sQI,\n author = {Nerissa E G Hoglen and Devanand S Manoli},\n booktitle = {Frontiers in Neural Circuits},\n journal = {Frontiers in Neural Circuits},\n title = {Cupid’s quiver: Integrating sensory cues in rodent mating systems},\n volume = {16},\n year = {2022}\n}\n"}, "referenceCount": 163, "publicationDate": "2022-07-25", "s2FieldsOfStudy": [{"source": "external", "category": "Medicine"}, {"source": "s2-fos-model", "category": "Biology"}, {"source": "s2-fos-model", "category": "Psychology"}], "publicationTypes": ["Review", "JournalArticle"], "publicationVenue": {"id": "09dc8437-eb87-49ec-a21b-ecb208a5890b", "url": "https://www.frontiersin.org/journals/neural-circuits", "issn": "1662-5110", "name": "Frontiers in Neural Circuits", "type": "journal", "alternate_urls": ["http://www.frontiersin.org/neuralcircuits/", "http://www.frontiersin.org/neural_circuits"], "alternate_names": ["Front Neural Circuit"]}, "influentialCitationCount": 0}', '2023-01-27 11:11:23.223751', '2023-01-27 11:11:23.223751', 'Cupid’s quiver: Integrating sensory cues in rodent mating systems', 2022, false, null, null, null, null);
INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('05f96723518f373016fc7f3ef85972880ff13e12', '{"url": "https://www.semanticscholar.org/paper/05f96723518f373016fc7f3ef85972880ff13e12", "year": 2022, "title": "Molecular and spatial profiling of the paraventricular nucleus of the thalamus", "venue": "bioRxiv", "authors": [{"name": "Cl Gao", "authorId": "91018010"}, {"name": "Chiraag A. Gohel", "authorId": "2179024176"}, {"name": "Y. Leng", "authorId": "143611034"}, {"name": "David Goldman", "authorId": "2064678672"}, {"name": "Ariel J. Levine", "authorId": "49559142"}, {"name": "M. Penzo", "authorId": "40649478"}], "journal": {"name": "bioRxiv"}, "paperId": "05f96723518f373016fc7f3ef85972880ff13e12", "abstract": "The paraventricular nucleus of the thalamus (PVT) is known to regulate various cognitive and behavioral processes. However, while functional diversity among PVT circuits has often been linked to cellular differences, the molecular identity and spatial distribution of PVT cell types remains unclear. To address this gap, here we used single nucleus RNA sequencing (snRNA-seq) and identified five molecularly distinct PVT neuronal subtypes. Additionally, multiplex fluorescent in situ hybridization of top marker genes revealed that PVT subtypes are organized by a combination of previously unidentified molecular gradients. Lastly, comparing our dataset with a recently published single-cell sequencing atlas of thalamus yielded novel insight into the PVT’s connectivity with cortex, including unexpected innervation of auditory and visual areas. This comparison also revealed that our data contains a largely non-overlapping transcriptomic map of multiple midline thalamic nuclei. Collectively, our findings uncover previously unknown features of the molecular diversity and anatomical organization of the PVT and provide a valuable resource for future investigations.", "citations": [], "externalIds": {"DOI": "10.1101/2022.07.19.500649", "CorpusId": 251021326}, "isOpenAccess": true, "citationCount": 0, "fieldsOfStudy": ["Biology"], "openAccessPdf": {"url": "https://www.biorxiv.org/content/biorxiv/early/2022/07/20/2022.07.19.500649.full.pdf", "status": "GREEN"}, "citationStyles": {"bibtex": "@None{Gao2022MolecularAS,\n author = {Cl Gao and Chiraag A. Gohel and Y. Leng and David Goldman and Ariel J. Levine and M. Penzo},\n booktitle = {bioRxiv},\n journal = {bioRxiv},\n title = {Molecular and spatial profiling of the paraventricular nucleus of the thalamus},\n year = {2022}\n}\n"}, "referenceCount": 183, "publicationDate": "2022-07-20", "s2FieldsOfStudy": [{"source": "external", "category": "Biology"}, {"source": "s2-fos-model", "category": "Biology"}], "publicationTypes": null, "publicationVenue": {"id": "027ffd21-ebb0-4af8-baf5-911124292fd0", "url": "http://biorxiv.org/", "name": "bioRxiv", "type": "journal"}, "influentialCitationCount": 0}', '2023-01-27 11:11:26.833113', '2023-01-27 11:11:26.833113', 'Molecular and spatial profiling of the paraventricular nucleus of the thalamus', 2022, false, null, null, null, null);
