INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('20aaf1e5f0b161b1d0e39e517c6f5c62618304ea', '{"url": "https://www.semanticscholar.org/paper/20aaf1e5f0b161b1d0e39e517c6f5c62618304ea", "year": 2022, "title": "An approximate line attractor in the hypothalamus that encodes an aggressive internal state", "venue": "bioRxiv", "authors": [{"name": "Aditya Nair", "authorId": "122947983"}, {"name": "T. Karigo", "authorId": "6836121"}, {"name": "Bin Yang", "authorId": "2118582910"}, {"name": "Scott W. Linderman", "authorId": "2342841"}, {"name": "David J Anderson", "authorId": "2116531695"}, {"name": "A. Kennedy", "authorId": "143988562"}], "journal": {"name": "bioRxiv"}, "paperId": "20aaf1e5f0b161b1d0e39e517c6f5c62618304ea", "abstract": "The hypothalamus plays a key role in regulating innate behaviors. It is widely believed to function as a system of ‘labeled lines’, containing behavior-specific neurons with characteristic transcriptomic and connectomic profiles. This view however fails to explain why, although activation of estrogen receptor-1 (Esr1) expressing neurons in the ventromedial hypothalamus (VMHvl) promotes aggression, few VMHvl neurons are tuned to attack. To address this paradox, we adopted an unsupervised dynamical systems framework to analyze population activity among VMHvlEsr1 neurons during aggression. We discovered that this activity contains an “integration” dimension exhibiting slow-ramping dynamics and persistent activity that correlates with escalating aggressiveness. These dynamics are implemented as an approximate line attractor in state space. Our analysis suggests a function for VMHvl to encode the intensity of behavior-relevant motive states using line attractors. This view reconciles observational and perturbational studies of VMHvl, and reveals a new mode of neural computation in the hypothalamus.", "citations": [{"title": "Learning Behavior Representations Through Multi-Timescale Bootstrapping", "paperId": "a03ff04bef6afdb55fb13aa13d8cbd3b0f8544e3"}], "externalIds": {"DOI": "10.1101/2022.04.19.488776", "CorpusId": 248326147}, "isOpenAccess": true, "citationCount": 1, "fieldsOfStudy": ["Biology"], "openAccessPdf": {"url": "https://authors.library.caltech.edu/114401/1/2022.04.19.488776v1.full.pdf", "status": "GREEN"}, "citationStyles": {"bibtex": "@None{Nair2022AnAL,\n author = {Aditya Nair and T. Karigo and Bin Yang and Scott W. Linderman and David J Anderson and A. Kennedy},\n booktitle = {bioRxiv},\n journal = {bioRxiv},\n title = {An approximate line attractor in the hypothalamus that encodes an aggressive internal state},\n year = {2022}\n}\n"}, "referenceCount": 66, "publicationDate": "2022-04-19", "s2FieldsOfStudy": [{"source": "external", "category": "Biology"}, {"source": "s2-fos-model", "category": "Biology"}, {"source": "s2-fos-model", "category": "Psychology"}], "publicationTypes": null, "publicationVenue": {"id": "027ffd21-ebb0-4af8-baf5-911124292fd0", "url": "http://biorxiv.org/", "name": "bioRxiv", "type": "journal"}, "influentialCitationCount": 0}', '2023-01-27 11:12:10.288447', '2023-01-27 11:12:10.288447', 'An approximate line attractor in the hypothalamus that encodes an aggressive internal state', 2022, false, null, null, null, null);
