INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('657abd1fde4137f673a50dc83f3115cb40f5bff3', '{"url": "https://www.semanticscholar.org/paper/657abd1fde4137f673a50dc83f3115cb40f5bff3", "year": 2022, "title": "The mushroom body output encodes behavioral decision during sensory-motor transformation", "venue": "bioRxiv", "authors": [{"name": "C. Arıcan", "authorId": "122057770"}, {"name": "F. Schmitt", "authorId": "2113772853"}, {"name": "W. Rössler", "authorId": "32273521"}, {"name": "M. Strube-Bloss", "authorId": "1401854824"}, {"name": "M. Nawrot", "authorId": "1680492"}], "journal": {"name": "bioRxiv"}, "paperId": "657abd1fde4137f673a50dc83f3115cb40f5bff3", "abstract": "Animal behavioral decisions are dynamically formed by evaluating momentary sensory evidence on the background of individual experience and the acute motivational state. In insects, the mushroom body (MB) has been implicated in forming associative memories and in assessing the appetitive or aversive valence of sensory stimuli to bias approach versus avoidance behavior. To study the MB involvement in innate feeding behavior we performed extracellular single-unit recordings from MB output neurons (MBONs) while simultaneously monitoring a defined feeding behavior in response to timed odor stimulation in naïve cockroaches. All animals expressed the feeding behavior almost exclusively in response to food odors. Likewise, MBON responses were invariably and strongly tuned to the same odors. Importantly, MBON responses were restricted to behaviorally responded trials, which allowed the accurate prediction of the occurrence versus non-occurrence of the feeding behavior in individual trials from the neuronal population activity. During responded trials the neuronal activity generally preceded the onset of the feeding behavior, indicating a causal relation. Our results contest the predominant view that MBONs encode stimulus valence. Rather, we conclude that the MB output dynamically encodes the behavioral decision to inform downstream motor networks.", "citations": [], "externalIds": {"DOI": "10.1101/2022.09.14.507924", "CorpusId": 252384663}, "isOpenAccess": true, "citationCount": 0, "fieldsOfStudy": ["Biology"], "openAccessPdf": {"url": "https://www.biorxiv.org/content/biorxiv/early/2022/09/16/2022.09.14.507924.full.pdf", "status": "GREEN"}, "citationStyles": {"bibtex": "@None{Arıcan2022TheMB,\n author = {C. Arıcan and F. Schmitt and W. Rössler and M. Strube-Bloss and M. Nawrot},\n booktitle = {bioRxiv},\n journal = {bioRxiv},\n title = {The mushroom body output encodes behavioral decision during sensory-motor transformation},\n year = {2022}\n}\n"}, "referenceCount": 47, "publicationDate": "2022-09-16", "s2FieldsOfStudy": [{"source": "external", "category": "Biology"}, {"source": "s2-fos-model", "category": "Psychology"}, {"source": "s2-fos-model", "category": "Biology"}], "publicationTypes": null, "publicationVenue": {"id": "027ffd21-ebb0-4af8-baf5-911124292fd0", "url": "http://biorxiv.org/", "name": "bioRxiv", "type": "journal"}, "influentialCitationCount": 0}', '2023-01-27 11:12:31.997204', '2023-01-27 11:12:31.997204', 'The mushroom body output encodes behavioral decision during sensory-motor transformation', 2022, false, null, null, null, null);
INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('c6802303cc977c6c67d959b167366f0bd644d697', '{"url": "https://www.semanticscholar.org/paper/c6802303cc977c6c67d959b167366f0bd644d697", "year": 2022, "title": "Generalization in Neural Networks: A Broad Survey", "venue": "ArXiv", "authors": [{"name": "Chris Rohlfs", "authorId": "19236748"}], "journal": {"name": "ArXiv", "volume": "abs/2209.01610"}, "paperId": "c6802303cc977c6c67d959b167366f0bd644d697", "abstract": "This paper reviews concepts, modeling approaches, and recent ﬁndings along a spectrum of diﬀerent levels of abstraction of neural network models including generalization across (1) Samples, (2) Distributions, (3) Domains, (4) Tasks, (5) Modalities, and (6) Scopes. Results on (1) sample generalization show that, in the case of ImageNet, nearly all the recent improvements reduced training error while overﬁtting stayed ﬂat; with nearly all the training error eliminated, future progress will require a focus on reducing overﬁtting. Perspectives from statistics highlight how (2) distribution generalization can be viewed alternately as a change in sample weights or a change in the input-output relationship. Transfer learning approaches to (3) domain generalization are summarized, as are recent advances and the wealth of domain adaptation benchmark datasets available. Recent breakthroughs surveyed in (4) task generalization include few-shot meta-learning approaches and the BERT NLP engine, and recent (5) modality generalization studies are discussed that integrate image and text data and that apply a biologically-inspired network across olfactory, visual, and auditory modalities. Recent (6) scope generalization results are reviewed that embed knowledge graphs into deep NLP approaches. Additionally, concepts from neuroscience are discussed on the modular architecture of brains and the steps by which dopamine-driven conditioning leads to abstract thinking.", "citations": [{"title": "Problem-dependent attention and effort in neural networks with application to image resolution and model selection", "paperId": "4e9bbe5abfdf7fece4e5a01e84f370ef26c15ab0"}], "externalIds": {"DOI": "10.48550/arXiv.2209.01610", "DBLP": "journals/corr/abs-2209-01610", "ArXiv": "2209.01610", "CorpusId": 252089037}, "isOpenAccess": false, "citationCount": 1, "fieldsOfStudy": ["Computer Science"], "openAccessPdf": null, "citationStyles": {"bibtex": "@[''JournalArticle'', ''Review'']{Rohlfs2022GeneralizationIN,\n author = {Chris Rohlfs},\n booktitle = {ArXiv},\n journal = {ArXiv},\n title = {Generalization in Neural Networks: A Broad Survey},\n volume = {abs/2209.01610},\n year = {2022}\n}\n"}, "referenceCount": 155, "publicationDate": "2022-09-04", "s2FieldsOfStudy": [{"source": "external", "category": "Computer Science"}, {"source": "s2-fos-model", "category": "Computer Science"}], "publicationTypes": ["JournalArticle", "Review"], "publicationVenue": null, "influentialCitationCount": 0}', '2023-01-27 11:12:35.778912', '2023-01-27 11:12:35.778912', 'Generalization in Neural Networks: A Broad Survey', 2022, false, null, null, null, null);
INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('59d7d778d23ea35a631bd1010e35e4064c4bdb63', '{"url": "https://www.semanticscholar.org/paper/59d7d778d23ea35a631bd1010e35e4064c4bdb63", "year": 2022, "title": "Dlg Is Required for Short-Term Memory and Interacts with NMDAR in the Drosophila Brain", "venue": "International Journal of Molecular Sciences", "authors": [{"name": "F. Bertin", "authorId": "1399803843"}, {"name": "G. Moya-Alvarado", "authorId": "1402166276"}, {"name": "Eduardo A. Quiroz-Manriquez", "authorId": "1438352052"}, {"name": "Andrés Ibacache", "authorId": "23694597"}, {"name": "Andrés Köhler-Solis", "authorId": "2181994240"}, {"name": "C. Oliva", "authorId": "145873692"}, {"name": "J. Sierralta", "authorId": "6232986"}], "journal": {"name": "International Journal of Molecular Sciences", "volume": "23"}, "paperId": "59d7d778d23ea35a631bd1010e35e4064c4bdb63", "abstract": "The vertebrates’ scaffold proteins of the Dlg-MAGUK family are involved in the recruitment, clustering, and anchoring of glutamate receptors to the postsynaptic density, particularly the NMDA subtype glutamate-receptors (NRs), necessary for long-term memory and LTP. In Drosophila, the only gene of the subfamily generates two main products, dlgA, broadly expressed, and dlgS97, restricted to the nervous system. In the Drosophila brain, NRs are expressed in the adult brain and are involved in memory, however, the role of Dlg in these processes and its relationship with NRs has been scarcely explored. Here, we show that the dlg mutants display defects in short-term memory in the olfactory associative-learning paradigm. These defects are dependent on the presence of DlgS97 in the Mushroom Body (MB) synapses. Moreover, Dlg is immunoprecipitated with NRs in the adult brain. Dlg is also expressed in the larval neuromuscular junction (NMJ) pre and post-synaptically and is important for development and synaptic function, however, NR is absent in this synapse. Despite that, we found changes in the short-term plasticity paradigms in dlg mutant larval NMJ. Together our results show that larval NMJ and the adult brain relies on Dlg for short-term memory/plasticity, but the mechanisms differ in the two types of synapses.", "citations": [], "externalIds": {"DOI": "10.3390/ijms23169187", "PubMed": "36012453", "CorpusId": 251663268, "PubMedCentral": "9409279"}, "isOpenAccess": true, "citationCount": 0, "fieldsOfStudy": ["Medicine"], "openAccessPdf": {"url": "https://www.mdpi.com/1422-0067/23/16/9187/pdf?version=1660641458", "status": "GOLD"}, "citationStyles": {"bibtex": "@[''JournalArticle'']{Bertin2022DlgIR,\n author = {F. Bertin and G. Moya-Alvarado and Eduardo A. Quiroz-Manriquez and Andrés Ibacache and Andrés Köhler-Solis and C. Oliva and J. Sierralta},\n booktitle = {International Journal of Molecular Sciences},\n journal = {International Journal of Molecular Sciences},\n title = {Dlg Is Required for Short-Term Memory and Interacts with NMDAR in the Drosophila Brain},\n volume = {23},\n year = {2022}\n}\n"}, "referenceCount": 64, "publicationDate": "2022-08-01", "s2FieldsOfStudy": [{"source": "external", "category": "Medicine"}, {"source": "s2-fos-model", "category": "Biology"}], "publicationTypes": ["JournalArticle"], "publicationVenue": {"id": "8506a01a-40b8-4e6f-bbb8-ce2492139c15", "url": "http://www.e-helvetica.nb.admin.ch/directAccess?callnumber=bel-157693", "issn": "1422-0067", "name": "International Journal of Molecular Sciences", "type": "journal", "alternate_urls": ["https://www.mdpi.com/journal/ijms", "http://www.mdpi.com/journal/ijms/", "http://nbn-resolving.de/urn/resolver.pl?urn=urn:nbn:ch:bel-157693"], "alternate_names": ["Int J Mol Sci"]}, "influentialCitationCount": 0}', '2023-01-27 11:12:39.382013', '2023-01-27 11:12:39.382013', 'Dlg Is Required for Short-Term Memory and Interacts with NMDAR in the Drosophila Brain', 2022, false, null, null, null, null);
INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('7a3fe29ec6b6e3ca8d2c6e9a4db8a3cbbf994fcc', '{"url": "https://www.semanticscholar.org/paper/7a3fe29ec6b6e3ca8d2c6e9a4db8a3cbbf994fcc", "year": 2022, "title": "Mechanisms of Variability Underlying Odor-Guided Locomotion", "venue": "Frontiers in Behavioral Neuroscience", "authors": [{"name": "Liangyu Tao", "authorId": "49839273"}, {"name": "Vikas Bhandawat", "authorId": "5667655"}], "journal": {"name": "Frontiers in Behavioral Neuroscience", "volume": "16"}, "paperId": "7a3fe29ec6b6e3ca8d2c6e9a4db8a3cbbf994fcc", "abstract": "Changes in locomotion mediated by odors (odor-guided locomotion) are an important mechanism by which animals discover resources important to their survival. Odor-guided locomotion, like most other behaviors, is highly variable. Variability in behavior can arise at many nodes along the circuit that performs sensorimotor transformation. We review these sources of variability in the context of the Drosophila olfactory system. While these sources of variability are important, using a model for locomotion, we show that another important contributor to behavioral variability is the stochastic nature of decision-making during locomotion as well as the persistence of these decisions: Flies choose the speed and curvature stochastically from a distribution and locomote with the same speed and curvature for extended periods. This stochasticity in locomotion will result in variability in behavior even if there is no noise in sensorimotor transformation. Overall, the noise in sensorimotor transformation is amplified by mechanisms of locomotion making odor-guided locomotion in flies highly variable.", "citations": [{"title": "Scalable Apparatus to Measure Posture and Locomotion (SAMPL): a high-throughput solution to study unconstrained vertical behavior in small animals", "paperId": "4a1e35ef6afe94d569103a262c8a3b099e8e3b86"}], "externalIds": {"DOI": "10.3389/fnbeh.2022.871884", "PubMed": "35600988", "CorpusId": 248507779, "PubMedCentral": "9115574"}, "isOpenAccess": true, "citationCount": 1, "fieldsOfStudy": ["Medicine"], "openAccessPdf": {"url": "https://www.frontiersin.org/articles/10.3389/fnbeh.2022.871884/pdf", "status": "GOLD"}, "citationStyles": {"bibtex": "@[''Review'', ''JournalArticle'']{Tao2022MechanismsOV,\n author = {Liangyu Tao and Vikas Bhandawat},\n booktitle = {Frontiers in Behavioral Neuroscience},\n journal = {Frontiers in Behavioral Neuroscience},\n title = {Mechanisms of Variability Underlying Odor-Guided Locomotion},\n volume = {16},\n year = {2022}\n}\n"}, "referenceCount": 115, "publicationDate": "2022-05-04", "s2FieldsOfStudy": [{"source": "external", "category": "Medicine"}, {"source": "s2-fos-model", "category": "Biology"}, {"source": "s2-fos-model", "category": "Psychology"}, {"source": "s2-fos-model", "category": "Engineering"}], "publicationTypes": ["Review", "JournalArticle"], "publicationVenue": {"id": "810ce699-eb80-4394-8aac-a6d3c87840ed", "url": "http://www.frontiersin.org/behavioral_neuroscience", "issn": "1662-5153", "name": "Frontiers in Behavioral Neuroscience", "type": "journal", "alternate_urls": ["http://www.frontiersin.org/behavioralneuroscience/", "https://www.frontiersin.org/journals/behavioral-neuroscience"], "alternate_names": ["Front Behav Neurosci"]}, "influentialCitationCount": 0}', '2023-01-27 11:12:42.984461', '2023-01-27 11:12:42.984461', 'Mechanisms of Variability Underlying Odor-Guided Locomotion', 2022, false, null, null, null, null);
INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('629baf118640ff96974ca7304d9c45b45ac5dad3', '{"url": "https://www.semanticscholar.org/paper/629baf118640ff96974ca7304d9c45b45ac5dad3", "year": 2021, "title": "Associative learning drives longitudinally graded presynaptic plasticity of neurotransmitter release along axonal compartments", "venue": "bioRxiv", "authors": [{"name": "Aaron L. Stahl", "authorId": "46569367"}, {"name": "Nathaniel C. Noyes", "authorId": "5648507"}, {"name": "Tamara Boto", "authorId": "4066347"}, {"name": "Miao Jing", "authorId": "50072050"}, {"name": "Jianzhi Zeng", "authorId": "40950581"}, {"name": "Lanikea B. King", "authorId": "47706007"}, {"name": "Yulong Li", "authorId": "2111353418"}, {"name": "Ronald L. Davis", "authorId": "7137209"}, {"name": "S. Tomchik", "authorId": "6482354"}], "journal": {"name": "eLife", "volume": "11"}, "paperId": "629baf118640ff96974ca7304d9c45b45ac5dad3", "abstract": "Anatomical and physiological compartmentalization of neurons is a mechanism to increase the computational capacity of a circuit, and a major question is what role axonal compartmentalization plays. Axonal compartmentalization may enable localized, presynaptic plasticity to alter neuronal output in a flexible, experience-dependent manner. Here we show that olfactory learning generates compartmentalized, bidirectional plasticity of acetylcholine release that varies across the longitudinal compartments of Drosophila mushroom body (MB) axons. The directionality of the learning-induced plasticity depends on the valence of the learning event (aversive vs. appetitive), varies linearly across proximal to distal compartments following appetitive conditioning, and correlates with learning-induced changes in downstream mushroom body output neurons (MBONs) that modulate behavioral action selection. Potentiation of acetylcholine release was dependent on the CaV2.1 calcium channel subunit cacophony. In addition, contrast between the positive conditioned stimulus and other odors required the inositol triphosphate receptor (IP3R), which was required to maintain responsivity to odors in untrained conditions. Downstream from the mushroom body, a set of MBONs that receive their input from the γ3 MB compartment were required for normal appetitive learning, suggesting that they represent a key node through which discriminative effects influence appetitive memory and decision-making. These data demonstrate that learning drives valence-correlated, compartmentalized, bidirectional potentiation and depression of synaptic neurotransmitter release, which rely on distinct mechanisms and are distributed across axonal compartments in a learning circuit.", "citations": [{"title": "Mechanisms controlling the trafficking, localization, and abundance of presynaptic Ca2+ channels", "paperId": "06720d2c308bb83982ab69a8e3c45fd99de4e9ae"}, {"title": "Postsynaptic plasticity of cholinergic synapses underlies the induction and expression of appetitive and familiarity memories in Drosophila", "paperId": "e9c16adbe744d5b41ce0aba74315a4061a6bf303"}, {"title": "One engram two readouts: stimulus dynamics switch a learned behavior in Drosophila", "paperId": "d0c772967d22ed3064619cab8f238d5c57b88793"}, {"title": "Local 5-HT signal bi-directionally regulates the coincidence time window of associative learning", "paperId": "81ccc0423c9759039d787471946b4048a00174ce"}, {"title": "Regulation of presynaptic Ca2+ channel abundance at active zones through a balance of delivery and turnover", "paperId": "30f0148863e943658fa5d2f4d0b941d626b9713b"}, {"title": "Postsynaptic plasticity of cholinergic synapses underlies the 2 induction and expression of appetitive and familiarity 3 memories in Drosophila 4 5 6 7", "paperId": "3778e26707b2f87fc221b3242ce3f14af824bd64"}], "externalIds": {"DOI": "10.7554/eLife.76712", "PubMed": "35285796", "CorpusId": 235414175, "PubMedCentral": "8956283"}, "isOpenAccess": false, "citationCount": 6, "fieldsOfStudy": ["Biology", "Medicine"], "openAccessPdf": null, "citationStyles": {"bibtex": "@[''JournalArticle'']{Stahl2021AssociativeLD,\n author = {Aaron L. Stahl and Nathaniel C. Noyes and Tamara Boto and Miao Jing and Jianzhi Zeng and Lanikea B. King and Yulong Li and Ronald L. Davis and S. Tomchik},\n booktitle = {bioRxiv},\n journal = {eLife},\n title = {Associative learning drives longitudinally graded presynaptic plasticity of neurotransmitter release along axonal compartments},\n volume = {11},\n year = {2021}\n}\n"}, "referenceCount": 100, "publicationDate": "2021-06-09", "s2FieldsOfStudy": [{"source": "external", "category": "Biology"}, {"source": "external", "category": "Medicine"}, {"source": "s2-fos-model", "category": "Biology"}, {"source": "s2-fos-model", "category": "Psychology"}], "publicationTypes": ["JournalArticle"], "publicationVenue": {"id": "027ffd21-ebb0-4af8-baf5-911124292fd0", "url": "http://biorxiv.org/", "name": "bioRxiv", "type": "journal"}, "influentialCitationCount": 0}', '2023-01-27 11:12:46.657573', '2023-01-27 11:12:46.657573', 'Associative learning drives longitudinally graded presynaptic plasticity of neurotransmitter release along axonal compartments', 2021, false, null, null, null, null);
INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('faf26098114b028e4125cc46d4d1fea6b2459d96', '{"url": "https://www.semanticscholar.org/paper/faf26098114b028e4125cc46d4d1fea6b2459d96", "year": 2021, "title": "Forecast-based attribution of a winter heatwave within the limit of predictability", "venue": "Proceedings of the National Academy of Sciences", "authors": [{"name": "N. Leach", "authorId": "114253776"}, {"name": "A. Weisheimer", "authorId": "5734349"}, {"name": "M. Allen", "authorId": "2354499"}, {"name": "T. Palmer", "authorId": "2054501665"}], "journal": {"name": "Proceedings of the National Academy of Sciences", "volume": "118"}, "paperId": "faf26098114b028e4125cc46d4d1fea6b2459d96", "abstract": "Significance The question of how humans have influenced individual extreme weather events is both scientifically and socially important. However, deficiencies in climate models’ representations of key mechanisms within the process chains that drive weather reduce our confidence in estimates of the human influence on extreme events. We propose that using forecast models that successfully predicted the event in question could increase the robustness of such estimates. Using a successful forecast means we can be confident that the model is able to faithfully represent the characteristics of the specific extreme event. We use this forecast-based methodology to estimate the direct radiative impact of increased CO2 concentrations (one component, but not the entirety, of human influence) on the European heatwave of February 2019. Attribution of extreme weather events has expanded rapidly as a field over the past decade. However, deficiencies in climate model representation of key dynamical drivers of extreme events have led to some concerns over the robustness of climate model–based attribution studies. It has also been suggested that the unconditioned risk-based approach to event attribution may result in false negative results due to dynamical noise overwhelming any climate change signal. The “storyline” attribution framework, in which the impact of climate change on individual drivers of an extreme event is examined, aims to mitigate these concerns. Here we propose a methodology for attribution of extreme weather events using the operational European Centre for Medium-Range Weather Forecasts (ECMWF) medium-range forecast model that successfully predicted the event. The use of a successful forecast ensures not only that the model is able to accurately represent the event in question, but also that the analysis is unequivocally an attribution of this specific event, rather than a mixture of multiple different events that share some characteristic. Since this attribution methodology is conditioned on the component of the event that was predictable at forecast initialization, we show how adjusting the lead time of the forecast can flexibly set the level of conditioning desired. This flexible adjustment of the conditioning allows us to synthesize between a storyline (highly conditioned) and a risk-based (relatively unconditioned) approach. We demonstrate this forecast-based methodology through a partial attribution of the direct radiative effect of increased CO2 concentrations on the exceptional European winter heatwave of February 2019.", "citations": [{"title": "Prediction and projection of heatwaves", "paperId": "8051868d9db4a2a088dafdf58b08fab4b816b59e"}, {"title": "On the role of anthropogenic warming and wetting in the July 2021 Henan record-shattering rainfall", "paperId": "c487e00f989bb76151c9a9563aed25907c76a258"}, {"title": "On the Potentials and Limitations of Attributing a Small‐Scale Climate Event", "paperId": "bb0f961d509c2080a2d183ac63a9c4c189a270ac"}, {"title": "LATE REQUEST FOR A SPECIAL PROJECT 2022–2024", "paperId": "aa0ef13453031f92b2511ed4eacf7d58f1f6f617"}], "externalIds": {"DOI": "10.5194/EGUSPHERE-EGU21-5731", "MAG": "3169673573", "PubMed": "34845022", "CorpusId": 236667892}, "isOpenAccess": true, "citationCount": 4, "fieldsOfStudy": ["Medicine", "Environmental Science"], "openAccessPdf": {"url": "https://ora.ox.ac.uk/objects/uuid:b13fd12e-cb86-4e7e-b73f-b6579fdd331a/download_file?safe_filename=Leach_et_al_2021_Forecast_based_attribution.pdf&file_format=pdf&type_of_work=Journal+article", "status": "GREEN"}, "citationStyles": {"bibtex": "@[''JournalArticle'']{Leach2021Forecast-basedAO,\n author = {N. Leach and A. Weisheimer and M. Allen and T. Palmer},\n booktitle = {Proceedings of the National Academy of Sciences},\n journal = {Proceedings of the National Academy of Sciences},\n title = {Forecast-based attribution of a winter heatwave within the limit of predictability},\n volume = {118},\n year = {2021}\n}\n"}, "referenceCount": 70, "publicationDate": "2021-03-03", "s2FieldsOfStudy": [{"source": "external", "category": "Medicine"}, {"source": "external", "category": "Environmental Science"}, {"source": "s2-fos-model", "category": "Environmental Science"}], "publicationTypes": ["JournalArticle"], "publicationVenue": null, "influentialCitationCount": 0}', '2023-01-27 11:19:50.488909', '2023-01-27 11:19:50.488909', 'Forecast-based attribution of a winter heatwave within the limit of predictability', 2021, false, null, null, null, null);
INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('75549ee37396bdb71d73c31b6b2506b2416219e0', '{"url": "https://www.semanticscholar.org/paper/75549ee37396bdb71d73c31b6b2506b2416219e0", "year": 2023, "title": "Language Cognition and Language Computation - Human and Machine Language Understanding", "venue": "ArXiv", "authors": [{"name": "Shaonan Wang", "authorId": "2761251"}, {"name": "N. Ding", "authorId": "2447541"}, {"name": "Nan Lin", "authorId": "2200169010"}, {"name": "Jiajun Zhang", "authorId": "2124819243"}, {"name": "Chengqing Zong", "authorId": "2064100826"}], "journal": {"name": "ArXiv", "volume": "abs/2301.04788"}, "paperId": "75549ee37396bdb71d73c31b6b2506b2416219e0", "abstract": "Language understanding is a key scientiﬁc issue in the ﬁelds of cognitive and computer science. However, the two disciplines diﬀer substantially in the speciﬁc research questions. Cognitive science focuses on analyzing the speciﬁc mechanism of the brain and investigating the brain’s response to language; few studies have examined the brain’s language system as a whole. By contrast, computer scientists focus on the eﬃciency of practical applications when choosing research questions but may ignore the most essential laws of language. Given these diﬀerences, can a combination of the disciplines oﬀer new insights for building intelligent language models and studying language cognitive mechanisms? In the following text, we ﬁrst review the research questions, history, and methods of language understanding in cognitive and computer science, focusing on the current progress and challenges. We then compare and contrast the research of language understanding in cognitive and computer sciences. Finally, we review existing work that combines insights from language cognition and language computation and oﬀer prospects for future development trends.", "citations": [], "externalIds": {"DOI": "10.48550/arXiv.2301.04788", "DBLP": "journals/corr/abs-2301-04788", "ArXiv": "2301.04788", "CorpusId": 255749334}, "isOpenAccess": false, "citationCount": 0, "fieldsOfStudy": ["Computer Science"], "openAccessPdf": null, "citationStyles": {"bibtex": "@[''JournalArticle'', ''Review'']{Wang2023LanguageCA,\n author = {Shaonan Wang and N. Ding and Nan Lin and Jiajun Zhang and Chengqing Zong},\n booktitle = {ArXiv},\n journal = {ArXiv},\n title = {Language Cognition and Language Computation - Human and Machine Language Understanding},\n volume = {abs/2301.04788},\n year = {2023}\n}\n"}, "referenceCount": 158, "publicationDate": "2023-01-12", "s2FieldsOfStudy": [{"source": "external", "category": "Computer Science"}, {"source": "s2-fos-model", "category": "Computer Science"}], "publicationTypes": ["JournalArticle", "Review"], "publicationVenue": null, "influentialCitationCount": 0}', '2023-01-27 11:19:54.089183', '2023-01-27 11:19:54.089183', 'Language Cognition and Language Computation - Human and Machine Language Understanding', 2023, false, null, null, null, null);
INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('738c8198b637049bae7010ab1c8684cd7c4db15c', '{"url": "https://www.semanticscholar.org/paper/738c8198b637049bae7010ab1c8684cd7c4db15c", "year": 2022, "title": "Modeling the Evolution of Biological Neural Networks Based on Caenorhabditis elegans Connectomes across Development", "venue": "Entropy", "authors": [{"name": "Hongfei Zhao", "authorId": "1973589292"}, {"name": "Zhiguo Shi", "authorId": "2144140139"}, {"name": "Zhefeng Gong", "authorId": "40384324"}, {"name": "Shibo He", "authorId": "35372976"}], "journal": {"name": "Entropy", "volume": "25"}, "paperId": "738c8198b637049bae7010ab1c8684cd7c4db15c", "abstract": "Knowledge of the structural properties of biological neural networks can help in understanding how particular responses and actions are generated. Recently, Witvliet et al. published the connectomes of eight isogenic Caenorhabditis elegans hermaphrodites at different postembryonic ages, from birth to adulthood. We analyzed the basic structural properties of these biological neural networks. From birth to adulthood, the asymmetry between in-degrees and out-degrees over the C. elegans neuronal network increased with age, in addition to an increase in the number of nodes and edges. The degree distributions were neither Poisson distributions nor pure power-law distributions. We have proposed a model of network evolution with different initial attractiveness for in-degrees and out-degrees of nodes and preferential attachment, which reproduces the asymmetry between in-degrees and out-degrees and similar degree distributions via the tuning of the initial attractiveness values. In this study, we present the well-preserved structural properties of C. elegans neuronal networks across development, and provide some insight into understanding the evolutionary processes of biological neural networks through a simple network model.", "citations": [], "externalIds": {"DOI": "10.3390/e25010051", "PubMed": "36673192", "CorpusId": 255251761, "PubMedCentral": "9857992"}, "isOpenAccess": true, "citationCount": 0, "fieldsOfStudy": ["Medicine"], "openAccessPdf": {"url": "https://www.mdpi.com/1099-4300/25/1/51/pdf?version=1672149702", "status": "GOLD"}, "citationStyles": {"bibtex": "@[''JournalArticle'']{Zhao2022ModelingTE,\n author = {Hongfei Zhao and Zhiguo Shi and Zhefeng Gong and Shibo He},\n booktitle = {Entropy},\n journal = {Entropy},\n title = {Modeling the Evolution of Biological Neural Networks Based on Caenorhabditis elegans Connectomes across Development},\n volume = {25},\n year = {2022}\n}\n"}, "referenceCount": 37, "publicationDate": "2022-12-27", "s2FieldsOfStudy": [{"source": "external", "category": "Medicine"}, {"source": "s2-fos-model", "category": "Biology"}], "publicationTypes": ["JournalArticle"], "publicationVenue": {"id": "8270cfe1-3713-4325-a7bd-c6a87eed889e", "url": "http://www.e-helvetica.nb.admin.ch/directAccess?callnumber=bel-155606", "issn": "1099-4300", "name": "Entropy", "type": "journal", "alternate_urls": ["http://www.mdpi.com/journal/entropy/", "http://nbn-resolving.de/urn/resolver.pl?urn=urn:nbn:ch:bel-155606", "https://www.mdpi.com/journal/entropy"]}, "influentialCitationCount": 0}', '2023-01-27 11:19:57.711451', '2023-01-27 11:19:57.711451', 'Modeling the Evolution of Biological Neural Networks Based on Caenorhabditis elegans Connectomes across Development', 2022, false, null, null, null, null);
INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('047e2633dbc775c4ec16173078b8d42c28fa2faf', '{"url": "https://www.semanticscholar.org/paper/047e2633dbc775c4ec16173078b8d42c28fa2faf", "year": 2022, "title": "Hebbian Continual Representation Learning", "venue": "ArXiv", "authors": [{"name": "P. Morawiecki", "authorId": "2837876"}, {"name": "Andrii Krutsylo", "authorId": "2107085005"}, {"name": "Maciej Wołczyk", "authorId": "73772332"}, {"name": "Marek Śmieja", "authorId": "49688210"}], "journal": {"name": "ArXiv", "volume": "abs/2207.04874"}, "paperId": "047e2633dbc775c4ec16173078b8d42c28fa2faf", "abstract": "Continual Learning aims to bring machine learning into a more realistic scenario, where tasks are learned sequentially and the i.i.d. assumption is not preserved. Although this setting is natural for biological systems, it proves very difﬁcult for machine learning models such as artiﬁcial neural networks. To reduce this performance gap, we investigate the question whether biologically inspired Hebbian learning is useful for tackling continual challenges. In particular, we highlight a realistic and often overlooked unsupervised setting, where the learner has to build representations without any supervision. By combining sparse neural networks with Hebbian learning principle, we build a simple yet effective alternative (HebbCL) to typical neural network models trained via the gradient descent. Due to Hebbian learning, the network have easily interpretable weights, which might be essential in critical application such as security or healthcare. We demonstrate the efﬁcacy of HebbCL in an unsupervised learning setting applied to MNIST and Omniglot datasets. We also adapt the algorithm to the supervised scenario and obtain promising results in the class-incremental learning.", "citations": [], "externalIds": {"DOI": "10.48550/arXiv.2207.04874", "DBLP": "journals/corr/abs-2207-04874", "ArXiv": "2207.04874", "CorpusId": 250426334}, "isOpenAccess": false, "citationCount": 0, "fieldsOfStudy": ["Computer Science"], "openAccessPdf": null, "citationStyles": {"bibtex": "@[''JournalArticle'']{Morawiecki2022HebbianCR,\n author = {P. Morawiecki and Andrii Krutsylo and Maciej Wołczyk and Marek Śmieja},\n booktitle = {ArXiv},\n journal = {ArXiv},\n title = {Hebbian Continual Representation Learning},\n volume = {abs/2207.04874},\n year = {2022}\n}\n"}, "referenceCount": 37, "publicationDate": "2022-06-28", "s2FieldsOfStudy": [{"source": "external", "category": "Computer Science"}, {"source": "s2-fos-model", "category": "Computer Science"}], "publicationTypes": ["JournalArticle"], "publicationVenue": null, "influentialCitationCount": 0}', '2023-01-27 11:20:01.334063', '2023-01-27 11:20:01.334063', 'Hebbian Continual Representation Learning', 2022, false, null, null, null, null);
INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('dd22dda4146046f940d8806359c0054ec76b267f', '{"url": "https://www.semanticscholar.org/paper/dd22dda4146046f940d8806359c0054ec76b267f", "year": 2022, "title": "Using Connectome Features to Constrain Echo State Networks", "venue": "ArXiv", "authors": [{"name": "J. Morra", "authorId": "9545446"}, {"name": "M. Daley", "authorId": "2061461427"}], "journal": {"name": "ArXiv", "volume": "abs/2206.02094"}, "paperId": "dd22dda4146046f940d8806359c0054ec76b267f", "abstract": "We report an improvement to the conventional Echo State Network (ESN), which already achieves competitive performance in one-dimensional time series prediction of dynamical systems. Our model – a 20 % -dense ESN with reservoir weights derived from a fruit ﬂy connectome (and from its bootstrapped distribution) – yields superior performance on a chaotic time series prediction task, and furthermore alleviates the ESN’s high-variance problem. We also ﬁnd that an arbitrary positioning of weights can degrade ESN performance and variance; and that this can be remedied in particular by employing connectome-derived weight positions. Herein we consider four connectome features – namely, the sparsity, positioning, distribution, and clustering of weights – and construct corresponding model classes (A, B, B 2 , C) from an appropriate null model ESN; one with its reservoir layer replaced by a fruit ﬂy connectivity matrix. After tuning relevant hyperparameters and selecting the best instance of each model class, we train and validate all models for multi-step prediction on size-variants (50, 250, 500, and 750 training input steps) of the Mackey-Glass chaotic time series; and compute their performance (Mean-Squared Error) and variance across train-validate trials.", "citations": [], "externalIds": {"DOI": "10.48550/arXiv.2206.02094", "DBLP": "journals/corr/abs-2206-02094", "ArXiv": "2206.02094", "CorpusId": 249394940}, "isOpenAccess": false, "citationCount": 0, "fieldsOfStudy": ["Computer Science"], "openAccessPdf": null, "citationStyles": {"bibtex": "@[''JournalArticle'']{Morra2022UsingCF,\n author = {J. Morra and M. Daley},\n booktitle = {ArXiv},\n journal = {ArXiv},\n title = {Using Connectome Features to Constrain Echo State Networks},\n volume = {abs/2206.02094},\n year = {2022}\n}\n"}, "referenceCount": 29, "publicationDate": "2022-06-05", "s2FieldsOfStudy": [{"source": "external", "category": "Computer Science"}, {"source": "s2-fos-model", "category": "Computer Science"}], "publicationTypes": ["JournalArticle"], "publicationVenue": null, "influentialCitationCount": 0}', '2023-01-27 11:20:04.965416', '2023-01-27 11:20:04.965416', 'Using Connectome Features to Constrain Echo State Networks', 2022, false, null, null, null, null);
INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('30c0218c2c53dc29a6ecb49ed08b2d5cf6da3dd5', '{"url": "https://www.semanticscholar.org/paper/30c0218c2c53dc29a6ecb49ed08b2d5cf6da3dd5", "year": 2022, "title": "Learning to modulate random weights can induce task-specific contexts for economical meta and continual learning", "venue": "ArXiv", "authors": [{"name": "Jinyung Hong", "authorId": "2152095222"}, {"name": "Theodore P. Pavlic", "authorId": "48184163"}], "journal": {"name": "ArXiv", "volume": "abs/2204.04297"}, "paperId": "30c0218c2c53dc29a6ecb49ed08b2d5cf6da3dd5", "abstract": ". Neural networks are vulnerable to catastrophic forgetting when data distributions are non-stationary during continual online learning; learning of a later task often leads to forgetting of an earlier task. One solution approach is model-agnostic continual meta-learning, whereby both task-speciﬁc and meta parameters are trained. Here, we depart from this view and introduce a novel neural-network architecture inspired by neuromodulation in biological nervous systems. Neuromodulation is the biological mechanism that dynamically controls and ﬁne-tunes synaptic dynamics to complement the behavioral context in real-time, which has received limited attention in machine learning. We introduce a single-hidden-layer network that learns only a relatively small context vector per task (task-speciﬁc parameters) that neuromodulates unchanging, randomized weights (meta parameters) that transform the input. We show that when task boundaries are available, this approach can eliminate catastrophic forgetting entirely while also drastically reducing the number of learnable parameters relative to other context-vector-based approaches. Furthermore, by combining this model with a simple meta-learning approach for inferring task identity, we demonstrate that the model can be generalized into a framework to perform continual learning without knowledge of task boundaries. Finally, we showcase the framework in a supervised continual online learning scenario and discuss the implications of the proposed formalism.", "citations": [], "externalIds": {"DOI": "10.48550/arXiv.2204.04297", "DBLP": "journals/corr/abs-2204-04297", "ArXiv": "2204.04297", "CorpusId": 248085183}, "isOpenAccess": false, "citationCount": 0, "fieldsOfStudy": ["Computer Science"], "openAccessPdf": null, "citationStyles": {"bibtex": "@[''JournalArticle'']{Hong2022LearningTM,\n author = {Jinyung Hong and Theodore P. Pavlic},\n booktitle = {ArXiv},\n journal = {ArXiv},\n title = {Learning to modulate random weights can induce task-specific contexts for economical meta and continual learning},\n volume = {abs/2204.04297},\n year = {2022}\n}\n"}, "referenceCount": 42, "publicationDate": "2022-04-08", "s2FieldsOfStudy": [{"source": "external", "category": "Computer Science"}, {"source": "s2-fos-model", "category": "Computer Science"}], "publicationTypes": ["JournalArticle"], "publicationVenue": null, "influentialCitationCount": 0}', '2023-01-27 11:20:08.570364', '2023-01-27 11:20:08.570364', 'Learning to modulate random weights can induce task-specific contexts for economical meta and continual learning', 2022, false, null, null, null, null);
INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('770144a0d32d5e66982d0cd7f6ec305de2af3b84', '{"url": "https://www.semanticscholar.org/paper/770144a0d32d5e66982d0cd7f6ec305de2af3b84", "year": 2021, "title": "Federated Nearest Neighbor Classification with a Colony of Fruit-Flies: With Supplement", "venue": "AAAI Conference on Artificial Intelligence", "authors": [{"name": "P. Ram", "authorId": "2944292"}, {"name": "Kaushik Sinha", "authorId": "40523939"}], "journal": {"pages": "8036-8044"}, "paperId": "770144a0d32d5e66982d0cd7f6ec305de2af3b84", "abstract": "The mathematical formalization of a neurological mechanism in the fruit-fly olfactory circuit as a locality sensitive hash (Flyhash) and bloom filter (FBF) has been recently proposed and \"reprogrammed\" for various learning tasks such as similarity search, outlier detection and text embeddings. We propose a novel reprogramming of this hash and bloom filter to emulate the canonical nearest neighbor classifier (NNC) in the challenging Federated Learning (FL) setup where training and test data are spread across parties and no data can leave their respective parties. Specifically, we utilize Flyhash and FBF to create the FlyNN classifier, and theoretically establish conditions where FlyNN matches NNC. We show how FlyNN is trained exactly in a FL setup with low communication overhead to produce FlyNNFL, and how it can be differentially private. Empirically, we demonstrate that (i) FlyNN matches NNC accuracy across 70 OpenML datasets, (ii) FlyNNFL training is highly scalable with low communication overhead, providing up to 8x speedup with 16 parties.", "citations": [], "externalIds": {"DOI": "10.1609/aaai.v36i7.20775", "DBLP": "journals/corr/abs-2112-07157", "ArXiv": "2112.07157", "CorpusId": 245131605}, "isOpenAccess": true, "citationCount": 0, "fieldsOfStudy": ["Computer Science"], "openAccessPdf": {"url": "https://ojs.aaai.org/index.php/AAAI/article/download/20775/20534", "status": "GOLD"}, "citationStyles": {"bibtex": "@[''JournalArticle'', ''Conference'']{Ram2021FederatedNN,\n author = {P. Ram and Kaushik Sinha},\n booktitle = {AAAI Conference on Artificial Intelligence},\n pages = {8036-8044},\n title = {Federated Nearest Neighbor Classification with a Colony of Fruit-Flies: With Supplement},\n year = {2021}\n}\n"}, "referenceCount": 54, "publicationDate": "2021-12-14", "s2FieldsOfStudy": [{"source": "external", "category": "Computer Science"}, {"source": "s2-fos-model", "category": "Computer Science"}], "publicationTypes": ["JournalArticle", "Conference"], "publicationVenue": {"id": "bdc2e585-4e48-4e36-8af1-6d859763d405", "url": "http://www.aaai.org/", "name": "AAAI Conference on Artificial Intelligence", "type": "conference", "alternate_names": ["National Conference on Artificial Intelligence", "National Conf Artif Intell", "AAAI Conf Artif Intell", "AAAI"]}, "influentialCitationCount": 0}', '2023-01-27 11:20:12.267562', '2023-01-27 11:20:12.267562', 'Federated Nearest Neighbor Classification with a Colony of Fruit-Flies: With Supplement', 2021, false, null, null, null, null);
INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('3e8a29d88f7227dba69ecd96424e41210ce6fdd2', '{"url": "https://www.semanticscholar.org/paper/3e8a29d88f7227dba69ecd96424e41210ce6fdd2", "year": 2022, "title": "Analogy-Guided Evolutionary Pretraining of Binary Word Embeddings", "venue": "AACL", "authors": [{"name": "R. A. Knipper", "authorId": "2192056783"}, {"name": "Md. Mahadi Hassan", "authorId": "2148762506"}, {"name": "Mehdi Sadi", "authorId": "2076720"}, {"name": "Shubhra (Santu) Karmaker", "authorId": "2692077"}], "journal": {"pages": "683-693"}, "paperId": "3e8a29d88f7227dba69ecd96424e41210ce6fdd2", "abstract": "As we begin to see low-powered computing paradigms (Neuromorphic Computing, Spiking Neural Networks, etc.) becoming more popular, learning binary word embeddings has become increasingly important for supporting NLP applications at the edge. Existing binary word embeddings are mostly derived from pretrained real-valued embeddings through different simple transformations, which often break the semantic consistency and the so-called “arithmetic” properties learned by the original, real-valued embeddings. This paper aims to address this limitation by introducing a new approach to learn binary embeddings from scratch, preserving the semantic relationships between words as well as the arithmetic properties of the embeddings themselves. To achieve this, we propose a novel genetic algorithm to learn the relationships between words from existing word analogy data-sets, carefully making sure that the arithmetic properties of the relationships are preserved. Evaluating our generated 16, 32, and 64-bit binary word embeddings on Mikolov’s word analogy task shows that more than 95% of the time, the best fit for the analogy is ranked in the top 5 most similar words in terms of cosine similarity.", "citations": [], "externalIds": {"ACL": "2022.aacl-main.52", "DBLP": "conf/ijcnlp/KnipperHSS22", "CorpusId": 253761965}, "isOpenAccess": false, "citationCount": 0, "fieldsOfStudy": ["Computer Science"], "openAccessPdf": null, "citationStyles": {"bibtex": "@[''JournalArticle'']{Knipper2022Analogy-GuidedEP,\n author = {R. A. Knipper and Md. Mahadi Hassan and Mehdi Sadi and Shubhra (Santu) Karmaker},\n booktitle = {AACL},\n pages = {683-693},\n title = {Analogy-Guided Evolutionary Pretraining of Binary Word Embeddings},\n year = {2022}\n}\n"}, "referenceCount": 45, "publicationDate": null, "s2FieldsOfStudy": [{"source": "external", "category": "Computer Science"}, {"source": "s2-fos-model", "category": "Computer Science"}], "publicationTypes": ["JournalArticle"], "publicationVenue": null, "influentialCitationCount": 0}', '2023-01-27 11:20:15.867575', '2023-01-27 11:20:15.867575', 'Analogy-Guided Evolutionary Pretraining of Binary Word Embeddings', 2022, false, null, null, null, null);
INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('88499bc8aaf92e9fa2d7d3ba5b0a1f20b3c07e0a', '{"url": "https://www.semanticscholar.org/paper/88499bc8aaf92e9fa2d7d3ba5b0a1f20b3c07e0a", "year": 2021, "title": "Learning Sparse Binary Code for Maximum Inner Product Search", "venue": "International Conference on Information and Knowledge Management", "authors": [{"name": "Changyi Ma", "authorId": "11617784"}, {"name": "Fangchen Yu", "authorId": "2155388233"}, {"name": "Yueyao Yu", "authorId": "72529955"}, {"name": "Wenye Li", "authorId": "13860615"}], "journal": {"name": "Proceedings of the 30th ACM International Conference on Information & Knowledge Management"}, "paperId": "88499bc8aaf92e9fa2d7d3ba5b0a1f20b3c07e0a", "abstract": "Maximum inner product search (MIPS), combined with the hashing method, has become a standard solution to similarity search problems. It often achieves an order of magnitude speedup over nearest neighbor search (NNS) under similar settings. Motivated by the work and achievements along this line, in this paper, we developed a sparse binary hashing method for MIPS to preserve the pairwise similarities with the support of two asymmetric hash functions. We proposed a simple and efficient algorithm that learns two hash functions for the query database and the search database respectively. We conducted experiments to evaluate the proposed method, relying on image retrieval tasks on four benchmark datasets. The empirical results clearly demonstrated the algorithm''s promising potential on practical applications in terms of search accuracy and scalability.", "citations": [{"title": "New Secure Sparse Inner Product with Applications to Machine Learning", "paperId": "db0aedd0429790e24b2d9fb5a776597a589f4fe2"}], "externalIds": {"DOI": "10.1145/3459637.3482132", "DBLP": "conf/cikm/MaYY021", "CorpusId": 240230606}, "isOpenAccess": false, "citationCount": 1, "fieldsOfStudy": ["Computer Science"], "openAccessPdf": null, "citationStyles": {"bibtex": "@[''JournalArticle'', ''Book'', ''Conference'']{Ma2021LearningSB,\n author = {Changyi Ma and Fangchen Yu and Yueyao Yu and Wenye Li},\n booktitle = {International Conference on Information and Knowledge Management},\n journal = {Proceedings of the 30th ACM International Conference on Information & Knowledge Management},\n title = {Learning Sparse Binary Code for Maximum Inner Product Search},\n year = {2021}\n}\n"}, "referenceCount": 45, "publicationDate": "2021-10-26", "s2FieldsOfStudy": [{"source": "external", "category": "Computer Science"}, {"source": "s2-fos-model", "category": "Computer Science"}], "publicationTypes": ["JournalArticle", "Book", "Conference"], "publicationVenue": {"id": "7431ff67-91dc-41fa-b322-1b1ca657025f", "url": "http://www.cikm.org/", "name": "International Conference on Information and Knowledge Management", "type": "conference", "alternate_names": ["Conference on Information and Knowledge Management", "Conf Inf Knowl Manag", "Int Conf Inf Knowl Manag", "CIKM"]}, "influentialCitationCount": 0}', '2023-01-27 11:20:19.486250', '2023-01-27 11:20:19.486250', 'Learning Sparse Binary Code for Maximum Inner Product Search', 2021, false, null, null, null, null);
INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('ae775979a3a94d9ce20256028eaf054fccf8dfa7', '{"url": "https://www.semanticscholar.org/paper/ae775979a3a94d9ce20256028eaf054fccf8dfa7", "year": 2021, "title": "Fine-Tuning Transformer-XL on Clinical Natural Language Processing", "venue": "", "authors": [{"name": "X. Zhan", "authorId": "83235944"}, {"name": "Yiheng Li", "authorId": "2110453456"}], "journal": null, "paperId": "ae775979a3a94d9ce20256028eaf054fccf8dfa7", "abstract": "Although many applications based-on mining clinical free text have been developed, the state-of-the-art transformer-based models have not been applied in clinical NLP. We aim to address the long-range dependencies in clinical free text caused by different sections with the latest Transformer-XL model by fine-tuning it on MIMIC-III clinical text. Having requested and cleaned the MIMIC-III clinical text based on self-developed rules, we prepared the data for training classifiers on diagnostic code prediction of 8 common cardiovascular diseases. We used huggingface API to fine-tune and evaluate Transformer-XL model on MIMIC-III dataset and compared the results with baseline methods including bag-of-words and TF-IDF. And the Transformer-XL outperformed the Bag-of-Words and TFIDF on 3 of 6 tasks, on which we have already got the results. Furthermore, the Transformer-XL was only fine-tuned for 1 epoch, and therefore we believe there is a promising potential for a better fine-tuned Transformer-XL to better predict the diagnostic codes accurately. The better accuracy of diagnostic codes aids in the structuring of free-text clinical notes, which can be better and easier for downstream machine learning tasks, such as survival predictions and multi-modality data fusion, because, the structured diagnostic codes can be fed into machine learning models than the unstructured data. 1 Key Information to include • External collaborators (if you have any): NA • External mentor (if you have any): Olivier Gevaert • Sharing project: NA", "citations": [], "externalIds": {"CorpusId": 235344288}, "isOpenAccess": false, "citationCount": 0, "fieldsOfStudy": null, "openAccessPdf": null, "citationStyles": {"bibtex": "@None{Zhan2021Fine-TuningTO,\n author = {X. Zhan and Yiheng Li},\n title = {Fine-Tuning Transformer-XL on Clinical Natural Language Processing},\n year = {2021}\n}\n"}, "referenceCount": 15, "publicationDate": null, "s2FieldsOfStudy": [{"source": "s2-fos-model", "category": "Computer Science"}], "publicationTypes": null, "publicationVenue": null, "influentialCitationCount": 0}', '2023-01-27 11:20:22.850138', '2023-01-27 11:20:22.850138', 'Fine-Tuning Transformer-XL on Clinical Natural Language Processing', 2021, false, null, null, null, null);
INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('c0a6bbd6edb02f00e9048ecfd4e49a01565176ca', '{"url": "https://www.semanticscholar.org/paper/c0a6bbd6edb02f00e9048ecfd4e49a01565176ca", "year": 2023, "title": "The making of the Drosophila mushroom body", "venue": "Frontiers in Physiology", "authors": [{"name": "Suewei Lin", "authorId": "47128088"}], "journal": {"volume": "14"}, "paperId": "c0a6bbd6edb02f00e9048ecfd4e49a01565176ca", "abstract": "The mushroom body (MB) is a computational center in the Drosophila brain. The intricate neural circuits of the mushroom body enable it to store associative memories and process sensory and internal state information. The mushroom body is composed of diverse types of neurons that are precisely assembled during development. Tremendous efforts have been made to unravel the molecular and cellular mechanisms that build the mushroom body. However, we are still at the beginning of this challenging quest, with many key aspects of mushroom body assembly remaining unexplored. In this review, I provide an in-depth overview of our current understanding of mushroom body development and pertinent knowledge gaps.", "citations": [], "externalIds": {"DOI": "10.3389/fphys.2023.1091248", "CorpusId": 255751492}, "isOpenAccess": true, "citationCount": 0, "fieldsOfStudy": null, "openAccessPdf": {"url": "https://www.frontiersin.org/articles/10.3389/fphys.2023.1091248/pdf", "status": "GOLD"}, "citationStyles": {"bibtex": "@[''Review'']{Lin2023TheMO,\n author = {Suewei Lin},\n booktitle = {Frontiers in Physiology},\n title = {The making of the Drosophila mushroom body},\n volume = {14},\n year = {2023}\n}\n"}, "referenceCount": 182, "publicationDate": "2023-01-13", "s2FieldsOfStudy": [{"source": "s2-fos-model", "category": "Biology"}], "publicationTypes": ["Review"], "publicationVenue": {"id": "e28a224b-8ddb-49a6-82d2-17f23c68b30c", "url": "https://www.frontiersin.org/journals/physiology", "issn": "1664-042X", "name": "Frontiers in Physiology", "type": "journal", "alternate_urls": ["http://www.frontiersin.org/physiology/about", "http://frontiersin.org/physiology"], "alternate_names": ["Front Physiol"]}, "influentialCitationCount": 0}', '2023-01-27 11:20:26.482123', '2023-01-27 11:20:26.482123', 'The making of the Drosophila mushroom body', 2023, false, null, null, null, null);
INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('9fdf220ab9abbb142010e79b7cb3e45e4791be3a', '{"url": "https://www.semanticscholar.org/paper/9fdf220ab9abbb142010e79b7cb3e45e4791be3a", "year": 2022, "title": "Visual projection neuron convergence and compensation in developing sensorimotor circuits in the Drosophila optic glomeruli", "venue": "bioRxiv", "authors": [{"name": "Brennan W. McFarland", "authorId": "90906816"}, {"name": "HyoJong Jang", "authorId": "2176984113"}, {"name": "Natalie Smolin", "authorId": "2151621871"}, {"name": "T. Godenschwege", "authorId": "4015232"}, {"name": "A. Nern", "authorId": "4469690"}, {"name": "Y. Kurmangaliyev", "authorId": "6993542"}, {"name": "Catherine R. von Reyn", "authorId": "5169555"}], "journal": {"name": "bioRxiv"}, "paperId": "9fdf220ab9abbb142010e79b7cb3e45e4791be3a", "abstract": "Visual features detected by the early visual system must be combined into higher order representations to guide behavioral decision. Although key developmental mechanisms that enable the separation of visual feature channels in early visual circuits have been discovered, relatively little is known about the mechanisms that underlie their convergence in later stages of visual processing. Here we explore the development of a functionally well-characterized sensorimotor circuit in Drosophila melanogaster, the convergence of visual projection neurons (VPNs) onto the dendrites of a large descending neuron called the giant fiber (GF). We find two VPNs encoding different visual features that target the same giant fiber dendrite establish their territories on the dendrite, in part, through sequential axon arrival during development prior to synaptogenesis. Physical occupancy is important to maintain territories, as we find the ablation of one VPN results in expanded dendrite territory of the remaining VPN, and that this compensation enables the GF to remain responsive to ethologically relevant visual stimuli. Our data highlight temporal mechanisms for visual feature convergence and promote the GF circuit, and the Drosophila optic glomeruli where VPN to GF connectivity resides, as an ideal developmental model for investigating complex wiring programs and plasticity in visual feature convergence.", "citations": [], "externalIds": {"DOI": "10.1101/2022.08.20.504653", "CorpusId": 251815491}, "isOpenAccess": true, "citationCount": 0, "fieldsOfStudy": ["Biology"], "openAccessPdf": {"url": "https://www.biorxiv.org/content/biorxiv/early/2022/08/22/2022.08.20.504653.full.pdf", "status": "GREEN"}, "citationStyles": {"bibtex": "@None{McFarland2022VisualPN,\n author = {Brennan W. McFarland and HyoJong Jang and Natalie Smolin and T. Godenschwege and A. Nern and Y. Kurmangaliyev and Catherine R. von Reyn},\n booktitle = {bioRxiv},\n journal = {bioRxiv},\n title = {Visual projection neuron convergence and compensation in developing sensorimotor circuits in the Drosophila optic glomeruli},\n year = {2022}\n}\n"}, "referenceCount": 119, "publicationDate": "2022-08-22", "s2FieldsOfStudy": [{"source": "external", "category": "Biology"}, {"source": "s2-fos-model", "category": "Biology"}], "publicationTypes": null, "publicationVenue": {"id": "027ffd21-ebb0-4af8-baf5-911124292fd0", "url": "http://biorxiv.org/", "name": "bioRxiv", "type": "journal"}, "influentialCitationCount": 0}', '2023-01-27 11:20:30.099323', '2023-01-27 11:20:30.099323', 'Visual projection neuron convergence and compensation in developing sensorimotor circuits in the Drosophila optic glomeruli', 2022, false, null, null, null, null);
