INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('28ac216ee31eead6d8f75cfd1a2f3caa57a23a68', '{"url": "https://www.semanticscholar.org/paper/28ac216ee31eead6d8f75cfd1a2f3caa57a23a68", "year": 2022, "title": "Diversification of neuropil organization in specialized and broadly tuned olfactory glomerular circuits in Drosophila melanogaster", "venue": "bioRxiv", "authors": [{"name": "Lydia Gruber", "authorId": "144153860"}, {"name": "R. Cantera", "authorId": "5194309"}, {"name": "M. W. Pleijzier", "authorId": "50817661"}, {"name": "B. Hansson", "authorId": "2199148"}, {"name": "J. Rybak", "authorId": "2052772"}], "journal": {"name": "bioRxiv"}, "paperId": "28ac216ee31eead6d8f75cfd1a2f3caa57a23a68", "abstract": "To manage the great complexity of detecting and identifying olfactory cues, the insect olfactory system has evolved two main strategies: combinatorial coding and specialized, narrowly tuned olfactory pathways. In combinatorial coding, odorants are encoded by activation of multiple, broadly tuned olfactory sensory neurons that innervate distinct sets of glomeruli. In specialized olfactory pathways, information regarding a single or a few odorants is processed in a discrete, narrowly tuned circuit within a dedicated glomerulus. Here, we compared the narrowly tuned glomerulus DA2 with the broadly tuned glomerulus DL5 at the ultrastructural level, by using volume based focused ion beam scanning electron microscopy. We provide a detailed analysis of neuronal innervation, synaptic composition as well as a circuit diagram of the major glomerular cell types: olfactory sensory neurons (OSNs), uniglomerular projection neurons (uPNs) and multiglomerular neurons (MGNs). By comparing our data with a previously mapped narrowly tuned glomerulus (VA1v), we disclose putative generic features of narrowly tuned glomerular circuits: a high density of neuronal fibers and synapses, a low degree of sensory lateralization, strong axo-axonic connections between OSNs as well as dendro-dendritic connections between uPNs, and a low degree of presynaptic inhibition at the OSN axons. We also show a unique property of the large uPN dendrite in DL5, which forms substantial amount of autapses.", "citations": [], "externalIds": {"DOI": "10.1101/2022.09.30.510181", "CorpusId": 252716718}, "isOpenAccess": true, "citationCount": 0, "fieldsOfStudy": ["Biology"], "openAccessPdf": {"url": "https://www.biorxiv.org/content/biorxiv/early/2022/10/02/2022.09.30.510181.full.pdf", "status": "GREEN"}, "citationStyles": {"bibtex": "@None{Gruber2022DiversificationON,\n author = {Lydia Gruber and R. Cantera and M. W. Pleijzier and B. Hansson and J. Rybak},\n booktitle = {bioRxiv},\n journal = {bioRxiv},\n title = {Diversification of neuropil organization in specialized and broadly tuned olfactory glomerular circuits in Drosophila melanogaster},\n year = {2022}\n}\n"}, "referenceCount": 176, "publicationDate": "2022-10-02", "s2FieldsOfStudy": [{"source": "external", "category": "Biology"}, {"source": "s2-fos-model", "category": "Biology"}], "publicationTypes": null, "publicationVenue": {"id": "027ffd21-ebb0-4af8-baf5-911124292fd0", "url": "http://biorxiv.org/", "name": "bioRxiv", "type": "journal"}, "influentialCitationCount": 0}', '2023-01-27 11:20:51.798004', '2023-01-27 11:20:51.798004', 'Diversification of neuropil organization in specialized and broadly tuned olfactory glomerular circuits in Drosophila melanogaster', 2022, false, null, null, null, null);
INSERT INTO public.paper (paper_id, s2ag_json_text, created, updated, title, pub_year, key_paper, pdf_url, can_get_pdf, local_pdf_location, notes) VALUES ('0010e084c682454aef23a37eb89228c310669aaa', '{"url": "https://www.semanticscholar.org/paper/0010e084c682454aef23a37eb89228c310669aaa", "year": 2022, "title": "Bisected graph matching improves automated pairing of bilaterally homologous neurons from connectomes", "venue": "bioRxiv", "authors": [{"name": "B. Pedigo", "authorId": "107899997"}, {"name": "Michael Winding", "authorId": "4144897"}, {"name": "Carey E. Priebe", "authorId": "2068800231"}, {"name": "J. Vogelstein", "authorId": "1717958"}], "journal": {"name": "bioRxiv"}, "paperId": "0010e084c682454aef23a37eb89228c310669aaa", "abstract": "Graph matching algorithms attempt to find the best correspondence between the nodes of two networks. These techniques have previously been used to match individual neurons in nanoscale connectomes – in particular, to find pairings of neurons across hemispheres. However, since graph matching techniques deal specifically with two isolated networks, they have only utilized the ipsilateral (same hemisphere) subgraphs when performing the matching. Here, we present a modification to a state-of-the-art graph matching algorithm which allows it to solve what we call the bisected graph matching problem. This modification allows us to also use connections between the brain hemispheres when predicting neuron pairs. Via simulations and real connectome datasets we show that when edge correlation is present between the contralateral (between hemisphere) subgraphs, this approach improves matching accuracy. We expect that our proposed method will improve future endeavors to accurately match neurons across hemispheres in connectomes, and be useful in other applications where the bisected graph matching problem arises.", "citations": [{"title": "Generative network modeling reveals quantitative definitions of bilateral symmetry exhibited by a w